# ClassLoader.ps1 - Smart dependency-aware class loading system
# Replaces brittle hardcoded file lists with auto-discovery and intelligent ordering

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Smart class loader that auto-discovers and loads PowerShell class files with dependency resolution

.DESCRIPTION
Features:
- Auto-discovers all .ps1 files in specified directories
- Respects load order via priority system
- Multi-pass loading with dependency retry logic
- Excludes test files automatically
- Detailed logging for troubleshooting
- Handles circular dependencies gracefully

.EXAMPLE
$loader = [ClassLoader]::new($PSScriptRoot)
$loader.AddDirectory("widgets", 100)
$loader.AddDirectory("base", 50)
$loader.LoadAll()
#>
class ClassLoader {
    [System.Collections.ArrayList]$LoadQueue = @()
    [System.Collections.ArrayList]$LoadedFiles = @()
    [System.Collections.ArrayList]$FailedFiles = @()
    [hashtable]$LoadStats = @{}
    [string]$BaseDirectory
    [int]$MaxRetries = 3
    [bool]$ExcludeTests = $true
    [bool]$VerboseLogging = $false

    ClassLoader([string]$baseDir) {
        $this.BaseDirectory = $baseDir
        $this.LoadStats = @{
            TotalFiles = 0
            Loaded = 0
            Failed = 0
            Skipped = 0
            Retries = 0
        }
    }

    <#
    .SYNOPSIS
    Add a directory to the load queue

    .PARAMETER relativePath
    Path relative to BaseDirectory (e.g., "widgets", "base", "screens")

    .PARAMETER priority
    Lower number = loaded first (e.g., base=10, widgets=50, screens=100)

    .PARAMETER recursive
    Whether to include subdirectories
    #>
    [void] AddDirectory([string]$relativePath, [int]$priority, [bool]$recursive = $false) {
        $fullPath = Join-Path $this.BaseDirectory $relativePath

        if (-not (Test-Path $fullPath)) {
            $this.Log("WARNING: Directory not found: $fullPath", "WARN")
            return
        }

        # Discover files
        $files = if ($recursive) {
            Get-ChildItem -Path $fullPath -Filter "*.ps1" -Recurse -File
        } else {
            Get-ChildItem -Path $fullPath -Filter "*.ps1" -File
        }

        foreach ($file in $files) {
            # Skip test files if configured
            if ($this.ExcludeTests -and $file.Name -match '^Test.*\.ps1$') {
                $this.Log("Skipping test file: $($file.Name)", "DEBUG")
                $this.LoadStats.Skipped++
                continue
            }

            # Check for priority override in file header
            $filePriority = $this.ExtractPriorityFromFile($file.FullName)
            if ($null -ne $filePriority) {
                $priority = $filePriority
            }

            $this.LoadQueue.Add(@{
                Path = $file.FullName
                Name = $file.Name
                Priority = $priority
                Directory = $relativePath
                Retries = 0
                LastError = $null
            }) | Out-Null

            $this.LoadStats.TotalFiles++
        }

        $fileCount = @($files).Count
        $this.Log("Discovered $fileCount files in $relativePath (priority=$priority)", "INFO")
    }

    <#
    .SYNOPSIS
    Extract load priority from file header comment
    Files can specify: # LoadPriority: 25
    #>
    [object] ExtractPriorityFromFile([string]$filePath) {
        try {
            $content = Get-Content -Path $filePath -TotalCount 10 -ErrorAction SilentlyContinue
            foreach ($line in $content) {
                if ($line -match '^\s*#\s*LoadPriority:\s*(\d+)') {
                    return [int]$matches[1]
                }
            }
        } catch {
            # Ignore errors reading file header
        }
        return $null
    }

    <#
    .SYNOPSIS
    Load all queued files with dependency resolution
    #>
    [void] LoadAll() {
        $this.Log("=== Starting ClassLoader ===", "INFO")
        $this.Log("Total files queued: $($this.LoadStats.TotalFiles)", "INFO")

        # Sort by priority (lower number first)
        $sortedQueue = $this.LoadQueue | Sort-Object { $_.Priority }, { $_.Name }

        # Multi-pass loading with retry logic
        $pass = 1
        $remainingFiles = [System.Collections.ArrayList]::new($sortedQueue)

        while ($remainingFiles.Count -gt 0 -and $pass -le $this.MaxRetries) {
            $this.Log("--- Load Pass $pass ($($remainingFiles.Count) files remaining) ---", "INFO")

            $stillFailing = [System.Collections.ArrayList]::new()

            foreach ($fileInfo in $remainingFiles) {
                $success = $this.LoadFile($fileInfo)

                if ($success) {
                    $this.LoadedFiles.Add($fileInfo) | Out-Null
                    $this.LoadStats.Loaded++
                } else {
                    # Check if error is due to missing type (dependency issue)
                    if ($fileInfo.LastError -match 'Unable to find type') {
                        if ($fileInfo.Retries -lt $this.MaxRetries) {
                            $fileInfo.Retries++
                            $stillFailing.Add($fileInfo) | Out-Null
                            $this.LoadStats.Retries++
                            $this.Log("Will retry: $($fileInfo.Name) (attempt $($fileInfo.Retries + 1))", "DEBUG")
                        } else {
                            $this.FailedFiles.Add($fileInfo) | Out-Null
                            $this.LoadStats.Failed++
                            $this.Log("FAILED after $($this.MaxRetries) attempts: $($fileInfo.Name)", "ERROR")
                            $this.Log("  Error: $($fileInfo.LastError)", "ERROR")
                        }
                    } else {
                        # Non-dependency error, fail immediately
                        $this.FailedFiles.Add($fileInfo) | Out-Null
                        $this.LoadStats.Failed++
                        $this.Log("FAILED (non-dependency error): $($fileInfo.Name)", "ERROR")
                        $this.Log("  Error: $($fileInfo.LastError)", "ERROR")
                    }
                }
            }

            # If no progress made this pass, break to avoid infinite loop
            if ($stillFailing.Count -eq $remainingFiles.Count) {
                $this.Log("No progress made in pass $pass - circular dependency or missing files", "WARN")
                foreach ($f in $stillFailing) {
                    $this.FailedFiles.Add($f) | Out-Null
                    $this.LoadStats.Failed++
                }
                break
            }

            $remainingFiles = $stillFailing
            $pass++
        }

        $this.PrintSummary()
    }

    <#
    .SYNOPSIS
    Load a single file with error handling
    #>
    [bool] LoadFile([hashtable]$fileInfo) {
        try {
            # Use dot-sourcing to load in current scope
            . $fileInfo.Path

            $this.Log("✓ Loaded: $($fileInfo.Name)", "DEBUG")
            return $true

        } catch {
            $fileInfo.LastError = $_.Exception.Message
            return $false
        }
    }

    <#
    .SYNOPSIS
    Print loading summary
    #>
    [void] PrintSummary() {
        $this.Log("=== ClassLoader Summary ===", "INFO")
        $this.Log("Total files discovered: $($this.LoadStats.TotalFiles)", "INFO")
        $this.Log("Successfully loaded: $($this.LoadStats.Loaded)", "INFO")
        $this.Log("Failed: $($this.LoadStats.Failed)", "INFO")
        $this.Log("Skipped (tests): $($this.LoadStats.Skipped)", "INFO")
        $this.Log("Total retries: $($this.LoadStats.Retries)", "INFO")

        if ($this.FailedFiles.Count -gt 0) {
            $this.Log("--- Failed Files ---", "ERROR")
            foreach ($f in $this.FailedFiles) {
                $this.Log("  ✗ $($f.Name): $($f.LastError)", "ERROR")
            }
        }
    }

    <#
    .SYNOPSIS
    Log message to console and file
    #>
    [void] Log([string]$message, [string]$level) {
        # Skip debug messages unless verbose
        if ($level -eq "DEBUG" -and -not $this.VerboseLogging) {
            return
        }

        # Log to PMC log file if available
        if ($global:PmcTuiLogFile) {
            $timestamp = Get-Date -Format "yyyy-MM-dd HH:mm:ss.fff"
            $logLine = "[$timestamp] [ClassLoader][$level] $message"
            Add-Content -Path $global:PmcTuiLogFile -Value $logLine
        }

        # Also log errors to file
        if ($level -in @("ERROR", "WARN")) {
            # Add-Content -Path "/tmp/pmc-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] [ClassLoader] [$level] $message"
        }
    }

    <#
    .SYNOPSIS
    Enable verbose logging for troubleshooting
    #>
    [void] EnableVerbose() {
        $this.VerboseLogging = $true
    }
}

<#
.SYNOPSIS
Helper function to create and configure a standard PMC class loader

.DESCRIPTION
Creates a ClassLoader configured with standard PMC directory structure:
- theme (priority 5) - Theme system
- widgets (priority 10) - Base widgets
- layout (priority 20) - Layout managers
- base (priority 30) - Base screen classes
- services (priority 40) - Service classes
- screens (priority 50) - Concrete screens (lazy-loaded via menu)
- helpers (priority 60) - Helper functions

.PARAMETER baseDirectory
Root directory (typically $PSScriptRoot of Start-PmcTUI.ps1)

.PARAMETER loadScreens
Whether to pre-load all screens (default: $false for lazy loading)

.EXAMPLE
$loader = New-PmcClassLoader $PSScriptRoot
$loader.LoadAll()
#>
function New-PmcClassLoader {
    param(
        [string]$baseDirectory,
        [bool]$loadScreens = $false,
        [bool]$verbose = $false
    )

    $loader = [ClassLoader]::new($baseDirectory)

    if ($verbose -or ($global:PmcTuiLogLevel -ge 3)) {
        $loader.EnableVerbose()
    }

    # Add directories in dependency order (lower priority = loaded first)
    $loader.AddDirectory("theme", 5)
    $loader.AddDirectory("widgets", 10)
    $loader.AddDirectory("layout", 20)
    $loader.AddDirectory("base", 30)
    $loader.AddDirectory("services", 40)

    # Screens are typically lazy-loaded via MenuRegistry
    # But can be pre-loaded for debugging or if lazy-loading causes issues
    if ($loadScreens) {
        $loader.AddDirectory("screens", 50)
    }

    $loader.AddDirectory("helpers", 60)

    return $loader
}

﻿# ServiceContainer.ps1 - Dependency Injection Container
# Manages lifecycle and dependencies of all services, screens, and widgets

using namespace System.Collections.Generic

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Dependency injection container for PMC TUI

.DESCRIPTION
ServiceContainer manages registration and resolution of all dependencies:
- Services (TaskStore, MenuRegistry, etc.)
- Configuration (Theme, Config)
- Screens (lazy loaded on demand)
- Widgets (created per screen)

Solves initialization order, circular dependencies, and timing issues.

.EXAMPLE
$container = [ServiceContainer]::new()
$container.Register('Theme', { Initialize-PmcThemeSystem; return $global:PmcTheme })
$container.Register('TaskStore', { param($c) return [TaskStore]::GetInstance() })
$theme = $container.Resolve('Theme')
#>
class ServiceContainer {
    # Registered service factories
    hidden [hashtable]$_factories = @{}

    # Resolved singleton instances
    hidden [hashtable]$_singletons = @{}

    # Registration flags
    hidden [hashtable]$_isSingleton = @{}

    # Resolution stack (for circular dependency detection)
    hidden [List[string]]$_resolutionStack = [List[string]]::new()

    <#
    .SYNOPSIS
    Register a service with a factory function

    .PARAMETER name
    Service name (e.g., 'Theme', 'TaskStore', 'TaskListScreen')

    .PARAMETER factory
    Scriptblock that creates the service. Receives container as parameter.

    .PARAMETER singleton
    If true, service is created once and cached. Default: true

    .EXAMPLE
    $container.Register('Theme', {
        Initialize-PmcThemeSystem
        return Get-PmcState -Section 'Display' | Select-Object -ExpandProperty Theme
    })

    .EXAMPLE
    $container.Register('TaskStore', {
        param($container)
        $theme = $container.Resolve('Theme')  # Depend on theme
        return [TaskStore]::GetInstance()
    })
    #>
    [void] Register([string]$name, [scriptblock]$factory, [bool]$singleton = $true) {
        if ($this._factories.ContainsKey($name)) {
            if ($global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [WARN] ServiceContainer: Re-registering service '$name'"
            }
        }

        $this._factories[$name] = $factory
        $this._isSingleton[$name] = $singleton

        if ($global:PmcTuiLogFile) {
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] ServiceContainer: Registered '$name' (singleton=$singleton)"
        }
    }

    <#
    .SYNOPSIS
    Resolve a service by name

    .PARAMETER name
    Service name to resolve

    .OUTPUTS
    Service instance

    .EXAMPLE
    $theme = $container.Resolve('Theme')
    $screen = $container.Resolve('TaskListScreen')
    #>
    [object] Resolve([string]$name) {
        # Check if already resolved (singleton)
        if ($this._isSingleton[$name] -and $this._singletons.ContainsKey($name)) {
            return $this._singletons[$name]
        }

        # Check if service registered
        if (-not $this._factories.ContainsKey($name)) {
            $error = "Service '$name' not registered in container"
            if ($global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] ServiceContainer: $error"
            }
            throw $error
        }

        # Detect circular dependencies
        if ($this._resolutionStack.Contains($name)) {
            $chain = ($this._resolutionStack -join ' -> ') + " -> $name"
            $error = "Circular dependency detected: $chain"
            if ($global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] ServiceContainer: $error"
            }
            throw $error
        }

        # Add to resolution stack
        $this._resolutionStack.Add($name)

        try {
            if ($global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] ServiceContainer: Resolving '$name'..."
            }

            # Invoke factory (pass container for nested resolution)
            $factory = $this._factories[$name]
            $instance = & $factory $this

            # Cache if singleton
            if ($this._isSingleton[$name]) {
                $this._singletons[$name] = $instance
            }

            if ($global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] ServiceContainer: Resolved '$name' successfully"
            }

            return $instance

        } catch {
            if ($global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] ServiceContainer: Failed to resolve '$name': $_"
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] Stack trace: $($_.ScriptStackTrace)"
            }
            throw
        } finally {
            # CRITICAL: Remove from resolution stack (cleanup on all paths)
            # Extra safety: wrap in try-catch to prevent double-fault
            try {
                $this._resolutionStack.Remove($name) | Out-Null
            } catch {
                # Resolution stack cleanup failed - log but don't throw
                if ($global:PmcTuiLogFile) {
                    Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] ServiceContainer: Failed to clean resolution stack: $_"
                }
            }
        }
    }

    <#
    .SYNOPSIS
    Check if a service is registered

    .PARAMETER name
    Service name

    .OUTPUTS
    Boolean
    #>
    [bool] IsRegistered([string]$name) {
        return $this._factories.ContainsKey($name)
    }

    <#
    .SYNOPSIS
    Get all registered service names

    .OUTPUTS
    Array of service names
    #>
    [array] GetRegisteredServices() {
        return $this._factories.Keys
    }

    <#
    .SYNOPSIS
    Clear all resolved singletons (for testing/reset)
    #>
    [void] ClearResolved() {
        $this._singletons.Clear()
        if ($global:PmcTuiLogFile) {
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] ServiceContainer: Cleared all resolved singletons"
        }
    }
}

# PmcApplication - Main application wrapper integrating PMC widgets with SpeedTUI
# Handles rendering engine, event loop, and screen management

using namespace System
using namespace System.Collections.Generic

Set-StrictMode -Version Latest

# NOTE: SpeedTUI, widgets, layout, and theme are loaded by Start-PmcTUI.ps1
# Do not load them again here to avoid circular dependencies and duplicate loading

<#
.SYNOPSIS
Main application class for PMC TUI

.DESCRIPTION
PmcApplication manages:
- SpeedTUI rendering engine (OptimizedRenderEngine)
- Screen stack and navigation
- Event loop and input handling
- Layout management
- Theme management

.EXAMPLE
$container = [ServiceContainer]::new()
$app = [PmcApplication]::new($container)
$app.PushScreen($taskScreen)
$app.Run()
#>
class PmcApplication {
    # === Core Components ===
    [object]$RenderEngine
    [object]$LayoutManager
    [object]$ThemeManager
    [object]$Container        # ServiceContainer for dependency injection

    # === Screen Management ===
    [object]$ScreenStack      # Stack of PmcScreen objects
    [object]$CurrentScreen = $null   # Currently active screen

    # === Terminal State ===
    [int]$TermWidth = 80
    [int]$TermHeight = 24
    [bool]$Running = $false

    # === Rendering State ===
    [bool]$IsDirty = $true  # Dirty flag - true when redraw needed
    [int]$RenderErrorCount = 0  # Track consecutive render errors for recovery

    # === Automation Support ===
    [string]$AutomationCommandFile = ""  # Path to command file for automation
    [string]$AutomationOutputFile = ""   # Path to output capture file
    [System.Collections.Queue]$CommandQueue = $null  # Queue of simulated key presses
    [bool]$AutomationMode = $false       # Enable automation features

    # === Event Handlers ===
    [scriptblock]$OnTerminalResize = $null
    [scriptblock]$OnError = $null

    # === Constructor ===
    PmcApplication([object]$container) {
        # Store container for passing to screens
        $this.Container = $container
        # Initialize render engine (HybridRenderEngine with Layout System)
        try {
            $this.RenderEngine = New-Object HybridRenderEngine
            if ($null -eq $this.RenderEngine) {
                throw "Failed to create HybridRenderEngine instance"
            }
            $this.RenderEngine.Initialize()
        }
        catch {
            # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcApplication] FATAL: Failed to initialize RenderEngine: $($_.Exception.Message)"
            throw
        }

        # Initialize layout manager
        $this.LayoutManager = New-Object PmcLayoutManager

        # Initialize theme manager
        $this.ThemeManager = $container.Resolve('ThemeManager')

        # Initialize screen stack
        $this.ScreenStack = New-Object "System.Collections.Generic.Stack[object]"

        # Get terminal size
        $this._UpdateTerminalSize()
    }

    # === Screen Management ===

    <#
    .SYNOPSIS
    Push a screen onto the stack and make it active

    .PARAMETER screen
    Screen object to push (should have Render() and HandleInput() methods)
    #>
    [void] PushScreen([object]$screen) {
        # Deactivate current screen
        if ($this.CurrentScreen) {
            if ($this.CurrentScreen.PSObject.Methods['OnExit']) {
                $this.CurrentScreen.OnDoExit()
            }
        }

        # DIFFERENTIAL RENDERING: Do NOT clear screen - let the new screen
        # overwrite the old content naturally. This prevents flicker.

        # Push new screen
        $this.ScreenStack.Push($screen)
        $this.CurrentScreen = $screen


        # }

        # Initialize screen with render engine and container
        if ($screen.PSObject.Methods['Initialize']) {
            $screen.Initialize($this.RenderEngine, $this.Container)
        }

        # Apply layout if screen has widgets
        if ($screen.PSObject.Methods['ApplyLayout']) {
            $screen.ApplyLayout($this.LayoutManager, $this.TermWidth, $this.TermHeight)
        }

        # Activate screen
        if ($screen.PSObject.Methods['OnEnter']) {
            $screen.OnEnter()
        }

        # Mark dirty for render
        $this.IsDirty = $true

    }

    <#
    .SYNOPSIS
    Pop current screen and return to previous

    .OUTPUTS
    The popped screen object
    #>
    [object] PopScreen() {
        if ($this.ScreenStack.Count -eq 0) {
            return $null
        }

        # Exit current screen
        $poppedScreen = $this.ScreenStack.Pop()
        if ($poppedScreen.PSObject.Methods['OnExit']) {
            $poppedScreen.OnDoExit()
        }

        # DIFFERENTIAL RENDERING: Do NOT clear screen - let the previous screen
        # overwrite the popped content naturally. This prevents flicker.

        # Restore previous screen
        if ($this.ScreenStack.Count -gt 0) {
            $this.CurrentScreen = $this.ScreenStack.Peek()

            # Re-enter previous screen
            if ($this.CurrentScreen.PSObject.Methods['OnEnter']) {
                $this.CurrentScreen.OnEnter()
            }

            # Mark dirty for render
            $this.IsDirty = $true
        }
        else {
            $this.CurrentScreen = $null
        }

        return $poppedScreen
    }

    <#
    .SYNOPSIS
    Clear screen stack and set a new root screen

    .PARAMETER screen
    New root screen
    #>
    [void] SetRootScreen([object]$screen) {
        # Clear stack
        while ($this.ScreenStack.Count -gt 0) {
            $this.PopScreen()
        }

        # Push new root
        $this.PushScreen($screen)
    }

    # === Rendering ===

    hidden [void] _RenderCurrentScreen() {
        # }

        if (-not $this.CurrentScreen) {
            # }
            return
        }


        # }

        try {
            # }

            # Check if screen requests full clear
            if ($this.CurrentScreen.NeedsClear) {
                # }
                $this.RenderEngine.RequestClear()
                $this.CurrentScreen.NeedsClear = $false
            }


            # }

            # USE SPEEDTUI PROPERLY - BeginFrame/WriteAt/EndFrame
            $this.RenderEngine.BeginFrame()


            # }

            # Get screen output (ANSI strings with position info)
            # Get screen output (ANSI strings with position info)
            # Get screen output (ANSI strings with position info)
            if ($this.CurrentScreen.PSObject.Methods['RenderToEngine']) {

                # }
                # New method: screen writes directly to engine
                $this.CurrentScreen.RenderToEngine($this.RenderEngine)
                # }
            }
            else {
                # Legacy fallback removed - all screens must support RenderToEngine
                throw "Screen type '$($this.CurrentScreen.GetType().Name)' does not implement RenderToEngine"
            }


            # }

            # EndFrame does differential rendering
            $this.RenderEngine.EndFrame()


            # }

            # Clear dirty flag after successful render
            $this.IsDirty = $false


            # }

        }
        catch {
            # RENDER ERROR - Try to recover gracefully
            $errorMsg = "Render error: $_"
            $errorLocation = "$($_.InvocationInfo.ScriptName):$($_.InvocationInfo.ScriptLineNumber)"

            # Log to file if available
            if ($global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] $errorMsg"
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] Location: $errorLocation"
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] Line: $($_.InvocationInfo.Line)"
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] Stack: $($_.ScriptStackTrace)"
            }

            # Increment error count
            if (-not $this.RenderErrorCount) { $this.RenderErrorCount = 0 }
            $this.RenderErrorCount++

            # If too many errors, then we need to fail
            if ($this.RenderErrorCount -gt 10) {
                # Too many errors - give up
                [Console]::Clear()
                [Console]::CursorVisible = $true
                [Console]::SetCursorPosition(0, 0)
                # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcApplication] TOO MANY RENDER ERRORS - EXITING"
                # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcApplication] Error: $errorMsg"
                # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcApplication] Location: $errorLocation"
                # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcApplication] The application experienced too many render errors."
                # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcApplication] Please restart the application."

                [Console]::ReadKey($true) | Out-Null
                $this.Stop()
                return
            }

            # Try to show error in a minimal way and continue
            try {
                # Clear screen and show error message
                [Console]::Clear()
                [Console]::SetCursorPosition(0, 0)
                # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcApplication] Render Error Occurred"
                # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcApplication] Error: $($_.Exception.Message)"

                $key = [Console]::ReadKey($true)
                if ($key.Key -eq 'Escape') {
                    $this.Stop()
                    return
                }

                # Try to recover by requesting full clear and redraw
                $this.RenderEngine.RequestClear()
                $this.IsDirty = $true

                # If current screen is problematic, try to go back
                if ($this.ScreenStack.Count -gt 1 -and $this.RenderErrorCount -gt 3) {
                    # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcApplication] Returning to previous screen due to errors..."
                    Start-Sleep -Milliseconds 500
                    $this.PopScreen()
                    $this.RenderErrorCount = 0  # Reset counter after navigation
                }

            }
            catch {
                # Can't even show the error message - now we really need to exit
                # }
                $this.Stop()
            }

            # Call error handler if registered
            if ($this.OnError) {
                try {
                    & $this.OnError $_
                }
                catch {
                    # Error handler failed, log it but continue
                    # }
                }
            }
        }
    }



    # === Event Loop ===

    <#
    .SYNOPSIS
    Start the application event loop

    .DESCRIPTION
    Runs until Stop() is called or screen stack is empty
    #>
    [void] Run() {
        $this.Running = $true

        # Hide cursor
        [Console]::CursorVisible = $false

        # Track iterations for terminal size check optimization
        $iteration = 0

        try {
            # Event loop - render only when dirty
            # }


            while ($this.Running -and $this.ScreenStack.Count -gt 0) {
                $hadInput = $false

                # Check for automation commands
                if ($this.AutomationMode) {
                    $this._ProcessAutomationCommands()
                }

                # Process queued automation commands first
                if ($this.AutomationMode -and $this.CommandQueue.Count -gt 0) {
                    $cmdString = $this.CommandQueue.Dequeue()
                    # }

                    try {
                        $key = $this._ParseCommand($cmdString)

                        # Global keys - Ctrl+Q to exit
                        if ($key.Modifiers -eq [ConsoleModifiers]::Control -and $key.Key -eq 'Q') {
                            $this.Stop()
                        }
                        elseif ($this.CurrentScreen -and $this.CurrentScreen.PSObject.Methods['HandleKeyPress']) {
                            $handled = $this.CurrentScreen.HandleKeyPress($key)
                            if ($handled) {
                                $hadInput = $true
                            }
                        }

                        # Capture screen after command
                        $this._CaptureScreen()
                    }
                    catch {
                        # }
                    }
                }

                # OPTIMIZATION: Drain ALL available input before rendering
                # This eliminates input lag from sleep delays
                try {
                    while ([Console]::KeyAvailable) {
                        $key = [Console]::ReadKey($true)

                        # Global keys - Ctrl+Q to exit
                        if ($key.Modifiers -eq [ConsoleModifiers]::Control -and $key.Key -eq 'Q') {
                            $this.Stop()
                            break
                        }

                        # Pass to current screen (screen handles its own menu)
                        if ($this.CurrentScreen) {
                            if ($this.CurrentScreen.PSObject.Methods['HandleKeyPress']) {
                                # }
                                $handled = $this.CurrentScreen.HandleKeyPress($key)
                                if ($handled) {
                                    $hadInput = $true
                                }
                            }
                            else {
                                # }
                            }
                        }
                    }
                }
                catch {
                    # Console input is redirected or unavailable - skip input processing
                    # This happens when running in non-interactive mode (e.g., piped input, automated tests)
                    # }
                }

                # Mark dirty if we processed input
                if ($hadInput) {
                    $this.IsDirty = $true
                }

                # Capture dirty state before rendering
                $wasActive = $this.IsDirty

                # OPTIMIZATION: Use centralized terminal service for resize detection
                # Only checks actual console every 100ms (cached)
                if ((-not $this.IsDirty) -or ($iteration % 10 -eq 0)) {
                    if (($this.RenderEngine.Width -ne [Console]::WindowWidth -or $this.RenderEngine.Height -ne [Console]::WindowHeight)) {
                        $dims = @{ Width = [Console]::WindowWidth; Height = [Console]::WindowHeight }
                        $this._HandleTerminalResize($dims.Width, $dims.Height)
                    }
                }

                if ($this.IsDirty) {
                    # Reset iteration counter when rendering
                    $iteration = 0
                }

                $iteration++

                # Only render when dirty (state changed)
                if ($this.IsDirty) {
                    # }
                    $this._RenderCurrentScreen()
                    $iteration = 0  # Reset counter after render
                }

                # Sleep longer when idle (no render) vs active
                if ($wasActive) {
                    Start-Sleep -Milliseconds 1  # ~1000 FPS max, instant response to input
                }
                else {
                    Start-Sleep -Milliseconds 50  # ~20 FPS when idle, reduced from 100ms for better responsiveness
                }
            }

        }
        finally {
            # CRITICAL: Flush pending changes before exit
            try {
                . "$PSScriptRoot/services/TaskStore.ps1"
                $store = [TaskStore]::GetInstance()
                if ($store.HasPendingChanges) {
                    # Write-PmcTuiLog "Flushing pending changes on exit..." "INFO"
                    $store.Flush()
                }
            }
            catch {
                # Write-PmcTuiLog "Failed to flush data on exit: $_" "ERROR"
                # Continue with cleanup even if flush fails
            }

            # Cleanup
            [Console]::CursorVisible = $true
            [Console]::Clear()
        }
    }

    <#
    .SYNOPSIS
    Stop the application event loop
    #>
    [void] Stop() {
        $this.Running = $false

        # Flush any pending TaskStore changes before exit
        try {
            $store = [TaskStore]::GetInstance()
            if ($null -ne $store -and $store.HasPendingChanges) {
                $store.Flush()
            }
        }
        catch {
            # TaskStore might not be available during shutdown - safe to ignore
            if (Get-Command Write-PmcTuiLog -ErrorAction SilentlyContinue) {
                # Write-PmcTuiLog "Stop: Could not flush TaskStore: $($_.Exception.Message)" "WARNING"
            }
        }
    }

    # === Automation Methods ===

    <#
    .SYNOPSIS
    Enable automation mode with command file and output capture

    .PARAMETER CommandFile
    Path to file containing commands (one per line)

    .PARAMETER OutputFile
    Path to file for capturing screen output
    #>
    [void] EnableAutomation([string]$CommandFile, [string]$OutputFile) {
        $this.AutomationMode = $true
        $this.AutomationCommandFile = $CommandFile
        $this.AutomationOutputFile = $OutputFile
        $this.CommandQueue = New-Object System.Collections.Queue


        # }
    }

    <#
    .SYNOPSIS
    Check for new commands and queue them
    #>
    hidden [void] _ProcessAutomationCommands() {
        if (-not $this.AutomationMode -or -not (Test-Path $this.AutomationCommandFile)) {
            return
        }

        try {
            $commands = Get-Content $this.AutomationCommandFile -ErrorAction SilentlyContinue
            if ($commands) {
                foreach ($cmd in $commands) {
                    if ($cmd -and $cmd.Trim() -ne '') {
                        $this.CommandQueue.Enqueue($cmd.Trim())
                    }
                }
                # Clear the command file after reading
                Clear-Content $this.AutomationCommandFile -ErrorAction SilentlyContinue


                # }
            }
        }
        catch {
            # }
        }
    }

    <#
    .SYNOPSIS
    Convert command string to ConsoleKeyInfo
    #>
    hidden [System.ConsoleKeyInfo] _ParseCommand([string]$command) {
        # Parse commands like "j", "k", "Enter", "Ctrl+Q", "Escape"
        $parts = $command -split '\+'
        $modifiers = [ConsoleModifiers]::None
        $keyName = $parts[-1]

        # Parse modifiers
        foreach ($part in $parts[0..($parts.Length - 2)]) {
            switch ($part.ToLower()) {
                'ctrl' { $modifiers = $modifiers -bor [ConsoleModifiers]::Control }
                'alt' { $modifiers = $modifiers -bor [ConsoleModifiers]::Alt }
                'shift' { $modifiers = $modifiers -bor [ConsoleModifiers]::Shift }
            }
        }

        # Parse key
        $key = [ConsoleKey]::A
        $keyChar = [char]0

        switch ($keyName.ToLower()) {
            'enter' { $key = [ConsoleKey]::Enter; $keyChar = "`r" }
            'escape' { $key = [ConsoleKey]::Escape; $keyChar = [char]27 }
            'esc' { $key = [ConsoleKey]::Escape; $keyChar = [char]27 }
            'tab' { $key = [ConsoleKey]::Tab; $keyChar = "`t" }
            'space' { $key = [ConsoleKey]::Spacebar; $keyChar = ' ' }
            'up' { $key = [ConsoleKey]::UpArrow; $keyChar = [char]0 }
            'down' { $key = [ConsoleKey]::DownArrow; $keyChar = [char]0 }
            'left' { $key = [ConsoleKey]::LeftArrow; $keyChar = [char]0 }
            'right' { $key = [ConsoleKey]::RightArrow; $keyChar = [char]0 }
            default {
                # Single character
                if ($keyName.Length -eq 1) {
                    $keyChar = $keyName[0]
                    $key = [ConsoleKey]::($keyName.ToUpper())
                }
            }
        }

        return New-Object System.ConsoleKeyInfo($keyChar, $key, ($modifiers -band [ConsoleModifiers]::Shift) -ne 0, ($modifiers -band [ConsoleModifiers]::Alt) -ne 0, ($modifiers -band [ConsoleModifiers]::Control) -ne 0)
    }

    <#
    .SYNOPSIS
    Capture current screen to output file
    #>
    hidden [void] _CaptureScreen() {
        if (-not $this.AutomationMode -or -not $this.AutomationOutputFile) {
            return
        }

        try {
            # Capture screen state information
            $screenInfo = @"
=== Screen Capture $(Get-Date -Format 'yyyy-MM-dd HH:mm:ss') ===
Current Screen: $($this.CurrentScreen.GetType().Name)
Terminal Size: $($this.TermWidth)x$($this.TermHeight)
Screen Stack Depth: $($this.ScreenStack.Count)

"@
            # Try to capture current screen's rendered content
            if ($this.CurrentScreen -and $this.CurrentScreen.PSObject.Properties['LastRenderedContent']) {
                $screenInfo += "Last Rendered Content:`n"
                $screenInfo += $this.CurrentScreen.LastRenderedContent
                $screenInfo += "`n"
            }

            Add-Content -Path $this.AutomationOutputFile -Value $screenInfo
        }
        catch {
            # }
        }
    }

    # === Terminal Management ===

    hidden [void] _UpdateTerminalSize() {
        # Use centralized terminal service (cached, optimized)
        $dims = @{ Width = [Console]::WindowWidth; Height = [Console]::WindowHeight }
        $this.TermWidth = $dims.Width
        $this.TermHeight = $dims.Height
    }

    hidden [void] _HandleTerminalResize([int]$newWidth, [int]$newHeight) {
        $this.TermWidth = $newWidth
        $this.TermHeight = $newHeight

        # Notify current screen
        if ($this.CurrentScreen) {
            if ($this.CurrentScreen.PSObject.Methods['OnTerminalResize']) {
                $this.CurrentScreen.OnTerminalResize($newWidth, $newHeight)
            }

            # Reapply layout
            if ($this.CurrentScreen.PSObject.Methods['ApplyLayout']) {
                $this.CurrentScreen.ApplyLayout($this.LayoutManager, $newWidth, $newHeight)
            }
        }

        # Fire event
        if ($this.OnTerminalResize) {
            & $this.OnTerminalResize $newWidth $newHeight
        }

        # Mark dirty for render
        $this.IsDirty = $true
    }

    # === Utility Methods ===

    <#
    .SYNOPSIS
    Get current terminal size

    .OUTPUTS
    Hashtable with Width and Height properties
    #>
    [hashtable] GetTerminalSize() {
        return @{
            Width  = $this.TermWidth
            Height = $this.TermHeight
        }
    }

    <#
    .SYNOPSIS
    Request a render on next frame

    .DESCRIPTION
    Schedules a re-render of the current screen by setting dirty flag
    #>
    [void] RequestRender() {
        if ($global:PmcTuiLogFile) {
            $caller = (Get-PSCallStack)[1]
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] RequestRender called from: $($caller.Command) at line $($caller.ScriptLineNumber)"
        }
        $this.IsDirty = $true
    }
}

# Classes exported automatically in PowerShell 5.1+

# PmcScreen - Base class for all PMC screens
# Provides standard screen lifecycle, layout, and widget management

using namespace System.Collections.Generic
using namespace System.Text

Set-StrictMode -Version Latest

# NOTE: All dependencies are loaded by Start-PmcTUI.ps1
# Do not load them again here to avoid circular dependencies and duplicate loading

<#
.SYNOPSIS
Base class for all PMC screens

.DESCRIPTION
PmcScreen provides:
- Standard widget composition (MenuBar, Header, Footer, StatusBar, Content)
- Layout management integration
- Screen lifecycle (OnEnter, OnExit, LoadData)
- Input handling delegation
- Rendering orchestration
- ServiceContainer dependency injection

.EXAMPLE
# Example: Custom screen implementation with ServiceContainer
# class MyCustomScreen : PmcScreen {
#     MyCustomScreen([object]$container) : base("MyScreen", "My Screen Title", $container) {
#         $this.Header.SetBreadcrumb(@("Home", "My Screen"))
#     }
#
#     [void] LoadData() {
#         # Access services via container
#         $taskStore = $this.Container.Get('TaskStore')
#         # Load your data...
#     }
#
#     [string] RenderContent() {
#         # Render your content...
#     }
#
# Example: Legacy constructor (backward compatible)
# class MyLegacyScreen : PmcScreen {
#     MyLegacyScreen() : base("MyScreen", "My Screen Title") {
#         # Works without container for backward compatibility
#     }
#>
class PmcScreen {
    # === Core Properties ===
    [string]$ScreenKey = ""
    [string]$ScreenTitle = ""

    # === Service Container ===
    [object]$Container = $null

    # === Standard Widgets ===
    [object]$MenuBar = $null
    [object]$Header = $null
    [object]$Footer = $null
    [object]$StatusBar = $null
    [object]$ContentWidgets

    # === Layout ===
    [object]$LayoutManager = $null
    [int]$TermWidth = 80
    [int]$TermHeight = 24

    # === State ===
    [bool]$IsActive = $false
    [object]$RenderEngine = $null
    [bool]$NeedsClear = $false  # Request full screen clear before next render

    # === Layout Methods ===

    <#
    .SYNOPSIS
    Handle screen resizing
    #>
    [void] Resize([int]$width, [int]$height) {
        $this.TermWidth = $width
        $this.TermHeight = $height

        # Delegate to ApplyLayout for correct positioning
        # ApplyLayout uses PmcLayoutManager which has the correct constraints:
        # - Footer: Y = 'BOTTOM-2' (height - 2)
        # - StatusBar: Y = 'BOTTOM' (height - 1)
        if ($this.LayoutManager) {
            $this.ApplyLayout($this.LayoutManager, $width, $height)
        }
    }

    # H-UI-4: Message queue for persistent status messages
    [System.Collections.Queue]$_messageQueue = [System.Collections.Queue]::new()
    [DateTime]$_lastMessageTime = [DateTime]::MinValue

    # === Event Handlers ===
    [scriptblock]$OnEnterHandler = $null
    [scriptblock]$OnExitHandler = $null

    # === Constructor (backward compatible - no container) ===
    PmcScreen([string]$key, [string]$title) {

        $this.ScreenKey = $key
        $this.ScreenTitle = $title
        $this.ContentWidgets = New-Object 'System.Collections.Generic.List[object]'

        # Create default widgets
        $this._CreateDefaultWidgets()
    }

    # === Constructor (with ServiceContainer) ===
    PmcScreen([string]$key, [string]$title, [object]$container) {

        $this.ScreenKey = $key
        $this.ScreenTitle = $title
        $this.Container = $container
        $this.ContentWidgets = New-Object 'System.Collections.Generic.List[object]'



        # Create default widgets
        $this._CreateDefaultWidgets()
    }

    hidden [void] _CreateDefaultWidgets() {
        # Menu bar - use shared MenuBar if available (populated by TaskListScreen)
        # CRITICAL: Check if variable exists AND is not null
        if ((Get-Variable -Name PmcSharedMenuBar -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcSharedMenuBar) {
            $this.MenuBar = $global:PmcSharedMenuBar
        }
        else {
            # Create default empty MenuBar (will be populated by TaskListScreen)
            $this.MenuBar = New-Object PmcMenuBar

            $this.MenuBar.AddMenu("Tasks", 'T', @())
            $this.MenuBar.AddMenu("Projects", 'P', @())
            $this.MenuBar.AddMenu("Time", 'M', @())
            $this.MenuBar.AddMenu("Tools", 'L', @())
            $this.MenuBar.AddMenu("Options", 'O', @())
            $this.MenuBar.AddMenu("Help", '?', @())
        }

        # Header
        $this.Header = New-Object PmcHeader -ArgumentList $this.ScreenTitle

        # Footer with standard shortcuts
        $this.Footer = New-Object PmcFooter
        $this.Footer.AddShortcut("Esc", "Back")
        $this.Footer.AddShortcut("F10", "Menu")

        # Status bar
        $this.StatusBar = New-Object PmcStatusBar
        $this.StatusBar.SetLeftText("Ready")
    }

    # === Lifecycle Methods ===

    <#
    .SYNOPSIS
    Called when screen becomes active

    .DESCRIPTION
    Override to perform initialization when screen is displayed
    #>
    [void] OnEnter() {
        # Ensure cursor is hidden by default (widgets will handle their own cursor rendering)
        try {
            [Console]::CursorVisible = $false
        } catch {
            # Ignore if console not available (e.g. in tests)
        }

        # Ensure menu bar is populated if empty
        if ($null -ne $this.MenuBar -and $this.MenuBar.Menus.Count -eq 0) {
            # Write-PmcTuiLog "PmcScreen.OnEnter: MenuBar empty, attempting to populate from registry" "DEBUG"
            if ($null -ne $global:PmcMenuRegistry) {
                # Write-PmcTuiLog "PmcScreen.OnEnter: Registry found, rebuilding menus" "DEBUG"
                $global:PmcMenuRegistry.BuildMenuBar($this.MenuBar)
            }
        }

        $this.IsActive = $true
        $this.LoadData()

        if ($this.OnEnterHandler) {
            & $this.OnEnterHandler $this
        }
    }

    <#
    .SYNOPSIS
    Called when screen becomes inactive

    .DESCRIPTION
    Override to perform cleanup when leaving screen
    #>
    [void] OnDoExit() {
        $this.IsActive = $false

        if ($this.OnExitHandler) {
            & $this.OnExitHandler $this
        }
    }

    <#
    .SYNOPSIS
    Load data for this screen

    .DESCRIPTION
    Override to load screen-specific data
    #>
    [void] LoadData() {
        # Override in subclass
    }

    # === Layout Management ===

    <#
    .SYNOPSIS
    Apply layout to all widgets

    .PARAMETER layoutManager
    Layout manager instance

    .PARAMETER termWidth
    Terminal width

    .PARAMETER termHeight
    Terminal height
    #>
    [void] ApplyLayout([object]$layoutManager, [int]$termWidth, [int]$termHeight) {
        try {
            $this.LayoutManager = $layoutManager
            $this.TermWidth = $termWidth
            $this.TermHeight = $termHeight

            # Apply layout to standard widgets
            if ($this.MenuBar) {
                $rect = $layoutManager.GetRegion('MenuBar', $termWidth, $termHeight)
                $this.MenuBar.SetPosition($rect.X, $rect.Y)
                $this.MenuBar.SetSize($rect.Width, $rect.Height)
            }

            if ($this.Header) {
                $rect = $layoutManager.GetRegion('Header', $termWidth, $termHeight)
                $this.Header.SetPosition($rect.X, $rect.Y)
                $this.Header.SetSize($rect.Width, $rect.Height)
            }

            if ($this.Footer) {
                $rect = $layoutManager.GetRegion('Footer', $termWidth, $termHeight)
                $this.Footer.SetPosition($rect.X, $rect.Y)
                $this.Footer.SetSize($rect.Width, $rect.Height)
            }

            if ($this.StatusBar) {
                $rect = $layoutManager.GetRegion('StatusBar', $termWidth, $termHeight)
                $this.StatusBar.SetPosition($rect.X, $rect.Y)
                $this.StatusBar.SetSize($rect.Width, $rect.Height)
            }

            # Apply layout to content widgets
            $this.ApplyContentLayout($layoutManager, $termWidth, $termHeight)
        }
        catch {
            # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcScreen] FATAL ERROR PmcScreen.ApplyLayout: $_"
            # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcScreen] Stack: $($_.ScriptStackTrace)"
            throw
        }
    }


    <#
    .SYNOPSIS
    Apply layout to content area widgets

    .DESCRIPTION
    Override to position custom content widgets
    #>
    [void] ApplyContentLayout([PmcLayoutManager]$layoutManager, [int]$termWidth, [int]$termHeight) {
        # Override in subclass to position content widgets
    }

    <#
    .SYNOPSIS
    Handle terminal resize

    .PARAMETER newWidth
    New terminal width

    .PARAMETER newHeight
    New terminal height
    #>
    [void] OnTerminalResize([int]$newWidth, [int]$newHeight) {
        if ($this.LayoutManager) {
            $this.ApplyLayout($this.LayoutManager, $newWidth, $newHeight)
        }
    }

    # === Rendering ===

    <#
    .SYNOPSIS
    Initialize widgets with render engine

    .PARAMETER renderEngine
    SpeedTUI render engine instance
    #>
    # Initialize with render engine only (backward compatible)
    [void] Initialize([object]$renderEngine) {
        $this.Initialize($renderEngine, $null)
    }

    # Initialize with render engine and container (new pattern)
    [void] Initialize([object]$renderEngine, [object]$container) {
        $this.RenderEngine = $renderEngine

        # Store container if provided
        if ($container) {
            $this.Container = $container
        }

        # Initialize standard widgets
        if ($this.MenuBar) {
            $this.MenuBar.Initialize($renderEngine)
        }
        if ($this.Header) {
            $this.Header.Initialize($renderEngine)
        }
        if ($this.Footer) {
            $this.Footer.Initialize($renderEngine)
        }
        if ($this.StatusBar) {
            $this.StatusBar.Initialize($renderEngine)
        }

        # Initialize content widgets
        foreach ($widget in $this.ContentWidgets) {
            $widget.Initialize($renderEngine)
        }
    }

    <#
    .SYNOPSIS
    Legacy Render method - DEPRECATED

    .DESCRIPTION
    This method has been removed to enforce native rendering.
    All rendering must now be done via RenderToEngine().
    #>
    [string] Render() {
        throw "LEGACY RENDER CALLED: All screens must implement RenderToEngine(). This method is deprecated and removed."
    }

    <#
    .SYNOPSIS
    Legacy RenderContent method - DEPRECATED
    #>
    [string] RenderContent() {
        return ""
    }

    <#
    .SYNOPSIS
    Render directly to engine

    .PARAMETER engine
    RenderEngine instance to write to

    .DESCRIPTION
    Renders screen by calling widget Render() methods and writing ANSI output to engine.
    Widgets use SpeedTUI's Render() → OnRender() pattern which returns ANSI strings.
    We parse those ANSI strings and write them to the engine using WriteAt().
    #>
    [void] RenderToEngine([object]$engine) {
        # Z-INDEX LAYER RENDERING
        # All rendering now uses explicit layers for proper z-ordering.

        # Layer 0: Background (Fill entire screen with theme background)
        # STRICT THEME ENFORCEMENT: No fallbacks. If theme property is missing, this MUST fail.
        $engine.BeginLayer([ZIndex]::Background)
        try {
            $fg = $this.GetThemedInt('Foreground.Primary')
            $bg = $this.GetThemedInt('Background.Primary')
            # Fill entire terminal with background color
            $engine.Fill(0, 0, $this.TermWidth, $this.TermHeight, ' ', $fg, $bg)
        }
        catch {
            # Fallback if theme fails (shouldn't happen with strict mode, but safe for base class)
        }

        # Layer 50: Header
        $engine.BeginLayer([ZIndex]::Header)
        if ($this.Header) {
            try {
                if ($this.Header.PSObject.Methods['RenderToEngine']) {
                    $this.Header.RenderToEngine($engine)
                }
            }
            catch {
                $this._HandleWidgetRenderError("Header", $_, $engine, 1)
            }
        }

        # Layer 10: Content (main screen content)
        $engine.BeginLayer([ZIndex]::Content)
        try {
            if ($this.PSObject.Methods['RenderContentToEngine'] -and
                $this.GetType().GetMethod('RenderContentToEngine').DeclaringType.Name -ne 'PmcScreen') {
                $this.RenderContentToEngine($engine)
            }
        }
        catch {
            $this._HandleWidgetRenderError("RenderContent", $_, $engine, 5)
        }

        # Layer 20: Panel (content widgets like FilterPanel, DatePicker, etc.)
        $engine.BeginLayer([ZIndex]::Panel)
        $widgetRow = 10
        # Write-PmcTuiLog "PmcScreen.RenderToEngine: Rendering $($this.ContentWidgets.Count) content widgets" "DEBUG"
        foreach ($widget in $this.ContentWidgets) {
            $widgetName = $(if ($widget.Name) { $widget.Name } else { $widget.GetType().Name })

            # Skip widgets that are explicitly hidden via Visible property
            if ($widget.PSObject.Properties['Visible'] -and -not $widget.Visible) {
                continue
            }

            try {
                if ($widget.PSObject.Methods['RenderToEngine']) {
                    # Write-PmcTuiLog "PmcScreen: Calling RenderToEngine on $widgetName" "DEBUG"
                    $widget.RenderToEngine($engine)
                }
                else {
                    # Write-PmcTuiLog "PmcScreen: Widget $widgetName missing RenderToEngine method" "WARN"
                }
            }
            catch {
                $this._HandleWidgetRenderError($widgetName, $_, $engine, $widgetRow)
                $widgetRow += 2
            }
        }

        # Layer 55: Footer
        $engine.BeginLayer([ZIndex]::Footer)
        if ($this.Footer) {
            try {
                if ($this.Footer.PSObject.Methods['RenderToEngine']) {
                    $this.Footer.RenderToEngine($engine)
                }
            }
            catch {
                $footerRow = [Math]::Max(20, $this.TermHeight - 4)
                $this._HandleWidgetRenderError("Footer", $_, $engine, $footerRow)
            }
        }

        # Layer 65: StatusBar
        $engine.BeginLayer([ZIndex]::StatusBar)
        if ($this.StatusBar) {
            try {
                if ($this.StatusBar.PSObject.Methods['RenderToEngine']) {
                    $this.StatusBar.RenderToEngine($engine)
                }
            }
            catch {
                $statusRow = [Math]::Max(22, $this.TermHeight - 2)
                $this._HandleWidgetRenderError("StatusBar", $_, $engine, $statusRow)
            }
        }

        # Layer 100: Dropdown (MenuBar with dropdowns)
        # CRITICAL: Render MenuBar LAST with highest z-index
        $engine.BeginLayer([ZIndex]::Dropdown)
        if ($this.MenuBar) {
            try {
                if ($this.MenuBar.PSObject.Methods['RenderToEngine']) {
                    $this.MenuBar.RenderToEngine($engine)
                }
            }
            catch {
                $this._HandleWidgetRenderError("MenuBar", $_, $engine, 0)
            }
        }
    }


    <#
    .SYNOPSIS
    Handle widget render errors gracefully

    .DESCRIPTION
    Shows error inline without crashing the app, logs the error,
    and allows the rest of the UI to continue rendering.
    #>
    hidden [void] _HandleWidgetRenderError([string]$widgetName, [object]$error, [object]$engine, [int]$row) {
        $errorMsg = "$widgetName render failed: $($error.Exception.Message)"
        $stackTrace = $error.ScriptStackTrace

        # Log error details
        if ($global:PmcTuiLogFile) {
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] Widget render error: $errorMsg"
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] Stack: $stackTrace"
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] TargetObject: $($error.TargetObject)"
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] InvocationInfo: $($error.InvocationInfo.Line)"
        }

        # Show error inline where widget would have rendered
        try {
            $engine.WriteAt(2, $row, "`e[1;31m[!] $widgetName Error: $($error.Exception.Message -replace "`n", " ")`e[0m")

            # If status bar is available, also show error there
            if ($this.StatusBar) {
                $this.SetStatusMessage("$widgetName render failed - see logs", "error")
            }
        }
        catch {
            # If we can't even write the error, just log it
        }
    }



