<#
.SYNOPSIS
Start PMC TUI with new architecture

.DESCRIPTION
Entry point for SpeedTUI-based PMC interface.
Creates application and launches screens.

.PARAMETER StartScreen
Which screen to launch (default: BlockedTasks)

.EXAMPLE
Start-PmcTUI
Start-PmcTUI -StartScreen BlockedTasks
#>
function Start-PmcTUI {
    param(
        [string]$StartScreen = "TaskList"
    )

    # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [Start-PmcTUI] Starting PMC TUI (SpeedTUI Architecture)..."
    # Write-PmcTuiLog "Starting PMC TUI with screen: $StartScreen" "INFO"

    try {
        # === Theme Self-Healing ===
        # CRITICAL FIX: Ensure theme configuration is valid before starting
        # If Active theme is set but Hex/Properties are missing, force a reload to populate them
        $debugLogPath = Join-Path (Split-Path (Split-Path (Split-Path $PSScriptRoot -Parent) -Parent) -Parent) 'data/logs/theme-debug.log'
        try {
            Add-Content -Path $debugLogPath -Value "[$(Get-Date -F 'HH:mm:ss.fff')] === THEME SELF-HEAL START ===" -ErrorAction SilentlyContinue
            $root = Split-Path (Split-Path (Split-Path $PSScriptRoot -Parent) -Parent) -Parent
            $configPath = Join-Path $root 'config.json'
            Add-Content -Path $debugLogPath -Value "[$(Get-Date -F 'HH:mm:ss.fff')] Config path: $configPath" -ErrorAction SilentlyContinue
            if (Test-Path $configPath) {
                $cfg = Get-Content $configPath -Raw | ConvertFrom-Json
                # STRICT MODE FIX: Safely check for Hex property existence
                $hasHex = $cfg.Display -and $cfg.Display.Theme -and $cfg.Display.Theme.PSObject.Properties['Hex']
                $hexValue = if ($hasHex) { $cfg.Display.Theme.Hex } else { $null }
                Add-Content -Path $debugLogPath -Value "[$(Get-Date -F 'HH:mm:ss.fff')] Config loaded. Active=$($cfg.Display.Theme.Active), Hex=$hexValue, hasHex=$hasHex" -ErrorAction SilentlyContinue
                if ($cfg.Display -and $cfg.Display.Theme -and $cfg.Display.Theme.Active) {
                    if (-not $hasHex) {
                        Add-Content -Path $debugLogPath -Value "[$(Get-Date -F 'HH:mm:ss.fff')] Missing Hex - calling Set-ActiveTheme for '$($cfg.Display.Theme.Active)'" -ErrorAction SilentlyContinue
                        Set-ActiveTheme -themeName $cfg.Display.Theme.Active
                        Add-Content -Path $debugLogPath -Value "[$(Get-Date -F 'HH:mm:ss.fff')] Set-ActiveTheme completed successfully" -ErrorAction SilentlyContinue
                    } else {
                        Add-Content -Path $debugLogPath -Value "[$(Get-Date -F 'HH:mm:ss.fff')] Hex already present, no repair needed" -ErrorAction SilentlyContinue
                    }
                }
            } else {
                Add-Content -Path $debugLogPath -Value "[$(Get-Date -F 'HH:mm:ss.fff')] Config file not found at: $configPath" -ErrorAction SilentlyContinue
            }
        } catch {
            Add-Content -Path $debugLogPath -Value "[$(Get-Date -F 'HH:mm:ss.fff')] EXCEPTION: $_" -ErrorAction SilentlyContinue
            Add-Content -Path $debugLogPath -Value "[$(Get-Date -F 'HH:mm:ss.fff')] Stack: $($_.ScriptStackTrace)" -ErrorAction SilentlyContinue
            # Continue startup - theme will use defaults
        }

        # === Clear stale global state ===
        # CRITICAL FIX: Clear shared menu bar and registry singleton to ensure fresh menus are loaded
        # This fixes the issue where Notes and Checklist screens don't appear after manifest updates
        $global:PmcSharedMenuBar = $null

        # Clear MenuRegistry singleton (it caches menu items across sessions)
        if ([MenuRegistry]) {
            [MenuRegistry]::_instance = $null
        }
        # Write-PmcTuiLog "Cleared stale PmcSharedMenuBar and MenuRegistry singleton" "INFO"

        # === Create DI Container ===
        # Write-PmcTuiLog "Creating ServiceContainer..." "INFO"
        $global:PmcContainer = [ServiceContainer]::new()
        # Write-PmcTuiLog "ServiceContainer created" "INFO"

        # === Register Core Services (in dependency order) ===

        # 1. Theme (no dependencies)
        # Write-PmcTuiLog "Registering Theme service..." "INFO"
        $global:PmcContainer.Register('Theme', {
                param($container)
                # Write-PmcTuiLog "Resolving Theme: Resetting singletons and initializing..." "INFO"

                # CRITICAL FIX: Reset singletons to ensure fresh theme data from config
                # This fixes gradient themes not loading because singletons cached old data
                [PmcThemeManager]::_instance = $null
                [PmcThemeEngine]::_instance = $null

                # Initialize Core Theme System with -Force to rebuild from config
                Initialize-PmcThemeSystem -Force

                # Get the Manager instance (which is now the source of truth)
                $manager = [PmcThemeManager]::GetInstance()
                $themeHex = $manager.GetCurrentThemeHex()

                # Write-PmcTuiLog "Theme initialized: $themeHex" "INFO"
                return $manager
            }, $true)

        # Register ThemeManager (depends on Theme)
        # Write-PmcTuiLog "Registering ThemeManager..." "INFO"
        $global:PmcContainer.Register('ThemeManager', {
                param($container)
                # Write-PmcTuiLog "Resolving ThemeManager..." "INFO"
                $null = $container.Resolve('Theme')
                return [PmcThemeManager]::GetInstance()
            }, $true)

        # 2. Config (no dependencies) - CACHED for performance
        # Write-PmcTuiLog "Registering Config service..." "INFO"
        $global:PmcContainer.Register('Config', {
                param($container)
                # Write-PmcTuiLog "Resolving Config..." "INFO"

                # Determine config path (same logic as Get-PmcConfig)
                # CRITICAL FIX: Use workspace root (three levels up from module dir)
                $root = Split-Path (Split-Path (Split-Path $PSScriptRoot -Parent) -Parent) -Parent
                $configPath = Join-Path $root 'config.json'

                # Use cached config for performance (eliminates repeated file I/O)
                try {
                    return [ConfigCache]::GetConfig($configPath)
                }
                catch {
                    # Write-PmcTuiLog "Config load failed, falling back to Get-PmcConfig: $_" "ERROR"
                    # Fallback to original method if cache fails
                    return Get-PmcConfig
                }
            }, $true)

        # 3. TaskStore (depends on Theme via state)
        # Write-PmcTuiLog "Registering TaskStore service..." "INFO"
        $global:PmcContainer.Register('TaskStore', {
                param($container)
                # Write-PmcTuiLog "Resolving TaskStore..." "INFO"
                # Ensure theme is initialized first
                $null = $container.Resolve('Theme')
                return [TaskStore]::GetInstance()
            }, $true)

        # 4. MenuRegistry (depends on Theme)
        # Write-PmcTuiLog "Registering MenuRegistry service..." "INFO"
        $global:PmcContainer.Register('MenuRegistry', {
                param($container)
                # Write-PmcTuiLog "Resolving MenuRegistry..." "INFO"
                # Ensure theme is initialized first
                $null = $container.Resolve('Theme')
                return [MenuRegistry]::GetInstance()
            }, $true)

        # 5. Application (depends on Theme)
        # Write-PmcTuiLog "Registering Application service..." "INFO"
        $global:PmcContainer.Register('Application', {
                param($container)
                # Write-PmcTuiLog "Resolving Application..." "INFO"
                # Ensure theme is initialized first
                $null = $container.Resolve('Theme')
                return [PmcApplication]::new($container)
            }, $true)

        # 6. CommandService (no dependencies)
        # Write-PmcTuiLog "Registering CommandService..." "INFO"
        $global:PmcContainer.Register('CommandService', {
                param($container)
                # Write-PmcTuiLog "Resolving CommandService..." "INFO"
                return [CommandService]::GetInstance()
            }, $true)

        # 7. ChecklistService (no dependencies)
        # Write-PmcTuiLog "Registering ChecklistService..." "INFO"
        $global:PmcContainer.Register('ChecklistService', {
                param($container)
                # Write-PmcTuiLog "Resolving ChecklistService..." "INFO"
                return [ChecklistService]::GetInstance()
            }, $true)

        # 8. NoteService (no dependencies)
        # Write-PmcTuiLog "Registering NoteService..." "INFO"
        $global:PmcContainer.Register('NoteService', {
                param($container)
                # Write-PmcTuiLog "Resolving NoteService..." "INFO"
                return [NoteService]::GetInstance()
            }, $true)

        # 9. ExcelMappingService (no dependencies)
        # Write-PmcTuiLog "Registering ExcelMappingService..." "INFO"
        $global:PmcContainer.Register('ExcelMappingService', {
                param($container)
                # Write-PmcTuiLog "Resolving ExcelMappingService..." "INFO"
                return [ExcelMappingService]::GetInstance()
            }, $true)

        # 10. PreferencesService (no dependencies)
        # Write-PmcTuiLog "Registering PreferencesService..." "INFO"
        $global:PmcContainer.Register('PreferencesService', {
                param($container)
                # Write-PmcTuiLog "Resolving PreferencesService..." "INFO"
                return [PreferencesService]::GetInstance()
            }, $true)

        # 11. Screen factories (depend on Application, TaskStore, etc.)
        # Write-PmcTuiLog "Registering screen factories..." "INFO"

        $global:PmcContainer.Register('TaskListScreen', {
                param($container)
                # Write-PmcTuiLog "Resolving TaskListScreen..." "INFO"
                # Ensure dependencies
                $null = $container.Resolve('Theme')
                $null = $container.Resolve('TaskStore')
                return [TaskListScreen]::new($container)
            }, $false)  # Not singleton - create new instance each time

        # === Resolve Application ===
        # Write-PmcTuiLog "Resolving Application from container..." "INFO"
        $global:PmcApp = $global:PmcContainer.Resolve('Application')
        # Write-PmcTuiLog "Application resolved and assigned to `$global:PmcApp" "INFO"

        # === Load Menus from Manifest ===
        # Write-PmcTuiLog "Loading menus from manifest..." "INFO"
        $menuRegistry = $global:PmcContainer.Resolve('MenuRegistry')
        $manifestPath = Join-Path $PSScriptRoot "screens/MenuItems.psd1"
        if (Test-Path $manifestPath) {
            $menuRegistry.LoadFromManifest($manifestPath, $global:PmcContainer)
            # Write-PmcTuiLog "Menus loaded from $manifestPath" "INFO"
        }
        else {
            # Write-PmcTuiLog "Menu manifest not found at $manifestPath" "ERROR"
        }

        # === Launch Initial Screen ===
        # Write-PmcTuiLog "Launching screen: $StartScreen" "INFO"
        switch ($StartScreen) {
            'TaskList' {
                # Write-PmcTuiLog "Resolving TaskListScreen from container..." "INFO"
                $screen = $global:PmcContainer.Resolve('TaskListScreen')
                # Write-PmcTuiLog "Pushing screen to app..." "INFO"
                try {
                    $global:PmcApp.PushScreen($screen)
                    # Write-PmcTuiLog "Screen pushed successfully" "INFO"
                }
                catch {
                    # Add-Content -Path "$(Join-Path ([System.IO.Path]::GetTempPath()) 'pmc_debug.txt')" -Value "[$(Get-Date)] FATAL ERROR IN PUSHSCREEN: $_"
                    # Add-Content -Path "$(Join-Path ([System.IO.Path]::GetTempPath()) 'pmc_debug.txt')" -Value "[$(Get-Date)] Stack Trace: $($_.ScriptStackTrace)"
                    throw
                }

            }
            'Demo' {
                # Write-PmcTuiLog "Loading DemoScreen (not containerized)..." "INFO"
                . "$PSScriptRoot/DemoScreen.ps1"
                $screen = [DemoScreen]::new()
                $global:PmcApp.PushScreen($screen)
                # Write-PmcTuiLog "Demo screen pushed" "INFO"
            }
            default {
                # Write-PmcTuiLog "Unknown screen: $StartScreen" "ERROR"
                throw "Unknown screen: $StartScreen"
            }
        }

        # Run event loop
        # Write-PmcTuiLog "Starting event loop..." "INFO"
        $global:PmcApp.Run()
        # Write-PmcTuiLog "Event loop exited normally" "INFO"

    }
    catch {
        # Write-PmcTuiLog "EXCEPTION: $_" "ERROR"
        # Write-PmcTuiLog "Stack trace: $($_.ScriptStackTrace)" "ERROR"
        # Write-PmcTuiLog "Exception details: $($_.Exception | Out-String)" "ERROR"

        # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [Start-PmcTUI] PMC TUI Error: $_"
        # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [Start-PmcTUI] Log file: $global:PmcTuiLogFile"
        # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [Start-PmcTUI] Stack trace: $($_.ScriptStackTrace)"
        throw
    }
    finally {
        # Cleanup
        # Write-PmcTuiLog "Cleanup - showing cursor and resetting terminal" "INFO"
        # Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [Start-PmcTUI] Log saved to: $global:PmcTuiLogFile"
    }
}

# Allow direct execution
# Allow direct execution
# Write-PmcDebugLog "[$(Get-Date -Format 'HH:mm:ss.fff')] [Start-PmcTUI] DEBUG: InvocationName='$($MyInvocation.InvocationName)' MyCommand='$($MyInvocation.MyCommand.Name)'"
if ($MyInvocation.InvocationName -ne '.' -and $MyInvocation.InvocationName -ne '&') {
    Start-PmcTUI @args
}

using namespace System
using namespace System.Collections.Generic
using namespace System.Text

# StandardListScreen.ps1 - Base class for ALL list-based screens
#
# This is the FOUNDATION for every screen that shows a list of items:
# - TaskListScreen
# - ProjectListScreen
# - TimeLogListScreen
# - SearchResultsScreen
# - etc.
#
# Provides:
# - UniversalList integration (columns, sorting, filtering, search)
# - FilterPanel integration (dynamic filter builder)
# - InlineEditor integration (add/edit items)
# - TaskStore integration (automatic CRUD + events)
# - Keyboard navigation (arrows, PageUp/Down, Home/End)
# - Action handling (Add, Edit, Delete, custom actions)
# - Automatic UI refresh on data changes
#
# Usage:
#   class TaskListScreen : StandardListScreen {
#       TaskListScreen() : base("TaskList", "My Tasks") {
#           # Configuration in constructor
#       }
#
#       [void] LoadData() {
#           $tasks = $this.Store.GetAllTasks() | Where { -not $_.completed }
#           $this.List.SetData($tasks)
#       }
#
#       [array] GetColumns() {
#           return @(
#               @{ Name='priority'; Label='Pri'; Width=4 }
#               @{ Name='text'; Label='Task'; Width=40 }
#               @{ Name='due'; Label='Due'; Width=12 }
#           )
#       }
#
#       [array] GetEditFields($item) {
#           return @(
#               @{ Name='text'; Type='text'; Label='Task'; Value=$item.text; Required=$true }
#               @{ Name='due'; Type='date'; Label='Due'; Value=$item.due }
#               @{ Name='priority'; Type='number'; Label='Priority'; Value=$item.priority; Min=0; Max=5 }
#           )
#       }
#   }

Set-StrictMode -Version Latest

# Load dependencies
# NOTE: These are now loaded by the launcher script in the correct order.
# Commenting out to avoid circular dependency issues.
# $scriptDir = Split-Path -Parent $PSScriptRoot
# . "$scriptDir/PmcScreen.ps1"
# . "$scriptDir/widgets/UniversalList.ps1"
# . "$scriptDir/widgets/FilterPanel.ps1"
# . "$scriptDir/widgets/InlineEditor.ps1"
# . "$scriptDir/services/TaskStore.ps1"

<#
.SYNOPSIS
Base class for all list-based screens in PMC TUI

.DESCRIPTION
StandardListScreen provides a complete list-viewing experience with:
- Universal list widget with columns, sorting, filtering
- Filter panel for advanced filtering
- Inline editor for add/edit operations
- TaskStore integration for automatic CRUD
- Event-driven UI updates
- Keyboard-driven navigation
- Extensible via abstract methods

Abstract Methods (override in subclasses):
- LoadData() - Load data into list
- GetColumns() - Define column configuration
- GetEditFields($item) - Define edit form fields

Optional Overrides:
- OnItemSelected($item) - Handle item selection
- OnItemActivated($item) - Handle item activation (Enter key)
- GetCustomActions() - Add custom actions beyond Add/Edit/Delete
- GetEntityType() - Return 'task', 'project', or 'timelog' for store operations

.EXAMPLE
# Example: List screen implementation
# class MyListScreen : StandardListScreen {
#     MyListScreen() : base("MyList", "My Items") {}
#
#     [void] LoadData() {
#         $items = $this.Store.GetAllItems()
#         $this.List.SetData($items)
#     }
#
#     [array] GetColumns() {
#         return @(
#             @{ Name='name'; Label='Name'; Width=40 }
#             @{ Name='status'; Label='Status'; Width=12 }
#         )
#     }
#
#     [array] GetEditFields($item) {
#         return @(
#             @{ Name='name'; Type='text'; Label='Name'; Value=$item.name; Required=$true }
#         )
#     }
# }
#>
class StandardListScreen : PmcScreen {
    # === Core Components ===
    [UniversalList]$List = $null
    [FilterPanel]$FilterPanel = $null
    [InlineEditor]$InlineEditor = $null
    [TaskStore]$Store = $null

    # === Component State ===
    [bool]$ShowFilterPanel = $false
    [bool]$ShowInlineEditor = $false
    [string]$EditorMode = ""  # 'add' or 'edit'
    [object]$CurrentEditItem = $null
    hidden [bool]$_isHandlingInput = $false  # Re-entry guard for HandleKeyPress

    # === Configuration ===
    [bool]$AllowAdd = $true
    [bool]$AllowEdit = $true
    [bool]$AllowDelete = $true
    [bool]$AllowFilter = $true
    [bool]$AllowSearch = $true
    [bool]$AllowMultiSelect = $true

    # === Constructor (backward compatible - no container) ===
    StandardListScreen([string]$key, [string]$title) : base($key, $title) {
        # UniversalList has its own status and action footer, so disable the screen's StatusBar
        # FIX: Re-enable StatusBar for delete confirmation and messages
        # $this.StatusBar = $null

        # Initialize components
        $this._InitializeComponents()
    }

    # === Constructor (with ServiceContainer) ===
    StandardListScreen([string]$key, [string]$title, [object]$container) : base($key, $title, $container) {
        # UniversalList has its own status and action footer, so disable the screen's StatusBar
        # FIX: Re-enable StatusBar for delete confirmation and messages
        # $this.StatusBar = $null

        # Initialize components
        $this._InitializeComponents()
    }

    # === Initialization ===

    <#
    .SYNOPSIS
    Initialize screen with render engine and load initial data
    #>
    [void] Initialize([object]$renderEngine) {

        # Call base class initialization
        ([PmcScreen]$this).Initialize($renderEngine)

        # Load data into the list
        $this.LoadData()

        $this.RefreshList()

    }

    # === Abstract Methods (MUST override) ===

    # === Layout Methods ===

    [void] Resize([int]$width, [int]$height) {
        # Set dimensions first
        $this.TermWidth = $width
        $this.TermHeight = $height

        # Ensure LayoutManager exists
        if (-not $this.LayoutManager) {
            $this.LayoutManager = [PmcLayoutManager]::new()
            $this.LayoutManager.DefineRegion('ListContent', 0, 6, '100%', 'FILL')
        }

        # Apply layout to Header, Footer, MenuBar, StatusBar via base
        if ($this.MenuBar) {
            $rect = $this.LayoutManager.GetRegion('MenuBar', $width, $height)
            $this.MenuBar.SetPosition($rect.X, $rect.Y)
            $this.MenuBar.SetSize($rect.Width, $rect.Height)
        }
        if ($this.Header) {
            $rect = $this.LayoutManager.GetRegion('Header', $width, $height)
            $this.Header.SetPosition($rect.X, $rect.Y)
            $this.Header.SetSize($rect.Width, $rect.Height)
        }
        if ($this.Footer) {
            $rect = $this.LayoutManager.GetRegion('Footer', $width, $height)
            $this.Footer.SetPosition($rect.X, $rect.Y)
            $this.Footer.SetSize($rect.Width, $rect.Height)
        }
        if ($this.StatusBar) {
            $rect = $this.LayoutManager.GetRegion('StatusBar', $width, $height)
            $this.StatusBar.SetPosition($rect.X, $rect.Y)
            $this.StatusBar.SetSize($rect.Width, $rect.Height)
        }

        # CRITICAL FIX: Let subclasses apply their own content layout AFTER chrome
        # Subclasses like TaskListScreen override ApplyContentLayout for 70/30 split
        $this.ApplyContentLayout($this.LayoutManager, $width, $height)

        # Resize overlays
        if ($this.FilterPanel) {
            $this.FilterPanel.SetPosition([Math]::Max(0, [Math]::Floor(($width - 80) / 2)), 5)
        }

        # InlineEditor resizes itself based on active row usually
    }

    # Default content layout - subclasses override for custom layouts like 70/30 split
    [void] ApplyContentLayout([PmcLayoutManager]$layoutManager, [int]$termWidth, [int]$termHeight) {
        # Full-width list for standard list screens
        if ($this.List) {
            try {
                $contentRect = $layoutManager.GetRegion('ListContent', $termWidth, $termHeight)
            } catch {
                # Fallback: define the region now
                $layoutManager.DefineRegion('ListContent', 0, 6, '100%', 'FILL')
                $contentRect = $layoutManager.GetRegion('ListContent', $termWidth, $termHeight)
            }
            $this.List.SetPosition($contentRect.X, $contentRect.Y)
            $this.List.SetSize($contentRect.Width, $contentRect.Height)
        }
    }

    # === Rendering ===

    <#
    .SYNOPSIS
    Render screen components to the engine
    #>
    [void] RenderToEngine([object]$engine) {

        # 1. Render base chrome (Menu, Header, Footer, etc.)
        ([PmcScreen]$this).RenderToEngine($engine)

        # 2. Render List (Main Content)
        if ($this.List) {
            # Ensure render mode is correct
            $this.List.RenderToEngine($engine)
        }

        # 3. Render Overlays (Z-ordered on top)
        if ($this.ShowFilterPanel -and $this.FilterPanel) {
            $this.FilterPanel.RenderToEngine($engine)
        }

        if ($this.ShowInlineEditor -and $this.InlineEditor) {
            # CRITICAL FIX: Position editor over the current row
            if ($this.List) {
                $selIndex = $this.List.GetSelectedIndex()
                $scrollOffset = $this.List.GetScrollOffset()
                $relativeIndex = $selIndex - $scrollOffset

                # Check if visible
                if ($relativeIndex -ge 0 -and $relativeIndex -lt ($this.List.Height - 4)) {
                    # List Y + TopBorder(1) + Header(1) + Separator(1) = Y+3 ??
                    # UniversalList render: Header=Y, Sep=Y+1, Row0=Y+2.
                    # Wait, if Title is empty:
                    # UniversalList (fixed layout):
                    # Y   : Top Border
                    # Y+1 : Header Labels
                    # Y+2 : Separator
                    # Y+3 : Row 0
                    # So offset should be +3.
                    $editY = $this.List.Y + 3 + $relativeIndex

                    # Update Editor Geometry to match row
                    $this.InlineEditor.X = $this.List.X + 2  # Inside border
                    $this.InlineEditor.Y = $editY
                    $this.InlineEditor.Width = $this.List.Width - 4 # Inside borders
                    $this.InlineEditor.Height = 1 # Single row mode

                    # Force layout recalc
                    # $this.InlineEditor.Resize(...) isn't needed if we set props directly 
                }
            }
            $this.InlineEditor.RenderToEngine($engine)
        }
    }

