function Get-PmcHelpData {
    param()
    $helpCategories = @()
    if ($Script:PmcHelpContent -and $Script:PmcHelpContent.Count -gt 0) {
        $id = 1
        foreach ($categoryEntry in $Script:PmcHelpContent.GetEnumerator()) {
            $helpCategories += [PSCustomObject]@{
                id = $id++
                Category = $categoryEntry.Key
                CommandCount = $categoryEntry.Value.Items.Count
                Description = $categoryEntry.Value.Description
            }
        }
    }
    return $helpCategories
}

﻿Set-StrictMode -Version Latest

function ConvertTo-PmcProjectObject {
    param(
        [Parameter(Mandatory=$true)]
        [object]$Project,
        [Parameter(Mandatory=$true)]
        [ref]$DataArray,
        [Parameter(Mandatory=$true)]
        [int]$Index
    )

    if ($Project -is [string]) {
        $newProject = [pscustomobject]@{
            name = $Project
        }
        $DataArray.Value[$Index] = $newProject
        return $newProject
    } else {
        return $Project
    }
}

﻿# DataBindingHelper.ps1 - Helper functions to bind PMC data to widgets
#
# Provides conversion functions between PMC data structures and widget field configurations:
# - PMC field schema → InlineEditor field definitions
# - Widget values → PMC data format
# - Type conversions (string, int, datetime, array)
# - Default value handling
#
# Usage:
#   # Convert PMC task to widget fields
#   $fields = ConvertTo-WidgetFields -Entity $task -EntityType 'task'
#
#   # Convert widget values back to PMC format
#   $pmcTask = ConvertFrom-WidgetValues -Values $widgetValues -EntityType 'task'
#
#   # Get field definitions for entity type
#   $fieldDefs = Get-EntityFieldDefinitions -EntityType 'task'

using namespace System
using namespace System.Collections.Generic

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Convert PMC entity to widget field definitions

.PARAMETER Entity
Entity hashtable (task, project, timelog)

.PARAMETER EntityType
Entity type: 'task', 'project', 'timelog'

.PARAMETER FieldSchema
Optional custom field schema (uses default if not provided)

.OUTPUTS
Array of field definition hashtables for InlineEditor
#>
function ConvertTo-WidgetFields {
    param(
        [Parameter(Mandatory=$true)]
        [hashtable]$Entity,

        [Parameter(Mandatory=$true)]
        [ValidateSet('task', 'project', 'timelog', 'custom')]
        [string]$EntityType,

        [Parameter(Mandatory=$false)]
        [hashtable[]]$FieldSchema = $null
    )

    # Use default schema if not provided
    if ($null -eq $FieldSchema) {
        $FieldSchema = Get-EntityFieldDefinitions -EntityType $EntityType
    }

    $widgetFields = @()

    foreach ($fieldDef in $FieldSchema) {
        $fieldName = $fieldDef.Name
        $fieldType = $fieldDef.Type
        $fieldLabel = $(if ($fieldDef.ContainsKey('Label')) { $fieldDef.Label } else { $fieldName })

        # Get current value from entity
        $value = $(if ($Entity.ContainsKey($fieldName)) { $Entity[$fieldName] } else { $null })

        # Build widget field definition
        $widgetField = @{
            Name = $fieldName
            Label = $fieldLabel
            Type = $fieldType
            Value = $value
        }

        # Add optional properties
        if ($fieldDef.ContainsKey('Required')) {
            $widgetField.Required = $fieldDef.Required
        }

        if ($fieldDef.ContainsKey('Min')) {
            $widgetField.Min = $fieldDef.Min
        }

        if ($fieldDef.ContainsKey('Max')) {
            $widgetField.Max = $fieldDef.Max
        }

        if ($fieldDef.ContainsKey('MaxLength')) {
            $widgetField.MaxLength = $fieldDef.MaxLength
        }

        if ($fieldDef.ContainsKey('Placeholder')) {
            $widgetField.Placeholder = $fieldDef.Placeholder
        }

        if ($fieldDef.ContainsKey('Options')) {
            $widgetField.Options = $fieldDef.Options
        }

        $widgetFields += $widgetField
    }

    return $widgetFields
}

<#
.SYNOPSIS
Convert widget values back to PMC entity format

.PARAMETER Values
Hashtable of widget values

.PARAMETER EntityType
Entity type: 'task', 'project', 'timelog'

.PARAMETER FieldSchema
Optional custom field schema (uses default if not provided)

.OUTPUTS
Hashtable in PMC entity format
#>
function ConvertFrom-WidgetValues {
    param(
        [Parameter(Mandatory=$true)]
        [hashtable]$Values,

        [Parameter(Mandatory=$true)]
        [ValidateSet('task', 'project', 'timelog', 'custom')]
        [string]$EntityType,

        [Parameter(Mandatory=$false)]
        [hashtable[]]$FieldSchema = $null
    )

    # Use default schema if not provided
    if ($null -eq $FieldSchema) {
        $FieldSchema = Get-EntityFieldDefinitions -EntityType $EntityType
    }

    $pmcEntity = @{}

    foreach ($fieldDef in $FieldSchema) {
        $fieldName = $fieldDef.Name
        $fieldType = $fieldDef.Type

        if ($Values.ContainsKey($fieldName)) {
            $value = $Values[$fieldName]

            # Type conversion
            $convertedValue = ConvertTo-PmcType -Value $value -TargetType $fieldType

            $pmcEntity[$fieldName] = $convertedValue
        }
    }

    return $pmcEntity
}

<#
.SYNOPSIS
Convert value to PMC type

.PARAMETER Value
Value to convert

.PARAMETER TargetType
Target PMC type: 'string', 'int', 'bool', 'datetime', 'array'

.OUTPUTS
Converted value
#>
function ConvertTo-PmcType {
    param(
        [Parameter(Mandatory=$true)]
        $Value,

        [Parameter(Mandatory=$true)]
        [string]$TargetType
    )

    if ($null -eq $Value) {
        return $null
    }

    switch ($TargetType) {
        'string' {
            return $Value.ToString()
        }
        'text' {
            return $Value.ToString()
        }
        'int' {
            return [int]$Value
        }
        'number' {
            return [int]$Value
        }
        'bool' {
            return [bool]$Value
        }
        'datetime' {
            if ($Value -is [DateTime]) {
                return $Value
            }
            return [DateTime]::Parse($Value.ToString())
        }
        'date' {
            if ($Value -is [DateTime]) {
                return $Value
            }
            return [DateTime]::Parse($Value.ToString())
        }
        'array' {
            if ($Value -is [array]) {
                return $Value
            }
            return @($Value)
        }
        'tags' {
            if ($Value -is [array]) {
                return $Value
            }
            return @($Value)
        }
        default {
            return $Value
        }
    }
}

<#
.SYNOPSIS
Get default field definitions for entity type

.PARAMETER EntityType
Entity type: 'task', 'project', 'timelog'

.OUTPUTS
Array of field definition hashtables
#>
function Get-EntityFieldDefinitions {
    param(
        [Parameter(Mandatory=$true)]
        [ValidateSet('task', 'project', 'timelog')]
        [string]$EntityType
    )

    switch ($EntityType) {
        'task' {
            return @(
                @{
                    Name = 'text'
                    Label = 'Task'
                    Type = 'text'
                    Required = $true
                    MaxLength = 500
                    Placeholder = 'What needs to be done?'
                }
                @{
                    Name = 'project'
                    Label = 'Project'
                    Type = 'project'
                    Required = $false
                }
                @{
                    Name = 'priority'
                    Label = 'Priority'
                    Type = 'number'
                    Required = $false
                    Min = 0
                    Max = 5
                    Value = 3
                }
                @{
                    Name = 'due'
                    Label = 'Due Date'
                    Type = 'date'
                    Required = $false
                }
                @{
                    Name = 'tags'
                    Label = 'Tags'
                    Type = 'tags'
                    Required = $false
                }
                @{
                    Name = 'notes'
                    Label = 'Notes'
                    Type = 'text'
                    Required = $false
                    MaxLength = 2000
                    Placeholder = 'Additional notes...'
                }
                @{
                    Name = 'completed'
                    Label = 'Completed'
                    Type = 'bool'
                    Required = $false
                    Value = $false
                }
            )
        }

        'project' {
            return @(
                @{
                    Name = 'name'
                    Label = 'Project Name'
                    Type = 'text'
                    Required = $true
                    MaxLength = 100
                    Placeholder = 'Project name'
                }
                @{
                    Name = 'description'
                    Label = 'Description'
                    Type = 'text'
                    Required = $false
                    MaxLength = 1000
                    Placeholder = 'Project description...'
                }
                @{
                    Name = 'status'
                    Label = 'Status'
                    Type = 'text'
                    Required = $false
                    Options = @('active', 'completed', 'archived', 'on-hold')
                    Value = 'active'
                }
                @{
                    Name = 'tags'
                    Label = 'Tags'
                    Type = 'tags'
                    Required = $false
                }
            )
        }

        'timelog' {
            return @(
                @{
                    Name = 'taskId'
                    Label = 'Task ID'
                    Type = 'text'
                    Required = $true
                }
                @{
                    Name = 'duration'
                    Label = 'Duration (minutes)'
                    Type = 'number'
                    Required = $true
                    Min = 1
                    Max = 1440
                    Value = 30
                }
                @{
                    Name = 'timestamp'
                    Label = 'Timestamp'
                    Type = 'datetime'
                    Required = $false
                    Value = (Get-Date)
                }
                @{
                    Name = 'notes'
                    Label = 'Notes'
                    Type = 'text'
                    Required = $false
                    MaxLength = 500
                }
            )
        }

        default {
            return @()
        }
    }
}

<#
.SYNOPSIS
Merge field definitions with entity values

.PARAMETER FieldDefinitions
Array of field definition hashtables

.PARAMETER Entity
Entity hashtable with values

.OUTPUTS
Array of field definitions with values populated
#>
function Merge-FieldDefinitionsWithEntity {
    param(
        [Parameter(Mandatory=$true)]
        [hashtable[]]$FieldDefinitions,

        [Parameter(Mandatory=$true)]
        [hashtable]$Entity
    )

    $mergedFields = @()

    foreach ($fieldDef in $FieldDefinitions) {
        $mergedField = $fieldDef.Clone()
        $fieldName = $mergedField.Name

        if ($Entity.ContainsKey($fieldName)) {
            $mergedField.Value = $Entity[$fieldName]
        }

        $mergedFields += $mergedField
    }

    return $mergedFields
}

<#
.SYNOPSIS
Get display value for a field

.PARAMETER Value
Field value

.PARAMETER FieldType
Field type

.OUTPUTS
Formatted display string
#>
function Get-FieldDisplayValue {
    param(
        [Parameter(Mandatory=$true)]
        $Value,

        [Parameter(Mandatory=$true)]
        [string]$FieldType
    )

    if ($null -eq $Value) {
        return "(empty)"
    }

    switch ($FieldType) {
        'date' {
            if ($Value -is [DateTime]) {
                return $Value.ToString("yyyy-MM-dd (ddd)")
            }
            return $Value.ToString()
        }
        'datetime' {
            if ($Value -is [DateTime]) {
                return $Value.ToString("yyyy-MM-dd HH:mm")
            }
            return $Value.ToString()
        }
        'bool' {
            return $(if ($Value) { "Yes" } else { "No" })
        }
        'tags' {
            if ($Value -is [array]) {
                if ($Value.Count -eq 0) {
                    return "(no tags)"
                }
                return "[" + ($Value -join "] [") + "]"
            }
            return $Value.ToString()
        }
        'array' {
            if ($Value -is [array]) {
                if ($Value.Count -eq 0) {
                    return "(empty array)"
                }
                return ($Value -join ", ")
            }
            return $Value.ToString()
        }
        default {
            return $Value.ToString()
        }
    }
}

<#
.SYNOPSIS
Validate field value against field definition

.PARAMETER Value
Field value

.PARAMETER FieldDefinition
Field definition hashtable

.OUTPUTS
Array of validation error messages (empty if valid)
#>
function Test-FieldValue {
    param(
        [Parameter(Mandatory=$true)]
        $Value,

        [Parameter(Mandatory=$true)]
        [hashtable]$FieldDefinition
    )

    $errors = @()
    $fieldName = $FieldDefinition.Name
    $fieldType = $FieldDefinition.Type

    # Required field check
    if ($FieldDefinition.ContainsKey('Required') -and $FieldDefinition.Required) {
        if ($null -eq $Value -or [string]::IsNullOrWhiteSpace($Value.ToString())) {
            $errors += "$fieldName is required"
            return $errors
        }
    }

    # Skip remaining checks if value is null/empty
    if ($null -eq $Value) {
        return $errors
    }

    # Type-specific validation
    switch ($fieldType) {
        'number' {
            if ($FieldDefinition.ContainsKey('Min') -and $Value -lt $FieldDefinition.Min) {
                $errors += "$fieldName must be >= $($FieldDefinition.Min)"
            }
            if ($FieldDefinition.ContainsKey('Max') -and $Value -gt $FieldDefinition.Max) {
                $errors += "$fieldName must be <= $($FieldDefinition.Max)"
            }
        }
        'text' {
            $strValue = $Value.ToString()
            if ($FieldDefinition.ContainsKey('MaxLength') -and $strValue.Length -gt $FieldDefinition.MaxLength) {
                $errors += "$fieldName must be <= $($FieldDefinition.MaxLength) characters"
            }
        }
    }

    return $errors
}

<#
.SYNOPSIS
Create UniversalList column definitions from field definitions

.PARAMETER FieldDefinitions
Array of field definition hashtables

.PARAMETER ColumnWidths
Optional hashtable of column widths (fieldName -> width)

.OUTPUTS
Array of column definition hashtables for UniversalList
#>
function ConvertTo-ListColumns {
    param(
        [Parameter(Mandatory=$true)]
        [hashtable[]]$FieldDefinitions,

        [Parameter(Mandatory=$false)]
        [hashtable]$ColumnWidths = @{}
    )

    $columns = @()

    foreach ($fieldDef in $FieldDefinitions) {
        $fieldName = $fieldDef.Name
        $fieldLabel = $(if ($fieldDef.ContainsKey('Label')) { $fieldDef.Label } else { $fieldName })
        $fieldType = $fieldDef.Type

        # Determine default width based on type
        $defaultWidth = switch ($fieldType) {
            'text' { 40 }
            'number' { 6 }
            'date' { 12 }
            'datetime' { 18 }
            'bool' { 8 }
            'tags' { 30 }
            'project' { 15 }
            default { 20 }
        }

        $width = $(if ($ColumnWidths.ContainsKey($fieldName)) { $ColumnWidths[$fieldName] } else { $defaultWidth })

        # Create column definition
        $column = @{
            Name = $fieldName
            Label = $fieldLabel
            Width = $width
            Align = 'left'
        }

        # Add formatter for specific types
        if ($fieldType -eq 'date' -or $fieldType -eq 'datetime') {
            $column.Format = {
                param($value)
                if ($null -ne $value -and $value -is [DateTime]) {
                    if ($fieldType -eq 'date') {
                        return $value.ToString("MMM dd yyyy")
                    } else {
                        return $value.ToString("MMM dd HH:mm")
                    }
                }
                return ""
            }
        }
        elseif ($fieldType -eq 'bool') {
            $column.Format = {
                param($value)
                return $(if ($value) { "[OK]" } else { "" })
            }
        }
        elseif ($fieldType -eq 'tags' -or $fieldType -eq 'array') {
            $column.Format = {
                param($value)
                if ($value -is [array]) {
                    return ($value -join ", ")
                }
                return ""
            }
        }

        $columns += $column
    }

    return $columns
}

# Export functions
Export-ModuleMember -Function @(
    'ConvertTo-WidgetFields',
    'ConvertFrom-WidgetValues',
    'ConvertTo-PmcType',
    'Get-EntityFieldDefinitions',
    'Merge-FieldDefinitionsWithEntity',
    'Get-FieldDisplayValue',
    'Test-FieldValue',
    'ConvertTo-ListColumns'
)

﻿# GapBuffer.ps1 - High-performance text buffer for editing operations
# Uses a gap buffer data structure for optimal performance on typical editing patterns
# Ported from Praxis to PMC ConsoleUI

Set-StrictMode -Version Latest

# Compile C# helper for high-performance buffer operations
try {
    [void][GapBufferUtils]
} catch {
    $csharpCode = @"
using System;
using System.Collections.Generic;

public class GapBufferUtils {
    public static int[] FindAll(char[] buffer, int gapStart, int gapEnd, char target) {
        List<int> indices = new List<int>();
        int gapSize = gapEnd - gapStart;

        // Search before gap
        for (int i = 0; i < gapStart; i++) {
            if (buffer[i] == target) {
                indices.Add(i);
            }
        }

        // Search after gap
        for (int i = gapEnd; i < buffer.Length; i++) {
            if (buffer[i] == target) {
                indices.Add(i - gapSize);
            }
        }

        return indices.ToArray();
    }

    public static int[] GetStatistics(char[] buffer, int gapStart, int gapEnd) {
        int lines = 1;
        int words = 0;
        int chars = 0;
        bool inWord = false;

        // Helper to process char
        Action<char> process = (c) => {
            chars++;
            if (c == '\n') lines++;
            if (char.IsWhiteSpace(c)) {
                inWord = false;
            } else if (!inWord) {
                words++;
                inWord = true;
            }
        };

        for (int i = 0; i < gapStart; i++) process(buffer[i]);
        for (int i = gapEnd; i < buffer.Length; i++) process(buffer[i]);

        return new int[] { lines, words, chars };
    }
}
"@
    Add-Type -TypeDefinition $csharpCode -Language CSharp
}

class GapBuffer {
    # Internal buffer with gap
    hidden [char[]]$_buffer
    hidden [int]$_gapStart
    hidden [int]$_gapEnd
    hidden [int]$_capacity

    # Buffer growth parameters
    hidden [int]$_initialCapacity = 1024
    hidden [double]$_growthFactor = 1.5
    hidden [int]$_minGapSize = 128

    # Statistics for debugging/optimization
    [int]$InsertCount = 0
    [int]$DeleteCount = 0
    [int]$MoveCount = 0
    [int]$GrowCount = 0

    GapBuffer() {
        $this._capacity = $this._initialCapacity
        $this._buffer = [char[]]::new($this._capacity)
        $this._gapStart = 0
        $this._gapEnd = $this._capacity
    }

    GapBuffer([int]$initialCapacity) {
        $this._capacity = [Math]::Max($initialCapacity, $this._minGapSize)
        $this._buffer = [char[]]::new($this._capacity)
        $this._gapStart = 0
        $this._gapEnd = $this._capacity
    }

    GapBuffer([string]$text) {
        $textLength = $text.Length
        $this._capacity = [Math]::Max($textLength + $this._minGapSize, $this._initialCapacity)
        $this._buffer = [char[]]::new($this._capacity)

        # Copy text to buffer
        if ($textLength -gt 0) {
            [array]::Copy($text.ToCharArray(), 0, $this._buffer, 0, $textLength)
        }

        $this._gapStart = $textLength
        $this._gapEnd = $this._capacity
    }

    # --- Public Properties ---

    [int] GetLength() {
        return $this._capacity - ($this._gapEnd - $this._gapStart)
    }

    [int] GetCapacity() {
        return $this._capacity
    }

    [int] GetGapSize() {
        return $this._gapEnd - $this._gapStart
    }

    # --- Core Operations ---

    [void] MoveGapTo([int]$position) {
        if ($position -lt 0 -or $position -gt $this.GetLength()) {
            throw "Position $position is out of range (0-$($this.GetLength()))"
        }

        if ($position -eq $this._gapStart) {
            return  # Gap is already at the correct position
        }

        $this.MoveCount++

        if ($position -lt $this._gapStart) {
            # Move gap left - shift text right
            $moveSize = $this._gapStart - $position
            $sourceStart = $position
            $destStart = $this._gapEnd - $moveSize

            # Validate destination index
            if ($destStart -lt 0 -or ($destStart + $moveSize) -gt $this._buffer.Length) {
                throw "Invalid gap buffer state: destStart=$destStart, moveSize=$moveSize, bufferLength=$($this._buffer.Length)"
            }

            [array]::Copy($this._buffer, $sourceStart, $this._buffer, $destStart, $moveSize)

            $this._gapStart = $position
            $this._gapEnd -= $moveSize
        } else {
            # Move gap right - shift text left
            $moveSize = $position - $this._gapStart
            $sourceStart = $this._gapEnd
            $destStart = $this._gapStart

            [array]::Copy($this._buffer, $sourceStart, $this._buffer, $destStart, $moveSize)

            $this._gapStart = $position
            $this._gapEnd += $moveSize
        }
    }

    [void] EnsureGapSize([int]$minSize) {
        $currentGapSize = $this._gapEnd - $this._gapStart
        if ($currentGapSize -ge $minSize) {
            return  # Gap is already large enough
        }

        # Calculate new capacity
        $currentLength = $this.GetLength()
        $neededCapacity = $currentLength + $minSize
        $newCapacity = [Math]::Max([int]($this._capacity * $this._growthFactor), $neededCapacity)

        $this.GrowCount++

        # Create new buffer
        $newBuffer = [char[]]::new($newCapacity)

        # Copy text before gap
        if ($this._gapStart -gt 0) {
            [array]::Copy($this._buffer, 0, $newBuffer, 0, $this._gapStart)
        }

        # Copy text after gap
        $textAfterGap = $this._capacity - $this._gapEnd
        if ($textAfterGap -gt 0) {
            $newGapEnd = $newCapacity - $textAfterGap
            [array]::Copy($this._buffer, $this._gapEnd, $newBuffer, $newGapEnd, $textAfterGap)
            $this._gapEnd = $newGapEnd
        } else {
            $this._gapEnd = $newCapacity
        }

        $this._buffer = $newBuffer
        $this._capacity = $newCapacity
    }

    # --- Text Operations ---

    [void] Insert([int]$position, [char]$char) {
        $this.MoveGapTo($position)
        $this.EnsureGapSize(1)

        $this._buffer[$this._gapStart] = $char
        $this._gapStart++
        $this.InsertCount++
    }

    [void] Insert([int]$position, [string]$text) {
        if ([string]::IsNullOrEmpty($text)) {
            return
        }

        $this.MoveGapTo($position)
        $this.EnsureGapSize($text.Length)

        $chars = $text.ToCharArray()
        [array]::Copy($chars, 0, $this._buffer, $this._gapStart, $chars.Length)
        $this._gapStart += $chars.Length
        $this.InsertCount++
    }

    [void] Delete([int]$position, [int]$count = 1) {
        if ($count -le 0) {
            return
        }

        $length = $this.GetLength()
        if ($position -lt 0 -or $position -ge $length) {
            return  # Position out of bounds
        }

        # Clamp count to available characters
        $count = [Math]::Min($count, $length - $position)
        if ($count -le 0) {
            return
        }

        $this.MoveGapTo($position)

        # Expand gap to include deleted characters
        $this._gapEnd += $count
        $this.DeleteCount++
    }

    [char] GetChar([int]$position) {
        $length = $this.GetLength()
        if ($position -lt 0 -or $position -ge $length) {
            return [char]0  # Return null character for out of bounds
        }

        if ($position -lt $this._gapStart) {
            return $this._buffer[$position]
        } else {
            return $this._buffer[$position + ($this._gapEnd - $this._gapStart)]
        }
    }

    [string] GetText([int]$start, [int]$count) {
        $length = $this.GetLength()
        if ($start -lt 0 -or $start -ge $length -or $count -le 0) {
            return ""
        }

        # Clamp count to available characters
        $count = [Math]::Min($count, $length - $start)
        $chars = [char[]]::new($count)

        # Optimize: Use Array.Copy instead of loop
        if ($start -lt $this._gapStart) {
            # Starts before gap
            $firstChunkLen = [Math]::Min($count, $this._gapStart - $start)
            [array]::Copy($this._buffer, $start, $chars, 0, $firstChunkLen)

            if ($firstChunkLen -lt $count) {
                # Spans across gap
                $secondChunkLen = $count - $firstChunkLen
                [array]::Copy($this._buffer, $this._gapEnd, $chars, $firstChunkLen, $secondChunkLen)
            }
        } else {
            # Starts after gap
            $bufferStart = $start + ($this._gapEnd - $this._gapStart)
            [array]::Copy($this._buffer, $bufferStart, $chars, 0, $count)
        }

        return [string]::new($chars)
    }

    [string] GetText() {
        return $this.GetText(0, $this.GetLength())
    }

    # Optimized search for all occurrences of a character
    # Returns an array of indices
    [int[]] FindAll([char]$char) {
        # Use reflection to avoid parse-time dependency on GapBufferUtils
        # which is defined via Add-Type in this same file
        $type = "GapBufferUtils" -as [type]
        if ($null -eq $type) { throw "GapBufferUtils type not found" }
        return $type::FindAll($this._buffer, $this._gapStart, $this._gapEnd, $char)
    }

    [string] GetSubstring([int]$start, [int]$length) {
        return $this.GetText($start, $length)
    }

    # --- Advanced Operations ---

    [void] Clear() {
        $this._gapStart = 0
        $this._gapEnd = $this._capacity

        # Optional: Clear the buffer for security
        [array]::Clear($this._buffer, 0, $this._capacity)
    }

    [void] SetText([string]$text) {
        $this.Clear()
        if (-not [string]::IsNullOrEmpty($text)) {
            $this.Insert(0, $text)
        }
    }

    [int] IndexOf([char]$char, [int]$startIndex = 0) {
        $length = $this.GetLength()
        for ($i = $startIndex; $i -lt $length; $i++) {
            if ($this.GetChar($i) -eq $char) {
                return $i
            }
        }
        return -1
    }

    [int] IndexOf([string]$text, [int]$startIndex = 0) {
        if ([string]::IsNullOrEmpty($text)) {
            return -1
        }

        $length = $this.GetLength()
        $textLength = $text.Length

        for ($i = $startIndex; $i -le $length - $textLength; $i++) {
            $match = $true
            for ($j = 0; $j -lt $textLength; $j++) {
                if ($this.GetChar($i + $j) -ne $text[$j]) {
                    $match = $false
                    break
                }
            }
            if ($match) {
                return $i
            }
        }
        return -1
    }

    [int] LastIndexOf([char]$char, [int]$startIndex = -1) {
        $length = $this.GetLength()
        if ($startIndex -eq -1) {
            $startIndex = $length - 1
        }

        for ($i = $startIndex; $i -ge 0; $i--) {
            if ($this.GetChar($i) -eq $char) {
                return $i
            }
        }
        return -1
    }

    # --- Debugging and Statistics ---

    [hashtable] GetStatistics() {
        return @{
            Length = $this.GetLength()
            Capacity = $this._capacity
            GapSize = $this.GetGapSize()
            GapStart = $this._gapStart
            GapEnd = $this._gapEnd
            InsertCount = $this.InsertCount
            DeleteCount = $this.DeleteCount
            MoveCount = $this.MoveCount
            GrowCount = $this.GrowCount
            Efficiency = $(if ($this.MoveCount -gt 0) {
                [Math]::Round(($this.InsertCount + $this.DeleteCount) / [double]$this.MoveCount, 2)
            } else {
                "N/A"
            })
        }
    }

    [hashtable] GetContentStatistics() {
        $type = "GapBufferUtils" -as [type]
        if ($null -ne $type) {
            $stats = $type::GetStatistics($this._buffer, $this._gapStart, $this._gapEnd)
            return @{
                Lines = $stats[0]
                Words = $stats[1]
                Chars = $stats[2]
            }
        }

        # Fallback if C# type not available (should not happen)
        return @{
            Lines = 0
            Words = 0
            Chars = 0
        }
    }

    [void] ResetStatistics() {
        $this.InsertCount = 0
        $this.DeleteCount = 0
        $this.MoveCount = 0
        $this.GrowCount = 0
    }

    [string] ToString() {
        return $this.GetText()
    }

    # --- Validation (for debugging) ---

    [bool] ValidateStructure() {
        if ($this._gapStart -lt 0 -or $this._gapStart -gt $this._capacity) {
            return $false
        }
        if ($this._gapEnd -lt $this._gapStart -or $this._gapEnd -gt $this._capacity) {
            return $false
        }
        if ($this._capacity -le 0) {
            return $false
        }
        return $true
    }
}

﻿# LinuxKeyHelper.ps1 - Handles escape sequence parsing for Ctrl+Arrow keys on Linux
# Workaround for Console.ReadKey not properly detecting Ctrl+Arrow on Linux terminals

Set-StrictMode -Version Latest

class LinuxKeyHelper {
    # Parse escape sequence for Ctrl+Arrow keys
    # Returns: "Ctrl+Up", "Ctrl+Down", "Ctrl+Left", "Ctrl+Right", or $null
    static [string] ParseCtrlArrow([ConsoleKeyInfo]$keyInfo) {
        # First check if ReadKey properly detected it (works on some terminals)
        $ctrl = $keyInfo.Modifiers -band [ConsoleModifiers]::Control
        if ($ctrl) {
            switch ($keyInfo.Key) {
                ([ConsoleKey]::UpArrow) { return "Ctrl+Up" }
                ([ConsoleKey]::DownArrow) { return "Ctrl+Down" }
                ([ConsoleKey]::LeftArrow) { return "Ctrl+Left" }
                ([ConsoleKey]::RightArrow) { return "Ctrl+Right" }
            }
        }

        # Linux workaround: Check if this is ESC (start of escape sequence)
        # Ctrl+Arrow sends: ESC [  1 ; 5 A/B/C/D
        # Where A=Up, B=Down, C=Right, D=Left, and 5=Ctrl modifier
        if ($keyInfo.Key -eq [ConsoleKey]::Escape) {
            # Need to read the rest of the sequence
            # This is tricky because ReadKey already consumed the ESC
            # We need to check if more keys are available immediately
            if ([Console]::KeyAvailable) {
                $next1 = [Console]::ReadKey($true)
                if ($next1.KeyChar -eq '[' -and [Console]::KeyAvailable) {
                    # Read the sequence: could be "1;5A" or just "A"
                    $sequence = ""
                    while ([Console]::KeyAvailable) {
                        $ch = [Console]::ReadKey($true)
                        $sequence += $ch.KeyChar
                        # Stop at letter (A/B/C/D)
                        if ($ch.KeyChar -match '[A-Z]') {
                            break
                        }
                    }

                    # Parse the sequence
                    # Format: "1;5A" = Ctrl+Up
                    # Format: "1;5B" = Ctrl+Down
                    # Format: "1;5C" = Ctrl+Right
                    # Format: "1;5D" = Ctrl+Left
                    if ($sequence -match '1;5([ABCD])') {
                        switch ($Matches[1]) {
                            'A' { return "Ctrl+Up" }
                            'B' { return "Ctrl+Down" }
                            'C' { return "Ctrl+Right" }
                            'D' { return "Ctrl+Left" }
                        }
                    }
                }
            }
        }

        return $null
    }

    # Check if a key is a Ctrl+Arrow and return the direction
    static [string] DetectCtrlArrow([ConsoleKeyInfo]$keyInfo) {
        return [LinuxKeyHelper]::ParseCtrlArrow($keyInfo)
    }
}

﻿# ThemeHelper.ps1 - Hot reload support for theme changes
# Allows themes to be changed without restarting the TUI

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Hot reload the theme system after a theme change

.DESCRIPTION
Reloads the theme engine with new colors from the palette,
invalidates caches, and forces a full screen redraw to apply
the new theme immediately without restarting the TUI.

.PARAMETER hexColor
Optional hex color to reload. If not provided, uses current theme from config.

.EXAMPLE
Invoke-ThemeHotReload "#33cc66"

.EXAMPLE
Invoke-ThemeHotReload  # Reload current theme
#>
function Invoke-ThemeHotReload {
    param(
        [string]$themeName = $null
    )

    try {
        if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
            Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [ThemeHotReload] Starting hot reload for theme: '$themeName'"
        }

        # 1. If theme name provided, save it to config
        if (-not [string]::IsNullOrEmpty($themeName)) {
            Set-ActiveTheme -themeName $themeName
            if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
                Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [ThemeHotReload] Set-ActiveTheme completed"
            }
        }

        # 2. Reinitialize PMC theme system to update state
        Initialize-PmcThemeSystem -Force
        if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
            Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [ThemeHotReload] Initialize-PmcThemeSystem -Force completed"
        }

        # 3. Reload PmcThemeManager (which loads from theme file)
        $themeManager = [PmcThemeManager]::GetInstance()
        $themeManager.Reload()
        if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
            Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [ThemeHotReload] PmcThemeManager.Reload completed"
        }

        # 4. Force full screen refresh if app is running
        if ($global:PmcApp) {
            # Request clear to invalidate render buffer
            $global:PmcApp.RenderEngine.RequestClear()

            # Mark current screen dirty to force redraw
            if ($global:PmcApp.CurrentScreen) {
                $global:PmcApp.CurrentScreen.NeedsClear = $true
            }

            # Request render on next frame
            $global:PmcApp.RequestRender()
        }

        return $true

    } catch {
        if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
            Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [ThemeHotReload] ERROR: $_"
        }
        return $false
    }
}

﻿# ValidationHelper.ps1 - Centralized Validation for PMC Entities
#
# Provides comprehensive validation for tasks, projects, and time logs with:
# - Required field validation
# - Type checking
# - Date range validation
# - Priority validation (0-5)
# - Duplicate detection
# - Custom validators via scriptblocks
# - Detailed error messages
#
# Usage:
#   $result = Test-TaskValid $task
#   if (-not $result.IsValid) {
#   }
#
#   $errors = Get-ValidationErrors $data @{
#       name = @{ Required = $true; Type = 'string'; MaxLength = 100 }
#       priority = @{ Required = $false; Type = 'int'; Min = 0; Max = 5 }
#   }

using namespace System
using namespace System.Collections.Generic

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Validation result object

.DESCRIPTION
Contains validation state and error messages
#>
class ValidationResult {
    [bool]$IsValid
    [List[string]]$Errors
    [hashtable]$FieldErrors  # Field-specific errors: @{ fieldName = @('error1', 'error2') }

    ValidationResult() {
        $this.IsValid = $true
        $this.Errors = [List[string]]::new()
        $this.FieldErrors = @{}
    }

    [void] AddError([string]$message) {
        $this.IsValid = $false
        $this.Errors.Add($message)
    }

    [void] AddFieldError([string]$fieldName, [string]$message) {
        $this.IsValid = $false
        $this.Errors.Add($message)

        if (-not $this.FieldErrors.ContainsKey($fieldName)) {
            $this.FieldErrors[$fieldName] = [List[string]]::new()
        }
        $this.FieldErrors[$fieldName].Add($message)
    }
}

<#
.SYNOPSIS
Validate a task entity

.PARAMETER task
Task hashtable to validate

.OUTPUTS
ValidationResult object

.EXAMPLE
$result = Test-TaskValid @{ text='Buy milk'; priority=3 }
#>
function Test-TaskValid {
    param(
        [Parameter(Mandatory=$true)]
        [hashtable]$task
    )

    $result = [ValidationResult]::new()

    # Required: text
    if (-not $task.ContainsKey('text') -or [string]::IsNullOrWhiteSpace($task.text)) {
        $result.AddFieldError('text', 'Task text is required')
    }

    # Optional: project (string)
    if ($task.ContainsKey('project') -and $task.project -isnot [string]) {
        $result.AddFieldError('project', 'Project must be a string')
    }

    # Optional: priority (0-5)
    if ($task.ContainsKey('priority')) {
        if ($task.priority -isnot [int]) {
            $result.AddFieldError('priority', 'Priority must be an integer')
        }
        elseif ($task.priority -lt 0 -or $task.priority -gt 5) {
            $result.AddFieldError('priority', 'Priority must be between 0 and 5')
        }
    }

    # Optional: due (DateTime)
    if ($task.ContainsKey('due') -and $task.due -ne $null) {
        if ($task.due -isnot [DateTime]) {
            $result.AddFieldError('due', 'Due date must be a DateTime')
        }
        else {
            # H-VAL-1: Date range validation - must be within reasonable range
            $minDate = [DateTime]::new(1900, 1, 1)
            $maxDate = [DateTime]::Now.AddYears(100)
            if ($task.due -lt $minDate -or $task.due -gt $maxDate) {
                $result.AddFieldError('due', 'Due date must be between 1900-01-01 and 100 years from now')
            }
        }
    }

    # Optional: tags (array)
    if ($task.ContainsKey('tags') -and $task.tags -ne $null) {
        if ($task.tags -isnot [array]) {
            $result.AddFieldError('tags', 'Tags must be an array')
        }
    }

    # Optional: completed (bool)
    if ($task.ContainsKey('completed') -and $task.completed -ne $null) {
        if ($task.completed -isnot [bool]) {
            $result.AddFieldError('completed', 'Completed must be a boolean')
        }
    }

    # Optional: status (string, limited values)
    if ($task.ContainsKey('status') -and $task.status -ne $null) {
        $validStatuses = @('todo', 'in-progress', 'done', 'blocked')
        if ($task.status -notin $validStatuses) {
            $result.AddFieldError('status', "Status must be one of: $($validStatuses -join ', ')")
        }
    }

    return $result
}

<#
.SYNOPSIS
Validate a project entity

.PARAMETER project
Project hashtable to validate

.PARAMETER existingProjects
Optional array of existing projects for duplicate checking

.OUTPUTS
ValidationResult object

.EXAMPLE
$result = Test-ProjectValid @{ name='MyProject'; description='...' }
#>
function Test-ProjectValid {
    param(
        [Parameter(Mandatory=$true)]
        [hashtable]$project,

        [Parameter(Mandatory=$false)]
        [array]$existingProjects = @()
    )

    $result = [ValidationResult]::new()

    # Required: name
    if (-not $project.ContainsKey('name') -or [string]::IsNullOrWhiteSpace($project.name)) {
        $result.AddFieldError('name', 'Project name is required')
    }
    else {
        # Check for duplicates
        $duplicate = $existingProjects | Where-Object { $_.name -eq $project.name }
        if ($duplicate) {
            $result.AddFieldError('name', "Project with name '$($project.name)' already exists")
        }
    }

    # Optional: description (string)
    if ($project.ContainsKey('description') -and $project.description -ne $null) {
        if ($project.description -isnot [string]) {
            $result.AddFieldError('description', 'Description must be a string')
        }
    }

    # Optional: status (string, limited values)
    if ($project.ContainsKey('status') -and $project.status -ne $null) {
        $validStatuses = @('active', 'archived', 'on-hold')
        if ($project.status -notin $validStatuses) {
            $result.AddFieldError('status', "Status must be one of: $($validStatuses -join ', ')")
        }
    }

    return $result
}

<#
.SYNOPSIS
Validate a time log entity

.PARAMETER timelog
Time log hashtable to validate

.PARAMETER taskExists
Optional scriptblock to check if task exists: { param($taskId) return $true/$false }

.OUTPUTS
ValidationResult object

.EXAMPLE
$result = Test-TimeLogValid @{ taskId='abc'; duration=30 }
#>
function Test-TimeLogValid {
    param(
        [Parameter(Mandatory=$true)]
        [hashtable]$timelog,

        [Parameter(Mandatory=$false)]
        [scriptblock]$taskExists = $null
    )

    $result = [ValidationResult]::new()

    # Required: taskId
    if (-not $timelog.ContainsKey('taskId') -or [string]::IsNullOrWhiteSpace($timelog.taskId)) {
        $result.AddFieldError('taskId', 'Task ID is required')
    }
    elseif ($null -ne $taskExists) {
        # Check if task exists
        $exists = & $taskExists $timelog.taskId
        if (-not $exists) {
            $result.AddFieldError('taskId', "Task with ID '$($timelog.taskId)' does not exist")
        }
    }

    # Required: duration (positive integer)
    if (-not $timelog.ContainsKey('duration')) {
        $result.AddFieldError('duration', 'Duration is required')
    }
    elseif ($timelog.duration -isnot [int]) {
        $result.AddFieldError('duration', 'Duration must be an integer')
    }
    elseif ($timelog.duration -le 0) {
        $result.AddFieldError('duration', 'Duration must be greater than 0')
    }
    # H-VAL-8: Add maximum duration check (1440 minutes = 24 hours)
    elseif ($timelog.duration -gt 1440) {
        $result.AddFieldError('duration', 'Duration must not exceed 1440 minutes (24 hours)')
    }

    # Optional: timestamp (DateTime)
    if ($timelog.ContainsKey('timestamp') -and $timelog.timestamp -ne $null) {
        if ($timelog.timestamp -isnot [DateTime]) {
            $result.AddFieldError('timestamp', 'Timestamp must be a DateTime')
        }
    }

    return $result
}

<#
.SYNOPSIS
Generic validation using a schema definition

.PARAMETER data
Data hashtable to validate

.PARAMETER schema
Validation schema: @{
    fieldName = @{
        Required = $true/$false
        Type = 'string'/'int'/'bool'/'datetime'/'array'
        Min = 0 (for int)
        Max = 100 (for int)
        MinLength = 1 (for string)
        MaxLength = 200 (for string)
        Pattern = '^[a-z]+$' (regex for string)
        Validator = { param($value) return $true/$false } (custom)
    }
}

.OUTPUTS
Array of validation error messages (empty if valid)

.EXAMPLE
$errors = Get-ValidationErrors $data @{
    name = @{ Required = $true; Type = 'string'; MaxLength = 100 }
    age = @{ Required = $false; Type = 'int'; Min = 0; Max = 120 }
}
#>
function Get-ValidationErrors {
    param(
        [Parameter(Mandatory=$true)]
        [hashtable]$data,

        [Parameter(Mandatory=$true)]
        [hashtable]$schema
    )

    $errors = @()

    foreach ($fieldName in $schema.Keys) {
        $fieldSchema = $schema[$fieldName]
        $fieldErrors = Test-FieldValid $fieldName $data[$fieldName] $fieldSchema -DataContainsKey $data.ContainsKey($fieldName)
        $errors += $fieldErrors
    }

    return $errors
}

<#
.SYNOPSIS
Validate a single field against a schema

.PARAMETER fieldName
Field name (for error messages)

.PARAMETER value
Field value

.PARAMETER schema
Field schema (see Get-ValidationErrors)

.PARAMETER DataContainsKey
Whether the data hashtable contains this key

.OUTPUTS
Array of validation error messages (empty if valid)
#>
function Test-FieldValid {
    param(
        [Parameter(Mandatory=$true)]
        [string]$fieldName,

        [Parameter(Mandatory=$false)]
        $value,

        [Parameter(Mandatory=$true)]
        [hashtable]$schema,

        [Parameter(Mandatory=$false)]
        [bool]$DataContainsKey = $true
    )

    $errors = @()

    # Check if required
    if ($schema.ContainsKey('Required') -and $schema.Required) {
        if (-not $DataContainsKey -or $null -eq $value -or ($value -is [string] -and [string]::IsNullOrWhiteSpace($value))) {
            $errors += "Field '$fieldName' is required"
            return $errors  # No point checking further if missing
        }
    }

    # If field is optional and not provided, skip validation
    if (-not $DataContainsKey -or $null -eq $value) {
        return $errors
    }

    # Type validation
    if ($schema.ContainsKey('Type')) {
        $expectedType = $schema.Type
        $isValid = switch ($expectedType) {
            'string' { $value -is [string] }
            'int' { $value -is [int] }
            'bool' { $value -is [bool] }
            'datetime' { $value -is [DateTime] }
            'array' { $value -is [array] }
            default { $true }
        }

        if (-not $isValid) {
            $errors += "Field '$fieldName' must be of type $expectedType"
            return $errors  # No point checking further if type is wrong
        }
    }

    # Integer validation
    if ($value -is [int]) {
        if ($schema.ContainsKey('Min') -and $value -lt $schema.Min) {
            $errors += "Field '$fieldName' must be at least $($schema.Min)"
        }
        if ($schema.ContainsKey('Max') -and $value -gt $schema.Max) {
            $errors += "Field '$fieldName' must be at most $($schema.Max)"
        }
    }

    # String validation
    if ($value -is [string]) {
        if ($schema.ContainsKey('MinLength') -and $value.Length -lt $schema.MinLength) {
            $errors += "Field '$fieldName' must be at least $($schema.MinLength) characters"
        }
        if ($schema.ContainsKey('MaxLength') -and $value.Length -gt $schema.MaxLength) {
            $errors += "Field '$fieldName' must be at most $($schema.MaxLength) characters"
        }
        if ($schema.ContainsKey('Pattern') -and $value -notmatch $schema.Pattern) {
            $errors += "Field '$fieldName' does not match required pattern"
        }
    }

    # Custom validator
    if ($schema.ContainsKey('Validator') -and $null -ne $schema.Validator) {
        try {
            $isValid = & $schema.Validator $value
            if (-not $isValid) {
                $errors += "Field '$fieldName' failed custom validation"
            }
        }
        catch {
            $errors += "Field '$fieldName' validator error: $($_.Exception.Message)"
        }
    }

    return $errors
}

Export-ModuleMember -Function Test-TaskValid, Test-ProjectValid, Test-TimeLogValid, Get-ValidationErrors, Test-FieldValid

﻿using namespace System.Collections.Generic

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Type normalization helpers to eliminate scattered type checking across the codebase.

.DESCRIPTION
Provides utility functions for handling mixed hashtable/PSCustomObject types that
result from different data sources (JSON, TaskStore, etc).

.NOTES
This module eliminates the need for repeated type checks like:
  if ($obj -is [hashtable]) { ... } else { ... }
#>

<#
.SYNOPSIS
Convert PSCustomObject or other types to hashtable format.

.DESCRIPTION
Normalizes data to hashtable format for consistent access patterns.
Handles null values, existing hashtables, and PSCustomObject conversion.

.PARAMETER obj
The object to normalize.

.EXAMPLE
$normalized = ConvertTo-NormalizedHashtable $task
#>
function ConvertTo-NormalizedHashtable {
    param([object]$obj)

    if ($null -eq $obj) { return $null }
    if ($obj -is [hashtable]) { return $obj }

    # Convert PSCustomObject to hashtable
    $hash = @{}
    foreach ($prop in $obj.PSObject.Properties) {
        $hash[$prop.Name] = $prop.Value
    }
    return $hash
}

<#
.SYNOPSIS
Safely get a property from hashtable or PSCustomObject.

.DESCRIPTION
Retrieves property value from either hashtable or PSCustomObject formats,
with fallback to default value if property doesn't exist.

Replaces patterns like:
  if ($obj -is [hashtable]) {
    if ($obj.ContainsKey('name')) { $obj['name'] } else { $default }
  } else {
    if ($null -ne ($obj.PSObject.Properties | Where-Object Name -eq 'name')) { $obj.name } else { $default }
  }

.PARAMETER obj
The object to query (hashtable or PSCustomObject).

.PARAMETER name
The property name to retrieve.

.PARAMETER default
The value to return if property doesn't exist. Default is $null.

.EXAMPLE
$id = $task.id
$parentId = $task.parent_id $null
#>
function Global:Get-SafeProperty {
    param(
        [object]$obj,
        [string]$name,
        [object]$default = $null
    )

    if ($null -eq $obj) { return $default }

    if ($obj -is [hashtable]) {
        if ($obj.ContainsKey($name)) {
            # CRITICAL: Use comma operator to prevent PowerShell from unwrapping single-element arrays
            return ,$obj[$name]
        }
        return $default
    }

    if ($null -ne ($obj.PSObject.Properties | Where-Object Name -eq $name)) {
        # CRITICAL: Use comma operator to prevent PowerShell from unwrapping single-element arrays
        return ,$obj.$name
    }

    return $default
}

<#
.SYNOPSIS
Check if an object has a property or key.

.DESCRIPTION
Checks both hashtable keys and PSCustomObject properties.

.PARAMETER obj
The object to check.

.PARAMETER name
The property/key name to look for.

.EXAMPLE
if (Test-SafeProperty $task 'parent_id') {
}
#>
function Global:Test-SafeProperty {
    param(
        [object]$obj,
        [string]$name
    )

    if ($null -eq $obj) { return $false }

    if ($obj -is [hashtable]) {
        return $obj.ContainsKey($name)
    }

    return $null -ne ($obj.PSObject.Properties | Where-Object Name -eq $name)
}

# TimeValidationHelper.ps1 - Validation helpers for time entry screens
# Extracted from TimeListScreen.ps1 to reduce code duplication

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Validate and convert hours input from form values

.PARAMETER values
Hashtable containing 'hours' key

.OUTPUTS
Hashtable with: @{ Valid = $bool; Minutes = $int; Hours = $double; ErrorMessage = $string }

.EXAMPLE
$result = ValidateHoursInput @{ hours = '2.5' }
if ($result.Valid) { $minutes = $result.Minutes }
#>
function ConvertTo-TimeMinutes {
    param(
        [Parameter(Mandatory=$true)]
        [hashtable]$values
    )

    $result = @{ Valid = $false; Minutes = 0; Hours = 0.0; ErrorMessage = '' }

    # Check required
    if (-not $values.ContainsKey('hours') -or [string]::IsNullOrWhiteSpace($values.hours)) {
        $result.ErrorMessage = "Hours field is required"
        return $result
    }

    # Convert to double
    try {
        $result.Hours = [double]$values.hours
    } catch {
        $result.ErrorMessage = "Invalid hours value: $($values.hours)"
        return $result
    }

    # Validate range
    if ($result.Hours -le 0) {
        $result.ErrorMessage = "Hours must be greater than 0"
        return $result
    }

    $maxHours = (Get-Variable -Name 'MAX_HOURS_PER_ENTRY' -Scope Global -ValueOnly -ErrorAction SilentlyContinue)
    if (-not $maxHours) { $maxHours = 24 }
    if ($result.Hours -gt $maxHours) {
        $result.ErrorMessage = "Hours must be $maxHours or less"
        return $result
    }

    # Convert to minutes with proper rounding
    $result.Minutes = [int][Math]::Round($result.Hours * 60)
    $result.Valid = $true
    return $result
}

<#
.SYNOPSIS
Safely parse date from form values, defaulting to today

.PARAMETER values
Hashtable containing 'date' key

.OUTPUTS
DateTime value (parsed from values or today's date)

.EXAMPLE
$date = ConvertTo-SafeDate @{ date = '2024-01-15' }
#>
function ConvertTo-SafeDate {
    param(
        [Parameter(Mandatory=$true)]
        [hashtable]$values
    )

    if ($values.ContainsKey('date') -and $values.date) {
        try {
            return [DateTime]$values.date
        } catch {
            # Write-PmcTuiLog "Failed to parse date '$($values.date)', using today" "WARNING"
        }
    }
    return [DateTime]::Today
}

<#
.SYNOPSIS
Build time entry data hashtable from form values

.PARAMETER values
Form values hashtable

.PARAMETER minutes
Pre-validated minutes value

.PARAMETER dateValue
Pre-validated date value

.OUTPUTS
Hashtable ready for TaskStore operations

.EXAMPLE
$data = Build-TimeEntryData -Values $values -Minutes 120 -DateValue (Get-Date)
#>
function Build-TimeEntryData {
    param(
        [Parameter(Mandatory=$true)]
        [hashtable]$values,

        [Parameter(Mandatory=$true)]
        [int]$minutes,

        [Parameter(Mandatory=$true)]
        [DateTime]$dateValue
    )

    return @{
        date = $dateValue
        task = $(if ($values.ContainsKey('task')) { $values.task } else { '' })
        project = $(if ($values.ContainsKey('project')) { $values.project } else { '' })
        timecode = $(if ($values.ContainsKey('timecode')) { $values.timecode } else { '' })
        id1 = $(if ($values.ContainsKey('id1')) { $values.id1 } else { '' })
        id2 = $(if ($values.ContainsKey('id2')) { $values.id2 } else { '' })
        minutes = $minutes
        notes = $(if ($values.ContainsKey('notes')) { $values.notes } else { '' })
    }
}

# Only export when running as a module, not when dot-sourced
if ($MyInvocation.InvocationName -ne '.') {
    Export-ModuleMember -Function ConvertTo-TimeMinutes, ConvertTo-SafeDate, Build-TimeEntryData
}

# InlineEditorFields.ps1 - Field type handlers for InlineEditor
#
# Extracted helpers for field value parsing and preview formatting
# Used by InlineEditor.ps1 to handle different field types

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Parse date text input into DateTime

.PARAMETER dateText
Raw text from date input field

.OUTPUTS
DateTime or $null if parsing fails

.EXAMPLE
$date = ConvertTo-DateFromText "+7"  # Returns 7 days from now
$date = ConvertTo-DateFromText "today"  # Returns today
$date = ConvertTo-DateFromText "2024-01-15"  # Returns parsed date
#>
function ConvertTo-DateFromText {
    param(
        [Parameter(Mandatory=$true)]
        [string]$dateText
    )

    $dateText = $dateText.Trim().ToLower()

    if ([string]::IsNullOrWhiteSpace($dateText)) {
        return $null
    }

    # Parse relative dates like "+7" or "-3"
    if ($dateText -match '^([+-])(\d+)$') {
        $sign = $matches[1]
        $days = [int]$matches[2]
        if ($sign -eq '+') {
            return [DateTime]::Now.AddDays($days)
        }
        else {
            return [DateTime]::Now.AddDays(-$days)
        }
    }

    # Special keywords
    switch ($dateText) {
        { $_ -in @('today', 't') } { return [DateTime]::Today }
        { $_ -in @('tomorrow', 'tom') } { return [DateTime]::Today.AddDays(1) }
        'yesterday' { return [DateTime]::Today.AddDays(-1) }
        'eom' { 
            $now = [DateTime]::Now
            return [DateTime]::new($now.Year, $now.Month, [DateTime]::DaysInMonth($now.Year, $now.Month))
        }
        'eoy' { return [DateTime]::new([DateTime]::Now.Year, 12, 31) }
        'som' {
            $now = [DateTime]::Now
            return [DateTime]::new($now.Year, $now.Month, 1)
        }
    }

    # Parse YYYYMMDD format (20251125)
    if ($dateText -match '^\d{8}$') {
        try {
            $year = [int]$dateText.Substring(0, 4)
            $month = [int]$dateText.Substring(4, 2)
            $day = [int]$dateText.Substring(6, 2)
            return [DateTime]::new($year, $month, $day)
        }
        catch {
            # Invalid date, fall through
        }
    }

    # Parse YYMMDD format (251125)
    if ($dateText -match '^\d{6}$') {
        try {
            $year = 2000 + [int]$dateText.Substring(0, 2)
            $month = [int]$dateText.Substring(2, 2)
            $day = [int]$dateText.Substring(4, 2)
            return [DateTime]::new($year, $month, $day)
        }
        catch {
            # Invalid date, fall through
        }
    }

    # Parse absolute dates (standard formats)
    try {
        return [DateTime]::Parse($dateText)
    }
    catch {
        return $null
    }
}

<#
.SYNOPSIS
Parse tags from comma-separated text

.PARAMETER tagsText
Comma-separated tag string

.OUTPUTS
Array of validated tag strings

.EXAMPLE
$tags = ConvertTo-TagsFromText "urgent, high-priority, bug"
#>
function ConvertTo-TagsFromText {
    param(
        [Parameter(Mandatory=$true)]
        [string]$tagsText
    )

    if ([string]::IsNullOrWhiteSpace($tagsText)) {
        return @()
    }

    # Split by comma and trim
    $tags = $tagsText -split ',' | ForEach-Object { $_.Trim() } | Where-Object { -not [string]::IsNullOrWhiteSpace($_) }

    # Validate tags match pattern ^[a-zA-Z0-9_-]+$
    $validTags = @()
    foreach ($tag in $tags) {
        if ($tag -match '^[a-zA-Z0-9_-]+$') {
            $validTags += $tag
        }
        else {
            # Write-PmcTuiLog "Invalid tag '$tag' - must contain only letters, numbers, underscore, or hyphen" "WARNING"
        }
    }

    return @($validTags)
}

<#
.SYNOPSIS
Format a number value with a visual slider

.PARAMETER Value
Current value

.PARAMETER Min
Minimum value

.PARAMETER Max
Maximum value

.OUTPUTS
String like "[----●-----] 4"

.EXAMPLE
Format-NumberSlider -Value 4 -Min 0 -Max 10
#>
function Format-NumberSlider {
    param(
        [Parameter(Mandatory=$true)]
        [int]$Value,

        [Parameter(Mandatory=$false)]
        [int]$Min = 0,

        [Parameter(Mandatory=$false)]
        [int]$Max = 10
    )

    $range = $Max - $Min
    $position = if ($range -gt 0) { [Math]::Floor(($Value - $Min) / $range * 10) } else { 0 }
    $slider = "[" + ("-" * $position) + "●" + ("-" * (10 - $position)) + "] $Value"
    return $slider
}

<#
.SYNOPSIS
Format tags array for display

.PARAMETER Tags
Array of tag strings

.OUTPUTS
String like "[tag1] [tag2]" or "(no tags)"

.EXAMPLE
Format-TagsDisplay @("urgent", "bug")
#>
function Format-TagsDisplay {
    param(
        [Parameter(Mandatory=$false)]
        [array]$Tags
    )

    if ($null -eq $Tags -or $Tags.Count -eq 0) {
        return "(no tags)"
    }
    return "[" + ($Tags -join "] [") + "]"
}

# Only export when running as a module, not when dot-sourced
if ($MyInvocation.InvocationName -ne '.') {
    Export-ModuleMember -Function ConvertTo-DateFromText, ConvertTo-TagsFromText, Format-NumberSlider, Format-TagsDisplay
}

# ThemeLoader.ps1 - Simple theme file loading
# Loads themes from themes/*.json files
# ONE path: Load theme file -> Use Properties

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Get list of available themes from themes/ directory

.OUTPUTS
Array of theme objects with Name, Hex, Description, Properties
#>
function Get-AvailableThemes {
    $themesDir = Join-Path $global:PmcAppRoot 'themes'
    $themes = @()

    if (Test-Path $themesDir) {
        $themeFiles = Get-ChildItem -Path $themesDir -Filter '*.json' -File
        foreach ($file in $themeFiles) {
            try {
                $theme = Get-Content $file.FullName -Raw | ConvertFrom-Json
                $themes += $theme
            }
            catch {
                # Debug only when flag is set
                if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
                    Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [ThemeLoader] ERROR loading $($file.Name): $_"
                }
            }
        }
    }

    return $themes
}

<#
.SYNOPSIS
Load a specific theme by name

.PARAMETER themeName
Name of the theme (matching filename without .json extension)

.OUTPUTS
Theme object with Name, Hex, Description, Properties, or $null if not found
#>
function Load-Theme {
    param(
        [Parameter(Mandatory=$true)]
        [string]$themeName
    )

    # Debug log path (same as Start-PmcTUI)
    $debugLog = $null
    try {
        $debugLog = Join-Path (Split-Path (Split-Path (Split-Path (Split-Path $PSScriptRoot -Parent) -Parent) -Parent) -Parent) 'data/logs/theme-debug.log'
    } catch { }

    $themesDir = Join-Path $global:PmcAppRoot 'themes'
    if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Load-Theme] themeName='$themeName' PmcAppRoot='$($global:PmcAppRoot)' themesDir='$themesDir'" -ErrorAction SilentlyContinue }

    # Fallback search if global root fails
    if (-not (Test-Path $themesDir)) {
        if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Load-Theme] themesDir not found, searching fallbacks..." -ErrorAction SilentlyContinue }
        $searchPaths = @(
            $PSScriptRoot,
            (Split-Path $PSScriptRoot -Parent),
            (Split-Path (Split-Path $PSScriptRoot -Parent) -Parent),
            (Split-Path (Split-Path (Split-Path $PSScriptRoot -Parent) -Parent) -Parent)
        )
        foreach ($path in $searchPaths) {
            if ($path -and (Test-Path (Join-Path $path "themes"))) {
                $themesDir = Join-Path $path "themes"
                if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Load-Theme] Found themes at fallback: $themesDir" -ErrorAction SilentlyContinue }
                break
            }
        }
    }

    $themeFile = Join-Path $themesDir "$($themeName.ToLower()).json"
    if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Load-Theme] Looking for: $themeFile (exists=$(Test-Path $themeFile))" -ErrorAction SilentlyContinue }

    if (Test-Path $themeFile) {
        try {
            $themeObj = Get-Content $themeFile -Raw | ConvertFrom-Json
            if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Load-Theme] JSON loaded. Name='$($themeObj.Name)' Hex='$($themeObj.Hex)'" -ErrorAction SilentlyContinue }
            # Convert Properties from PSCustomObject to hashtable
            $props = @{}
            if ($themeObj.Properties) {
                $themeObj.Properties.PSObject.Properties | ForEach-Object {
                    $propValue = @{
                        Type = $_.Value.Type
                    }
                    # Solid types have Color, Gradient types have Start/End
                    if ($_.Value.PSObject.Properties['Color']) {
                        $propValue['Color'] = $_.Value.Color
                    }
                    if ($_.Value.PSObject.Properties['Start']) {
                        $propValue['Start'] = $_.Value.Start
                    }
                    if ($_.Value.PSObject.Properties['End']) {
                        $propValue['End'] = $_.Value.End
                    }
                    $props[$_.Name] = $propValue
                }
            }
            $theme = @{
                Name = $themeObj.Name
                Hex = $themeObj.Hex
                Description = $themeObj.Description
                Properties = $props
                Warning = $(if ($props.ContainsKey('Foreground.Warning')) { $props['Foreground.Warning'] } else { 'Red' })
            }
            if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Load-Theme] Returning theme with Hex='$($theme.Hex)'" -ErrorAction SilentlyContinue }
            return $theme
        }
        catch {
            if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Load-Theme] EXCEPTION: $_" -ErrorAction SilentlyContinue }
            if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
                Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [ThemeLoader] FATAL ERROR loading theme '$themeName': $_"
            }
            throw "Failed to load theme '$themeName': $_"
        }
    }

    if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Load-Theme] Theme file NOT FOUND, returning null" -ErrorAction SilentlyContinue }
    if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
        Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [ThemeLoader] Theme file not found: $themeFile"
    }
    return $null
}


<#
.SYNOPSIS
Get the currently active theme

.DESCRIPTION
Reads active theme name from config.json, loads that theme file

.OUTPUTS
Theme object with Properties, or default theme if not found
#>
function Get-ActiveTheme {
    $themeName = 'default'  # Default if nothing configured

    try {
        $cfg = Get-PmcConfig
        if ($cfg -and $cfg.Display -and $cfg.Display.Theme -and $cfg.Display.Theme.Active) {
            $themeName = $cfg.Display.Theme.Active
        }
    }
    catch {
        if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
            Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [ThemeLoader] ERROR reading config: $_"
        }
    }

    $theme = Load-Theme -themeName $themeName

    # If theme not found, try default
    if (-not $theme -and $themeName -ne 'default') {
        if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
            Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [ThemeLoader] Theme '$themeName' not found, falling back to default"
        }
        $theme = Load-Theme -themeName 'default'
    }

    return $theme
}

<#
.SYNOPSIS
Set the active theme by name

.PARAMETER themeName
Name of the theme to set as active
#>
function Set-ActiveTheme {
    param(
        [Parameter(Mandatory=$true)]
        [string]$themeName
    )

    # Debug log path
    $debugLog = $null
    try {
        $debugLog = Join-Path (Split-Path (Split-Path (Split-Path (Split-Path $PSScriptRoot -Parent) -Parent) -Parent) -Parent) 'data/logs/theme-debug.log'
    } catch { }
    if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Set-ActiveTheme] Starting for themeName='$themeName'" -ErrorAction SilentlyContinue }

    $cfg = Get-PmcConfig
    if (-not $cfg) { $cfg = @{} }
    if (-not $cfg.Display) { $cfg.Display = @{} }
    if (-not $cfg.Display.Theme) { $cfg.Display.Theme = @{} }

    $cfg.Display.Theme.Active = $themeName.ToLower()
    if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Set-ActiveTheme] Set Active='$($cfg.Display.Theme.Active)'" -ErrorAction SilentlyContinue }

    # Load the theme to get its hex color
    $theme = Load-Theme -themeName $themeName
    if (-not $theme) {
        if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Set-ActiveTheme] Load-Theme returned null!" -ErrorAction SilentlyContinue }
        throw "Set-ActiveTheme: Failed to load theme '$themeName' - theme file may not exist in themes/ directory"
    }
    if (-not $theme.Hex) {
        if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Set-ActiveTheme] Theme has no Hex property!" -ErrorAction SilentlyContinue }
        throw "Set-ActiveTheme: Theme '$themeName' loaded but has no Hex property defined"
    }
    $cfg.Display.Theme.Hex = $theme.Hex
    if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Set-ActiveTheme] Set Hex='$($cfg.Display.Theme.Hex)'" -ErrorAction SilentlyContinue }

    # Remove old Properties - no longer needed in config (STRICT MODE FIX: safe check)
    if ($cfg.Display.Theme.PSObject.Properties['Properties']) { $cfg.Display.Theme.PSObject.Properties.Remove('Properties') }

    if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Set-ActiveTheme] Calling Save-PmcConfig..." -ErrorAction SilentlyContinue }
    Save-PmcConfig $cfg
    if ($debugLog) { Add-Content -Path $debugLog -Value "[$(Get-Date -F 'HH:mm:ss.fff')] [Set-ActiveTheme] Save-PmcConfig returned" -ErrorAction SilentlyContinue }
}



Export-ModuleMember -Function Get-AvailableThemes, Load-Theme, Get-ActiveTheme, Set-ActiveTheme

