﻿using namespace System.Collections.Generic
using namespace System.Text

# SettingsScreen - PMC TUI Settings configuration
# Interactive screen for viewing and modifying PMC settings


Set-StrictMode -Version Latest

# NOTE: PmcScreen is loaded by Start-PmcTUI.ps1 - don't load again
# . "$PSScriptRoot/../PmcScreen.ps1"

# LOW FIX SS-L1, SS-L2, SS-L3: Define constants for column widths and limits
$script:SETTING_NAME_WIDTH = 20
$script:SETTING_VALUE_WIDTH = 30
$global:MIN_PRINTABLE_CHAR = 32
$global:MAX_PRINTABLE_CHAR = 126

<#
.SYNOPSIS
Settings screen for configuring PMC TUI preferences

.DESCRIPTION
Interactive settings screen showing:
- Data file location
- Default project
- Theme selection
- Auto-save settings
- Backup settings
Navigation: Up/Down to select, Enter to edit, Esc to exit
#>
class SettingsScreen : PmcScreen {
    # Data
    [array]$SettingsList = @()
    [int]$SelectedIndex = 0
    [string]$InputMode = 'none'  # 'none', 'edit'
    [string]$InputBuffer = ''
    [int]$EditingIndex = -1

    # Static: Register menu items
    static [void] RegisterMenuItems([object]$registry) {
        $registry.AddMenuItem('Options', 'Settings', 'S', {
            # CRITICAL FIX SS-C1: Validate file exists before dot-sourcing
            $scriptPath = "$PSScriptRoot/SettingsScreen.ps1"
            if (-not (Test-Path $scriptPath)) {
                # Write-PmcTuiLog "SettingsScreen.ps1 not found at: $scriptPath" "ERROR"
                throw "SettingsScreen.ps1 not found"
            }
            . $scriptPath
            $global:PmcApp.PushScreen((New-Object -TypeName SettingsScreen))
        }, 20)
    }

    # LOW FIX SS-L4: Extract common initialization to helper method (DRY principle)
    hidden [void] ConfigureScreen() {
        # Configure header
        $this.Header.SetBreadcrumb(@("Home", "Settings"))

        # Configure footer with shortcuts
        $this.Footer.ClearShortcuts()
        $this.Footer.AddShortcut("Up/Down", "Select")
        $this.Footer.AddShortcut("Enter", "Edit")
        $this.Footer.AddShortcut("Esc", "Back")

        # NOTE: _SetupMenus() removed - MenuRegistry handles menu population via static RegisterMenuItems()
        # Old pattern was adding duplicate/misplaced menu items
    }

    # Constructor
    SettingsScreen() : base("Settings", "Settings") {
        $this.ConfigureScreen()
    }

    # Constructor with container (DI-enabled)
    SettingsScreen([object]$container) : base("Settings", "Settings", $container) {
        $this.ConfigureScreen()
    }

    [void] LoadData() {
        # SS-M2 FIX: Defensive check layering documentation
        # LAYER 1: Default values are set BEFORE external function calls
        # This ensures settings always have valid values even if external functions fail
        $dataFile = "~/.pmc/data.json"
        try {
            # LAYER 2: Try to get actual value from external function
            # HIGH FIX SS-H1: Validate returned path exists and is readable
            $tempPath = Get-PmcTaskFilePath
            if ($null -ne $tempPath -and (Test-Path $tempPath)) {
                $dataFile = $tempPath
            } else {
                # Write-PmcTuiLog "SettingsScreen: Get-PmcTaskFilePath returned invalid path: $tempPath" "WARNING"
            }
        } catch {
            # LAYER 3: Silently fall back to default if function fails
            # Write-PmcTuiLog "SettingsScreen: Get-PmcTaskFilePath failed: $($_.Exception.Message)" "WARNING"
        }

        # Same defensive layering for current context
        $currentContext = "inbox"
        try {
            # HIGH FIX SS-H2: Validate returned context against safe values
            $tempContext = Get-PmcCurrentContext
            if ($null -ne $tempContext -and -not [string]::IsNullOrWhiteSpace($tempContext)) {
                $currentContext = $tempContext
            } else {
                # Write-PmcTuiLog "SettingsScreen: Get-PmcCurrentContext returned invalid value" "WARNING"
            }
        } catch {
            # Use default if Get-PmcCurrentContext fails
            # Write-PmcTuiLog "SettingsScreen: Get-PmcCurrentContext failed: $($_.Exception.Message)" "WARNING"
        }

        $this.SettingsList = @(
            @{
                name = "Data File"
                key = "dataFile"
                value = $dataFile
                editable = $false
                description = "Location of PMC data storage"
            }
            @{
                name = "Default Project"
                key = "defaultProject"
                value = $currentContext
                editable = $true
                description = "Default project for new tasks"
            }
            @{
                name = "Auto-save"
                key = "autoSave"
                value = "enabled"
                editable = $false
                description = "Automatically save changes"
            }
            @{
                name = "Theme"
                key = "theme"
                value = "default"
                editable = $false
                action = "launchThemeEditor"
                description = "UI color theme (press Enter to change)"
            }
            @{
                name = "TUI Version"
                key = "version"
                value = "1.0.0"
                editable = $false
                description = "PMC TUI version"
            }
        )

        $this.ShowStatus("$($this.SettingsList.Count) settings available")
    }

    [string] RenderContent() {
        $sb = [System.Text.StringBuilder]::new(4096)

        if (-not $this.LayoutManager) {
            return $sb.ToString()
        }

        # Get content area
        $contentRect = $this.LayoutManager.GetRegion('Content', $this.TermWidth, $this.TermHeight)

        # Colors
        $textColor = $this.Header.GetThemedFg('Foreground.Field')
        $highlightColor = $this.Header.GetThemedFg('Foreground.FieldFocused')
        $mutedColor = $this.Header.GetThemedFg('Foreground.Muted')
        $selectedBg = $this.Header.GetThemedBg('Background.FieldFocused', 80, 0)
        $selectedFg = $this.Header.GetThemedFg('Foreground.Field')
        $cursorColor = $this.Header.GetThemedAnsi('Accent', $false)
        $headerColor = $this.Header.GetThemedFg('Foreground.Muted')
        $reset = "`e[0m"

        # Column widths
        # MEDIUM FIX SS-M3: Use script-level constants for column widths
        $nameWidth = $script:SETTING_NAME_WIDTH
        $valueWidth = $script:SETTING_VALUE_WIDTH
        $descWidth = $contentRect.Width - $nameWidth - $valueWidth - 10

        # Render column headers
        $headerY = $this.Header.Y + 3
        $sb.Append($this.Header.BuildMoveTo($contentRect.X + 4, $headerY))
        $sb.Append($headerColor)
        $sb.Append("SETTING".PadRight($nameWidth))
        $sb.Append("VALUE".PadRight($valueWidth))
        $sb.Append("DESCRIPTION")
        $sb.Append($reset)

        # Render settings rows
        $startY = $headerY + 2
        $maxLines = $contentRect.Height - 4

        for ($i = 0; $i -lt [Math]::Min($this.SettingsList.Count, $maxLines); $i++) {
            $setting = $this.SettingsList[$i]
            $y = $startY + $i
            $isSelected = ($i -eq $this.SelectedIndex)
            $isEditing = ($i -eq $this.EditingIndex) -and ($this.InputMode -eq 'edit')

            # Cursor
            $sb.Append($this.Header.BuildMoveTo($contentRect.X + 2, $y))
            if ($isSelected) {
                $sb.Append($cursorColor)
                $cursorChar = $(if ($isEditing) { "E" } else { ">" })
                $sb.Append($cursorChar)
                $sb.Append($reset)
            } else {
                $sb.Append(" ")
            }

            # Setting name column
            $x = $contentRect.X + 4
            $sb.Append($this.Header.BuildMoveTo($x, $y))
            if ($isSelected -and -not $isEditing) {
                $sb.Append($selectedBg)
                $sb.Append($selectedFg)
            } else {
                $sb.Append($textColor)
            }
            $displayName = $setting.name
            if ($displayName.Length -gt $nameWidth) {
                $displayName = $displayName.Substring(0, $nameWidth - 3) + "..."
            }
            $sb.Append($displayName.PadRight($nameWidth))
            $sb.Append($reset)
            $x += $nameWidth

            # Value column
            $sb.Append($this.Header.BuildMoveTo($x, $y))
            if ($isEditing) {
                # Show edit buffer
                $sb.Append($cursorColor)
                $sb.Append(($this.InputBuffer + "_").PadRight($valueWidth))
                $sb.Append($reset)
            } else {
                $sb.Append($highlightColor)
                $displayValue = $setting.value
                if ($displayValue.Length -gt $valueWidth) {
                    $displayValue = $displayValue.Substring(0, $valueWidth - 3) + "..."
                }
                $sb.Append($displayValue.PadRight($valueWidth))
                $sb.Append($reset)
            }
            $x += $valueWidth

            # Description column
            $sb.Append($this.Header.BuildMoveTo($x, $y))
            $sb.Append($mutedColor)
            $displayDesc = $setting.description
            if ($displayDesc.Length -gt $descWidth) {
                $displayDesc = $displayDesc.Substring(0, $descWidth - 3) + "..."
            }
            $sb.Append($displayDesc)
            $sb.Append($reset)
        }

        return $sb.ToString()
    }

    [bool] HandleKeyPress([ConsoleKeyInfo]$keyInfo) {
        # Handle input mode
        if ($this.InputMode -eq 'edit') {
            return $this._HandleEditMode($keyInfo)
        }

        switch ($keyInfo.Key) {
            'UpArrow' {
                if ($this.SelectedIndex -gt 0) {
                    $this.SelectedIndex--
                }
                return $true
            }
            'DownArrow' {
                if ($this.SelectedIndex -lt ($this.SettingsList.Count - 1)) {
                    $this.SelectedIndex++
                }
                return $true
            }
            'Enter' {
                if ($this.SettingsList.Count -gt 0) {
                    $this._StartEdit()
                }
                return $true
            }
            'Escape' {
                # Go back to previous screen
                if ($global:PmcApp) {
                    $global:PmcApp.PopScreen()
                }
                return $true
            }
        }
        return $false
    }

    hidden [void] _StartEdit() {
        $setting = $this.SettingsList[$this.SelectedIndex]

        # Check if this setting has a special action
        if ($setting.action) {
            switch ($setting.action) {
                'launchThemeEditor' {
                    # SS-M1 FIX: Enhanced try-catch with comprehensive error handling
                    # Lazy-load Theme Editor screen
                    try {
                        # Validate file exists before dot-sourcing
                        $themeEditorPath = "$PSScriptRoot/ThemeEditorScreen.ps1"
                        if (-not (Test-Path $themeEditorPath)) {
                            throw "ThemeEditorScreen.ps1 not found at expected path: $themeEditorPath"
                        }

                        . $themeEditorPath

                        # HIGH FIX SET-H1: Use try-catch around class instantiation instead of PSTypeName check
                        # PSTypeName check may not work reliably in all PowerShell versions
                        if ($global:PmcApp) {
                            try {
                                $themeScreen = New-Object ThemeEditorScreen
                            } catch {
                                throw "ThemeEditorScreen class not available after loading file: $_"
                            }
                            if ($null -eq $themeScreen) {
                                throw "ThemeEditorScreen constructor returned null"
                            }
                            $global:PmcApp.PushScreen($themeScreen)
                        } else {
                            throw "PmcApp global variable is not available"
                        }
                    } catch {
                        try { $this.ShowError("Failed to load theme editor: $($_.Exception.Message)") } catch { }
                        # Write-PmcTuiLog "Failed to load ThemeEditorScreen: $_" "ERROR"
                        # Write-PmcTuiLog "Stack trace: $($_.ScriptStackTrace)" "ERROR"
                    }
                    return
                }
            }
        }

        # SS-M2 FIX: Defensive check layering - verify setting is editable
        if (-not $setting.editable) {
            # LAYER 1: Try to show error to user via UI
            # LAYER 2: Catch and suppress any UI errors (ShowError might fail if screen not active)
            try { $this.ShowError("$($setting.name) is read-only") } catch { }
            return
        }

        $this.InputMode = 'edit'
        $this.EditingIndex = $this.SelectedIndex
        $this.InputBuffer = $setting.value
        # Defensive layer: Protect against ShowStatus failures
        try { $this.ShowStatus("Edit value (Enter: save, Esc: cancel)") } catch { }
    }

    hidden [bool] _HandleEditMode([ConsoleKeyInfo]$keyInfo) {
        switch ($keyInfo.Key) {
            'Escape' {
                $this._CancelEdit()
                return $true
            }
            'Enter' {
                $this._SaveEdit()
                return $true
            }
            'Backspace' {
                if ($this.InputBuffer.Length > 0) {
                    $this.InputBuffer = $this.InputBuffer.Substring(0, $this.InputBuffer.Length - 1)
                }
                return $true
            }
            default {
                # EDGE FIX SS-E1: Use script-level constants for printable character range
                if ($keyInfo.KeyChar -ge $global:MIN_PRINTABLE_CHAR -and $keyInfo.KeyChar -le $global:MAX_PRINTABLE_CHAR) {
                    $this.InputBuffer += $keyInfo.KeyChar
                }
                return $true
            }
        }
        return $false  # Fallback return
    }

    hidden [void] _SaveEdit() {
        $setting = $this.SettingsList[$this.EditingIndex]
        $oldValue = $setting.value
        $newValue = $this.InputBuffer

        # CRITICAL FIX SS-C2: Validate input before assignment
        if ($null -eq $newValue) {
            # Write-PmcTuiLog "SettingsScreen: Cannot set null value for $($setting.key)" "ERROR"
            $this.ShowMessage("Invalid value", "error")
            return
        }

        # Update the setting value
        $setting.value = $newValue

        # SS-M2 FIX: Defensive check layering documentation for setting persistence
        # LAYER 1: Optimistically update in-memory value first
        # LAYER 2: Try to persist to backend based on setting type
        # LAYER 3: Revert in-memory value if persistence fails
        # LAYER 4: Show user-friendly error message
        # SS-H1 FIX: Apply the setting based on key with proper persistence for each editable setting
        switch ($setting.key) {
            'defaultProject' {
                try {
                    # Check if Set-PmcFocus command exists
                    if (-not (Get-Command -Name 'Set-PmcFocus' -ErrorAction SilentlyContinue)) {
                        throw "Set-PmcFocus command not available"
                    }
                    # Use Set-PmcFocus to change the current context
                    # Create proper PmcCommandContext with project name in FreeText
                    $context = [PmcCommandContext]::new('focus', 'set')
                    $context.FreeText = @($newValue)
                    Set-PmcFocus -Context $context
                    $this.ShowSuccess("Default project updated to '$newValue'")
                } catch {
                    $this.ShowError("Failed to set default project: $_")
                    # Revert the value
                    $setting.value = $oldValue
                }
            }
            'autoSave' {
                # TODO: Implement persistence for auto-save setting
                # For now, show warning that this setting is not persisted
                $this.ShowError("Auto-save setting persistence not yet implemented")
                $setting.value = $oldValue
            }
            default {
                # Default case: Warn that persistence is not implemented
                # This prevents false success messages for settings without persistence logic
                $this.ShowError("Persistence not implemented for setting '$($setting.name)'. Changes will not be saved.")
                $setting.value = $oldValue
            }
        }

        # Reset edit mode
        $this.InputMode = 'none'
        $this.EditingIndex = -1
        $this.InputBuffer = ''
    }

    hidden [void] _CancelEdit() {
        $this.InputMode = 'none'
        $this.EditingIndex = -1
        $this.InputBuffer = ''
        $this.ShowStatus("Edit cancelled")
    }
}

﻿using namespace System.Collections.Generic
using namespace System.Text

# ThemeEditorScreen - Theme selection and preview
# Allows users to view available themes and apply them

Set-StrictMode -Version Latest

class ThemeEditorScreen : PmcScreen {
    [array]$Themes = @()
    [int]$SelectedIndex = 0
    [string]$CurrentTheme = "Default"
    hidden [int]$_contentY = 8
    hidden [int]$_contentHeight = 13

    ThemeEditorScreen() : base("ThemeEditor", "Theme Editor") {
        $this.Header.SetBreadcrumb(@("Home", "Options", "Themes"))
        $this.Footer.ClearShortcuts()
        $this.Footer.AddShortcut("Up/Down", "Select")
        $this.Footer.AddShortcut("Enter", "Apply")
        $this.Footer.AddShortcut("T", "Test")
        $this.Footer.AddShortcut("Esc", "Back")
    }

    ThemeEditorScreen([object]$container) : base("ThemeEditor", "Theme Editor", $container) {
        $this.Header.SetBreadcrumb(@("Home", "Options", "Themes"))
        $this.Footer.ClearShortcuts()
        $this.Footer.AddShortcut("Up/Down", "Select")
        $this.Footer.AddShortcut("Enter", "Apply")
        $this.Footer.AddShortcut("T", "Test")
        $this.Footer.AddShortcut("Esc", "Back")
    }

    [void] Initialize([object]$renderEngine, [object]$container) {
        $this.RenderEngine = $renderEngine
        $this.Container = $container
        $this.TermWidth = $renderEngine.Width
        $this.TermHeight = $renderEngine.Height

        if (-not $this.LayoutManager) {
            $this.LayoutManager = [PmcLayoutManager]::new()
        }

        $headerRect = $this.LayoutManager.GetRegion('Header', $this.TermWidth, $this.TermHeight)
        $this.Header.X = $headerRect.X
        $this.Header.Y = $headerRect.Y
        $this.Header.Width = $headerRect.Width
        $this.Header.Height = $headerRect.Height

        $footerRect = $this.LayoutManager.GetRegion('Footer', $this.TermWidth, $this.TermHeight)
        $this.Footer.X = $footerRect.X
        $this.Footer.Y = $footerRect.Y
        $this.Footer.Width = $footerRect.Width

        $statusBarRect = $this.LayoutManager.GetRegion('StatusBar', $this.TermWidth, $this.TermHeight)
        $this.StatusBar.X = $statusBarRect.X
        $this.StatusBar.Y = $statusBarRect.Y
        $this.StatusBar.Width = $statusBarRect.Width

        $contentRect = $this.LayoutManager.GetRegion('Content', $this.TermWidth, $this.TermHeight)
        $this._contentY = $contentRect.Y
        $this._contentHeight = $contentRect.Height
    }

    # === GRADIENT RENDERING ===
    # Renders text with per-character horizontal gradient (magenta to cyan)
    hidden [void] _RenderGradientText([object]$engine, [int]$x, [int]$y, [string]$text, [string]$startHex, [string]$endHex, [object]$bgColor) {
        if ([string]::IsNullOrEmpty($text)) { return }

        # Parse start color
        $sHex = $startHex.TrimStart('#')
        $sR = [Convert]::ToInt32($sHex.Substring(0, 2), 16)
        $sG = [Convert]::ToInt32($sHex.Substring(2, 2), 16)
        $sB = [Convert]::ToInt32($sHex.Substring(4, 2), 16)

        # Parse end color
        $eHex = $endHex.TrimStart('#')
        $eR = [Convert]::ToInt32($eHex.Substring(0, 2), 16)
        $eG = [Convert]::ToInt32($eHex.Substring(2, 2), 16)
        $eB = [Convert]::ToInt32($eHex.Substring(4, 2), 16)

        $len = $text.Length
        for ($i = 0; $i -lt $len; $i++) {
            $t = if ($len -eq 1) { 0 } else { $i / ($len - 1) }

            # Linear interpolation
            $r = [int]($sR + ($eR - $sR) * $t)
            $g = [int]($sG + ($eG - $sG) * $t)
            $b = [int]($sB + ($eB - $sB) * $t)

            # Clamp to 0-255
            $r = [Math]::Max(0, [Math]::Min(255, $r))
            $g = [Math]::Max(0, [Math]::Min(255, $g))
            $b = [Math]::Max(0, [Math]::Min(255, $b))

            # Convert to int for WriteAt (packed RGB)
            $fg = ($r -shl 16) -bor ($g -shl 8) -bor $b

            $char = $text[$i]
            $engine.WriteAt($x + $i, $y, [string]$char, $fg, $bgColor)
        }
    }

    [void] LoadData() {
        $this.ShowStatus("Loading themes...")

        try {
            # Load themes from theme files
            $this.Themes = Get-AvailableThemes

            # Get current theme name from config
            try {
                $cfg = Get-PmcConfig
                if ($cfg -and $cfg.Display -and $cfg.Display.Theme -and $cfg.Display.Theme.Active) {
                    $activeTheme = $cfg.Display.Theme.Active
                    foreach ($theme in $this.Themes) {
                        if ($theme.Name.ToLower() -eq $activeTheme.ToLower()) {
                            $this.CurrentTheme = $theme.Name
                            break
                        }
                    }
                }
            } catch { }

            $count = $(if ($this.Themes) { $this.Themes.Count } else { 0 })
            $this.ShowSuccess("$count themes available")
        }
        catch {
            $this.ShowError("Failed to load themes: $_")
            $this.Themes = @()
        }
    }

    [void] RenderContentToEngine([object]$engine) {
        $textColor = $this.Header.GetThemedColorInt('Foreground.Field')
        $selectedBg = $this.Header.GetThemedColorInt('Background.FieldFocused')
        $selectedFg = $this.Header.GetThemedColorInt('Foreground.Field')
        $cursorColor = $this.Header.GetThemedColorInt('Foreground.FieldFocused')
        $mutedColor = $this.Header.GetThemedColorInt('Foreground.Muted')
        $headerColor = $this.Header.GetThemedColorInt('Foreground.Muted')
        $bg = $this.Header.GetThemedColorInt('Background.Primary')

        $y = $this._contentY

        $engine.WriteAt($this.Header.X + 4, $y, "THEME NAME", $headerColor, $bg)
        $engine.WriteAt($this.Header.X + 19, $y, "DESCRIPTION", $headerColor, $bg)
        $engine.WriteAt($this.Header.X + 55, $y, "STATUS", $headerColor, $bg)
        $y++

        $startY = $y + 1
        $maxLines = $this._contentHeight - 8

        for ($i = 0; $i -lt [Math]::Min($this.Themes.Count, $maxLines); $i++) {
            $theme = $this.Themes[$i]
            $rowY = $startY + $i
            $isSelected = ($i -eq $this.SelectedIndex)
            $isCurrent = ($theme.Name -eq $this.CurrentTheme)

            $rowBg = $(if ($isSelected) { $selectedBg } else { $bg })
            $rowFg = $(if ($isSelected) { $selectedFg } else { $textColor })

            if ($isSelected) {
                $engine.WriteAt($this.Header.X + 2, $rowY, ">", $cursorColor, $bg)
            }

            $x = $this.Header.X + 4

            # GRADIENT: Render Synthwave theme with per-character magenta→cyan gradient
            if ($theme.Name -eq "Synthwave") {
                $this._RenderGradientText($engine, $x, $rowY, $theme.Name.PadRight(15), "#ff00ff", "#00ffff", $rowBg)
                $x += 15
                $this._RenderGradientText($engine, $x, $rowY, $theme.Description.PadRight(36), "#ff00ff", "#00ffff", $rowBg)
                $x += 36
            }
            else {
                $engine.WriteAt($x, $rowY, $theme.Name.PadRight(15), $rowFg, $rowBg)
                $x += 15
                $descFg = $(if ($isSelected) { $selectedFg } else { $mutedColor })
                $engine.WriteAt($x, $rowY, $theme.Description.PadRight(36), $descFg, $rowBg)
                $x += 36
            }

            if ($isCurrent) {
                $statusColor = $this.Header.GetThemedColorInt('Foreground.Success')
                $engine.WriteAt($x, $rowY, "[CURRENT]", $statusColor, $bg)
            }
        }

        # Show color preview for selected theme
        if ($this.SelectedIndex -ge 0 -and $this.SelectedIndex -lt $this.Themes.Count) {
            $theme = $this.Themes[$this.SelectedIndex]
            $previewY = $startY + [Math]::Min($this.Themes.Count, $maxLines) + 2

            if ($previewY -lt $this.Footer.Y - 2) {
                $engine.WriteAt($this.Header.X + 4, $previewY, "━" * 50, $headerColor, $bg)
                $previewY++

                $engine.WriteAt($this.Header.X + 4, $previewY, "Selected: ", $textColor, $bg)

                # Gradient preview for Synthwave
                if ($theme.Name -eq "Synthwave") {
                    $this._RenderGradientText($engine, $this.Header.X + 14, $previewY, $theme.Name, "#ff00ff", "#00ffff", $bg)
                }
                else {
                    $engine.WriteAt($this.Header.X + 14, $previewY, $theme.Name, $this.Header.GetThemedColorInt('Foreground.FieldFocused'), $bg)
                }
                $previewY++

                $engine.WriteAt($this.Header.X + 4, $previewY, "Hex Code: ", $mutedColor, $bg)
                $engine.WriteAt($this.Header.X + 14, $previewY, $theme.Hex, $this.Header.GetThemedColorInt('Foreground.Success'), $bg)
                $previewY++

                $engine.WriteAt($this.Header.X + 4, $previewY, "Description: ", $mutedColor, $bg)
                if ($theme.Name -eq "Synthwave") {
                    $this._RenderGradientText($engine, $this.Header.X + 17, $previewY, $theme.Description, "#ff00ff", "#00ffff", $bg)
                }
                else {
                    $engine.WriteAt($this.Header.X + 17, $previewY, $theme.Description, $textColor, $bg)
                }
                $previewY += 2

                $engine.WriteAt($this.Header.X + 4, $previewY, "Press Enter to apply, T to test, Esc to cancel", $headerColor, $bg)
            }
        }
    }

    [string] RenderContent() { return "" }

    [bool] HandleKeyPress([ConsoleKeyInfo]$keyInfo) {
        $handled = ([PmcScreen]$this).HandleKeyPress($keyInfo)
        if ($handled) { return $true }

        $keyChar = [char]::ToLower($keyInfo.KeyChar)
        switch ($keyInfo.Key) {
            'UpArrow' { if ($this.SelectedIndex -gt 0) { $this.SelectedIndex--; return $true } }
            'DownArrow' { if ($this.SelectedIndex -lt ($this.Themes.Count - 1)) { $this.SelectedIndex++; return $true } }
            'Enter' { $this._ApplyTheme(); return $true }
            'Escape' { if ($global:PmcApp) { $global:PmcApp.PopScreen() }; return $true }
        }

        switch ($keyChar) {
            't' { $this._TestTheme(); return $true }
        }

        return $false
    }

    hidden [void] _ApplyTheme() {
        if ($this.SelectedIndex -lt 0 -or $this.SelectedIndex -ge $this.Themes.Count) { return }
        $theme = $this.Themes[$this.SelectedIndex]

        try {
            $this.CurrentTheme = $theme.Name
            $reloadSuccess = Invoke-ThemeHotReload $theme.Name

            if ($reloadSuccess) {
                try { $this.ShowSuccess("Theme applied! Changes visible immediately.") } catch { }
            }
            else {
                Start-Sleep -Milliseconds 800
                if ($global:PmcApp) {
                    $global:PmcApp.RenderEngine.RequestClear()
                    $global:PmcApp.PopScreen()
                }
            }
        }
        catch {
            try { $this.ShowError("Failed to apply theme: $_") } catch { }
        }
    }

    hidden [void] _TestTheme() {
        if ($this.SelectedIndex -lt 0 -or $this.SelectedIndex -ge $this.Themes.Count) { return }
        $theme = $this.Themes[$this.SelectedIndex]
        $this.ShowStatus("Testing theme: $($theme.Name) - Press any key to return")
    }

    hidden [void] _ResetTheme() {
        $this.CurrentTheme = "Default"
        $this.SelectedIndex = 0
        $this.ShowSuccess("Reset to default theme")
    }
}

function Show-ThemeEditorScreen {
    param([object]$App)
    if (-not $App) { throw "PmcApplication required" }
    $screen = New-Object ThemeEditorScreen
    $App.PushScreen($screen)
}

﻿using namespace System.Collections.Generic
using namespace System.Text

# WeeklyTimeReportScreen - Weekly time tracking report
# Shows time entries grouped by project with daily breakdown (Mon-Fri)
# Matches the old renderer's weekly report functionality


Set-StrictMode -Version Latest

. "$PSScriptRoot/../PmcScreen.ps1"

<#
.SYNOPSIS
Weekly time tracking report screen

.DESCRIPTION
Shows time summary grouped by project with daily breakdown.
Displays:
- Hours per day (Mon-Fri) for each project
- Total hours per project
- Grand total
- Week navigation (=/-keys)
Based on the old renderer's DrawWeeklyReport implementation.
#>
class WeeklyTimeReportScreen : PmcScreen {
    # Data
    [hashtable]$ProjectSummaries = @{}
    [double]$GrandTotal = 0
    [int]$WeekOffset = 0
    [DateTime]$WeekStart
    [DateTime]$WeekEnd
    [string]$WeekHeader = ""
    [string]$WeekIndicator = ""
    [TaskStore]$Store = $null

    # TS-M4/TS-M5 FIX: Make week days configurable
    # Set to 7 for full week (Mon-Sun), or 5 for business week (Mon-Fri)
    [int]$WeekDays = 7
    [bool]$IncludeWeekends = $true

    # Static: Register menu items
    static [void] RegisterMenuItems([object]$registry) {
        $registry.AddMenuItem('Time', 'Weekly Report', 'W', {
                . "$PSScriptRoot/WeeklyTimeReportScreen.ps1"
                $global:PmcApp.PushScreen((New-Object -TypeName WeeklyTimeReportScreen))
            }, 10)
    }

    # Constructor
    WeeklyTimeReportScreen() : base("WeeklyTimeReport", "Weekly Time Report") {
        # Initialize TaskStore
        $this.Store = [TaskStore]::GetInstance()

        # Configure header
        $this.Header.SetBreadcrumb(@("Home", "Time Entries", "Weekly Report"))

        # Configure footer with shortcuts
        $this.Footer.ClearShortcuts()
        $this.Footer.AddShortcut("=", "Next Week")
        $this.Footer.AddShortcut("-", "Prev Week")
        $this.Footer.AddShortcut("R", "Refresh")
        $this.Footer.AddShortcut("Esc", "Back")
        $this.Footer.AddShortcut("Ctrl+Q", "Quit")

        # NOTE: _SetupMenus() removed - MenuRegistry handles menu population via static RegisterMenuItems()
        # Old pattern was adding duplicate/misplaced menu items
    }

    # Constructor with container (DI-enabled)
    WeeklyTimeReportScreen([object]$container) : base("WeeklyTimeReport", "Weekly Time Report", $container) {
        # Initialize TaskStore
        $this.Store = [TaskStore]::GetInstance()

        # Configure header
        $this.Header.SetBreadcrumb(@("Home", "Time Entries", "Weekly Report"))

        # Configure footer with shortcuts
        $this.Footer.ClearShortcuts()
        $this.Footer.AddShortcut("=", "Next Week")
        $this.Footer.AddShortcut("-", "Prev Week")
        $this.Footer.AddShortcut("R", "Refresh")
        $this.Footer.AddShortcut("Esc", "Back")
        $this.Footer.AddShortcut("Ctrl+Q", "Quit")

        # NOTE: _SetupMenus() removed - MenuRegistry handles menu population via static RegisterMenuItems()
        # Old pattern was adding duplicate/misplaced menu items
    }

    # OnEnter - Load data when screen becomes active (consistent with other screens)
    [void] OnEnter() {
        # Call parent to ensure proper lifecycle (sets IsActive, calls LoadData, executes OnEnterHandler)
        ([PmcScreen]$this).OnEnter()
    }

    [void] LoadData() {
        $this.ShowStatus("Loading weekly time report...")

        try {
            # Calculate week start (Monday) and end (Friday or Sunday based on config)
            $today = Get-Date
            $daysFromMonday = ($today.DayOfWeek.value__ + 6) % 7
            $thisMonday = $today.AddDays(-$daysFromMonday).Date
            $this.WeekStart = $thisMonday.AddDays($this.WeekOffset * 7)
            # TS-M4/TS-M5 FIX: Week end depends on configured week length
            $this.WeekEnd = $this.WeekStart.AddDays($this.WeekDays - 1)

            # Format week header
            $this.WeekHeader = "Week of {0} - {1}" -f $this.WeekStart.ToString('MMM dd'), $this.WeekEnd.ToString('MMM dd, yyyy')

            # Add indicator for current/past/future week
            if ($this.WeekOffset -eq 0) {
                $this.WeekIndicator = ' (This Week)'
            }
            elseif ($this.WeekOffset -lt 0) {
                $weeks = [Math]::Abs($this.WeekOffset)
                $plural = $(if ($weeks -gt 1) { 's' } else { '' })
                $this.WeekIndicator = " ($weeks week$plural ago)"
            }
            else {
                $plural = $(if ($this.WeekOffset -gt 1) { 's' } else { '' })
                $this.WeekIndicator = " ($($this.WeekOffset) week$plural from now)"
            }

            # Use TaskStore singleton instead of loading from disk
            $logs = $this.Store.GetAllTimeLogs()

            # TS-M4/TS-M5 FIX: Filter logs for the week (configurable: Mon-Fri or Mon-Sun)
            $weekLogs = @()
            for ($d = 0; $d -lt $this.WeekDays; $d++) {
                $dayDate = $this.WeekStart.AddDays($d).ToString('yyyy-MM-dd')
                $dayLogs = $logs | Where-Object {
                    $dateStr = $(if ($_.date -is [DateTime]) {
                            $_.date.ToString('yyyy-MM-dd')
                        }
                        else {
                            $_.date
                        })
                    $dateStr -eq $dayDate
                }
                $weekLogs += $dayLogs
            }

            # Group by project/id1
            $this.ProjectSummaries = @{}
            $this.GrandTotal = 0

            foreach ($log in $weekLogs) {
                # Determine grouping key
                $key = ''
                if ($log.ContainsKey('id1') -and $log.id1) {
                    $key = "#$($log.id1)"
                }
                else {
                    $name = $(if ($log.ContainsKey('project')) { $log.project } else { '' })
                    if (-not $name) { $name = 'Unknown' }
                    $key = $name
                }

                # Initialize project entry if needed
                if (-not $this.ProjectSummaries.ContainsKey($key)) {
                    $name = ''
                    $id1 = ''
                    if ($log.ContainsKey('id1') -and $log.id1) {
                        $id1 = $log.id1
                        $name = $(if ($log.ContainsKey('project')) { $log.project } else { '' })
                        if (-not $name) { $name = '' }
                    }
                    else {
                        $name = $(if ($log.ContainsKey('project')) { $log.project } else { '' })
                        if (-not $name) { $name = 'Unknown' }
                    }

                    # TS-M4/TS-M5 FIX: Include Sat/Sun columns
                    $this.ProjectSummaries[$key] = @{
                        Name  = $name
                        ID1   = $id1
                        Mon   = 0.0
                        Tue   = 0.0
                        Wed   = 0.0
                        Thu   = 0.0
                        Fri   = 0.0
                        Sat   = 0.0
                        Sun   = 0.0
                        Total = 0.0
                    }
                }

                # Add hours to appropriate day
                # TS-H3 FIX: Add error handling for unsafe DateTime cast
                if (-not $log.ContainsKey('date')) {
                    # Write-PmcTuiLog "WeeklyTimeReportScreen: Log entry missing date field" "WARNING"
                    continue
                }
                $logDate = $null
                try {
                    $logDate = $(if ($log.date -is [DateTime]) {
                            $log.date
                        }
                        else {
                            [datetime]::Parse($log.date)
                        })
                }
                catch {
                    # Write-PmcTuiLog "WeeklyTimeReportScreen: Failed to parse date '$($log.date)': $_" "WARNING"
                    continue  # Skip this log entry
                }

                if (-not $log.ContainsKey('minutes')) {
                    # Write-PmcTuiLog "WeeklyTimeReportScreen: Log entry missing minutes field" "WARNING"
                    continue
                }
                $dayIndex = ($logDate.DayOfWeek.value__ + 6) % 7  # 0=Mon, 1=Tue, ..., 5=Sat, 6=Sun
                $hours = [Math]::Round($log.minutes / 60.0, 1)

                # TS-M4/TS-M5 FIX: Handle all 7 days including Saturday and Sunday
                switch ($dayIndex) {
                    0 { $this.ProjectSummaries[$key].Mon += $hours }
                    1 { $this.ProjectSummaries[$key].Tue += $hours }
                    2 { $this.ProjectSummaries[$key].Wed += $hours }
                    3 { $this.ProjectSummaries[$key].Thu += $hours }
                    4 { $this.ProjectSummaries[$key].Fri += $hours }
                    5 { $this.ProjectSummaries[$key].Sat += $hours }
                    6 { $this.ProjectSummaries[$key].Sun += $hours }
                    default {
                        # Write-PmcTuiLog "WeeklyTimeReportScreen: Unexpected day index $dayIndex" "WARNING"
                    }
                }
                $this.ProjectSummaries[$key].Total += $hours
                $this.GrandTotal += $hours
            }

            # Update status
            if ($weekLogs.Count -eq 0) {
                $this.ShowStatus("No time entries for this week")
            }
            else {
                $this.ShowStatus("$($this.ProjectSummaries.Count) projects, $($this.GrandTotal.ToString('0.0')) hours total")
            }

        }
        catch {
            $this.ShowError("Failed to load weekly time report: $_")
            $this.ProjectSummaries = @{}
            $this.GrandTotal = 0
        }
    }

    [void] RenderContentToEngine([object]$engine) {
        if (-not $this.LayoutManager) { return }

        if ($this.ProjectSummaries.Count -eq 0) {
            $this._RenderEmptyStateToEngine($engine)
        }
        else {
            $this._RenderReportToEngine($engine)
        }
    }

    [string] RenderContent() { return "" }

    hidden [void] _RenderEmptyStateToEngine([object]$engine) {
        $contentRect = $this.LayoutManager.GetRegion('Content', $this.TermWidth, $this.TermHeight)

        # Colors
        $textColor = $this.GetThemedInt('Foreground.Field')
        $highlightColor = $this.GetThemedInt('Foreground.FieldFocused')
        $bg = $this.GetThemedInt('Background.Primary')

        # Week header
        $y = $contentRect.Y + 2

        $headerText = "$($this.WeekHeader)$($this.WeekIndicator)"
        $engine.WriteAt($contentRect.X + 4, $y, $headerText, $highlightColor, $bg)
        $y += 2

        # No entries message
        $message = "No time entries for this week"
        $engine.WriteAt($contentRect.X + 4, $y, $message, $textColor, $bg)
    }

    hidden [void] _RenderReportToEngine([object]$engine) {
        $contentRect = $this.LayoutManager.GetRegion('Content', $this.TermWidth, $this.TermHeight)

        # Colors
        $textColor = $this.GetThemedInt('Foreground.Field')
        $highlightColor = $this.GetThemedInt('Foreground.FieldFocused')
        $mutedColor = $this.GetThemedInt('Foreground.Muted')
        $headerColor = $this.GetThemedInt('Foreground.Muted')
        $successColor = $this.GetThemedInt('Foreground.Success')
        $warningColor = $this.GetThemedInt('Foreground.Warning')
        $bg = $this.GetThemedInt('Background.Primary')

        $y = $contentRect.Y + 1

        # Week header
        $headerText = "$($this.WeekHeader)$($this.WeekIndicator)"
        $engine.WriteAt($contentRect.X + 4, $y, $headerText, $highlightColor, $bg)
        $y += 2

        # Column headers
        $headerY = $y
        $headers = ""
        if ($this.IncludeWeekends) {
            $headers = "Name                 ID1   Mon    Tue    Wed    Thu    Fri    Sat    Sun    Total"
        }
        else {
            $headers = "Name                 ID1   Mon    Tue    Wed    Thu    Fri    Total"
        }
        $engine.WriteAt($contentRect.X + 4, $headerY, $headers, $headerColor, $bg)
        $y++

        # Separator line
        $sepLen = if ($this.IncludeWeekends) { 89 } else { 75 }
        $engine.WriteAt($contentRect.X + 4, $y, "-" * $sepLen, $mutedColor, $bg)
        $y++

        # Project rows - sorted by key
        $sortedProjects = $this.ProjectSummaries.GetEnumerator() | Sort-Object Key

        foreach ($entry in $sortedProjects) {
            $d = $entry.Value

            $x = $contentRect.X + 4

            # Name (20 chars, left-aligned)
            $name = $d.Name
            if ($name.Length -gt 20) {
                $name = $name.Substring(0, 17) + "..."
            }
            $engine.WriteAt($x, $y, $name.PadRight(20), $textColor, $bg)
            $x += 21 # 20 + 1 space

            # ID1 (5 chars, left-aligned)
            $id1Display = "$($d.ID1)"
            if ($id1Display.Length -gt 5) {
                $id1Display = $id1Display.Substring(0, 5)
            }
            $engine.WriteAt($x, $y, $id1Display.PadRight(5), $textColor, $bg)
            $x += 6 # 5 + 1 space

            # Day columns (6 chars each, right-aligned with 1 decimal)
            $days = @($d.Mon, $d.Tue, $d.Wed, $d.Thu, $d.Fri)
            if ($this.IncludeWeekends) { 
                $days += $d.Sat
                $days += $d.Sun 
            }

            foreach ($dayVal in $days) {
                $engine.WriteAt($x, $y, $dayVal.ToString("0.0").PadLeft(6), $successColor, $bg)
                $x += 7 # 6 + 1 space
            }

            # Total (8 chars, right-aligned with 1 decimal)
            $engine.WriteAt($x, $y, $d.Total.ToString("0.0").PadLeft(8), $warningColor, $bg)

            $y++
        }

        # Footer separator
        $y++
        $engine.WriteAt($contentRect.X + 4, $y, "-" * $sepLen, $mutedColor, $bg)
        $y++

        # Total row
        $x = $contentRect.X + 4
        $padding = if ($this.IncludeWeekends) { 72 } else { 58 }
        $x += $padding

        $engine.WriteAt($x, $y, "Total: ", $highlightColor, $bg)
        $x += 7

        $engine.WriteAt($x, $y, $this.GrandTotal.ToString("0.0").PadLeft(8), $highlightColor, $bg)
    }

    [bool] HandleKeyPress([ConsoleKeyInfo]$keyInfo) {
        # CRITICAL: Call parent FIRST for MenuBar, F10, Alt+keys, content widgets
        $handled = ([PmcScreen]$this).HandleKeyPress($keyInfo)
        if ($handled) { return $true }

        # Week navigation with arrow keys
        if ($keyInfo.Key -eq 'LeftArrow') {
            $this.WeekOffset--
            $this.NeedsClear = $true  # Force full screen clear to prevent duplication
            $this.LoadData()
            return $true
        }

        if ($keyInfo.Key -eq 'RightArrow') {
            $this.WeekOffset++
            $this.NeedsClear = $true  # Force full screen clear to prevent duplication
            $this.LoadData()
            return $true
        }

        # Week navigation with = and - keys
        if ($keyInfo.KeyChar -eq '=') {
            $this.WeekOffset++
            $this.NeedsClear = $true  # Force full screen clear to prevent duplication
            $this.LoadData()
            return $true
        }

        if ($keyInfo.KeyChar -eq '-') {
            $this.WeekOffset--
            $this.NeedsClear = $true  # Force full screen clear to prevent duplication
            $this.LoadData()
            return $true
        }

        # Refresh
        if ($keyInfo.KeyChar -eq 'r' -or $keyInfo.KeyChar -eq 'R') {
            $this.LoadData()
            return $true
        }

        return $false
    }
}

