using namespace System.Collections.Generic
using namespace System.Text

# ChecklistTemplatesFolderScreen - Manage folder-based checklist templates
# Templates are simple .txt files in checklist_templates/ folder

Set-StrictMode -Version Latest

. "$PSScriptRoot/../base/StandardListScreen.ps1"
. "$PSScriptRoot/../services/ChecklistService.ps1"
. "$PSScriptRoot/../widgets/ProjectPicker.ps1"

class ChecklistTemplatesFolderScreen : StandardListScreen {
    hidden [ChecklistService]$_checklistService = $null
    hidden [string]$_templatesFolder = ""

    # Project Picker for Import
    hidden [ProjectPicker]$_projectPicker = $null
    hidden [bool]$_showProjectPicker = $false

    # Static: Register menu items
    static [void] RegisterMenuItems([object]$registry) {
        $registry.AddMenuItem('Tools', 'Checklist Templates', 'H', {
            . "$PSScriptRoot/ChecklistTemplatesFolderScreen.ps1"
            $global:PmcApp.PushScreen((New-Object -TypeName ChecklistTemplatesFolderScreen))
        }, 30)
    }

    # Constructors
    ChecklistTemplatesFolderScreen() : base("ChecklistTemplates", "Checklist Templates") {
        $this._InitializeScreen()
    }

    ChecklistTemplatesFolderScreen([object]$container) : base("ChecklistTemplates", "Checklist Templates", $container) {
        $this._InitializeScreen()
    }

    hidden [void] _InitializeScreen() {
        $this._checklistService = [ChecklistService]::GetInstance()

        # Determine templates folder (at PMC root)
        $pmcRoot = Split-Path -Parent (Split-Path -Parent (Split-Path -Parent $PSScriptRoot))
        $this._templatesFolder = Join-Path $pmcRoot "checklist_templates"

        if (-not (Test-Path $this._templatesFolder)) {
            New-Item -ItemType Directory -Path $this._templatesFolder -Force | Out-Null
        }

        $this.AllowAdd = $true
        $this.AllowEdit = $true
        $this.AllowDelete = $true
        $this.AllowFilter = $false

        $this.Header.SetBreadcrumb(@("Home", "Tools", "Checklist Templates"))
    }

    # === Abstract Method Implementations ===

    [string] GetEntityType() { return 'checklist_template_file' }

    [array] GetColumns() {
        return @(
            @{ Name='name'; Label='Template Name'; Width=40; Sortable=$true }
            @{ Name='item_count'; Label='Items'; Width=8; Sortable=$true; Align='right' }
            @{ Name='modified'; Label='Modified'; Width=20; Sortable=$true }
        )
    }

    [void] LoadData() {
        $items = $this.LoadItems()
        $this.List.SetData($items)
    }

    [array] LoadItems() {
        $templates = @()
        $files = Get-ChildItem -Path $this._templatesFolder -Filter "*.txt" -File -ErrorAction SilentlyContinue

        foreach ($file in $files) {
            $lineCount = 0
            try {
                $content = Get-Content -Path $file.FullName -ErrorAction Stop
                $lineCount = @($content | Where-Object { -not [string]::IsNullOrWhiteSpace($_) }).Count
            } catch { $lineCount = 0 }

            $templates += @{
                name = $file.BaseName
                file_path = $file.FullName
                item_count = $lineCount
                modified = $file.LastWriteTime.ToString("yyyy-MM-dd HH:mm")
            }
        }
        return $templates
    }

    [array] GetEditFields([object]$item) {
        if ($null -eq $item -or $item.Count -eq 0) {
            return @(@{ Name='name'; Type='text'; Label='Template Name'; Required=$true; Value='' })
        } else {
            return @(@{ Name='name'; Type='text'; Label='Template Name'; Required=$true; Value=$item.name })
        }
    }

    [void] OnItemCreated([hashtable]$values) {
        try {
            if (-not $values.ContainsKey('name') -or [string]::IsNullOrWhiteSpace($values.name)) {
                $this.SetStatusMessage("Template name is required", "error")
                return
            }

            $name = $values.name
            $fileName = "$name.txt"
            $filePath = Join-Path $this._templatesFolder $fileName

            if (Test-Path $filePath) {
                $this.SetStatusMessage("Template '$name' already exists", "error")
                return
            }

            $content = "# Checklist template: $name`n# Each line will become a checklist item`n# Delete these comment lines and add your items below`n`n"
            Set-Content -Path $filePath -Value $content -Encoding utf8

            $this.SetStatusMessage("Template '$name' created. Press Enter to edit.", "success")
            $this.LoadData()
        } catch {
            $this.SetStatusMessage("Error creating template: $($_.Exception.Message)", "error")
        }
    }

    [void] OnItemUpdated([object]$item, [hashtable]$values) {
        try {
            $oldName = $(if ($item -is [hashtable]) { $item['name'] } else { $item.name })
            $oldPath = $(if ($item -is [hashtable]) { $item['file_path'] } else { $item.file_path })

            if (-not $values.ContainsKey('name') -or [string]::IsNullOrWhiteSpace($values.name)) {
                $this.SetStatusMessage("Template name is required", "error")
                return
            }

            $newName = $values.name
            $newPath = Join-Path $this._templatesFolder "$newName.txt"

            if ($oldPath -eq $newPath) { return }

            if (Test-Path $newPath) {
                $this.SetStatusMessage("Template '$newName' already exists", "error")
                return
            }

            Move-Item -Path $oldPath -Destination $newPath -Force
            $this.SetStatusMessage("Template renamed to '$newName'", "success")
            $this.LoadData()
        } catch {
            $this.SetStatusMessage("Error renaming template: $($_.Exception.Message)", "error")
        }
    }

    [void] OnItemDeleted([object]$item) {
        try {
            $name = $(if ($item -is [hashtable]) { $item['name'] } else { $item.name })
            $filePath = $(if ($item -is [hashtable]) { $item['file_path'] } else { $item.file_path })

            if (Test-Path $filePath) {
                Remove-Item -Path $filePath -Force
                $this.SetStatusMessage("Template '$name' deleted", "success")
                $this.LoadData()
            } else {
                $this.SetStatusMessage("Template file not found", "error")
            }
        } catch {
            $this.SetStatusMessage("Error deleting template: $($_.Exception.Message)", "error")
        }
    }

    [void] OnItemActivated($item) {
        $filePath = $(if ($item -is [hashtable]) { $item['file_path'] } else { $item.file_path })

        try {
            if (Test-Path $filePath) {
                if ($IsWindows -or $env:OS -match "Windows") {
                    Start-Process notepad.exe -ArgumentList $filePath
                } else {
                     $this.SetStatusMessage("Edit: $filePath (use external editor)", "info")
                }
            } else {
                $this.SetStatusMessage("Template file not found", "error")
            }
        } catch {
            $this.SetStatusMessage("Error opening template: $($_.Exception.Message)", "error")
        }
    }

    [array] GetCustomActions() {
        $self = $this
        return @(
            @{
                Key = 'I'
                Label = 'Import to Project'
                Callback = {
                    $selected = $self.List.GetSelectedItem()
                    if ($selected) {
                        $self._ImportToProject($selected)
                    }
                }.GetNewClosure()
            }
            @{
                Key = 'O'
                Label = 'Open/Edit'
                Callback = {
                    $selected = $self.List.GetSelectedItem()
                    if ($selected) {
                        $self.OnItemActivated($selected)
                    }
                }.GetNewClosure()
            }
        )
    }

    # === Import Feature ===

    hidden [void] _ImportToProject($template) {
        $this._projectPicker = [ProjectPicker]::new()
        $this._projectPicker.SetSize(60, 20)

        # Center Logic
        $pW = 60
        $pH = 20
        $this._projectPicker.SetPosition(
            [Math]::Max(0, [Math]::Floor(($this.TermWidth - $pW) / 2)),
            [Math]::Max(0, [Math]::Floor(($this.TermHeight - $pH) / 2))
        )

        $self = $this
        $this._projectPicker.OnProjectSelected = {
            param($projectName)
            if ($projectName) {
                $self._DoImport($template, $projectName)
            }
            $self._showProjectPicker = $false
            $self._projectPicker = $null
            $self.NeedsClear = $true
        }.GetNewClosure()

        $this._projectPicker.OnCancelled = {
            $self._showProjectPicker = $false
            $self._projectPicker = $null
            $self.NeedsClear = $true
        }.GetNewClosure()

        $this._showProjectPicker = $true
        $this.NeedsClear = $true
    }

    hidden [void] _DoImport($template, $projectName) {
        $filePath = if ($template -is [hashtable]) { $template.file_path } else { $template.file_path }
        if (-not (Test-Path $filePath)) { 
             $this.SetStatusMessage("Template file missing", "error")
             return 
        }

        try {
            $lines = Get-Content $filePath | Where-Object { 
                -not [string]::IsNullOrWhiteSpace($_) -and -not $_.Trim().StartsWith("#") 
            }

            if ($lines.Count -eq 0) {
                 $this.SetStatusMessage("Template is empty", "error")
                 return
            }

            $title = if ($template -is [hashtable]) { $template.name } else { $template.name }

            # Create instance
            $this._checklistService.CreateBlankInstance($title, "project", $projectName, $lines)
            $this.SetStatusMessage("Imported '$title' to project '$projectName'", "success")
        }
        catch {
            $this.SetStatusMessage("Import failed: $($_.Exception.Message)", "error")
        }
    }

    # === Render Overrides ===

    [void] RenderContentToEngine([object]$engine) {
        # Render base list
        ([StandardListScreen]$this).RenderContentToEngine($engine)

        # Render Picker Overlay
        if ($this._showProjectPicker -and $this._projectPicker) {
             # Re-center if terminal resized
            $pW = 60
            $pH = 20
            $this._projectPicker.SetPosition(
                [Math]::Max(0, [Math]::Floor(($this.TermWidth - $pW) / 2)),
                [Math]::Max(0, [Math]::Floor(($this.TermHeight - $pH) / 2))
            )

            $this._projectPicker.RenderToEngine($engine)
        }
    }

    [bool] HandleKeyPress([ConsoleKeyInfo]$keyInfo) {
        if ($this._showProjectPicker -and $this._projectPicker) {
            return $this._projectPicker.HandleInput($keyInfo)
        }

        return ([StandardListScreen]$this).HandleKeyPress($keyInfo)
    }
}

﻿using namespace System.Collections.Generic
using namespace System.Text

# ChecklistTemplatesScreen - Manage checklist templates
# Templates are reusable checklist definitions

Set-StrictMode -Version Latest

. "$PSScriptRoot/../base/StandardListScreen.ps1"
. "$PSScriptRoot/../services/ChecklistService.ps1"

<#
.SYNOPSIS
Checklist template management screen

.DESCRIPTION
Manage reusable checklist templates:
- Add/Edit/Delete templates
- View template items
- Create instances from templates
- Category organization
#>
class ChecklistTemplatesScreen : StandardListScreen {
    hidden [ChecklistService]$_checklistService = $null

    # Static: Register menu items
    static [void] RegisterMenuItems([object]$registry) {
        $registry.AddMenuItem('Tools', 'Checklist Templates', 'H', {
            . "$PSScriptRoot/ChecklistTemplatesScreen.ps1"
            $global:PmcApp.PushScreen((New-Object -TypeName ChecklistTemplatesScreen))
        }, 30)
    }

    # Legacy constructor (backward compatible)
    ChecklistTemplatesScreen() : base("ChecklistTemplates", "Checklist Templates") {
        $this._InitializeScreen()
    }

    # Container constructor
    ChecklistTemplatesScreen([object]$container) : base("ChecklistTemplates", "Checklist Templates", $container) {
        $this._InitializeScreen()
    }

    hidden [void] _InitializeScreen() {
        # Initialize service
        $this._checklistService = [ChecklistService]::GetInstance()

        # Configure capabilities
        $this.AllowAdd = $true
        $this.AllowEdit = $true
        $this.AllowDelete = $true
        $this.AllowFilter = $false

        # Configure header
        $this.Header.SetBreadcrumb(@("Home", "Tools", "Checklist Templates"))

        # Setup event handlers
        $self = $this
        $this._checklistService.OnChecklistsChanged = {
            if ($null -ne $self -and $self.IsActive) {
                $self.LoadData()
            }
        }.GetNewClosure()
    }

    # === Abstract Method Implementations ===

    [string] GetEntityType() {
        return 'checklist_template'
    }

    [array] GetColumns() {
        return @(
            @{ Name='name'; Label='Template Name'; Width=30 }
            @{ Name='category'; Label='Category'; Width=20 }
            @{ Name='item_count'; Label='Items'; Width=8 }
            @{ Name='description'; Label='Description'; Width=50 }
        )
    }

    [void] LoadData() {
        $items = $this.LoadItems()
        $this.List.SetData($items)
    }

    [array] LoadItems() {
        $templates = @($this._checklistService.GetAllTemplates())

        # Format for display
        foreach ($template in $templates) {
            if ($template.ContainsKey('items')) {
                $template['item_count'] = $template.items.Count
            } else {
                $template['item_count'] = 0
            }
        }

        return $templates
    }

    [array] GetEditFields([object]$item) {
        if ($null -eq $item -or $item.Count -eq 0) {
            # New template - empty fields
            return @(
                @{ Name='name'; Type='text'; Label='Template Name'; Required=$true; Value='' }
                @{ Name='category'; Type='text'; Label='Category'; Value='General' }
                @{ Name='description'; Type='text'; Label='Description'; Value='' }
                @{ Name='items'; Type='textarea'; Label='Items (pipe-separated)'; Value=''; MaxLength=5000 }
            )
        } else {
            # Existing template - populate from item
            $itemsText = ''
            if ($item.ContainsKey('items') -and $item.items) {
                $itemTexts = @($item.items | ForEach-Object { $_.text })
                $itemsText = $itemTexts -join " | "
            }

            return @(
                @{ Name='name'; Type='text'; Label='Template Name'; Required=$true; Value=$item.name }
                @{ Name='category'; Type='text'; Label='Category'; Value=$item.category }
                @{ Name='description'; Type='text'; Label='Description'; Value=$item.description }
                @{ Name='items'; Type='textarea'; Label='Items (pipe-separated)'; Value=$itemsText; MaxLength=5000 }
            )
        }
    }

    [void] OnItemCreated([hashtable]$values) {
        try {
            # SAVE FIX: Validate and safe access
            if (-not $values.ContainsKey('name') -or [string]::IsNullOrWhiteSpace($values.name)) {
                $this.SetStatusMessage("Template name is required", "error")
                return
            }

            # Parse items (newline-separated)
            $itemTexts = @()
            if ($values.ContainsKey('items') -and -not [string]::IsNullOrWhiteSpace($values.items)) {
                $itemTexts = @($values.items -split "`n" | ForEach-Object { $_.Trim() } | Where-Object { $_ })
            }

            if ($itemTexts.Count -eq 0) {
                $this.SetStatusMessage("Template must have at least one item", "error")
                return
            }

            $name = $values.name
            $description = $(if ($values.ContainsKey('description')) { $values.description } else { '' })
            $category = $(if ($values.ContainsKey('category')) { $values.category } else { '' })

            $this._checklistService.CreateTemplate($name, $description, $category, $itemTexts)

            $this.SetStatusMessage("Template '$name' created", "success")
        } catch {
            $this.SetStatusMessage("Error creating template: $($_.Exception.Message)", "error")
        }
    }

    [void] OnItemUpdated([object]$item, [hashtable]$values) {
        try {
            $itemId = $(if ($item -is [hashtable]) { $item['id'] } else { $item.id })

            # Parse items (newline-separated)
            $itemTexts = @()
            if ($values.ContainsKey('items') -and -not [string]::IsNullOrWhiteSpace($values.items)) {
                $itemTexts = @($values.items -split "`n" | ForEach-Object { $_.Trim() } | Where-Object { $_ })
            }

            if ($itemTexts.Count -eq 0) {
                $this.SetStatusMessage("Template must have at least one item", "error")
                return
            }

            # Convert to item objects
            $items = @()
            $order = 1
            foreach ($text in $itemTexts) {
                $items += @{
                    text = $text
                    order = $order++
                }
            }

            # ENDEMIC FIX: Safe value access
            $changes = @{
                name = $(if ($values.ContainsKey('name')) { $values.name } else { '' })
                category = $(if ($values.ContainsKey('category')) { $values.category } else { '' })
                description = $(if ($values.ContainsKey('description')) { $values.description } else { '' })
                items = $items
            }

            if ([string]::IsNullOrWhiteSpace($changes.name)) {
                $this.SetStatusMessage("Template name is required", "error")
                return
            }

            $this._checklistService.UpdateTemplate($itemId, $changes)
            $this.SetStatusMessage("Template '$($changes.name)' updated", "success")
        } catch {
            $this.SetStatusMessage("Error updating template: $($_.Exception.Message)", "error")
        }
    }

    [void] OnItemDeleted([object]$item) {
        try {
            $itemId = $(if ($item -is [hashtable]) { $item['id'] } else { $item.id })
            $itemName = $(if ($item -is [hashtable]) { $item['name'] } else { $item.name })

            if ($itemId) {
                $this._checklistService.DeleteTemplate($itemId)
                $this.SetStatusMessage("Template '$itemName' deleted", "success")
            } else {
                $this.SetStatusMessage("Cannot delete template without ID", "error")
            }
        } catch {
            $this.SetStatusMessage("Error deleting template: $($_.Exception.Message)", "error")
        }
    }
}

﻿using namespace System.Collections.Generic
using namespace System.Text

# ChecklistsLauncherScreen - Select project to view checklists
# Simple project picker that then opens ChecklistsMenuScreen for selected project

Set-StrictMode -Version Latest

. "$PSScriptRoot/../base/StandardListScreen.ps1"

<#
.SYNOPSIS
Checklist launcher - select project to view its checklists

.DESCRIPTION
Shows list of all projects. Selecting a project opens ChecklistsMenuScreen for that project.
#>
class ChecklistsLauncherScreen : StandardListScreen {
    hidden [TaskStore]$_store = $null

    # Static: Register menu items
    static [void] RegisterMenuItems([object]$registry) {
        $registry.AddMenuItem('Tools', 'Checklists', 'C', {
            . "$PSScriptRoot/ChecklistsLauncherScreen.ps1"
            $global:PmcApp.PushScreen((New-Object -TypeName ChecklistsLauncherScreen))
        }, 25)
    }

    # Legacy constructor
    ChecklistsLauncherScreen() : base("ChecklistsLauncher", "Select Project for Checklists") {
        $this._InitializeScreen()
    }

    # Container constructor
    ChecklistsLauncherScreen([object]$container) : base("ChecklistsLauncher", "Select Project for Checklists", $container) {
        $this._InitializeScreen()
    }

    hidden [void] _InitializeScreen() {
        $this._store = [TaskStore]::GetInstance()

        # Configure capabilities
        $this.AllowAdd = $false
        $this.AllowEdit = $false
        $this.AllowDelete = $false
        $this.AllowFilter = $false

        # Configure header
        $this.Header.SetBreadcrumb(@("Home", "Tools", "Checklists"))
    }

    # === Abstract Method Implementations ===

    [string] GetEntityType() {
        return 'project'
    }

    [array] GetColumns() {
        return @(
            @{ Name='name'; Label='Project Name'; Width=60; Sortable=$true }
        )
    }

    [void] LoadData() {
        $projects = @($this._store.GetAllProjects())

        # Convert to array of hashtables with name
        $items = @()
        foreach ($project in $projects) {
            if ($project -is [string]) {
                $items += @{ name = $project }
            } elseif ($project -is [hashtable]) {
                $items += @{ name = $project['name'] }
            } else {
                $items += @{ name = $project.name }
            }
        }

        $this.List.SetData($items)
    }

    [array] GetEditFields([object]$item) {
        # Not used
        return @()
    }

    [void] OnItemActivated($item) {
        # Get project name
        $projectName = $(if ($item -is [hashtable]) { $item['name'] } else { $item.name })

        if ([string]::IsNullOrWhiteSpace($projectName)) {
            $this.SetStatusMessage("Invalid project", "error")
            return
        }

        # Open ChecklistsMenuScreen for this project
        . "$PSScriptRoot/ChecklistsMenuScreen.ps1"
        $checklistsScreen = New-Object ChecklistsMenuScreen -ArgumentList "project", $projectName, $projectName
        $global:PmcApp.PushScreen($checklistsScreen)
    }

    [array] GetCustomActions() {
        $self = $this
        return @(
            @{
                Key = 'O'
                Label = 'Open'
                Callback = {
                    $selected = $self.List.GetSelectedItem()
                    if ($selected) {
                        $self.OnItemActivated($selected)
                    }
                }.GetNewClosure()
            }
        )
    }
}

﻿using namespace System.Collections.Generic
using namespace System.Text

# ChecklistsMenuScreen - List checklists for a project/task
# Shows checklist instances owned by a specific entity

Set-StrictMode -Version Latest

. "$PSScriptRoot/../base/StandardListScreen.ps1"
. "$PSScriptRoot/../services/ChecklistService.ps1"

<#
.SYNOPSIS
Checklists menu screen for viewing checklists attached to project/task

.DESCRIPTION
Shows all checklist instances for a given owner:
- View checklist progress
- Open checklist editor
- Add from template
- Create blank checklist
- Delete checklist
#>
class ChecklistsMenuScreen : StandardListScreen {
    hidden [ChecklistService]$_checklistService = $null
    hidden [string]$_ownerType = ""
    hidden [string]$_ownerId = ""
    hidden [string]$_ownerName = ""

    # Constructor
    ChecklistsMenuScreen([string]$ownerType, [string]$ownerId, [string]$ownerName) : base("ChecklistsMenu", "Checklists") {
        $this._ownerType = $ownerType
        $this._ownerId = $ownerId
        $this._ownerName = $ownerName
        $this._checklistService = [ChecklistService]::GetInstance()

        # Configure capabilities
        $this.AllowAdd = $true
        $this.AllowEdit = $false  # Use Enter to open editor instead
        $this.AllowDelete = $true
        $this.AllowFilter = $false

        # Configure header
        $ownerLabel = $(if ($ownerType -eq "project") { "Project" } elseif ($ownerType -eq "task") { "Task" } else { "Global" })
        $this.Header.SetBreadcrumb(@($ownerLabel, $ownerName, "Checklists"))

        # Update screen title
        $this.ScreenTitle = "Checklists - $ownerName"

        # Setup event handlers
        $self = $this
        $this._checklistService.OnChecklistsChanged = {
            if ($null -ne $self -and $self.IsActive) {
                $self.LoadData()
            }
        }.GetNewClosure()
    }

    ChecklistsMenuScreen([string]$ownerType, [string]$ownerId, [string]$ownerName, [object]$container) : base("ChecklistsMenu", "Checklists", $container) {
        $this._ownerType = $ownerType
        $this._ownerId = $ownerId
        $this._ownerName = $ownerName
        $this._checklistService = [ChecklistService]::GetInstance()

        # Configure capabilities
        $this.AllowAdd = $true
        $this.AllowEdit = $false  # Use Enter to open editor instead
        $this.AllowDelete = $true
        $this.AllowFilter = $false

        # Configure header
        $ownerLabel = $(if ($ownerType -eq "project") { "Project" } elseif ($ownerType -eq "task") { "Task" } else { "Global" })
        $this.Header.SetBreadcrumb(@($ownerLabel, $ownerName, "Checklists"))

        # Update screen title
        $this.ScreenTitle = "Checklists - $ownerName"

        # Setup event handlers
        $self = $this
        $this._checklistService.OnChecklistsChanged = {
            if ($null -ne $self -and $self.IsActive) {
                $self.LoadData()
            }
        }.GetNewClosure()
    }

    # === Abstract Method Implementations ===

    [string] GetEntityType() {
        return 'checklist'
    }

    [array] GetColumns() {
        return @(
            @{ Name='title'; Label='Checklist'; Width=40 }
            @{ Name='progress_display'; Label='Progress'; Width=20 }
            @{ Name='percent_complete'; Label='%'; Width=5 }
            @{ Name='modified_display'; Label='Modified'; Width=12 }
        )
    }

    [void] LoadData() {
        $items = $this.LoadItems()
        $this.List.SetData($items)
    }

    [array] LoadItems() {
        # Write-PmcTuiLog "ChecklistsMenuScreen.LoadItems: Loading checklists for owner type=$($this._ownerType) id=$($this._ownerId)" "INFO"
        $checklists = @($this._checklistService.GetInstancesByOwner($this._ownerType, $this._ownerId))
        # Write-PmcTuiLog "ChecklistsMenuScreen.LoadItems: Loaded $($checklists.Count) checklists" "INFO"

        # Format for display
        foreach ($checklist in $checklists) {
            # Progress display
            $checklist['progress_display'] = "[$($checklist.completed_count)/$($checklist.total_count)]"

            # Modified date
            if ($checklist.ContainsKey('modified') -and $checklist.modified -is [DateTime]) {
                $checklist['modified_display'] = $checklist.modified.ToString('yyyy-MM-dd')
            } else {
                $checklist['modified_display'] = ''
            }
        }

        return $checklists
    }

    [array] GetEditFields([object]$item) {
        # For adding new checklist
        return @(
            @{ Name='title'; Type='text'; Label='Checklist Title'; Required=$true; Value='' }
            @{ Name='template_id'; Type='text'; Label='Template ID (leave blank for blank checklist)'; Value='' }
            @{ Name='items'; Type='text'; Label='Items (comma-separated, if blank checklist)'; Value=''; MaxLength=1000 }
        )
    }

    [void] OnItemCreated([hashtable]$values) {
        try {
            # SAVE FIX: Safe property access
            if ($values.ContainsKey('template_id') -and -not [string]::IsNullOrWhiteSpace($values.template_id)) {
                # Create from template
                $instance = $this._checklistService.CreateInstanceFromTemplate(
                    $values.template_id,
                    $this._ownerType,
                    $this._ownerId
                )
                $instanceTitle = $(if ($instance -and $instance.PSObject.Properties['title']) { $instance.title } else { 'Checklist' })
                $this.SetStatusMessage("Checklist '$instanceTitle' created from template", "success")
            } else {
                # Create blank checklist - validate title
                if (-not $values.ContainsKey('title') -or [string]::IsNullOrWhiteSpace($values.title)) {
                    $this.SetStatusMessage("Checklist title is required", "error")
                    return
                }

                $itemTexts = @()
                if ($values.ContainsKey('items') -and -not [string]::IsNullOrWhiteSpace($values.items)) {
                    $itemTexts = @($values.items -split ',' | ForEach-Object { $_.Trim() } | Where-Object { $_ })
                }

                if ($itemTexts.Count -eq 0) {
                    $this.SetStatusMessage("Blank checklist must have at least one item", "error")
                    return
                }

                $instance = $this._checklistService.CreateBlankInstance(
                    $values.title,
                    $this._ownerType,
                    $this._ownerId,
                    $itemTexts
                )
                $instanceTitle = $(if ($instance -and $instance.PSObject.Properties['title']) { $instance.title } else { 'Checklist' })
                $this.SetStatusMessage("Checklist '$instanceTitle' created", "success")
            }
        } catch {
            $this.SetStatusMessage("Error creating checklist: $($_.Exception.Message)", "error")
        }
    }

    [void] OnItemUpdated([object]$item, [hashtable]$values) {
        # Not used - we open editor instead
    }

    [void] OnItemDeleted([object]$item) {
        try {
            $itemId = $(if ($item -is [hashtable]) { $item['id'] } else { $item.id })
            $itemTitle = $(if ($item -is [hashtable]) { $item['title'] } else { $item.title })

            if ($itemId) {
                $this._checklistService.DeleteInstance($itemId)
                $this.SetStatusMessage("Checklist '$itemTitle' deleted", "success")
            } else {
                $this.SetStatusMessage("Cannot delete checklist without ID", "error")
            }
        } catch {
            $this.SetStatusMessage("Error deleting checklist: $($_.Exception.Message)", "error")
        }
    }

    # Override OnItemActivated to open editor
    [void] OnItemActivated([object]$item) {
        if ($null -eq $item) {
            return
        }

        $itemId = $(if ($item -is [hashtable]) { $item['id'] } else { $item.id })
        if ($itemId) {
            . "$PSScriptRoot/ChecklistEditorScreen.ps1"
            $editorScreen = New-Object ChecklistEditorScreen -ArgumentList $itemId
            $global:PmcApp.PushScreen($editorScreen)
        }
    }

    # Custom action: Show template picker
    [void] ShowTemplatePicker() {
        . "$PSScriptRoot/ChecklistTemplatesFolderScreen.ps1"
        $templateScreen = New-Object ChecklistTemplatesFolderScreen
        $global:PmcApp.PushScreen($templateScreen)
    }

    [array] GetCustomActions() {
        return @(
            @{
                Label = "Templates (T)"
                Key = 't'
                Callback = {
                    $this.ShowTemplatePicker()
                }.GetNewClosure()
            }
        )
    }
}

using namespace System.Collections.Generic
using namespace System.Text

# ExcelImportScreen - Import project from Excel
# Multi-step wizard: Source -> Profile -> Preview -> Import

Set-StrictMode -Version Latest

. "$PSScriptRoot/../PmcScreen.ps1"
. "$PSScriptRoot/../services/ExcelComReader.ps1"
. "$PSScriptRoot/../services/ExcelMappingService.ps1"
. "$PSScriptRoot/../widgets/PmcFilePicker.ps1"

# Constants
$global:MAX_PREVIEW_ROWS = 15
$script:EXCEL_ATTACH_MAX_RETRIES = 3
$script:EXCEL_ATTACH_RETRY_DELAY_MS = 500
$global:MAX_CELLS_TO_READ = 100
$global:MIN_VALID_YEAR = 1950
$global:MAX_VALID_YEAR = 2100

<#
.SYNOPSIS
Excel import wizard screen

.DESCRIPTION
Import project data from Excel file:
- Step 1: Choose source (running Excel or file)
- Step 2: Select profile
- Step 3: Preview data
- Step 4: Confirm and import
#>
class ExcelImportScreen : PmcScreen {
    hidden [ExcelComReader]$_reader = $null
    hidden [ExcelMappingService]$_mappingService = $null
    hidden [object]$_activeProfile = $null
    hidden [hashtable]$_previewData = @{}
    hidden [int]$_step = 1
    hidden [int]$_selectedOption = 0
    hidden [string]$_errorMessage = ""
    [TaskStore]$Store = $null

    # File Picker State
    hidden [PmcFilePicker]$_filePicker = $null
    hidden [bool]$_showFilePicker = $false

    # Static: Register menu items
    static [void] RegisterMenuItems([object]$registry) {
        $registry.AddMenuItem('Projects', 'Import from Excel', 'I', {
                . "$PSScriptRoot/ExcelImportScreen.ps1"
                $global:PmcApp.PushScreen((New-Object -TypeName ExcelImportScreen))
            }, 40)
    }

    # Constructor
    ExcelImportScreen() : base("ExcelImport", "Import from Excel") {
        $this._Initialize()
    }

    # Constructor with container
    ExcelImportScreen([object]$container) : base("ExcelImport", "Import from Excel", $container) {
        $this._Initialize()
    }

    hidden [void] _Initialize() {
        try {
            $this._reader = [ExcelComReader]::new()
        }
        catch {
            $this._errorMessage = "Excel COM not available: $($_.Exception.Message). Excel must be installed."
        }

        $this._mappingService = [ExcelMappingService]::GetInstance()
        $this.Store = [TaskStore]::GetInstance()

        if ($null -eq $this.Store) {
            throw "Failed to initialize TaskStore singleton."
        }

        # Configure header
        $this.Header.SetBreadcrumb(@("Projects", "Import from Excel"))

        # Configure footer
        $this.Footer.ClearShortcuts()
        $this.Footer.AddShortcut("Enter", "Next")
        $this.Footer.AddShortcut("Esc", "Cancel")
    }

    [void] OnDoExit() {
        $this.IsActive = $false
        if ($null -ne $this._reader) {
            $this._reader.Close()
        }
    }

    # === Rendering ===

    [void] RenderContentToEngine([object]$engine) {
        # Calculate content area
        $contentY = 6
        $contentWidth = $this.TermWidth
        $y = $contentY + 2

        # Render based on step
        switch ($this._step) {
            1 { $this._RenderStep1Engine($engine, $y, $contentWidth) }
            2 { $this._RenderStep2Engine($engine, $y, $contentWidth) }
            3 { $this._RenderStep3Engine($engine, $y, $contentWidth) }
            4 { $this._RenderStep4Engine($engine, $y, $contentWidth) }
        }

        # Render error if any
        if (-not [string]::IsNullOrEmpty($this._errorMessage)) {
            $errorFg = $this.GetThemedInt('Foreground.Error')
            $textBg = $this.GetThemedInt('Background.Field')
            $engine.WriteAt(2, $this.TermHeight - 5, "Error: $($this._errorMessage)", $errorFg, $textBg)
        }

        # Render File Picker Overlay
        if ($this._showFilePicker -and $this._filePicker) {
            # Center the picker
            $pickerWidth = [Math]::Min(70, $this.TermWidth - 4)
            $pickerHeight = [Math]::Min(20, $this.TermHeight - 4)
            $pickerX = [Math]::Floor(($this.TermWidth - $pickerWidth) / 2)
            $pickerY = [Math]::Floor(($this.TermHeight - $pickerHeight) / 2)

            $this._filePicker.X = $pickerX
            $this._filePicker.Y = $pickerY
            $this._filePicker.Width = $pickerWidth
            $this._filePicker.Height = $pickerHeight

            # Ensure z-index is top
            $engine.BeginLayer(100)
            $this._filePicker.RenderToEngine($engine)
        }
    }

    hidden [void] _RenderStep1Engine([object]$engine, [int]$y, [int]$width) {
        $titleFg = $this.GetThemedInt('Foreground.Title')
        $errorFg = $this.GetThemedInt('Foreground.Error')
        $mutedFg = $this.GetThemedInt('Foreground.Muted')
        $selFg = $this.GetThemedInt('Foreground.RowSelected')
        $selBg = $this.GetThemedInt('Background.RowSelected')
        $textBg = $this.GetThemedInt('Background.Field')

        $engine.WriteAt(2, $y, "Step 1: Connect to Excel", $titleFg, $textBg)
        $y += 2

        if ($null -eq $this._reader) {
            $engine.WriteAt(4, $y, "Excel COM is not available on this system.", $errorFg, $textBg)
            $y += 2
            $engine.WriteAt(4, $y, "This feature requires Microsoft Excel to be installed.", $mutedFg, $textBg)
            $y++
            $engine.WriteAt(4, $y, "Press Esc to return to the project list.", $mutedFg, $textBg)
            return
        }

        # Option 1
        $fg1 = $(if ($this._selectedOption -eq 0) { $selFg } else { $mutedFg })
        $bg1 = $(if ($this._selectedOption -eq 0) { $selBg } else { $textBg })
        $engine.WriteAt(4, $y, "1. Attach to running Excel instance", $fg1, $bg1)
        $y++

        # Option 2
        $fg2 = $(if ($this._selectedOption -eq 1) { $selFg } else { $mutedFg })
        $bg2 = $(if ($this._selectedOption -eq 1) { $selBg } else { $textBg })
        $engine.WriteAt(4, $y, "2. Open Excel file...", $fg2, $bg2)
        $y += 2

        $hint = $(if ($this._selectedOption -eq 0) { "(Make sure Excel is running with your workbook open)" } else { "(Browse for an Excel file to import)" })
        $engine.WriteAt(4, $y, $hint, $mutedFg, $textBg)
    }

    hidden [void] _RenderStep2Engine([object]$engine, [int]$y, [int]$width) {
        $titleFg = $this.GetThemedInt('Foreground.Title')
        $selFg = $this.GetThemedInt('Foreground.RowSelected')
        $selBg = $this.GetThemedInt('Background.RowSelected')
        $textFg = $this.GetThemedInt('Foreground.Field')
        $textBg = $this.GetThemedInt('Background.Field')

        $engine.WriteAt(2, $y, "Step 2: Select Import Profile", $titleFg, $textBg)
        $y += 2

        $profiles = @($this._mappingService.GetAllProfiles())
        if ($null -eq $profiles -or $profiles.Count -eq 0) {
            $engine.WriteAt(4, $y, "No profiles found. Please create a profile first.", $textFg, $textBg)
            return
        }

        $activeProfile = $this._mappingService.GetActiveProfile()
        $activeId = $null
        if ($null -ne $activeProfile) {
            if ($activeProfile -is [hashtable] -and $activeProfile.ContainsKey('id')) { $activeId = $activeProfile['id'] }
            elseif ($activeProfile.PSObject.Properties['id']) { $activeId = $activeProfile.id }
        }

        for ($i = 0; $i -lt $profiles.Count; $i++) {
            $profile = $profiles[$i]
            if ($null -eq $profile) { continue }

            $profileId = $null
            $profileName = 'Unnamed'
            if ($profile -is [hashtable]) {
                if ($profile.ContainsKey('id')) { $profileId = $profile['id'] }
                if ($profile.ContainsKey('name')) { $profileName = $profile['name'] }
            } else {
                if ($profile.PSObject.Properties['id']) { $profileId = $profile.id }
                if ($profile.PSObject.Properties['name']) { $profileName = $profile.name }
            }

            $isActive = $(if ($profileId -eq $activeId) { " [ACTIVE]" } else { "" })
            $text = "$($i + 1). $profileName$isActive"

            $fg = $(if ($i -eq $this._selectedOption) { $selFg } else { $textFg })
            $bg = $(if ($i -eq $this._selectedOption) { $selBg } else { $textBg })
            $engine.WriteAt(4, $y + $i, $text, $fg, $bg)
        }
    }

    hidden [void] _RenderStep3Engine([object]$engine, [int]$y, [int]$width) {
        $titleFg = $this.GetThemedInt('Foreground.Title')
        $textFg = $this.GetThemedInt('Foreground.Field')
        $textBg = $this.GetThemedInt('Background.Field')

        $engine.WriteAt(2, $y, "Step 3: Preview Import Data", $titleFg, $textBg)
        $y += 2

        if ($null -eq $this._activeProfile) {
            $engine.WriteAt(4, $y, "No profile selected", $textFg, $textBg)
            return
        }

        $profileName = 'Unnamed Profile'
        if ($this._activeProfile -is [hashtable] -and $this._activeProfile.ContainsKey('name')) {
            $profileName = $this._activeProfile['name']
        } elseif ($this._activeProfile.PSObject.Properties['name']) {
            $profileName = $this._activeProfile.name
        }

        $engine.WriteAt(4, $y, "Profile: $profileName", $textFg, $textBg)
        $y += 2

        $maxRows = $global:MAX_PREVIEW_ROWS
        $rowCount = 0

        foreach ($mapping in $this._activeProfile['mappings']) {
            if ($rowCount -ge $maxRows) { break }

            $fieldName = $mapping['display_name']
            $value = "(empty)"
            if ($this._previewData.ContainsKey($mapping['excel_cell'])) {
                $cellValue = $this._previewData[$mapping['excel_cell']]
                if (-not [string]::IsNullOrWhiteSpace($cellValue)) { $value = $cellValue }
            }

            $required = $(if ($mapping['required']) { "*" } else { " " })
            $engine.WriteAt(4, $y + $rowCount, "$required$($fieldName): $value", $textFg, $textBg)
            $rowCount++
        }
    }

    hidden [void] _RenderStep4Engine([object]$engine, [int]$y, [int]$width) {
        $titleFg = $this.GetThemedInt('Foreground.Title')
        $textFg = $this.GetThemedInt('Foreground.Field')
        $textBg = $this.GetThemedInt('Background.Field')

        $engine.WriteAt(2, $y, "Step 4: Import Complete", $titleFg, $textBg)
        $y += 2
        $engine.WriteAt(4, $y, "Project imported successfully!", $textFg, $textBg)
        $y += 2
        $engine.WriteAt(4, $y, "Press Esc to return to project list.", $textFg, $textBg)
    }

    # === Input Handling ===

    [bool] HandleKeyPress([ConsoleKeyInfo]$keyInfo) {
        # 1. Handle File Picker (Exclusive)
        if ($this._showFilePicker -and $this._filePicker) {
            $handled = $this._filePicker.HandleInput($keyInfo)

            if ($this._filePicker.IsComplete) {
                if ($this._filePicker.Result) {
                    $this._OpenFile($this._filePicker.SelectedPath)
                }
                # Close picker
                $this._showFilePicker = $false
                $this._filePicker = $null
                $this.NeedsClear = $true
            }
            return $true
        }

        # 2. Call parent for standard shortcuts (F10, Alt+Keys)
        $handled = ([PmcScreen]$this).HandleKeyPress($keyInfo)
        if ($handled) { return $true }

        # 3. Wizard Navigation
        $this._errorMessage = ""

        # Up/Down
        if ($keyInfo.Key -eq ([ConsoleKey]::UpArrow)) {
            if ($this._selectedOption -gt 0) {
                $this._selectedOption--
            }
            return $true
        }

        if ($keyInfo.Key -eq ([ConsoleKey]::DownArrow)) {
            $maxOptions = $this._GetMaxOptions()
            if ($maxOptions -gt 0 -and $this._selectedOption -lt $maxOptions - 1) {
                $this._selectedOption++
            }
            return $true
        }

        # Enter - Next Step / Action
        if ($keyInfo.Key -eq ([ConsoleKey]::Enter)) {
            $this._ProcessStep()
            return $true
        }

        # Escape - Back / Cancel
        if ($keyInfo.Key -eq ([ConsoleKey]::Escape)) {
            if ($this._step -eq 1) {
                $global:PmcApp.PopScreen()
            }
            else {
                $this._step--
                $this._selectedOption = 0
            }
            return $true
        }

        return $false
    }

    hidden [int] _GetMaxOptions() {
        switch ($this._step) {
            1 { return 2 }
            2 { return @($this._mappingService.GetAllProfiles()).Count }
            3 { return 1 }
            4 { return 1 }
            default { return 0 }
        }
        return 0
    }

    hidden [void] _ProcessStep() {
        # Pre-checks
        if ($null -eq $this._reader -and $this._step -eq 1) {
            $this._errorMessage = "Excel COM not available."
            return
        }

        try {
            switch ($this._step) {
                1 { # Connect
                    if ($this._selectedOption -eq 0) {
                        # Attach to running Excel
                        $this._AttachToExcel()
                    } else {
                        # Open File Picker
                        $this._InitFilePicker()
                    }
                }
                2 { # Select Profile
                    $this._SelectProfile()
                }
                3 { # Validate & Import
                    $this._ImportProject()
                    $this._step = 4
                    $this._selectedOption = 0
                }
                4 { # Done
                    $global:PmcApp.PopScreen()
                }
            }
        }
        catch {
            $this._errorMessage = $_.Exception.Message
        }
    }

    # === Helper Methods ===

    hidden [void] _InitFilePicker() {
        $startPath = [Environment]::GetFolderPath('UserProfile')
        $this._filePicker = [PmcFilePicker]::new($startPath, $false)
        $this._showFilePicker = $true
        $this.NeedsClear = $true
    }

    hidden [void] _OpenFile([string]$path) {
        if (-not [string]::IsNullOrWhiteSpace($path)) {
            try {
                $this._reader.OpenFile($path)
                $this._CheckWorkbook()
                $this._step = 2
                $this._selectedOption = 0
            } catch {
                $this._errorMessage = "Failed to open file: $($_.Exception.Message)"
            }
        }
    }

    hidden [void] _AttachToExcel() {
        $maxRetries = $script:EXCEL_ATTACH_MAX_RETRIES
        $retryDelay = $script:EXCEL_ATTACH_RETRY_DELAY_MS

        for ($retry = 0; $retry -lt $maxRetries; $retry++) {
            try {
                $this._reader.AttachToRunningExcel()
                $this._CheckWorkbook()

                # Success
                $this._step = 2
                $this._selectedOption = 0
                return
            }
            catch {
                if ($retry -lt ($maxRetries - 1)) {
                    Start-Sleep -Milliseconds $retryDelay
                }
            }
        }
        throw "Failed to attach to Excel. Make sure it is running with a workbook open."
    }

    hidden [void] _CheckWorkbook() {
        $wb = $this._reader.GetWorkbook()
        if ($null -eq $wb -or $null -eq $wb.Sheets -or $wb.Sheets.Count -eq 0) {
            throw "Workbook has no accessible sheets"
        }
    }

    hidden [void] _SelectProfile() {
        $profiles = @($this._mappingService.GetAllProfiles())
        if ($this._selectedOption -lt $profiles.Count) {
            $this._activeProfile = $profiles[$this._selectedOption]

            if ($null -eq $this._activeProfile['mappings'] -or $this._activeProfile['mappings'].Count -eq 0) {
                throw "Profile has no mappings"
            }

            # Read Preview
            $cellsToRead = @($this._activeProfile['mappings'] | ForEach-Object { $_['excel_cell'] })
            if ($cellsToRead.Count -gt $global:MAX_CELLS_TO_READ) {
                $cellsToRead = $cellsToRead | Select-Object -First $global:MAX_CELLS_TO_READ
            }

            $this._previewData = $this._reader.ReadCells($cellsToRead)
            if ($null -eq $this._previewData) { $this._previewData = @{} }

            $this._step = 3
            $this._selectedOption = 0
        }
    }

    hidden [void] _ImportProject() {
        if ($null -eq $this._activeProfile) { throw "No profile" }

        $projectData = @{}

        foreach ($mapping in $this._activeProfile['mappings']) {
            $cell = $mapping['excel_cell']
            $val = if ($this._previewData.ContainsKey($cell)) { $this._previewData[$cell] } else { $null }

            # Type Conversion
            $converted = $val
            switch ($mapping['data_type']) {
                'int' { 
                    try { $converted = if ($val) { [long]$val } else { 0 } } catch { throw "Invalid int: $val" }
                }
                'bool' {
                    try { $converted = if ($val) { [bool]$val } else { $false } } catch { throw "Invalid bool: $val" }
                }
                'date' {
                    try { 
                        if ($val) {
                            $d = [datetime]$val
                            if ($d.Year -lt $global:MIN_VALID_YEAR -or $d.Year -gt $global:MAX_VALID_YEAR) {
                                throw "Date out of range"
                            }
                            $converted = $d
                        } else { $converted = $null }
                    } catch { throw "Invalid date: $val" }
                }
            }

            # Required check
            if ($mapping['required'] -and $null -eq $converted) {
                throw "Field '$($mapping['display_name'])' is required"
            }

            $projectData[$mapping['project_property']] = $converted
        }

        if (-not $projectData['name']) { throw "Project name is required" }

        $success = $this.Store.AddProject($projectData)
        if (-not $success) { throw "Store add failed: $($this.Store.LastError)" }

        if (-not $this.Store.Flush()) { throw "Save to disk failed" }
    }
}

﻿using namespace System.Collections.Generic
using namespace System.Text

# ExcelMappingEditorScreen - Edit field mappings for a profile
# Shows list of field mappings with add/edit/delete

Set-StrictMode -Version Latest

. "$PSScriptRoot/../base/StandardListScreen.ps1"
. "$PSScriptRoot/../services/ExcelMappingService.ps1"

<#
.SYNOPSIS
Excel field mapping editor screen

.DESCRIPTION
Edit field mappings for a specific Excel profile:
- Add/Edit/Delete field mappings
- Configure Excel cell, project property, data type
- Set required fields
- Manage sort order
#>
class ExcelMappingEditorScreen : StandardListScreen {
    hidden [ExcelMappingService]$_mappingService = $null
    hidden [string]$_profileId = ""
    hidden [string]$_profileName = ""

    # Constructor
    ExcelMappingEditorScreen([string]$profileId, [string]$profileName) : base("ExcelMappings", "Field Mappings") {
        $this._profileId = $profileId
        $this._profileName = $profileName
        $this._mappingService = [ExcelMappingService]::GetInstance()

        # Configure capabilities
        $this.AllowAdd = $true
        $this.AllowEdit = $true
        $this.AllowDelete = $true
        $this.AllowFilter = $false

        # Configure header
        $this.Header.SetBreadcrumb(@("Home", "Projects", "Excel Profiles", $profileName, "Mappings"))
        $this.ScreenTitle = "Mappings - $profileName"

        # Setup event handlers
        $self = $this
        $this._mappingService.OnProfilesChanged = {
            if ($null -ne $self -and $self.IsActive) {
                $self.LoadData()
            }
        }.GetNewClosure()
    }

    ExcelMappingEditorScreen([string]$profileId, [string]$profileName, [object]$container) : base("ExcelMappings", "Field Mappings", $container) {
        $this._profileId = $profileId
        $this._profileName = $profileName
        $this._mappingService = [ExcelMappingService]::GetInstance()

        # Configure capabilities
        $this.AllowAdd = $true
        $this.AllowEdit = $true
        $this.AllowDelete = $true
        $this.AllowFilter = $false

        # Configure header
        $this.Header.SetBreadcrumb(@("Home", "Projects", "Excel Profiles", $profileName, "Mappings"))
        $this.ScreenTitle = "Mappings - $profileName"

        # Setup event handlers
        $self = $this
        $this._mappingService.OnProfilesChanged = {
            if ($null -ne $self -and $self.IsActive) {
                $self.LoadData()
            }
        }.GetNewClosure()
    }

    [void] OnDoExit() {
        ([StandardListScreen]$this).OnDoExit()
        $this._mappingService.OnProfilesChanged = $null
    }

    # === Abstract Method Implementations ===

    [string] GetEntityType() {
        # Non-standard type, won't wire to TaskStore
        return 'field_mapping'
    }

    [array] GetColumns() {
        return @(
            @{ Name='display_name'; Label='Field Name'; Width=25 }
            @{ Name='excel_cell'; Label='Excel Cell'; Width=12 }
            @{ Name='project_property'; Label='Property'; Width=20 }
            @{ Name='data_type'; Label='Type'; Width=10 }
            @{ Name='required_display'; Label='Required'; Width=10 }
        )
    }

    [void] LoadData() {
        $items = $this.LoadItems()
        $this.List.SetData($items)
    }

    [array] LoadItems() {
        $mappings = @($this._mappingService.GetMappings($this._profileId))

        # Format for display
        foreach ($mapping in $mappings) {
            $mapping['required_display'] = $(if ($mapping['required']) { "Yes" } else { "No" })
        }

        return $mappings
    }

    [array] GetEditFields([object]$item) {
        if ($null -eq $item -or $item.Count -eq 0) {
            # New mapping
            return @(
                @{ Name='display_name'; Type='text'; Label='Display Name'; Required=$true; Value='' }
                @{ Name='excel_cell'; Type='text'; Label='Excel Cell (e.g., W3)'; Required=$true; Value='' }
                @{ Name='project_property'; Type='text'; Label='Project Property'; Required=$true; Value='' }
                @{ Name='data_type'; Type='text'; Label='Data Type (string/int/date/bool)'; Value='string' }
                @{ Name='required'; Type='text'; Label='Required (true/false)'; Value='false' }
                @{ Name='include_in_export'; Type='text'; Label='Include in Export (true/false)'; Value='true' }
                @{ Name='sort_order'; Type='text'; Label='Sort Order'; Value='1' }
            )
        } else {
            # Existing mapping - use hashtable accessor for consistency
            $displayName = $(if ($item -is [hashtable]) { $item['display_name'] } else { $item.display_name })
            $excelCell = $(if ($item -is [hashtable]) { $item['excel_cell'] } else { $item.excel_cell })
            $projectProperty = $(if ($item -is [hashtable]) { $item['project_property'] } else { $item.project_property })
            $dataType = $(if ($item -is [hashtable]) { $item['data_type'] } else { $item.data_type })
            $required = $(if ($item -is [hashtable]) { $item['required'] } else { $item.required })
            $includeInExport = $(if ($item -is [hashtable]) { $item['include_in_export'] } else { $item.include_in_export })
            $sortOrder = $(if ($item -is [hashtable]) { $item['sort_order'] } else { $item.sort_order })

            # HIGH FIX #10: Robust boolean parsing with error handling
            $requiredBool = try {
                if ($required -is [bool]) {
                    $required
                } else {
                    [bool]::Parse($required)
                }
            } catch {
                # Write-PmcTuiLog "Invalid boolean value for required: '$required', defaulting to false" "WARNING"
                $false
            }

            $includeInExportBool = try {
                if ($includeInExport -is [bool]) {
                    $includeInExport
                } else {
                    [bool]::Parse($includeInExport)
                }
            } catch {
                # Write-PmcTuiLog "Invalid boolean value for includeInExport: '$includeInExport', defaulting to true" "WARNING"
                $true
            }

            return @(
                @{ Name='display_name'; Type='text'; Label='Display Name'; Required=$true; Value=$displayName }
                @{ Name='excel_cell'; Type='text'; Label='Excel Cell (e.g., W3)'; Required=$true; Value=$excelCell }
                @{ Name='project_property'; Type='text'; Label='Project Property'; Required=$true; Value=$projectProperty }
                @{ Name='data_type'; Type='text'; Label='Data Type (string/int/date/bool)'; Value=$dataType }
                @{ Name='required'; Type='text'; Label='Required (true/false)'; Value=(if ($requiredBool) { 'true' } else { 'false' }) }
                @{ Name='include_in_export'; Type='text'; Label='Include in Export (true/false)'; Value=(if ($includeInExportBool) { 'true' } else { 'false' }) }
                @{ Name='sort_order'; Type='text'; Label='Sort Order'; Value=$sortOrder.ToString() }
            )
        }
    }

    [void] OnItemCreated([hashtable]$values) {
        try {
            # Validate Excel cell address format
            if ($values.excel_cell -notmatch '^[A-Z]+\d+$') {
                $this.SetStatusMessage("Invalid Excel cell address: $($values.excel_cell). Use format like 'A1' or 'W3'", "error")
                return
            }

            # Validate data type
            $validTypes = @('string', 'int', 'date', 'bool')
            if ($values.data_type -notin $validTypes) {
                $this.SetStatusMessage("Invalid data type '$($values.data_type)'. Must be one of: $($validTypes -join ', ')", "error")
                return
            }

            # HIGH FIX #10: Parse boolean values robustly
            $required = try { [bool]::Parse($values.required) } catch { $false }
            $includeInExport = try { [bool]::Parse($values.include_in_export) } catch { $true }

            # Validate and parse sort order
            $sortOrder = 1
            if ($values.sort_order -match '^\d+$') {
                $sortOrder = [int]$values.sort_order
            } else {
                throw "Sort order must be a number"
            }

            $mappingData = @{
                display_name = $values.display_name
                excel_cell = $values.excel_cell
                project_property = $values.project_property
                data_type = $values.data_type
                required = $required
                include_in_export = $includeInExport
                sort_order = $sortOrder
            }

            $this._mappingService.AddMapping($this._profileId, $mappingData)
            $this.SetStatusMessage("Mapping '$($values.display_name)' added", "success")
        } catch {
            $this.SetStatusMessage("Error adding mapping: $($_.Exception.Message)", "error")
        }
    }

    [void] OnItemUpdated([object]$item, [hashtable]$values) {
        try {
            $itemId = $(if ($item -is [hashtable]) { $item['id'] } else { $item.id })

            # Validate Excel cell address format
            if ($values.excel_cell -notmatch '^[A-Z]+\d+$') {
                $this.SetStatusMessage("Invalid Excel cell address: $($values.excel_cell). Use format like 'A1' or 'W3'", "error")
                return
            }

            # Validate data type
            $validTypes = @('string', 'int', 'date', 'bool')
            if ($values.data_type -notin $validTypes) {
                $this.SetStatusMessage("Invalid data type '$($values.data_type)'. Must be one of: $($validTypes -join ', ')", "error")
                return
            }

            # HIGH FIX #10: Parse boolean values robustly
            $required = try { [bool]::Parse($values.required) } catch { $false }
            $includeInExport = try { [bool]::Parse($values.include_in_export) } catch { $true }

            # Validate and parse sort order
            $sortOrder = 1
            if ($values.sort_order -match '^\d+$') {
                $sortOrder = [int]$values.sort_order
            } else {
                throw "Sort order must be a number"
            }

            $changes = @{
                display_name = $values.display_name
                excel_cell = $values.excel_cell
                project_property = $values.project_property
                data_type = $values.data_type
                required = $required
                include_in_export = $includeInExport
                sort_order = $sortOrder
            }

            $this._mappingService.UpdateMapping($this._profileId, $itemId, $changes)
            $this.SetStatusMessage("Mapping '$($values.display_name)' updated", "success")
        } catch {
            $this.SetStatusMessage("Error updating mapping: $($_.Exception.Message)", "error")
        }
    }

    [void] OnItemDeleted([object]$item) {
        try {
            $itemId = $(if ($item -is [hashtable]) { $item['id'] } else { $item.id })
            $itemName = $(if ($item -is [hashtable]) { $item['display_name'] } else { $item.display_name })

            if ($itemId) {
                $this._mappingService.DeleteMapping($this._profileId, $itemId)
                $this.SetStatusMessage("Mapping '$itemName' deleted", "success")
            } else {
                $this.SetStatusMessage("Cannot delete mapping without ID", "error")
            }
        } catch {
            $this.SetStatusMessage("Error deleting mapping: $($_.Exception.Message)", "error")
        }
    }
}

using namespace System.Collections.Generic
