<#
.SYNOPSIS
Excel profile management screen

.DESCRIPTION
Manage Excel import mapping profiles:
- Add/Edit/Delete profiles
- Set active profile
- Edit field mappings (opens ExcelMappingEditorScreen)
#>
class ExcelProfileManagerScreen : StandardListScreen {
    hidden [ExcelMappingService]$_mappingService = $null

    # Static: Register menu items
    static [void] RegisterMenuItems([object]$registry) {
        $registry.AddMenuItem('Projects', 'Excel Profiles', 'M', {
            . "$PSScriptRoot/ExcelProfileManagerScreen.ps1"
            $global:PmcApp.PushScreen((New-Object -TypeName ExcelProfileManagerScreen))
        }, 50)
    }

    # Constructor
    ExcelProfileManagerScreen() : base("ExcelProfiles", "Excel Import Profiles") {

        # Initialize service
        $this._mappingService = [ExcelMappingService]::GetInstance()

        # Configure capabilities
        $this.AllowAdd = $true
        $this.AllowEdit = $true
        $this.AllowDelete = $true
        $this.AllowFilter = $false

        # Configure header
        $this.Header.SetBreadcrumb(@("Home", "Projects", "Excel Profiles"))

        # Setup event handlers
        $self = $this
        $this._mappingService.OnProfilesChanged = {
            if ($null -ne $self -and $self.IsActive) {
                $self.LoadData()
            }
        }.GetNewClosure()
    }

    # Constructor with container
    ExcelProfileManagerScreen([object]$container) : base("ExcelProfiles", "Excel Import Profiles", $container) {

        # Initialize service
        $this._mappingService = [ExcelMappingService]::GetInstance()

        # Configure capabilities
        $this.AllowAdd = $true
        $this.AllowEdit = $true
        $this.AllowDelete = $true
        $this.AllowFilter = $false

        # Configure header
        $this.Header.SetBreadcrumb(@("Home", "Projects", "Excel Profiles"))

        # Setup event handlers
        $self = $this
        $this._mappingService.OnProfilesChanged = {
            if ($null -ne $self -and $self.IsActive) {
                $self.LoadData()
            }
        }.GetNewClosure()
    }

    [void] OnDoExit() {
        ([StandardListScreen]$this).OnDoExit()
        $this._mappingService.OnProfilesChanged = $null
    }

    # === Abstract Method Implementations ===

    [string] GetEntityType() {
        # Non-standard type, won't wire to TaskStore
        return 'excel_profile'
    }

    [array] GetColumns() {
        return @(
            @{ Name='name'; Label='Profile Name'; Width=30 }
            @{ Name='description'; Label='Description'; Width=40 }
            @{ Name='mapping_count'; Label='Fields'; Width=8 }
            @{ Name='is_active'; Label='Active'; Width=8 }
        )
    }

    [void] LoadData() {
        try {
            $items = $this.LoadItems()
            $this.List.SetData($items)
        } catch {
            throw
        }
    }

    # Helper method - not part of StandardListScreen contract
    [array] LoadItems() {
        $profiles = @($this._mappingService.GetAllProfiles())
        # Write-PmcTuiLog "ExcelProfileManagerScreen.LoadItems: Got $($profiles.Count) profiles from service" "DEBUG"

        $activeProfile = $this._mappingService.GetActiveProfile()
        $activeId = $(if ($activeProfile) { $activeProfile['id'] } else { $null })

        # Format for display
        foreach ($profile in $profiles) {
            if ($null -ne $profile) {
                # Write-PmcTuiLog "ExcelProfileManagerScreen.LoadItems: Processing profile - type=$($profile.GetType().Name) isHashtable=$($profile -is [hashtable])" "DEBUG"
                if ($profile -is [hashtable]) {
                    # Write-PmcTuiLog "ExcelProfileManagerScreen.LoadItems: Profile keys: $($profile.Keys -join ', ')" "DEBUG"
                    # Write-PmcTuiLog "ExcelProfileManagerScreen.LoadItems: Profile name='$($profile['name'])' desc='$($profile['description'])'" "DEBUG"
                }

                $profile['mapping_count'] = $(if ($profile['mappings']) { $profile['mappings'].Count } else { 0 })
                $profile['is_active'] = $(if ($profile['id'] -eq $activeId) { "Yes" } else { "No" })

                # Write-PmcTuiLog "ExcelProfileManagerScreen.LoadItems: After formatting - mapping_count=$($profile['mapping_count']) is_active=$($profile['is_active'])" "DEBUG"
            } else {
                # Write-PmcTuiLog "ExcelProfileManagerScreen.LoadItems: Null profile in list" "DEBUG"
            }
        }

        return $profiles
    }

    [array] GetEditFields([object]$item) {
        if ($null -eq $item -or $item.Count -eq 0) {
            # New profile
            return @(
                @{ Name='name'; Type='text'; Label='Profile Name'; Required=$true; Value='' }
                @{ Name='description'; Type='text'; Label='Description'; Value='' }
                @{ Name='start_cell'; Type='text'; Label='Start Cell'; Value='A1' }
            )
        } else {
            # Existing profile - use hashtable accessor for consistency
            $name = $(if ($item -is [hashtable]) { $item['name'] } else { $item.name })
            $description = $(if ($item -is [hashtable]) { $item['description'] } else { $item.description })
            $startCell = $(if ($item -is [hashtable]) { $item['start_cell'] } else { $item.start_cell })

            return @(
                @{ Name='name'; Type='text'; Label='Profile Name'; Required=$true; Value=$name }
                @{ Name='description'; Type='text'; Label='Description'; Value=$description }
                @{ Name='start_cell'; Type='text'; Label='Start Cell'; Value=$startCell }
            )
        }
    }

    [void] OnItemCreated([hashtable]$values) {
        try {
            # ENDEMIC FIX: Safe value access and validation
            $name = $(if ($values.ContainsKey('name')) { $values.name } else { '' })
            $description = $(if ($values.ContainsKey('description')) { $values.description } else { '' })
            $startCell = $(if ($values.ContainsKey('start_cell')) { $values.start_cell } else { '' })

            if ([string]::IsNullOrWhiteSpace($name)) {
                $this.SetStatusMessage("Profile name is required", "error")
                return
            }

            $this._mappingService.CreateProfile($name, $description, $startCell)

            $this.SetStatusMessage("Profile '$name' created", "success")
        } catch {
            $this.SetStatusMessage("Error creating profile: $($_.Exception.Message)", "error")
        }
    }

    [void] OnItemUpdated([object]$item, [hashtable]$values) {
        try {
            $itemId = $(if ($item -is [hashtable]) { $item['id'] } else { $item.id })

            # ENDEMIC FIX: Safe value access
            $changes = @{
                name = $(if ($values.ContainsKey('name')) { $values.name } else { '' })
                description = $(if ($values.ContainsKey('description')) { $values.description } else { '' })
                start_cell = $(if ($values.ContainsKey('start_cell')) { $values.start_cell } else { '' })
            }

            # Validate required fields
            if ([string]::IsNullOrWhiteSpace($changes.name)) {
                $this.SetStatusMessage("Profile name is required", "error")
                return
            }

            $this._mappingService.UpdateProfile($itemId, $changes)
            $this.SetStatusMessage("Profile '$($changes.name)' updated", "success")
        } catch {
            $this.SetStatusMessage("Error updating profile: $($_.Exception.Message)", "error")
        }
    }

    [void] OnItemDeleted([object]$item) {
        try {
            $itemId = $(if ($item -is [hashtable]) { $item['id'] } else { $item.id })
            $itemName = $(if ($item -is [hashtable]) { $item['name'] } else { $item.name })

            if ($itemId) {
                $this._mappingService.DeleteProfile($itemId)
                $this.SetStatusMessage("Profile '$itemName' deleted", "success")
            } else {
                $this.SetStatusMessage("Cannot delete profile without ID", "error")
            }
        } catch {
            $this.SetStatusMessage("Error deleting profile: $($_.Exception.Message)", "error")
        }
    }

    # Override OnItemActivated to open mapping editor
    [void] OnItemActivated([object]$item) {
        if ($null -eq $item) {
            return
        }

        $itemId = $(if ($item -is [hashtable]) { $item['id'] } else { $item.id })
        $itemName = $(if ($item -is [hashtable]) { $item['name'] } else { $item.name })

        if ($itemId) {
            . "$PSScriptRoot/ExcelMappingEditorScreen.ps1"
            $editorScreen = New-Object ExcelMappingEditorScreen -ArgumentList $itemId, $itemName
            $global:PmcApp.PushScreen($editorScreen)
        }
    }

    # Custom action: Set as active profile
    [void] SetActiveProfile() {
        $selectedItem = $this.List.GetSelectedItem()
        if ($null -eq $selectedItem) {
            $this.SetStatusMessage("No profile selected", "error")
            return
        }

        $itemId = $(if ($selectedItem -is [hashtable]) { $selectedItem['id'] } else { $selectedItem.id })
        $itemName = $(if ($selectedItem -is [hashtable]) { $selectedItem['name'] } else { $selectedItem.name })

        try {
            $this._mappingService.SetActiveProfile($itemId)
            $this.SetStatusMessage("Active profile set to '$itemName'", "success")
            $this.LoadData()
        } catch {
            # Write-PmcTuiLog "SetActiveProfile: Error setting active profile '$itemName' - $_" "ERROR"
            $this.SetStatusMessage("Error setting active profile: $($_.Exception.Message)", "error")
        }
    }

    [array] GetCustomActions() {
        return @(
            @{
                Label = "Set Active (S)"
                Key = 's'
                Callback = {
                    $this.SetActiveProfile()
                }.GetNewClosure()
            }
        )
    }
}

﻿# NotesMenuScreen.ps1 - List all notes with add/edit/delete capabilities
#
# Displays a list of all notes using StandardListScreen base class
# Allows creating new notes, editing existing notes, and deleting notes
#
# Usage:
#   $screen = New-Object NotesMenuScreen
#   $global:PmcApp.PushScreen($screen)

using namespace System
using namespace System.Collections.Generic

Set-StrictMode -Version Latest

. "$PSScriptRoot/../base/StandardListScreen.ps1"


class NotesMenuScreen : StandardListScreen {
    # === Configuration ===
    hidden [FileNoteService]$_noteService = $null
    hidden [string]$_ownerType = "global"
    hidden [string]$_ownerId = $null
    hidden [string]$_ownerName = ""

    # === Constructor ===
    # Legacy constructor (backward compatible)
    NotesMenuScreen() : base("NotesList", "Notes") {
        $this._InitializeScreen("global", $null, "")
    }

    # Container constructor
    NotesMenuScreen([object]$container) : base("NotesList", "Notes", $container) {
        $this._InitializeScreen("global", $null, "")
    }

    # Legacy constructor with owner parameters
    NotesMenuScreen([string]$ownerType, [string]$ownerId, [string]$ownerName) : base("NotesList", "Notes") {
        $this._InitializeScreen($ownerType, $ownerId, $ownerName)
    }

    # Container constructor with owner parameters
    NotesMenuScreen([string]$ownerType, [string]$ownerId, [string]$ownerName, [object]$container) : base("NotesList", "Notes", $container) {
        $this._InitializeScreen($ownerType, $ownerId, $ownerName)
    }

    hidden [void] _InitializeScreen([string]$ownerType, [string]$ownerId, [string]$ownerName) {
        $this._ownerType = $ownerType
        $this._ownerId = $ownerId
        $this._ownerName = $ownerName

        # Get note service instance
        $this._noteService = [FileNoteService]::GetInstance()

        # Subscribe to note changes
        # Note: Callback may be invoked when screen is not active, so check first
        $self = $this
        $this._noteService.OnNotesChanged = {
            if ($null -ne $self -and $self.IsActive) {
                $self.LoadData()
            }
        }.GetNewClosure()

        # Configure screen
        $this.AllowAdd = $true
        $this.AllowEdit = $true
        $this.AllowDelete = $true
        $this.AllowFilter = $true
        $this.AllowSearch = $true

        # Update screen title and breadcrumb based on owner
        if ($this._ownerType -ne "global") {
            $ownerLabel = $(if ($ownerType -eq "project") { "Project" } elseif ($ownerType -eq "task") { "Task" } else { "Global" })
            $this.ScreenTitle = "Notes - $ownerName"
            $this.Header.SetBreadcrumb(@($ownerLabel, $ownerName, "Notes"))
        }
    }

    # === Abstract Methods Implementation ===

    <#
    .SYNOPSIS
    Load notes data into the list
    #>
    [void] LoadData() {
        # Write-PmcTuiLog "NotesMenuScreen.LoadData: Loading notes for owner=$($this._ownerType):$($this._ownerId)" "INFO"

        try {
            # Get notes from service (filtered by owner if specified)
            if ($this._ownerType -eq "global" -or $null -eq $this._ownerId) {
                # Write-PmcTuiLog "NotesMenuScreen.LoadData: Getting all notes (global)" "INFO"
                $notes = $this._noteService.GetAllNotes()
            } else {
                # Write-PmcTuiLog "NotesMenuScreen.LoadData: Getting notes by owner type=$($this._ownerType) id=$($this._ownerId)" "INFO"
                $notes = $this._noteService.GetNotesByOwner($this._ownerType, $this._ownerId)
            }

            # Ensure we have an array
            if ($null -eq $notes) {
                $notes = @()
            } elseif ($notes -isnot [array]) {
                $notes = @($notes)
            }

            # Write-PmcTuiLog "NotesMenuScreen.LoadData: Loaded $($notes.Count) notes" "INFO"

            # Set data in list
            $this.List.SetData($notes)

        } catch {
            # Write-PmcTuiLog "NotesMenuScreen.LoadData: Error - $_" "ERROR"
            $this.List.SetData(@())
        }
    }

    <#
    .SYNOPSIS
    Define columns for the notes list
    #>
    [array] GetColumns() {
        return @(
            @{
                Name = 'title'
                Label = 'Title'
                Width = 40
                Sortable = $true
                Searchable = $true
            }
            @{
                Name = 'modified'
                Label = 'Modified'
                Width = 20
                Sortable = $true
                Formatter = {
                    param($value)
                    if ($value -is [datetime]) {
                        return $value.ToString("yyyy-MM-dd HH:mm")
                    }
                    return ""
                }
            }
            @{
                Name = 'line_count'
                Label = 'Lines'
                Width = 8
                Sortable = $true
                Align = 'right'
            }
            @{
                Name = 'word_count'
                Label = 'Words'
                Width = 8
                Sortable = $true
                Align = 'right'
            }
            @{
                Name = 'tags'
                Label = 'Tags'
                Width = 20
                Formatter = {
                    param($value)
                    if ($value -and $value.Count -gt 0) {
                        return ($value -join ", ")
                    }
                    return ""
                }
            }
        )
    }

    <#
    .SYNOPSIS
    Define fields for the add/edit inline editor
    #>
    [array] GetEditFields($item) {
        $title = ""
        $tags = ""

        if ($item) {
            if ($item -is [hashtable]) {
                $title = $(if ($item.ContainsKey('title')) { $item['title'] } else { "" })
                $tags = $(if ($item.ContainsKey('tags') -and $item['tags']) { ($item['tags'] -join ", ") } else { "" })
            } else {
                $title = $(if ($item.title) { $item.title } else { "" })
                $tags = $(if ($item.tags) { ($item.tags -join ", ") } else { "" })
            }
        }

        return @(
            @{
                Name = 'title'
                Type = 'text'
                Label = 'Title'
                Value = $title
                Required = $true
                MaxLength = 100
            }
            @{
                Name = 'tags'
                Type = 'text'
                Label = 'Tags (comma-separated)'
                Value = $tags
                Required = $false
                MaxLength = 200
            }
        )
    }

    # === Event Handlers ===

    <#
    .SYNOPSIS
    Handle item activation (Enter key) - open note editor
    #>
    [void] OnItemActivated($item) {
        # Get ID from item (handle both hashtable and object)
        $noteId = $null
        if ($item) {
            if ($item -is [hashtable]) {
                $noteId = $item['id']
            } else {
                $noteId = $item.id
            }
        }

        if ($noteId) {
            # Write-PmcTuiLog "NotesMenuScreen.OnItemActivated: Opening note $noteId" "INFO"

            # Load NoteEditorScreen
            $editorScreenPath = Join-Path $PSScriptRoot "NoteEditorScreen.ps1"
            # Write-PmcTuiLog "NotesMenuScreen.OnItemActivated: Editor path: $editorScreenPath" "DEBUG"

            if (Test-Path $editorScreenPath) {
                # Write-PmcTuiLog "NotesMenuScreen.OnItemActivated: Loading NoteEditorScreen.ps1" "DEBUG"
                . $editorScreenPath

                # Write-PmcTuiLog "NotesMenuScreen.OnItemActivated: Creating NoteEditorScreen instance" "DEBUG"
                $editorScreen = New-Object NoteEditorScreen -ArgumentList $noteId

                # Write-PmcTuiLog "NotesMenuScreen.OnItemActivated: Pushing screen to app" "DEBUG"
                $global:PmcApp.PushScreen($editorScreen)

                # Write-PmcTuiLog "NotesMenuScreen.OnItemActivated: Screen pushed successfully" "INFO"
            } else {
                # Write-PmcTuiLog "NotesMenuScreen.OnItemActivated: NoteEditorScreen.ps1 not found at $editorScreenPath" "ERROR"
            }
        } else {
            # Write-PmcTuiLog "NotesMenuScreen.OnItemActivated: No noteId found in item" "ERROR"
        }
    }

    <#
    .SYNOPSIS
    Handle add new note (called by StandardListScreen)
    #>
    [void] OnItemCreated([hashtable]$data) {
        $this.OnAddItem($data)
    }

    <#
    .SYNOPSIS
    Handle add new note
    #>
    [void] OnAddItem([hashtable]$data) {
        # SAVE FIX: Safe property access and validation
        $title = $(if ($data.ContainsKey('title')) { $data.title } else { '' })
        # Write-PmcTuiLog "NotesMenuScreen.OnAddItem: Creating note '$title'" "DEBUG"

        try {
            # Validate title
            if ([string]::IsNullOrWhiteSpace($title)) {
                $this.SetStatusMessage("Note title is required", "error")
                return
            }

            # Parse tags
            $tags = @()
            if ($data.ContainsKey('tags') -and -not [string]::IsNullOrWhiteSpace($data.tags)) {
                $tags = $data.tags -split ',' | ForEach-Object { $_.Trim() } | Where-Object { $_ -ne "" }
            }

            # Create note with owner info
            # Write-PmcTuiLog "NotesMenuScreen.OnAddItem: Calling CreateNote with title='$title' tags=$($tags.Count) owner=$($this._ownerType):$($this._ownerId)" "DEBUG"
            $note = $this._noteService.CreateNote($title, $tags, $this._ownerType, $this._ownerId)

            if ($null -eq $note) {
                # Write-PmcTuiLog "NotesMenuScreen.OnAddItem: CreateNote returned null!" "ERROR"
                return
            }

            # Write-PmcTuiLog "NotesMenuScreen.OnAddItem: Created note, checking for id..." "DEBUG"
            $noteId = $(if ($note -is [hashtable]) { $note['id'] } else { $note.id })
            # Write-PmcTuiLog "NotesMenuScreen.OnAddItem: Note ID = $noteId" "INFO"

            # Refresh list (will happen automatically via event callback)
            # Open the new note in editor
            $this.OnItemActivated($note)

        } catch {
            # Write-PmcTuiLog "NotesMenuScreen.OnAddItem: Error - $_" "ERROR"
            # Write-PmcTuiLog "NotesMenuScreen.OnAddItem: Stack trace - $($_.ScriptStackTrace)" "ERROR"
        }
    }

    <#
    .SYNOPSIS
    Handle edit note metadata (called by StandardListScreen)
    #>
    [void] OnItemUpdated($item, [hashtable]$data) {
        $this.OnEditItem($item, $data)
    }

    <#
    .SYNOPSIS
    Handle edit note metadata
    #>
    [void] OnEditItem($item, [hashtable]$data) {
        # SAVE FIX: Safe property access
        $itemId = $(if ($item -is [hashtable]) { $item['id'] } else { $item.id })
        # Write-PmcTuiLog "NotesMenuScreen.OnEditItem: Updating note $itemId" "DEBUG"

        try {
            # Validate title
            if (-not $data.ContainsKey('title') -or [string]::IsNullOrWhiteSpace($data.title)) {
                $this.SetStatusMessage("Note title is required", "error")
                return
            }

            # Parse tags
            $tags = @()
            if ($data.ContainsKey('tags') -and $data.tags -and $data.tags.Trim() -ne "") {
                $tags = $data.tags -split ',' | ForEach-Object { $_.Trim() } | Where-Object { $_ -ne "" }
            }

            # Update note metadata
            $changes = @{
                title = $data.title
                tags = $tags
            }

            $this._noteService.UpdateNoteMetadata($item.id, $changes)

            # Write-PmcTuiLog "NotesMenuScreen.OnEditItem: Updated note $($item.id)" "INFO"

            # Refresh list (will happen automatically via event callback)

        } catch {
            # Write-PmcTuiLog "NotesMenuScreen.OnEditItem: Error - $_" "ERROR"
            $this.SetStatusMessage("Failed to update note: $($_.Exception.Message)", "error")
        }
    }

    <#
    .SYNOPSIS
    Handle delete note (called by StandardListScreen)
    #>
    [void] OnItemDeleted($item) {
        $this.OnDeleteItem($item)
    }

    <#
    .SYNOPSIS
    Handle delete note
    #>
    [void] OnDeleteItem($item) {
        # Write-PmcTuiLog "NotesMenuScreen.OnDeleteItem: Deleting note $($item.id)" "DEBUG"

        try {
            $this._noteService.DeleteNote($item.id)

            # Write-PmcTuiLog "NotesMenuScreen.OnDeleteItem: Deleted note $($item.id)" "INFO"

            # Refresh list (will happen automatically via event callback)

        } catch {
            # Write-PmcTuiLog "NotesMenuScreen.OnDeleteItem: Error - $_" "ERROR"
            $this.SetStatusMessage("Failed to delete note: $($_.Exception.Message)", "error")
        }
    }

    # === Custom Actions ===

    <#
    .SYNOPSIS
    Add custom keyboard shortcuts
    #>
    [array] GetCustomActions() {
        $self = $this
        return @(
            @{
                Key = 'O'
                Label = 'Open'
                Callback = {
                    $selected = $self.List.GetSelectedItem()
                    if ($selected) {
                        $self.OnItemActivated($selected)
                    }
                }.GetNewClosure()
            },
            @{
                Key = 'P'
                Label = 'Assign Project'
                Callback = {
                    $selected = $self.List.GetSelectedItem()
                    if ($selected) {
                        $self._AssignToProject($selected)
                    }
                }.GetNewClosure()
            }
        )
    }

    hidden [void] _AssignToProject($note) {
        # Show project picker and reassign note
        $noteId = $(if ($note -is [hashtable]) { $note['id'] } else { $note.id })
        $noteTitle = $(if ($note -is [hashtable]) { $note['title'] } else { $note.title })

        # Get all projects
        $store = [TaskStore]::GetInstance()
        $projects = @($store.GetAllProjects())

        if ($projects.Count -eq 0) {
            $this.SetStatusMessage("No projects available", "error")
            return
        }

        # Simple inline selection - show project list in status bar
        # For now, just prompt for project name
        # TODO: Use proper project picker widget when available
        $this.SetStatusMessage("Assign '$noteTitle' to project (type name): ", "info")
        $this.Render() | Out-Host

        # Read project name from user
        [Console]::CursorVisible = $true
        $projectName = [Console]::ReadLine()
        [Console]::CursorVisible = $false

        if ([string]::IsNullOrWhiteSpace($projectName)) {
            $this.SetStatusMessage("Assignment cancelled", "info")
            return
        }

        # Verify project exists
        $projectExists = $projects | Where-Object {
            ($_ -is [string] -and $_ -eq $projectName) -or
            ((Get-SafeProperty $_ 'name') -eq $projectName)
        } | Select-Object -First 1

        if (-not $projectExists) {
            $this.SetStatusMessage("Project '$projectName' not found", "error")
            return
        }

        # Reassign note
        try {
            $changes = @{
                owner_type = "project"
                owner_id = $projectName
            }
            $this._noteService.UpdateNoteMetadata($noteId, $changes)
            $this.SetStatusMessage("Note assigned to '$projectName'", "success")

            # Refresh list
            $this.LoadData()
        } catch {
            $this.SetStatusMessage("Failed to assign note: $($_.Exception.Message)", "error")
        }
    }

    # === Menu Registration ===

    # H-MEM-2: Cleanup event subscriptions when screen exits
    <#
    .SYNOPSIS
    Called when the screen is about to be exited
    #>
    [void] OnDoExit() {
        # Unsubscribe from note service events to prevent memory leaks
        if ($this._noteService) {
            $this._noteService.OnNotesChanged = $null
        }
        # Write-PmcTuiLog "NotesMenuScreen.OnExit: Cleaned up event subscriptions" "DEBUG"
    }

    <#
    .SYNOPSIS
    Register menu items for this screen
    #>
    static [void] RegisterMenuItems([object]$registry) {
        $registry.AddMenuItem('Tools', 'Notes', 'N', {
            . "$PSScriptRoot/NotesMenuScreen.ps1"
            $global:PmcApp.PushScreen((New-Object -TypeName NotesMenuScreen))
        }, 20)
    }
}

using namespace System.Collections.Generic
using namespace System.Text

# ChecklistEditorScreen - Edit checklist instance
# Shows items with completion checkboxes using UniversalList

Set-StrictMode -Version Latest

. "$PSScriptRoot/../PmcScreen.ps1"
. "$PSScriptRoot/../services/ChecklistService.ps1"
. "$PSScriptRoot/../widgets/UniversalList.ps1"

<#
.SYNOPSIS
Checklist editor screen for managing checklist instance items

.DESCRIPTION
Edit checklist instance:
- Toggle item completion (Space/Enter)
- View progress
- Navigate with arrow keys
#>
class ChecklistEditorScreen : PmcScreen {
    hidden [ChecklistService]$_checklistService = $null
    hidden [string]$_instanceId = ""
    hidden [object]$_instance = $null

    [UniversalList]$List = $null

    # Constructors
    ChecklistEditorScreen([string]$instanceId) : base("ChecklistEditor", "Checklist") {
        $this._InitializeScreen($instanceId)
    }

    ChecklistEditorScreen([string]$instanceId, [object]$container) : base("ChecklistEditor", "Checklist", $container) {
        $this._InitializeScreen($instanceId)
    }

    hidden [void] _InitializeScreen([string]$instanceId) {
        $this._instanceId = $instanceId
        $this._checklistService = [ChecklistService]::GetInstance()

        # Initialize UniversalList
        $this.List = [UniversalList]::new()

        # Configure columns
        $this.List.SetColumns(@(
            @{Name='completed_display'; Label='Done'; Width=6; Align='center'}
            @{Name='text'; Label='Item'; Width=60}
        ))

        # Add spacebar toggle action (using ' ' char)
        $self = $this
        $this.List.AddAction(' ', 'Toggle', { 
            $item = $self.List.GetSelectedItem()
            if ($item) { $self._ToggleItem($item) }
        }.GetNewClosure())

        # Add widget to content widgets list (for automatic lifecycle if PmcScreen supports it)
        # But we'll manually position/render in RenderContentToEngine to be safe
        $this.AddContentWidget($this.List)

        # Configure Footer
        $this.Footer.ClearShortcuts()
        $this.Footer.AddShortcut("Space", "Toggle")
        $this.Footer.AddShortcut("Esc", "Back")
    }

    [void] LoadData() {
        # Load instance
        $this._instance = $this._checklistService.GetInstance($this._instanceId)
        if (-not $this._instance) {
            $this.SetStatusMessage("Checklist not found", "error")
            return
        }

        $this.ScreenTitle = $this._instance.title
        $this.Header.SetBreadcrumb(@("Checklists", $this._instance.title))

        # Map items to list format
        $listItems = @()
        $index = 0
        foreach ($item in $this._instance.items) {
            $listItems += @{
                _index = $index  # Keep track of original index for toggle
                text = $item.text
                completed = $item.completed
                completed_display = if ($item.completed) { "[X]" } else { "[ ]" }
            }
            $index++
        }
        $this.List.SetData($listItems)

        # Update progress in Status Bar
        $prog = "Progress: $($this._instance.completed_count)/$($this._instance.total_count) ($($this._instance.percent_complete)%)"
        if ($this.StatusBar) { 
            $this.StatusBar.SetRightText($prog)
        }
    }

    hidden [void] _ToggleItem($item) {
        if ($null -eq $item) { return }
        try {
            $this._checklistService.ToggleItem($this._instanceId, $item._index)
            $this.LoadData()
            $this.SetStatusMessage("Item toggled", "success")
        }
        catch {
            $this.SetStatusMessage("Error: $($_.Exception.Message)", "error")
        }
    }

    [void] RenderContentToEngine([object]$engine) {
        # Position the list to fill the content area
        # X=0, Y=6 (standard header height)
        # Width=TermWidth, Height=TermHeight - 8 (Header+Footer+Status)

        $this.List.X = 0
        $this.List.Y = 6
        $this.List.Width = $this.TermWidth
        $this.List.Height = [Math]::Max(5, $this.TermHeight - 8)

        # Render list
        $this.List.RenderToEngine($engine)
    }

    [bool] HandleKeyPress([ConsoleKeyInfo]$keyInfo) {
        # 1. Base PmcScreen (MenuBar, F10, etc.)
        if (([PmcScreen]$this).HandleKeyPress($keyInfo)) { return $true }

        # 2. UniversalList
        if ($this.List.HandleInput($keyInfo)) { return $true }

        # 3. Global Screen Shortcuts

        # Esc - Back
        if ($keyInfo.Key -eq 'Escape') {
            $global:PmcApp.PopScreen()
            return $true
        }

        # Manual Space toggle (redundancy in case action fails)
        if ($keyInfo.Key -eq 'Spacebar') {
             $item = $this.List.GetSelectedItem()
             if ($item) { 
                $this._ToggleItem($item) 
                return $true
             }
        }

        return $false
    }
}

﻿using namespace System.Collections.Generic
using namespace System.Text

# TimeListScreen - Time tracking list with full CRUD operations
# Uses UniversalList widget and InlineEditor for consistent UX


Set-StrictMode -Version Latest

. "$PSScriptRoot/../base/StandardListScreen.ps1"
. "$PSScriptRoot/../widgets/TimeEntryDetailDialog.ps1"

# LOW FIX TLS-L2, L3, L4: Define constants for magic numbers and limits
$global:MAX_TASK_LENGTH = 200
$global:MAX_TASK_TRUNCATE_LENGTH = 197
$global:MAX_NOTES_LENGTH = 300
$global:MAX_NOTES_TRUNCATE_LENGTH = 297
$global:MAX_HOURS_PER_ENTRY = 24
$global:MIN_HOURS_PER_ENTRY = 0.25
$script:DIALOG_TIMEOUT_ITERATIONS = 36000  # 36000 * 50ms = 30 minutes
$script:DIALOG_POLL_INTERVAL_MS = 50

<#
.SYNOPSIS
Time tracking list screen with CRUD operations

.DESCRIPTION
Shows all time entries with:
- Add/Edit/Delete via InlineEditor (a/e/d keys)
- View entries by task or project
- Generate time reports
- Automatic aggregation by date/project/timecode
- Press Enter on aggregated entries (shown with count) to see individual entries
- Filter by date range
#>
class TimeListScreen : StandardListScreen {

    # Static: Register menu items
    static [void] RegisterMenuItems([object]$registry) {
        $registry.AddMenuItem('Time', 'Time Tracking', 'T', {
            . "$PSScriptRoot/TimeListScreen.ps1"
            $global:PmcApp.PushScreen((New-Object -TypeName TimeListScreen))
        }, 5)
    }

    # LOW FIX TLS-L1: Extract common initialization to helper method (DRY principle)
    hidden [void] ConfigureCapabilities() {
        # Configure capabilities
        $this.AllowAdd = $true
        $this.AllowEdit = $true
        $this.AllowDelete = $true
        $this.AllowFilter = $true

        # Configure inline editor layout mode for horizontal (inline) editing
        $this.InlineEditor.LayoutMode = 'horizontal'

        # Configure header
        $this.Header.SetBreadcrumb(@("Home", "Time Tracking"))

        # Load time entries
        $this.RefreshList()
    }

    # Constructor
    TimeListScreen() : base("TimeList", "Time Tracking") {
        $this.ConfigureCapabilities()
    }

    # Constructor with container (DI-enabled)
    TimeListScreen([object]$container) : base("TimeList", "Time Tracking", $container) {
        # Write-PmcTuiLog "TimeListScreen: Constructor called, about to ConfigureCapabilities"
        $this.ConfigureCapabilities()
        # Write-PmcTuiLog "TimeListScreen: Constructor complete"
    }

    # === Abstract Method Implementations ===

    # Get entity type for store operations
    [string] GetEntityType() {
        return 'timelog'
    }

    # Define columns for list display
    [array] GetColumns() {
        return @(
            @{ Name='date_display'; Label='Date'; Width=12 },
            @{ Name='task'; Label='Task'; Width=25 },
            @{ Name='project'; Label='Project'; Width=16 },
            @{ Name='id1'; Label='ID1'; Width=10 },
            @{ Name='id2'; Label='ID2'; Width=10 },
            @{ Name='duration'; Label='Duration'; Width=18 },
            @{ Name='notes'; Label='Notes'; Width=40 }
        )
    }

    # Load data and refresh list (required by StandardListScreen)
    [void] LoadData() {
        # Write-PmcTuiLog "TimeListScreen.LoadData: START"
        $items = $this.LoadItems()
        # Write-PmcTuiLog "TimeListScreen.LoadData: LoadItems completed, checking type"
        # Write-PmcTuiLog "TimeListScreen.LoadData: items type=$($items.GetType().FullName)"
        if ($null -eq $items) {
            # Write-PmcTuiLog "TimeListScreen.LoadData: items is null, setting to empty array" "WARNING"
            $items = @()
        }
        # Write-PmcTuiLog "TimeListScreen.LoadData: About to count items"
        $itemCount = $(if ($items -is [array]) { $items.Count } else { 1 })
        # Write-PmcTuiLog "TimeListScreen.LoadData: LoadItems returned $itemCount items"
        # Write-PmcTuiLog "TimeListScreen.LoadData: Calling SetData"
        $this.List.SetData($items)
        # Write-PmcTuiLog "TimeListScreen.LoadData: COMPLETE"
    }

    # Load items from data store
    [array] LoadItems() {
        # CRITICAL FIX TLS-C1: Add null check on GetAllTimeLogs()
        $entries = $this.Store.GetAllTimeLogs()
        if ($null -eq $entries) {
            # Write-PmcTuiLog "TimeListScreen.LoadItems: GetAllTimeLogs() returned null" "ERROR"
            $entries = @()
        }

        # TS-M1 FIX: Track failed date parses to provide user feedback
        $failedDateParses = 0

        # Group entries by date, project, and timecode
        $grouped = @{}
        foreach ($entry in $entries) {
            # TIM-1 FIX: Format date for grouping with error handling
            $dateStr = ''
            if ($entry.ContainsKey('date') -and $entry.date) {
                try {
                    if ($entry.date -is [DateTime]) {
                        $dateStr = $entry.date.ToString('yyyy-MM-dd')
                    } else {
                        # Try to parse as DateTime
                        $parsedDate = [DateTime]::Parse($entry.date)
                        $dateStr = $parsedDate.ToString('yyyy-MM-dd')
                    }
                } catch {
                    # TS-M1 FIX: Instead of empty string, use original date value with marker
                    # This preserves data context and prevents incorrect grouping
                    $dateStr = "INVALID:$($entry.date)"
                    $failedDateParses++
                    # Write-PmcTuiLog "TimeListScreen.LoadItems: Failed to parse date '$($entry.date)': $_" "WARNING"
                }
            }

            # Create grouping key
            # TS-M3 FIX: Sanitize components to prevent pipe character breaking grouping
            $project = $(if ($entry.ContainsKey('project')) { $entry.project } else { '' })
            $id1 = $(if ($entry.ContainsKey('id1')) { $entry.id1 } else { '' })

            # Replace pipe characters in components to prevent grouping key corruption
            $dateStrSafe = $dateStr -replace '\|', '_'
            $projectSafe = $project -replace '\|', '_'
            $id1Safe = $id1 -replace '\|', '_'

            $groupKey = "$dateStrSafe|$projectSafe|$id1Safe"

            # Initialize group if needed
            if (-not $grouped.ContainsKey($groupKey)) {
                $grouped[$groupKey] = @{
                    date = $entry.date
                    date_display = $dateStr
                    project = $project
                    id1 = $id1
                    id2 = $(if ($entry.ContainsKey('id2')) { $entry.id2 } else { '' })
                    task = $(if ($entry.ContainsKey('task')) { $entry.task } else { '' })
                    notes = $(if ($entry.ContainsKey('notes')) { $entry.notes } else { '' })
                    minutes = 0
                    entry_count = 0
                    entry_ids = @()
                }
            }

            # Aggregate minutes
            if ($entry.ContainsKey('minutes')) {
                $grouped[$groupKey].minutes += $entry.minutes
            }
            $grouped[$groupKey].entry_count++
            if ($entry.ContainsKey('id')) {
                $grouped[$groupKey].entry_ids += $entry.id
            }

            # Store original entry for drill-down
            if (-not $grouped[$groupKey].ContainsKey('original_entries')) {
                $grouped[$groupKey].original_entries = @()
            }
            $grouped[$groupKey].original_entries += $entry

            # TS-M3 FIX: Simplify task/notes concatenation and prevent excessive string length
            # MEDIUM FIX TLS-M1: Use script-level constants for length limits
            # Concatenate tasks if multiple (with length limit to prevent memory issues)
            if ($entry.ContainsKey('task') -and $entry.task) {
                $currentTask = $grouped[$groupKey].task
                if ($currentTask -and $currentTask -ne $entry.task) {
                    # Limit concatenated task length to prevent excessive growth
                    $newTask = "$currentTask; $($entry.task)"
                    $grouped[$groupKey].task = $(if ($newTask.Length -gt $global:MAX_TASK_LENGTH) {
                        $newTask.Substring(0, $global:MAX_TASK_TRUNCATE_LENGTH) + "..."
                    } else {
                        $newTask
                    })
                } elseif (-not $currentTask) {
                    $grouped[$groupKey].task = $entry.task
                }
            }

            # Concatenate notes if multiple (with length limit to prevent memory issues)
            # MEDIUM FIX TLS-M1: Use script-level constants for length limits
            if ($entry.ContainsKey('notes') -and $entry.notes) {
                $currentNotes = $grouped[$groupKey].notes
                if ($currentNotes -and $currentNotes -ne $entry.notes) {
                    # Limit concatenated notes length to prevent excessive growth
                    $newNotes = "$currentNotes; $($entry.notes)"
                    $grouped[$groupKey].notes = $(if ($newNotes.Length -gt $global:MAX_NOTES_LENGTH) {
                        $newNotes.Substring(0, $global:MAX_NOTES_TRUNCATE_LENGTH) + "..."
                    } else {
                        $newNotes
                    })
                } elseif (-not $currentNotes) {
                    $grouped[$groupKey].notes = $entry.notes
                }
            }
        }

        # Convert to array and format
        $aggregated = @()
        foreach ($key in $grouped.Keys) {
            $entry = $grouped[$key]

            # Format duration as HH:MM with null checks
            # CRITICAL FIX TLS-C2: Validate numeric type before division/modulo
            if ($entry.ContainsKey('minutes') -and $null -ne $entry.minutes) {
                $numericMinutes = 0
                if ([double]::TryParse($entry.minutes, [ref]$numericMinutes)) {
                    $hours = [int][Math]::Floor($numericMinutes / 60)
                    $mins = [int]($numericMinutes % 60)
                    $entry['duration'] = "{0:D2}:{1:D2}" -f $hours, $mins
                } else {
                    # Write-PmcTuiLog "TimeListScreen.LoadItems: Invalid minutes value: $($entry.minutes)" "WARNING"
                    $entry['duration'] = "00:00"
                }
            } else {
                $entry['duration'] = "00:00"
            }

            # Add indicator if aggregated
            if ($entry.entry_count -gt 1) {
                $entry['duration'] = "$($entry.duration) ($($entry.entry_count))"
            }

            # DEBUG: Log the keys in this entry
            $keysStr = ($entry.Keys | Sort-Object) -join ', '
            # Write-PmcTuiLog "TimeListScreen.LoadItems: Created entry with keys: $keysStr"
            # Write-PmcTuiLog "TimeListScreen.LoadItems: date_display='$($entry.date_display)' date='$($entry.date)'"

            $aggregated += $entry
        }

        # TS-M1 FIX: Notify user if there were failed date parses
        if ($failedDateParses -gt 0) {
            # Write-PmcTuiLog "TimeListScreen.LoadItems: $failedDateParses time entries had unparseable dates" "WARNING"
            $this.SetStatusMessage("Warning: $failedDateParses entries have invalid dates", "warning")
        }

        # Sort by date descending (most recent first)
        # HIGH FIX TLS-H5: Handle null dates in sort
        $sorted = $aggregated | Sort-Object { if ($null -ne $_.date) { $_.date } else { [DateTime]::MaxValue } } -Descending
        # Ensure we always return an array (PowerShell returns single object if count=1)
        return @($sorted)
    }

    # Define columns for list display
    [array] GetListColumns() {
        return @(
            @{ Name='date_display'; Header='Date'; Width=12 }
            @{ Name='task'; Header='Task'; Width=25 }
            @{ Name='project'; Header='Project'; Width=16 }
            @{ Name='id1'; Header='ID1/Code'; Width=10 }
            @{ Name='id2'; Header='ID2'; Width=10 }
            @{ Name='duration'; Header='Duration'; Width=18 }
            @{ Name='notes'; Header='Notes'; Width=40 }
        )
    }

    # Define edit fields for InlineEditor
    [array] GetEditFields([object]$item) {
        # CRITICAL: Match GetColumns() field widths exactly
        # GetColumns defines: date_display=12, task=25, project=16, id1=10, id2=10, duration=18, notes=40
        $dateWidth = 12      # Matches date_display column
        $taskWidth = 25      # Matches task column
        $projectWidth = 16   # Matches project column
        $id1Width = 10       # Matches id1 column
        $id2Width = 10       # Matches id2 column
        $hoursWidth = 18     # Matches duration column
        $notesWidth = 40     # Matches notes column

        if ($null -eq $item -or ($item -is [hashtable] -and $item.Count -eq 0)) {
            # New time entry - empty fields
            return @(
                @{ Name='date'; Type='date'; Label='Date'; Required=$true; Value=[DateTime]::Now; Width=$dateWidth }
                @{ Name='task'; Type='text'; Label='Task'; Value=''; Width=$taskWidth }
                @{ Name='project'; Type='project'; Label='Project'; Value=''; Width=$projectWidth }
                @{ Name='id1'; Type='text'; Label='ID1'; Value=''; MaxLength=10; Width=$id1Width }
                @{ Name='id2'; Type='text'; Label='ID2'; Value=''; MaxLength=10; Width=$id2Width }
                # MEDIUM FIX TMS-M3 & TLS-M2: Use constant for max hours validation
                @{ Name='hours'; Type='number'; Label='Hours'; Min=$global:MIN_HOURS_PER_ENTRY; Max=$global:MAX_HOURS_PER_ENTRY; Step=0.25; Value=$global:MIN_HOURS_PER_ENTRY; Width=$hoursWidth }
                @{ Name='notes'; Type='text'; Label='Notes'; Value=''; Width=$notesWidth }
            )
        } else {
            # Existing time entry - populate from item
            $projectVal = $(if ($item.ContainsKey('project')) { $item.project } else { '' })
            $id1Val = $(if ($item.ContainsKey('id1')) { $item.id1 } else { '' })
            $id2Val = $(if ($item.ContainsKey('id2')) { $item.id2 } else { '' })
            # Convert minutes to hours for display
            $hoursVal = $(if ($item.ContainsKey('minutes')) { [math]::Round($item.minutes / 60, 2) } else { 0.25 })
            # HIGH FIX TLS-H1: Add null check for task field
            $taskVal = $(if ($item.ContainsKey('task')) { $item.task } else { '' })
            # HIGH FIX TLS-H2: Add null check for notes field
            $notesVal = $(if ($item.ContainsKey('notes')) { $item.notes } else { '' })
            return @(
                @{ Name='date'; Type='date'; Label='Date'; Required=$true; Value=$item.date; Width=$dateWidth }
                @{ Name='task'; Type='text'; Label='Task'; Value=$taskVal; Width=$taskWidth }
                @{ Name='project'; Type='project'; Label='Project'; Value=$projectVal; Width=$projectWidth }
                @{ Name='id1'; Type='text'; Label='ID1'; Value=$id1Val; MaxLength=10; Width=$id1Width }
                @{ Name='id2'; Type='text'; Label='ID2'; Value=$id2Val; MaxLength=10; Width=$id2Width }
                # MEDIUM FIX TMS-M3 & TLS-M2: Use constant for max hours validation
                @{ Name='hours'; Type='number'; Label='Hours'; Min=$global:MIN_HOURS_PER_ENTRY; Max=$global:MAX_HOURS_PER_ENTRY; Step=0.25; Value=$hoursVal; Width=$hoursWidth }
                @{ Name='notes'; Type='text'; Label='Notes'; Value=$notesVal; Width=$notesWidth }
            )
        }
    }

    # Helper: Get ID1/ID2 values from project if project is selected
    hidden [void] PopulateIDsFromProject([hashtable]$timeData) {
        if ($timeData.ContainsKey('project') -and -not [string]::IsNullOrWhiteSpace($timeData.project)) {
            # Project is selected - try to get ID1/ID2 from it
            $project = $this.Store.GetProject($timeData.project)
            if ($project) {
                # Write-PmcTuiLog "TimeListScreen.PopulateIDsFromProject: Found project '$($timeData.project)'" "DEBUG"

                # If project has ID1, use it (unless user already entered a value)
                if (-not [string]::IsNullOrWhiteSpace($project.ID1) -and [string]::IsNullOrWhiteSpace($timeData.id1)) {
                    $timeData.id1 = $project.ID1
                    # Write-PmcTuiLog "TimeListScreen.PopulateIDsFromProject: Set id1 from project: '$($timeData.id1)'" "DEBUG"
                }

                # If project has ID2, use it (unless user already entered a value)
                if (-not [string]::IsNullOrWhiteSpace($project.ID2) -and [string]::IsNullOrWhiteSpace($timeData.id2)) {
                    $timeData.id2 = $project.ID2
                    # Write-PmcTuiLog "TimeListScreen.PopulateIDsFromProject: Set id2 from project: '$($timeData.id2)'" "DEBUG"
                }
            } else {
                # Write-PmcTuiLog "TimeListScreen.PopulateIDsFromProject: Project '$($timeData.project)' not found" "WARNING"
            }
        } else {
            # No project selected - clear ID1/ID2 if they should come from project
            # Write-PmcTuiLog "TimeListScreen.PopulateIDsFromProject: No project selected, keeping user-entered ID1/ID2 values" "DEBUG"
        }
    }

    # Handle item creation
    [void] OnItemCreated([hashtable]$values) {
        if ($global:PmcTuiLogFile -and $global:PmcTuiLogLevel -ge 3) {
            # Write-PmcTuiLog "TimeListScreen.OnItemCreated: CALLED with values: $($values | ConvertTo-Json -Compress)" "DEBUG"
        }
        try {
            # ENDEMIC FIX: Safe conversion with validation
            if (-not $values.ContainsKey('hours') -or [string]::IsNullOrWhiteSpace($values.hours)) {
                # Write-PmcTuiLog "TimeListScreen.OnItemCreated: Hours validation failed" "DEBUG"
                $this.SetStatusMessage("Hours field is required", "error")
                return
            }
            # Write-PmcTuiLog "TimeListScreen.OnItemCreated: Hours validation passed" "DEBUG"

            $hoursValue = 0.0
            try {
                $hoursValue = [double]$values.hours
            } catch {
                # Write-PmcTuiLog "TimeListScreen.OnItemCreated: Hours conversion failed" "DEBUG"
                $this.SetStatusMessage("Invalid hours value: $($values.hours)", "error")
                return
            }

            if ($global:PmcTuiLogFile -and $global:PmcTuiLogLevel -ge 3) {
                # Write-PmcTuiLog "TimeListScreen.OnItemCreated: Hours value=$hoursValue, MAX_HOURS_PER_ENTRY=$global:MAX_HOURS_PER_ENTRY" "DEBUG"
            }

            # Validate hour range
            # MEDIUM FIX TLS-M3: Use constant for hours validation
            if ($hoursValue -le 0) {
                # Write-PmcTuiLog "TimeListScreen.OnItemCreated: Hours <= 0" "DEBUG"
                $this.SetStatusMessage("Hours must be greater than 0", "error")
                return
            }
            if ($hoursValue -gt $global:MAX_HOURS_PER_ENTRY) {
                # Write-PmcTuiLog "TimeListScreen.OnItemCreated: Hours > MAX ($hoursValue > $global:MAX_HOURS_PER_ENTRY)" "DEBUG"
                $this.SetStatusMessage("Hours must be $global:MAX_HOURS_PER_ENTRY or less", "error")
                return
            }
            # Write-PmcTuiLog "TimeListScreen.OnItemCreated: Hour range validation passed" "DEBUG"

            # HIGH FIX TMS-H3: Use Math.Round instead of [int] to prevent precision loss
            # 2.75 hours = 165 minutes (not 165.0 truncated to 165)
            # CRITICAL: Cast to [int] because validation requires int type
            $minutes = [int][Math]::Round($hoursValue * 60)
            # Write-PmcTuiLog "TimeListScreen.OnItemCreated: Calculated minutes=$minutes" "DEBUG"

            # Safe date conversion
            $dateValue = [DateTime]::Today
            if ($values.ContainsKey('date') -and $values.date) {
                try {
                    $dateValue = [DateTime]$values.date
                } catch {
                    # Write-PmcTuiLog "Failed to parse date '$($values.date)', using today" "WARNING"
                }
            }
            # Write-PmcTuiLog "TimeListScreen.OnItemCreated: Date=$dateValue" "DEBUG"

            $timeData = @{
                date = $dateValue
                task = $(if ($values.ContainsKey('task')) { $values.task } else { '' })
                project = $(if ($values.ContainsKey('project')) { $values.project } else { '' })
                id1 = $(if ($values.ContainsKey('id1')) { $values.id1 } else { '' })
                id2 = $(if ($values.ContainsKey('id2')) { $values.id2 } else { '' })
                minutes = $minutes
                notes = $(if ($values.ContainsKey('notes')) { $values.notes } else { '' })
                created = [DateTime]::Now
            }

            # Populate ID1/ID2 from project if project is selected and IDs are empty
            $this.PopulateIDsFromProject($timeData)

            # Write-PmcTuiLog "TimeListScreen.OnItemCreated: About to save time entry - id1='$($timeData.id1)' id2='$($timeData.id2)'" "DEBUG"
            # Write-PmcTuiLog "TimeListScreen.OnItemCreated: Calling Store.AddTimeLog..." "DEBUG"

            $success = $this.Store.AddTimeLog($timeData)
            # Write-PmcTuiLog "TimeListScreen.OnItemCreated: AddTimeLog returned success=$success" "DEBUG"
            if (-not $success) {
                # Write-PmcTuiLog "TimeListScreen.OnItemCreated: Store.LastError=$($this.Store.LastError)" "ERROR"
            }

            $statusMsg = "Time entry added: {0:F2} hours" -f $hoursValue
            if ($success) {
                $this.SetStatusMessage($statusMsg, "success")
            } else {
                $this.SetStatusMessage("Failed to add time entry: $($this.Store.LastError)", "error")
            }
        } catch {
            # Write-PmcTuiLog "OnItemCreated exception: $_" "ERROR"
            $this.SetStatusMessage("Unexpected error: $($_.Exception.Message)", "error")
        }
    }

    # Handle item update
    [void] OnItemUpdated([object]$item, [hashtable]$values) {
        try {
            # ENDEMIC FIX: Safe conversion with validation
            if (-not $values.ContainsKey('hours') -or [string]::IsNullOrWhiteSpace($values.hours)) {
                $this.SetStatusMessage("Hours field is required", "error")
                return
            }

            $hoursValue = 0.0
            try {
                $hoursValue = [double]$values.hours
            } catch {
                $this.SetStatusMessage("Invalid hours value: $($values.hours)", "error")
                return
            }

            # Validate hour range
            # MEDIUM FIX TLS-M3: Use constant for hours validation
            if ($hoursValue -le 0) {
                $this.SetStatusMessage("Hours must be greater than 0", "error")
                return
            }
            if ($hoursValue -gt $global:MAX_HOURS_PER_ENTRY) {
                $this.SetStatusMessage("Hours must be $global:MAX_HOURS_PER_ENTRY or less", "error")
                return
            }

            # HIGH FIX TMS-H3: Use Math.Round instead of [int] to prevent precision loss
            # 2.75 hours = 165 minutes (not 165.0 truncated to 165)
            # CRITICAL: Cast to [int] because validation requires int type
            $minutes = [int][Math]::Round($hoursValue * 60)

            # Safe date conversion
            $dateValue = [DateTime]::Today
            if ($values.ContainsKey('date') -and $values.date) {
                try {
                    $dateValue = [DateTime]$values.date
                } catch {
                    # Write-PmcTuiLog "Failed to parse date '$($values.date)', using today" "WARNING"
                }
            }

            $changes = @{
                date = $dateValue
                task = $(if ($values.ContainsKey('task')) { $values.task } else { '' })
                project = $(if ($values.ContainsKey('project')) { $values.project } else { '' })
                id1 = $(if ($values.ContainsKey('id1')) { $values.id1 } else { '' })
                id2 = $(if ($values.ContainsKey('id2')) { $values.id2 } else { '' })
                minutes = $minutes
                notes = $(if ($values.ContainsKey('notes')) { $values.notes } else { '' })
            }

            # Populate ID1/ID2 from project if project is selected and IDs are empty
            $this.PopulateIDsFromProject($changes)

            # Write-PmcTuiLog "TimeListScreen.OnItemUpdated: About to save changes - id1='$($changes.id1)' id2='$($changes.id2)'" "DEBUG"

            # Update time log via TaskStore
            if ($item.ContainsKey('id') -and -not [string]::IsNullOrWhiteSpace($item.id)) {
                # Write-PmcTuiLog "TimeListScreen.OnItemUpdated: Calling Store.UpdateTimeLog with id=$($item.id)" "DEBUG"
                $success = $this.Store.UpdateTimeLog($item.id, $changes)
                if ($success) {
                    $this.SetStatusMessage("Time entry updated", "success")
                    # TS-M6 FIX: Use RefreshList() instead of LoadData() for incremental refresh
                    # RefreshList() is more efficient than full LoadData() for single item updates
                    $this.RefreshList()
                } else {
                    $this.SetStatusMessage("Failed to update time entry: $($this.Store.LastError)", "error")
                }
            } else {
                $this.SetStatusMessage("Cannot update time entry without ID", "error")
            }
        } catch {
            # Write-PmcTuiLog "OnItemUpdated exception: $_" "ERROR"
            $this.SetStatusMessage("Unexpected error: $($_.Exception.Message)", "error")
        }
    }

    # Handle item deletion
    [void] OnItemDeleted([object]$item) {
        # HIGH FIX TLS-H3: Validate ID is not empty/whitespace
        if ($item.ContainsKey('id') -and -not [string]::IsNullOrWhiteSpace($item.id)) {
            $success = $this.Store.DeleteTimeLog($item.id)
            if ($success) {
                $this.SetStatusMessage("Time entry deleted", "success")
            } else {
                $this.SetStatusMessage("Failed to delete time entry: $($this.Store.LastError)", "error")
            }
        } else {
            $this.SetStatusMessage("Cannot delete time entry without ID", "error")
        }
    }

    # Virtual method called when inline editor is confirmed
    # This method is called by StandardListScreen when inline editing is confirmed
    # TimeListScreen already handles inline editing through OnItemCreated and OnItemUpdated,
    # so this is a no-op to prevent method-not-found errors
    [void] OnInlineEditConfirmed([hashtable]$values) {
        if ($null -eq $values) {
            # Write-PmcTuiLog "TimeListScreen.OnInlineEditConfirmed called with null values" "WARNING"
            return
        }
        # Write-PmcTuiLog "TimeListScreen.OnInlineEditConfirmed called with values: $($values.Keys -join ',')" "DEBUG"

        # Route to OnItemCreated or OnItemUpdated based on mode
        $isAddMode = ($this.EditorMode -eq 'add')

        if ($isAddMode) {
            # Write-PmcTuiLog "OnInlineEditConfirmed: Processing ADD mode for time entry" "INFO"
            $this.OnItemCreated($values)
        }
        else {
            # Write-PmcTuiLog "OnInlineEditConfirmed: Processing EDIT mode for time entry" "INFO"
            if ($this.CurrentEditItem) {
                $this.OnItemUpdated($this.CurrentEditItem, $values)
            }
            else {
                # Write-PmcTuiLog "OnInlineEditConfirmed: EDIT mode but no CurrentEditItem!" "ERROR"
            }
        }
    }

    # Virtual method called when inline editor is cancelled
    # This method is called by StandardListScreen when inline editing is cancelled
    # TimeListScreen already handles inline editing through its own callbacks,
    # so this is a no-op to prevent method-not-found errors
    [void] OnInlineEditCancelled() {
        # Write-PmcTuiLog "TimeListScreen.OnInlineEditCancelled called" "DEBUG"
        # No-op: TimeListScreen handles inline editor callbacks directly
    }


    # Get custom actions for footer display
    [array] GetCustomActions() {
        $self = $this
        return @(
            @{ Key='w'; Label='Week Report'; Callback={
                $screenPath = "$PSScriptRoot/WeeklyTimeReportScreen.ps1"
                . $screenPath
                $screen = New-Object WeeklyTimeReportScreen
                $global:PmcApp.PushScreen($screen)
            }.GetNewClosure() },
            @{ Key='g'; Label='Generate'; Callback={
                $self.GenerateReport()
            }.GetNewClosure() }
        )
    }

