    # Override: Apply 70/30 split layout for List and DetailPane
    [void] ApplyContentLayout([PmcLayoutManager]$layoutManager, [int]$termWidth, [int]$termHeight) {
        # Don't call base - we handle everything ourselves
        $rect = $layoutManager.GetRegion('Content', $termWidth, $termHeight)

        if ($this._showDetailPane -and $this.DetailPane) {
            # 70/30 split within the content region
            $listWidth = [Math]::Floor($rect.Width * 0.70)
            $detailWidth = $rect.Width - $listWidth - 1  # -1 for spacing

            $this.List.SetPosition($rect.X, $rect.Y)
            $this.List.SetSize($listWidth, $rect.Height)

            # CRITICAL FIX: Re-calculate and apply columns to match new list width
            # This ensures percentages in GetColumns() use the new $listWidth
            $newCols = $this.GetColumns()
            $this.List.SetColumns($newCols)

            $this.DetailPane.SetPosition($rect.X + $listWidth + 1, $rect.Y)
            $this.DetailPane.SetSize($detailWidth, $rect.Height)
            $this.DetailPane.Visible = $true
            $this.DetailPane.ShowBorder = $true # Ensure border is on

            # Position TextAreaEditor INSIDE the DetailPane (inset by 1 for border + 1 for padding = 2)
            # This prevents overwriting the border and fixes cursor alignment
            if ($this._detailEditor) {
                $editorX = $rect.X + $listWidth + 1 + 2
                $editorY = $rect.Y + 1
                $editorW = $detailWidth - 4
                $editorH = $rect.Height - 2

                # Ensure dimensions are positive
                if ($editorW -lt 1) { $editorW = 1 }
                if ($editorH -lt 1) { $editorH = 1 }

                $this._detailEditor.SetBounds($editorX, $editorY, $editorW, $editorH)
                # Visibility is controlled in RenderContentToEngine, but ensure it's technically 'visible' for input
                $this._detailEditor.Visible = $true
            }
        } else {
            # Full width list
            $this.List.SetPosition($rect.X, $rect.Y)
            $this.List.SetSize($rect.Width, $rect.Height)

            # CRITICAL FIX: Re-calculate columns for full width
            $newCols = $this.GetColumns()
            $this.List.SetColumns($newCols)

            if ($this.DetailPane) {
            if ($this._detailEditor) {
                $this._detailEditor.Visible = $false
            }
                $this.DetailPane.Visible = $false
            }
        }

        # Invalidate cache to force column recalculation
        $this.List.InvalidateCache()
    }

    # Override: Update detail pane when selection changes
    [void] OnItemSelected($item) {
        # Call base implementation
        ([StandardListScreen]$this).OnItemSelected($item)

        # Update both DetailPane and TextAreaEditor with task description
        if ($this._showDetailPane) {
            if ($null -ne $item) {
                $desc = Get-SafeProperty $item 'description' -Default ""
                # Apply word wrap to description
                # Calculate wrap width based on DetailPane width (or default if not visible yet)
                $wrapWidth = $(if ($this.DetailPane.Width -gt 4) { $this.DetailPane.Width - 4 } else { 40 })
                $wrappedDesc = $this._WrapText($desc, $wrapWidth)

                # Update both widgets
                if ($this._detailEditor) {
                    $this._detailEditor.SetText($wrappedDesc)
                }
                if ($this.DetailPane) {
                    $this.DetailPane.SetContent($wrappedDesc, 'left')
                }
            } else {
                if ($this._detailEditor) {
                    $this._detailEditor.SetText("")
                }
                if ($this.DetailPane) {
                    $this.DetailPane.SetContent("Select a task to view details", 'left')
                }
            }
        }
    }

    # Toggle detail pane visibility
