﻿using namespace System.Collections.Generic
using namespace System.Text

# CommandLibraryScreen - Command library management using StandardListScreen
# Allows users to save, manage, and copy frequently used commands

Set-StrictMode -Version Latest

. "$PSScriptRoot/../base/StandardListScreen.ps1"
. "$PSScriptRoot/../services/CommandService.ps1"

<#
.SYNOPSIS
Command library management screen

.DESCRIPTION
Manage a library of saved commands:
- Add/Edit/Delete commands
- Copy commands to clipboard
- Track usage statistics
#>
class CommandLibraryScreen : StandardListScreen {
    hidden [object]$_commandService = $null

    # Container constructor
    CommandLibraryScreen([object]$container) : base("CommandLibrary", "Command Library", $container) {
        $this._InitializeScreen()
    }

    hidden [void] _InitializeScreen() {
        # Initialize service
        $this._commandService = [CommandService]::GetInstance()

        # Configure capabilities
        $this.AllowAdd = $true
        $this.AllowEdit = $true
        $this.AllowDelete = $true
        $this.AllowFilter = $true

        # Configure header
        $this.Header.SetBreadcrumb(@("Home", "Tools", "Command Library"))

        # Configure footer shortcuts
        $this.Footer.ClearShortcuts()
        $this.Footer.AddShortcut("A", "Add")
        $this.Footer.AddShortcut("E", "Edit")
        $this.Footer.AddShortcut("D", "Delete")
        $this.Footer.AddShortcut("Enter/C", "Copy")
        $this.Footer.AddShortcut("Esc", "Back")

        # Setup event handlers
        $self = $this
        $this._commandService.OnCommandsChanged = {
            if ($null -ne $self -and $self.IsActive) {
                $self.LoadData()
            }
        }.GetNewClosure()
    }

    # Required by StandardListScreen - loads and displays data
    [void] LoadData() {
        $items = $this.LoadItems()
        $columns = $this.GetColumns()
        $this.List.SetColumns($columns)
        $this.List.SetData($items)
    }

    # === Abstract Method Implementations ===

    # Get entity type for store operations
    [string] GetEntityType() {
        return 'command'
    }

    # Define columns for list display
    [array] GetColumns() {
        # Calculate column widths dynamically based on List widget width
        # Account for 2 separators (2 spaces each = 4 chars) between 3 columns + borders
        $availableWidth = $(if ($this.List -and $this.List.Width -gt 4) { $this.List.Width - 4 - 4 } else { 110 })

        # Proportions: Name=30%, Category=20%, Command=50%
        $nameWidth = [Math]::Max(15, [Math]::Floor($availableWidth * 0.30))
        $categoryWidth = [Math]::Max(10, [Math]::Floor($availableWidth * 0.20))
        $commandWidth = [Math]::Max(20, [Math]::Floor($availableWidth * 0.50))

        return @(
            @{ Name = 'name'; Label = 'Name'; Width = $nameWidth; Align = 'left' }
            @{ Name = 'category'; Label = 'Category'; Width = $categoryWidth; Align = 'left' }
            @{ Name = 'command_text'; Label = 'Command'; Width = $commandWidth; Align = 'left' }
        )
    }

    # Load items from data store
    [array] LoadItems() {
        $commands = @($this._commandService.GetAllCommands())

        # Format for display
        foreach ($cmd in $commands) {
            # Format tags for display
            if ($cmd.ContainsKey('tags') -and $cmd.tags -is [array]) {
                $cmd['tags_display'] = $cmd.tags -join ', '
            } else {
                $cmd['tags_display'] = ''
            }
        }

        return $commands
    }

    # Define filter fields for StandardListScreen
    [array] GetFilterFields() {
        return @(
            @{ Name='category'; Label='Category'; Type='text' }
            @{ Name='tags'; Label='Tags (comma-separated)'; Type='text' }
        )
    }

    # Apply filters to items
    [array] ApplyFilters([array]$items, [hashtable]$filters) {
        if ($null -eq $filters -or $filters.Count -eq 0) {
            return $items
        }

        $filtered = $items

        # Filter by category
        if ($filters.ContainsKey('category') -and -not [string]::IsNullOrWhiteSpace($filters.category)) {
            $categoryFilter = $filters.category.Trim()
            $filtered = @($filtered | Where-Object {
                $itemCategory = $(if ($_ -is [hashtable]) { $_['category'] } else { $_.category })
                $itemCategory -like "*$categoryFilter*"
            })
        }

        # Filter by tags
        if ($filters.ContainsKey('tags') -and -not [string]::IsNullOrWhiteSpace($filters.tags)) {
            $tagFilters = @($filters.tags -split ',' | ForEach-Object { $_.Trim() } | Where-Object { $_ })
            $filtered = @($filtered | Where-Object {
                $itemTags = $(if ($_ -is [hashtable]) { $_['tags'] } else { $_.tags })
                if ($null -eq $itemTags -or $itemTags.Count -eq 0) {
                    return $false
                }
                # Check if item has any of the specified tags
                $hasTag = $false
                foreach ($filterTag in $tagFilters) {
                    foreach ($itemTag in $itemTags) {
                        if ($itemTag -like "*$filterTag*") {
                            $hasTag = $true
                            break
                        }
                    }
                    if ($hasTag) { break }
                }
                return $hasTag
            })
        }

        return $filtered
    }

    # Define edit fields for InlineEditor
    [array] GetEditFields([object]$item) {
        # Calculate field widths - MUST MATCH GetColumns() exactly
        $availableWidth = $(if ($this.List -and $this.List.Width -gt 4) { $this.List.Width - 4 - 4 } else { 103 })
        $nameWidth = [Math]::Max(20, [Math]::Floor($availableWidth * 0.30))
        $categoryWidth = [Math]::Max(12, [Math]::Floor($availableWidth * 0.20))
        $descWidth = [Math]::Max(30, [Math]::Floor($availableWidth * 0.50))

        if ($null -eq $item -or $item.Count -eq 0) {
            # New command - empty fields (3 fields to match 3 columns)
            return @(
                @{ Name='name'; Type='text'; Label=''; Required=$true; Value=''; Width=$nameWidth; Placeholder='Command name' }
                @{ Name='category'; Type='text'; Label=''; Value='General'; Width=$categoryWidth; Placeholder='Category' }
                @{ Name='description'; Type='text'; Label=''; Value=''; Width=$descWidth; Placeholder='Enter command text (will be copied to clipboard)'; Required=$true }
            )
        } else {
            # Existing command - populate from item (3 fields to match 3 columns)
            return @(
                @{ Name='name'; Type='text'; Label=''; Required=$true; Value=$item.name; Width=$nameWidth; Placeholder='Command name' }
                @{ Name='category'; Type='text'; Label=''; Value=$item.category; Width=$categoryWidth; Placeholder='Category' }
                @{ Name='description'; Type='text'; Label=''; Value=$item.command_text; Width=$descWidth; Placeholder='Enter command text (will be copied to clipboard)'; Required=$true }
            )
        }
    }

    # Handle item creation
    [void] OnItemCreated([hashtable]$values) {
        try {
            # VALIDATION: Command name is required
            if (-not $values.ContainsKey('name') -or [string]::IsNullOrWhiteSpace($values.name)) {
                $this.SetStatusMessage("Command name is required", "error")
                return
            }

            # VALIDATION: Command text is required (stored in 'description' field for column alignment)
            if (-not $values.ContainsKey('description') -or [string]::IsNullOrWhiteSpace($values.description)) {
                $this.SetStatusMessage("Command text is required - enter the text you want to copy in the 'Command' field", "error")
                return
            }

            $tags = $(if ($values.ContainsKey('tags') -and -not [string]::IsNullOrWhiteSpace($values.tags)) {
                @($values.tags -split ',' | ForEach-Object { $_.Trim() } | Where-Object { $_ })
            } else {
                @()
            })

            $name = $values.name
            # description field IS the command text (for column alignment)
            $commandText = $values.description
            $category = $(if ($values.ContainsKey('category')) { $values.category } else { '' })
            $description = ''  # We don't have a separate description field

            $this._commandService.CreateCommand($name, $commandText, $category, $description, $tags)

            $this.SetStatusMessage("Command '$name' saved", "success")
        } catch {
            $this.SetStatusMessage("Error creating command: $($_.Exception.Message)", "error")
        }
    }

    # Handle item update
    [void] OnItemUpdated([object]$item, [hashtable]$values) {
        try {
            $itemId = $(if ($item -is [hashtable]) { $item['id'] } else { $item.id })

            $tags = $(if ($values.ContainsKey('tags') -and -not [string]::IsNullOrWhiteSpace($values.tags)) {
                @($values.tags -split ',' | ForEach-Object { $_.Trim() } | Where-Object { $_ })
            } else {
                @()
            })

            # ENDEMIC FIX: Safe value access
            $changes = @{
                name = $(if ($values.ContainsKey('name')) { $values.name } else { '' })
                category = $(if ($values.ContainsKey('category')) { $values.category } else { '' })
                command_text = $(if ($values.ContainsKey('description')) { $values.description } else { '' })
                description = ''  # description field IS command_text
                tags = $tags
            }

            if ([string]::IsNullOrWhiteSpace($changes.name)) {
                $this.SetStatusMessage("Command name is required", "error")
                return
            }

            $this._commandService.UpdateCommand($itemId, $changes)
            $this.SetStatusMessage("Command '$($changes.name)' updated", "success")
        } catch {
            $this.SetStatusMessage("Error updating command: $($_.Exception.Message)", "error")
        }
    }

    # Handle item deletion
    [void] OnItemDeleted([object]$item) {
        try {
            $itemId = $(if ($item -is [hashtable]) { $item['id'] } else { $item.id })
            $itemName = $(if ($item -is [hashtable]) { $item['name'] } else { $item.name })

            if ($itemId) {
                $this._commandService.DeleteCommand($itemId)
                $this.SetStatusMessage("Command '$itemName' deleted", "success")
            } else {
                $this.SetStatusMessage("Cannot delete command without ID", "error")
            }
        } catch {
            $this.SetStatusMessage("Error deleting command: $($_.Exception.Message)", "error")
        }
    }

    # Virtual method called when inline editor is confirmed
    [void] OnInlineEditConfirmed([hashtable]$values) {
        if ($null -eq $values) {
            return
        }

        # Determine if we're adding a new command or editing existing one
        $isAddMode = ($this.EditorMode -eq 'add')

        if ($isAddMode) {
            $this.OnItemCreated($values)
        }
        else {
            if ($this.CurrentEditItem) {
                $this.OnItemUpdated($this.CurrentEditItem, $values)
            }
        }
    }

    # Virtual method called when inline editor is cancelled
    [void] OnInlineEditCancelled() {
        # No-op: StandardListScreen handles the UI updates
    }

    # === Custom Actions ===

    # Copy the selected command to clipboard
    [void] CopyCommand() {
        $selectedItem = $this.List.GetSelectedItem()
        if ($null -eq $selectedItem) {
            $this.SetStatusMessage("No command selected - select a command and press Enter to copy", "error")
            return
        }

        $commandText = $(if ($selectedItem -is [hashtable]) { $selectedItem['command_text'] } else { $selectedItem.command_text })
        $commandName = $(if ($selectedItem -is [hashtable]) { $selectedItem['name'] } else { $selectedItem.name })
        $commandId = $(if ($selectedItem -is [hashtable]) { $selectedItem['id'] } else { $selectedItem.id })

        if ([string]::IsNullOrEmpty($commandText)) {
            $this.SetStatusMessage("Command '$commandName' has no text to copy - edit it and add command text in the 'Command' field", "error")
            return
        }

        try {
            # Copy to clipboard (Windows only)
            Set-Clipboard -Value $commandText

            # Update usage statistics
            if ($commandId) {
                $this._commandService.IncrementUsageCount($commandId)
            }

            $this.SetStatusMessage("Copied to clipboard: $commandText", "success")
        } catch {
            $this.SetStatusMessage("Failed to copy to clipboard: $($_.Exception.Message)", "error")
        }
    }

    # === Input Handling ===

    [bool] HandleKeyPress([ConsoleKeyInfo]$keyInfo) {
        # CRITICAL FIX: Only handle custom keys when NOT in edit mode
        # This allows typing 'c' in command name field and using Enter to save
        if (-not $this.ShowInlineEditor -and -not $this.ShowFilterPanel) {
            # Custom key: Enter = Copy command to clipboard (only when not editing)
            if ($keyInfo.Key -eq ([ConsoleKey]::Enter)) {
                $this.CopyCommand()
                return $true
            }

            # Custom key: C = Copy command to clipboard (only when not editing)
            if ($keyInfo.Key -eq ([ConsoleKey]::C)) {
                $this.CopyCommand()
                return $true
            }
        }

        # Call parent handler for list navigation, add/edit/delete
        # When in edit mode, parent will handle Enter to save
        $handled = ([StandardListScreen]$this).HandleKeyPress($keyInfo)
        if ($handled) { return $true }

        return $false
    }
}

﻿# ChecklistService.ps1 - Service for managing checklists and templates
#
# Provides CRUD operations for checklist templates and instances
# Templates are reusable checklist definitions
# Instances are attached to projects/tasks with completion tracking
#
# Usage:
#   $service = [ChecklistService]::GetInstance()
#   $template = $service.CreateTemplate("Code Review", @("Check tests", "Review security"))
#   $instance = $service.CreateInstanceFromTemplate($templateId, "project", $projectId)

using namespace System
using namespace System.Collections.Generic

Set-StrictMode -Version Latest

class ChecklistService {
    # === Singleton Instance ===
    static hidden [ChecklistService]$_instance = $null
    static hidden [object]$_instanceLock = [object]::new()

    # === Configuration ===
    hidden [string]$_checklistsDir
    hidden [string]$_templatesFile
    hidden [string]$_instancesFile

    # === In-memory cache ===
    hidden [hashtable]$_templatesCache = @{}
    hidden [hashtable]$_instancesCache = @{}
    hidden [datetime]$_cacheLoadTime = [datetime]::MinValue

    # === Event Callbacks ===
    [scriptblock]$OnTemplateAdded = {}
    [scriptblock]$OnTemplateUpdated = {}
    [scriptblock]$OnTemplateDeleted = {}
    [scriptblock]$OnInstanceAdded = {}
    [scriptblock]$OnInstanceUpdated = {}
    [scriptblock]$OnInstanceDeleted = {}
    [scriptblock]$OnChecklistsChanged = {}

    # === Singleton Access ===
    static [ChecklistService] GetInstance() {
        if ([ChecklistService]::_instance -eq $null) {
            [System.Threading.Monitor]::Enter([ChecklistService]::_instanceLock)
            try {
                if ([ChecklistService]::_instance -eq $null) {
                    [ChecklistService]::_instance = [ChecklistService]::new()
                }
            } finally {
                [System.Threading.Monitor]::Exit([ChecklistService]::_instanceLock)
            }
        }
        return [ChecklistService]::_instance
    }

    # === Constructor (Private - use GetInstance) ===
    ChecklistService() {
        # Determine checklists directory relative to PMC root
        $pmcRoot = Split-Path -Parent (Split-Path -Parent (Split-Path -Parent $PSScriptRoot))
        $this._checklistsDir = Join-Path $pmcRoot "checklists"
        $this._templatesFile = Join-Path $this._checklistsDir "templates.json"
        $this._instancesFile = Join-Path $this._checklistsDir "instances.json"

        # Ensure checklists directory exists
        if (-not (Test-Path $this._checklistsDir)) {
            New-Item -ItemType Directory -Path $this._checklistsDir -Force | Out-Null
        }

        # Load metadata
        $this.LoadTemplates()
        $this.LoadInstances()
    }

    # === Template Management ===
    hidden [void] LoadTemplates() {
        if (Test-Path $this._templatesFile) {
            try {
                $json = Get-Content $this._templatesFile -Raw | ConvertFrom-Json -Depth 10
                foreach ($template in $json.templates) {
                    $items = @()
                    foreach ($item in $template.items) {
                        $items += @{
                            text = $item.text
                            order = $item.order
                        }
                    }

                    $this._templatesCache[$template.id] = @{
                        id = $template.id
                        name = $template.name
                        description = $template.description
                        category = $template.category
                        items = $items
                        created = [datetime]::Parse($template.created)
                        modified = [datetime]::Parse($template.modified)
                    }
                }
                $this._cacheLoadTime = [datetime]::Now
            } catch {
                # Write-PmcTuiLog "Failed to load checklist templates: $_" "ERROR"
                $this._templatesCache = @{}
            }
        }
    }

    hidden [void] SaveTemplates() {
        try {
            $templates = $this._templatesCache.Values | ForEach-Object {
                @{
                    id = $_.id
                    name = $_.name
                    description = $_.description
                    category = $_.category
                    items = $_.items
                    created = $_.created.ToString("o")
                    modified = $_.modified.ToString("o")
                }
            }

            $metadata = @{
                schema_version = 1
                templates = $templates
            }

            # Atomic save
            $tempFile = "$($this._templatesFile).tmp"
            $metadata | ConvertTo-Json -Depth 10 | Set-Content -Path $tempFile -Encoding utf8

            if (Test-Path $this._templatesFile) {
                Copy-Item $this._templatesFile "$($this._templatesFile).bak" -Force
            }

            Move-Item -Path $tempFile -Destination $this._templatesFile -Force

        } catch {
            # Write-PmcTuiLog "Failed to save checklist templates: $_" "ERROR"
            throw
        }
    }

    # === Instance Management ===
    hidden [void] LoadInstances() {
        if (Test-Path $this._instancesFile) {
            try {
                $json = Get-Content $this._instancesFile -Raw | ConvertFrom-Json -Depth 10
                foreach ($instance in $json.instances) {
                    $items = @()
                    foreach ($item in $instance.items) {
                        $items += @{
                            text = $item.text
                            completed = $item.completed
                            completed_date = $(if ($item.completed_date) { [datetime]::Parse($item.completed_date) } else { $null })
                            order = $item.order
                        }
                    }

                    $this._instancesCache[$instance.id] = @{
                        id = $instance.id
                        title = $instance.title
                        template_id = $instance.template_id
                        owner_type = $instance.owner_type
                        owner_id = $instance.owner_id
                        items = $items
                        completed_count = $instance.completed_count
                        total_count = $instance.total_count
                        percent_complete = $instance.percent_complete
                        created = [datetime]::Parse($instance.created)
                        modified = [datetime]::Parse($instance.modified)
                    }
                }
            } catch {
                # Write-PmcTuiLog "Failed to load checklist instances: $_" "ERROR"
                $this._instancesCache = @{}
            }
        }
    }

    hidden [void] SaveInstances() {
        try {
            $instances = $this._instancesCache.Values | ForEach-Object {
                $items = @()
                foreach ($item in $_.items) {
                    $items += @{
                        text = $item.text
                        completed = $item.completed
                        completed_date = $(if ($item.completed_date) { $item.completed_date.ToString("o") } else { $null })
                        order = $item.order
                    }
                }

                @{
                    id = $_.id
                    title = $_.title
                    template_id = $_.template_id
                    owner_type = $_.owner_type
                    owner_id = $_.owner_id
                    items = $items
                    completed_count = $_.completed_count
                    total_count = $_.total_count
                    percent_complete = $_.percent_complete
                    created = $_.created.ToString("o")
                    modified = $_.modified.ToString("o")
                }
            }

            $metadata = @{
                schema_version = 1
                instances = $instances
            }

            # Atomic save
            $tempFile = "$($this._instancesFile).tmp"
            $metadata | ConvertTo-Json -Depth 10 | Set-Content -Path $tempFile -Encoding utf8

            if (Test-Path $this._instancesFile) {
                Copy-Item $this._instancesFile "$($this._instancesFile).bak" -Force
            }

            Move-Item -Path $tempFile -Destination $this._instancesFile -Force

        } catch {
            # Write-PmcTuiLog "Failed to save checklist instances: $_" "ERROR"
            throw
        }
    }

    # === Template CRUD Operations ===

    [array] GetAllTemplates() {
        return @($this._templatesCache.Values | Sort-Object -Property name)
    }

    [object] GetTemplate([string]$templateId) {
        if ($this._templatesCache.ContainsKey($templateId)) {
            return $this._templatesCache[$templateId]
        }
        return $null
    }

    [object] CreateTemplate([string]$name, [string]$description, [string]$category, [array]$itemTexts) {
        $templateId = [guid]::NewGuid().ToString()

        $items = @()
        $order = 1
        foreach ($text in $itemTexts) {
            $items += @{
                text = $text
                order = $order++
            }
        }

        $template = @{
            id = $templateId
            name = $name
            description = $description
            category = $category
            items = $items
            created = [datetime]::Now
            modified = [datetime]::Now
        }

        $this._templatesCache[$templateId] = $template
        $this.SaveTemplates()

        if ($this.OnTemplateAdded) {
            & $this.OnTemplateAdded $template
        }
        if ($this.OnChecklistsChanged) {
            & $this.OnChecklistsChanged
        }

        return $template
    }

    [void] UpdateTemplate([string]$templateId, [hashtable]$changes) {
        if (-not $this._templatesCache.ContainsKey($templateId)) {
            throw "Template not found: $templateId"
        }

        $template = $this._templatesCache[$templateId]

        if ($changes.ContainsKey('name')) { $template.name = $changes.name }
        if ($changes.ContainsKey('description')) { $template.description = $changes.description }
        if ($changes.ContainsKey('category')) { $template.category = $changes.category }
        if ($changes.ContainsKey('items')) { $template.items = $changes.items }

        $template.modified = [datetime]::Now

        $this.SaveTemplates()

        if ($this.OnTemplateUpdated) {
            & $this.OnTemplateUpdated $template
        }
        if ($this.OnChecklistsChanged) {
            & $this.OnChecklistsChanged
        }
    }

    [void] DeleteTemplate([string]$templateId) {
        if (-not $this._templatesCache.ContainsKey($templateId)) {
            throw "Template not found: $templateId"
        }

        $template = $this._templatesCache[$templateId]
        $this._templatesCache.Remove($templateId)
        $this.SaveTemplates()

        if ($this.OnTemplateDeleted) {
            & $this.OnTemplateDeleted $template
        }
        if ($this.OnChecklistsChanged) {
            & $this.OnChecklistsChanged
        }
    }

    # === Instance CRUD Operations ===

    [array] GetAllInstances() {
        return @($this._instancesCache.Values | Sort-Object -Property modified -Descending)
    }

    [array] GetInstancesByOwner([string]$ownerType, [string]$ownerId) {
        return @($this._instancesCache.Values | Where-Object {
            $_.owner_type -eq $ownerType -and $_.owner_id -eq $ownerId
        } | Sort-Object -Property created -Descending)
    }

    [object] GetInstance([string]$instanceId) {
        if ($this._instancesCache.ContainsKey($instanceId)) {
            return $this._instancesCache[$instanceId]
        }
        return $null
    }

    [object] CreateInstanceFromTemplate([string]$templateId, [string]$ownerType, [string]$ownerId) {
        $template = $this.GetTemplate($templateId)
        if (-not $template) {
            throw "Template not found: $templateId"
        }

        $instanceId = [guid]::NewGuid().ToString()

        # Copy items from template
        $items = @()
        foreach ($templateItem in $template.items) {
            $items += @{
                text = $templateItem.text
                completed = $false
                completed_date = $null
                order = $templateItem.order
            }
        }

        $instance = @{
            id = $instanceId
            title = $template.name
            template_id = $templateId
            owner_type = $ownerType
            owner_id = $ownerId
            items = $items
            completed_count = 0
            total_count = $items.Count
            percent_complete = 0
            created = [datetime]::Now
            modified = [datetime]::Now
        }

        $this._instancesCache[$instanceId] = $instance
        $this.SaveInstances()

        if ($this.OnInstanceAdded) {
            & $this.OnInstanceAdded $instance
        }
        if ($this.OnChecklistsChanged) {
            & $this.OnChecklistsChanged
        }

        return $instance
    }

    [object] CreateBlankInstance([string]$title, [string]$ownerType, [string]$ownerId, [array]$itemTexts) {
        $instanceId = [guid]::NewGuid().ToString()

        $items = @()
        $order = 1
        foreach ($text in $itemTexts) {
            $items += @{
                text = $text
                completed = $false
                completed_date = $null
                order = $order++
            }
        }

        $instance = @{
            id = $instanceId
            title = $title
            template_id = $null
            owner_type = $ownerType
            owner_id = $ownerId
            items = $items
            completed_count = 0
            total_count = $items.Count
            percent_complete = 0
            created = [datetime]::Now
            modified = [datetime]::Now
        }

        $this._instancesCache[$instanceId] = $instance
        $this.SaveInstances()

        if ($this.OnInstanceAdded) {
            & $this.OnInstanceAdded $instance
        }
        if ($this.OnChecklistsChanged) {
            & $this.OnChecklistsChanged
        }

        return $instance
    }

    [void] ToggleItem([string]$instanceId, [int]$itemIndex) {
        if (-not $this._instancesCache.ContainsKey($instanceId)) {
            throw "Instance not found: $instanceId"
        }

        $instance = $this._instancesCache[$instanceId]
        if ($itemIndex -lt 0 -or $itemIndex -ge $instance.items.Count) {
            throw "Invalid item index: $itemIndex"
        }

        $item = $instance.items[$itemIndex]
        $item.completed = -not $item.completed
        $item.completed_date = $(if ($item.completed) { [datetime]::Now } else { $null })

        # Recalculate stats
        $completed = @($instance.items | Where-Object { $_.completed }).Count
        $instance.completed_count = $completed
        $instance.percent_complete = $(if ($instance.total_count -gt 0) {
            [Math]::Round(($completed / $instance.total_count) * 100, 0)
        } else {
            0
        })
        $instance.modified = [datetime]::Now

        $this.SaveInstances()

        if ($this.OnInstanceUpdated) {
            & $this.OnInstanceUpdated $instance
        }
        if ($this.OnChecklistsChanged) {
            & $this.OnChecklistsChanged
        }
    }

    [void] UpdateInstance([string]$instanceId, [hashtable]$changes) {
        if (-not $this._instancesCache.ContainsKey($instanceId)) {
            throw "Instance not found: $instanceId"
        }

        $instance = $this._instancesCache[$instanceId]

        if ($changes.ContainsKey('title')) { $instance.title = $changes.title }
        if ($changes.ContainsKey('items')) {
            $instance.items = $changes.items
            # Recalculate stats
            $instance.total_count = $instance.items.Count
            $completed = @($instance.items | Where-Object { $_.completed }).Count
            $instance.completed_count = $completed
            $instance.percent_complete = $(if ($instance.total_count -gt 0) {
                [Math]::Round(($completed / $instance.total_count) * 100, 0)
            } else {
                0
            })
        }

        $instance.modified = [datetime]::Now

        $this.SaveInstances()

        if ($this.OnInstanceUpdated) {
            & $this.OnInstanceUpdated $instance
        }
        if ($this.OnChecklistsChanged) {
            & $this.OnChecklistsChanged
        }
    }

    [void] DeleteInstance([string]$instanceId) {
        if (-not $this._instancesCache.ContainsKey($instanceId)) {
            throw "Instance not found: $instanceId"
        }

        $instance = $this._instancesCache[$instanceId]
        $this._instancesCache.Remove($instanceId)
        $this.SaveInstances()

        if ($this.OnInstanceDeleted) {
            & $this.OnInstanceDeleted $instance
        }
        if ($this.OnChecklistsChanged) {
            & $this.OnChecklistsChanged
        }
    }
}

﻿# CommandService.ps1 - Service for managing command library
#
# Provides CRUD operations for saved commands with file-based storage
# Commands are stored in commands/ subdirectory as JSON metadata
#
# Usage:
#   $service = [CommandService]::GetInstance()
#   $commands = $service.GetAllCommands()
#   $service.CreateCommand("Deploy Script", "kubectl apply -f deploy.yaml")
#   $service.DeleteCommand($commandId)

using namespace System
using namespace System.Collections.Generic

Set-StrictMode -Version Latest

class CommandService {
    # === Singleton Instance ===
    static hidden [CommandService]$_instance = $null
    static hidden [object]$_instanceLock = [object]::new()

    # === Configuration ===
    hidden [string]$_commandsDir
    hidden [string]$_metadataFile

    # === In-memory cache ===
    hidden [hashtable]$_commandsCache = @{}
    hidden [datetime]$_cacheLoadTime = [datetime]::MinValue

    # === Event Callbacks ===
    [scriptblock]$OnCommandAdded = {}
    [scriptblock]$OnCommandUpdated = {}
    [scriptblock]$OnCommandDeleted = {}
    [scriptblock]$OnCommandsChanged = {}

    # === Singleton Access ===
    static [CommandService] GetInstance() {
        if ([CommandService]::_instance -eq $null) {
            [System.Threading.Monitor]::Enter([CommandService]::_instanceLock)
            try {
                if ([CommandService]::_instance -eq $null) {
                    [CommandService]::_instance = [CommandService]::new()
                }
            } finally {
                [System.Threading.Monitor]::Exit([CommandService]::_instanceLock)
            }
        }
        return [CommandService]::_instance
    }

    # === Constructor (Private - use GetInstance) ===
    CommandService() {
        # Determine commands directory relative to PMC root
        $pmcRoot = Split-Path -Parent (Split-Path -Parent (Split-Path -Parent $PSScriptRoot))
        $this._commandsDir = Join-Path $pmcRoot "commands"
        $this._metadataFile = Join-Path $this._commandsDir "commands_metadata.json"

        # Ensure commands directory exists
        if (-not (Test-Path $this._commandsDir)) {
            New-Item -ItemType Directory -Path $this._commandsDir -Force | Out-Null
        }

        # Load metadata
        $this.LoadMetadata()
    }

    # === Metadata Management ===
    hidden [void] LoadMetadata() {
        if (Test-Path $this._metadataFile) {
            try {
                $json = Get-Content $this._metadataFile -Raw | ConvertFrom-Json -Depth 10
                foreach ($cmd in $json.commands) {
                    $this._commandsCache[$cmd.id] = @{
                        id = $cmd.id
                        name = $cmd.name
                        category = $cmd.category
                        command_text = $cmd.command_text
                        description = $cmd.description
                        tags = $cmd.tags
                        created = [datetime]::Parse($cmd.created)
                        modified = [datetime]::Parse($cmd.modified)
                        usage_count = $cmd.usage_count
                        last_used = $(if ($cmd.last_used) { [datetime]::Parse($cmd.last_used) } else { $null })
                    }
                }
                $this._cacheLoadTime = [datetime]::Now
            } catch {
                # Write-PmcTuiLog "Failed to load commands metadata: $_" "ERROR"
                $this._commandsCache = @{}
            }
        }
    }

    hidden [void] SaveMetadata() {
        try {
            $commands = $this._commandsCache.Values | ForEach-Object {
                @{
                    id = $_.id
                    name = $_.name
                    category = $_.category
                    command_text = $_.command_text
                    description = $_.description
                    tags = $_.tags
                    created = $_.created.ToString("o")
                    modified = $_.modified.ToString("o")
                    usage_count = $_.usage_count
                    last_used = $(if ($_.last_used) { $_.last_used.ToString("o") } else { $null })
                }
            }

            $metadata = @{
                schema_version = 1
                commands = $commands
            }

            # Atomic save: write to temp file, then rename
            $tempFile = "$($this._metadataFile).tmp"
            $metadata | ConvertTo-Json -Depth 10 | Set-Content -Path $tempFile -Encoding utf8

            # Backup existing file
            if (Test-Path $this._metadataFile) {
                Copy-Item $this._metadataFile "$($this._metadataFile).bak" -Force
            }

            # Rename temp to actual
            Move-Item -Path $tempFile -Destination $this._metadataFile -Force

        } catch {
            # Write-PmcTuiLog "Failed to save commands metadata: $_" "ERROR"
            throw
        }
    }

    # === CRUD Operations ===

    [array] GetAllCommands() {
        $commands = @($this._commandsCache.Values | Sort-Object -Property modified -Descending)
        return $commands
    }

    [object] GetCommand([string]$commandId) {
        if ($this._commandsCache.ContainsKey($commandId)) {
            return $this._commandsCache[$commandId]
        }
        return $null
    }

    [object] CreateCommand([string]$name, [string]$commandText) {
        return $this.CreateCommand($name, $commandText, "General", "", @())
    }

    [object] CreateCommand([string]$name, [string]$commandText, [string]$category, [string]$description, [array]$tags) {
        # Generate unique ID
        $commandId = [guid]::NewGuid().ToString()

        # Create command object
        $command = @{
            id = $commandId
            name = $name
            category = $category
            command_text = $commandText
            description = $description
            tags = $tags
            created = [datetime]::Now
            modified = [datetime]::Now
            usage_count = 0
            last_used = $null
        }

        # Add to cache
        $this._commandsCache[$commandId] = $command

        # Save metadata
        $this.SaveMetadata()

        # Fire event
        if ($this.OnCommandAdded) {
            & $this.OnCommandAdded $command
        }
        if ($this.OnCommandsChanged) {
            & $this.OnCommandsChanged
        }

        return $command
    }

    [void] UpdateCommand([string]$commandId, [hashtable]$changes) {
        if (-not $this._commandsCache.ContainsKey($commandId)) {
            throw "Command not found: $commandId"
        }

        $command = $this._commandsCache[$commandId]

        # Apply changes
        if ($changes.ContainsKey('name')) { $command.name = $changes.name }
        if ($changes.ContainsKey('category')) { $command.category = $changes.category }
        if ($changes.ContainsKey('command_text')) { $command.command_text = $changes.command_text }
        if ($changes.ContainsKey('description')) { $command.description = $changes.description }
        if ($changes.ContainsKey('tags')) { $command.tags = $changes.tags }

        $command.modified = [datetime]::Now

        # Save metadata
        $this.SaveMetadata()

        # Fire event
        if ($this.OnCommandUpdated) {
            & $this.OnCommandUpdated $command
        }
        if ($this.OnCommandsChanged) {
            & $this.OnCommandsChanged
        }
    }

    [void] DeleteCommand([string]$commandId) {
        if (-not $this._commandsCache.ContainsKey($commandId)) {
            throw "Command not found: $commandId"
        }

        $command = $this._commandsCache[$commandId]

        # Remove from cache
        $this._commandsCache.Remove($commandId)

        # Save metadata
        $this.SaveMetadata()

        # Fire event
        if ($this.OnCommandDeleted) {
            & $this.OnCommandDeleted $command
        }
        if ($this.OnCommandsChanged) {
            & $this.OnCommandsChanged
        }
    }

    [void] IncrementUsageCount([string]$commandId) {
        if (-not $this._commandsCache.ContainsKey($commandId)) {
            throw "Command not found: $commandId"
        }

        $command = $this._commandsCache[$commandId]
        $command.usage_count++
        $command.last_used = [datetime]::Now

        # Save metadata
        $this.SaveMetadata()

        # Fire event
        if ($this.OnCommandsChanged) {
            & $this.OnCommandsChanged
        }
    }
}

﻿# ExcelComReader.ps1 - COM automation for reading Excel files
#
# Provides COM-based Excel reading capabilities
# Can attach to running Excel instance or open files programmatically
#
# Usage:
#   $reader = [ExcelComReader]::new()
#   $reader.AttachToRunningExcel()
#   $value = $reader.ReadCell("W3")
#   $reader.Close()

using namespace System
using namespace System.Runtime.InteropServices

Set-StrictMode -Version Latest

class ExcelComReader {
    # COM objects
    hidden [object]$_excelApp = $null
    hidden [object]$_workbook = $null
    hidden [object]$_worksheet = $null
    hidden [bool]$_isAttached = $false  # Did we create Excel or attach to existing?

    # State
    [bool]$IsOpen = $false
    [string]$FilePath = ""
    [int]$ActiveSheet = 1

    # Constructor
    ExcelComReader() {
        # Nothing to initialize
    }

    # Validate Excel cell address format (e.g., "A1", "W3", "AA100", "AAA1")
    # ES-H5 FIX: Make regex case-insensitive and add null check
    # The [A-Z]+ pattern with + quantifier properly matches multi-letter columns
    hidden [bool] IsValidCellAddress([string]$address) {
        if ([string]::IsNullOrWhiteSpace($address)) {
            return $false
        }
        # Case-insensitive match: accepts A1, AA1, AAA1, a1, aa1, etc.
        return $address -match '(?i)^[A-Z]+\d+$'
    }

    # Attach to running Excel instance
    [void] AttachToRunningExcel() {
        try {
            $this._excelApp = [Marshal]::GetActiveObject("Excel.Application")

            # CRITICAL FIX ES-C2: Validate COM object is functional
            if ($null -eq $this._excelApp) {
                throw "Excel COM object is null"
            }
            # Test if Excel is responsive
            $null = $this._excelApp.Name  # Will throw if Excel is not responsive

            # LOW FIX ES-L1: Validate Excel is not in edit mode
            try {
                $isInEditMode = $this._excelApp.Interactive -eq $false
                if ($isInEditMode) {
                    # Write-PmcTuiLog "Excel may be in edit mode or protected view - attempting to continue" "WARNING"
                }
            } catch {
                # If we can't check edit mode, log warning but continue
                # Write-PmcTuiLog "Cannot verify Excel edit mode status: $($_.Exception.Message)" "WARNING"
            }

            $this._isAttached = $true

            # KSV2-M3 FIX: Validate Worksheets collection exists before access
            if ($this._excelApp.Workbooks.Count -gt 0) {
                $this._workbook = $this._excelApp.ActiveWorkbook
                if ($null -ne $this._workbook -and
                    $null -ne $this._workbook.Worksheets -and
                    $this._workbook.Worksheets.Count -gt 0) {
                    $this._worksheet = $this._workbook.ActiveSheet
                    $this.ActiveSheet = $this._worksheet.Index
                }
            }

            $this.IsOpen = $true
            # Write-PmcTuiLog "ExcelComReader: Attached to running Excel instance" "INFO"

        } catch {
            # Write-PmcTuiLog "ExcelComReader: Failed to attach to Excel - $_" "ERROR"
            throw "Excel is not running or not accessible. Please open Excel first and try again."
        }
    }

    # Open Excel file programmatically
    [void] OpenFile([string]$filePath) {
        if (-not (Test-Path $filePath)) {
            throw "File not found: $filePath"
        }

        try {
            $this._excelApp = New-Object -ComObject Excel.Application

            # CRITICAL FIX ES-C2: Validate COM object is functional
            if ($null -eq $this._excelApp) {
                throw "Excel COM object is null"
            }
            # Test if Excel is responsive
            $null = $this._excelApp.Name  # Will throw if Excel is not responsive

            $this._excelApp.Visible = $false
            $this._excelApp.DisplayAlerts = $false
            $this._isAttached = $false

            # MEDIUM FIX #16: Add file lock detection and user-friendly error handling
            try {
                $this._workbook = $this._excelApp.Workbooks.Open($filePath)
            } catch {
                # Common COM errors for file locks or permissions
                $errorMsg = $_.Exception.Message
                $lockError = $errorMsg -match 'locked|in use|permission denied|cannot access|0x800A03EC'
                if ($lockError) {
                    $this.Close()
                    throw "Cannot open file - it may be open in another program, locked by the file system, or you may not have permission to access it. Please close the file in other programs and try again."
                }
                throw
            }

            # KSV2-M3 FIX: Validate Worksheets collection exists before access
            if ($null -ne $this._workbook -and
                $null -ne $this._workbook.Worksheets -and
                $this._workbook.Worksheets.Count -gt 0) {
                $this._worksheet = $this._workbook.Worksheets.Item(1)
                $this.ActiveSheet = 1
            }

            $this.FilePath = $filePath
            $this.IsOpen = $true
            # Write-PmcTuiLog "ExcelComReader: Opened file $filePath" "INFO"

        } catch {
            # Write-PmcTuiLog "ExcelComReader: Failed to open file - $_" "ERROR"
            $this.Close()
            throw "Could not open Excel file: $_"
        }
    }

    # Set active worksheet by index (1-based)
    [void] SetActiveSheet([int]$sheetIndex) {
        if (-not $this.IsOpen -or $null -eq $this._workbook) {
            throw "No workbook is open"
        }

        # KSV2-M3 FIX: Validate Worksheets collection exists before access
        if ($null -eq $this._workbook.Worksheets) {
            throw "Workbook has no Worksheets collection"
        }

        if ($sheetIndex -lt 1 -or $sheetIndex -gt $this._workbook.Worksheets.Count) {
            throw "Sheet index out of range: $sheetIndex (workbook has $($this._workbook.Worksheets.Count) sheets)"
        }

        $this._worksheet = $this._workbook.Worksheets.Item($sheetIndex)
        $this.ActiveSheet = $sheetIndex
    }

    # Set active worksheet by name
    [void] SetActiveSheetByName([string]$sheetName) {
        if (-not $this.IsOpen -or $null -eq $this._workbook) {
            throw "No workbook is open"
        }

        # KSV2-M3 FIX: Validate Worksheets collection exists before access
        if ($null -eq $this._workbook.Worksheets) {
            throw "Workbook has no Worksheets collection"
        }

        try {
            $this._worksheet = $this._workbook.Worksheets.Item($sheetName)
            $this.ActiveSheet = $this._worksheet.Index
        } catch {
            throw "Sheet not found: $sheetName"
        }
    }

    # Read single cell value
    [object] ReadCell([string]$cellAddress) {
        if (-not $this.IsOpen -or $null -eq $this._worksheet) {
            throw "No worksheet is active"
        }

        if (-not $this.IsValidCellAddress($cellAddress)) {
            throw "Invalid Excel cell address: $cellAddress (expected format like 'A1' or 'W3')"
        }

        $cell = $null
        try {
            $cell = $this._worksheet.Range($cellAddress)
            $value = $cell.Value2
            return $value
        } catch {
            # Write-PmcTuiLog "ExcelComReader: Error reading cell $cellAddress - $_" "ERROR"
            return $null
        } finally {
            if ($null -ne $cell) {
                [Marshal]::ReleaseComObject($cell) | Out-Null
            }

            # CRITICAL FIX ES-C5: Aggressive COM cleanup to prevent memory leaks
            [System.GC]::Collect()
            [System.GC]::WaitForPendingFinalizers()
            [System.GC]::Collect()
        }
    }

    # Read range of cells (returns hashtable of address => value)
    [hashtable] ReadRange([string]$startCell, [string]$endCell) {
        if (-not $this.IsOpen -or $null -eq $this._worksheet) {
            throw "No worksheet is active"
        }

        if (-not $this.IsValidCellAddress($startCell)) {
            throw "Invalid Excel cell address: $startCell (expected format like 'A1' or 'W3')"
        }
        if (-not $this.IsValidCellAddress($endCell)) {
            throw "Invalid Excel cell address: $endCell (expected format like 'A1' or 'W3')"
        }

        $cellData = @{}
        $range = $null
        $cells = $null  # CRITICAL FIX #2: Track Cells collection COM object
        $cellsToRelease = [System.Collections.ArrayList]::new()

        try {
            $range = $this._worksheet.Range("$startCell`:$endCell")

            if ($null -ne $range) {
                # CRITICAL FIX #2: Get Cells collection explicitly for proper COM cleanup
                $cells = $range.Cells
                if ($null -ne $cells) {
                    foreach ($cell in $cells) {
                        $address = $cell.Address($false, $false)  # Get address like "W3"
                        $cellData[$address] = $cell.Value2
                        [void]$cellsToRelease.Add($cell)
                    }
                }
            }

            return $cellData

        } catch {
            # Write-PmcTuiLog "ExcelComReader: Error reading range $startCell`:$endCell - $_" "ERROR"
            return $cellData
        } finally {
            # Release all cell COM objects
            foreach ($cell in $cellsToRelease) {
                try {
                    [Marshal]::ReleaseComObject($cell) | Out-Null
                } catch {
                    # Write-PmcTuiLog "Failed to release COM object (cell): $($_.Exception.Message)" "WARNING"
                }
            }
            # CRITICAL FIX #2: Release Cells collection COM object
            if ($null -ne $cells) {
                try {
                    [Marshal]::ReleaseComObject($cells) | Out-Null
                } catch {
                    # Write-PmcTuiLog "Failed to release COM object (cells collection): $($_.Exception.Message)" "WARNING"
                }
            }
            # Release range COM object
            if ($null -ne $range) {
                try {
                    [Marshal]::ReleaseComObject($range) | Out-Null
                } catch {
                    # Write-PmcTuiLog "Failed to release COM object (range): $($_.Exception.Message)" "WARNING"
                }
            }
        }
    }

    # Read multiple specific cells (returns hashtable of address => value)
    [hashtable] ReadCells([array]$cellAddresses) {
        if (-not $this.IsOpen -or $null -eq $this._worksheet) {
            throw "No worksheet is active"
        }

        $cellData = @{}

        foreach ($address in $cellAddresses) {
            try {
                $value = $this.ReadCell($address)
                $cellData[$address] = $value
            } catch {
                # Write-PmcTuiLog "ExcelComReader: Error reading cell $address - $_" "WARN"
                $cellData[$address] = $null
            }
        }

        return $cellData
    }

    # Get worksheet names
    [array] GetSheetNames() {
        if (-not $this.IsOpen -or $null -eq $this._workbook) {
            return @()
        }

        # KSV2-M3 FIX: Validate Worksheets collection exists before access
        if ($null -eq $this._workbook.Worksheets) {
            # Write-PmcTuiLog "Workbook has no Worksheets collection" "WARNING"
            return @()
        }

        $names = @()
        $sheets = $null
        try {
            $sheets = $this._workbook.Worksheets
            foreach ($sheet in $sheets) {
                # LOW FIX ES-L4: Add null check in GetSheetNames loop
                if ($null -ne $sheet -and $null -ne $sheet.Name) {
                    $names += $sheet.Name
                }
                try {
                    if ($null -ne $sheet) {
                        [Marshal]::ReleaseComObject($sheet) | Out-Null
                    }
                } catch {
                    # Write-PmcTuiLog "Failed to release COM object (sheet): $($_.Exception.Message)" "WARNING"
                }
            }
        } finally {
            if ($null -ne $sheets) {
                try {
                    [Marshal]::ReleaseComObject($sheets) | Out-Null
                } catch {
                    # Write-PmcTuiLog "Failed to release COM object (sheets collection): $($_.Exception.Message)" "WARNING"
                }
            }
        }
        return $names
    }

    # Close and cleanup
    # NOTE: Caller MUST call Close() explicitly - PowerShell classes do not support finalizers
    [void] Close() {
        if ($this.IsOpen) {
            # Isolate each cleanup operation to prevent cascading failures

            # Release worksheet COM object
            if ($null -ne $this._worksheet) {
                try {
                    [Marshal]::ReleaseComObject($this._worksheet) | Out-Null
                } catch {
                    # Write-PmcTuiLog "ExcelComReader: Error releasing worksheet COM object - $_" "WARN"
                }
                $this._worksheet = $null
            }

            # Close and release workbook
            if ($null -ne $this._workbook) {
                if (-not $this._isAttached) {
                    try {
                        $this._workbook.Close($false)  # Don't save changes
                    } catch {
                        # Write-PmcTuiLog "ExcelComReader: Error closing workbook - $_" "WARN"
                    }
                }
                try {
                    [Marshal]::ReleaseComObject($this._workbook) | Out-Null
                } catch {
                    # Write-PmcTuiLog "ExcelComReader: Error releasing workbook COM object - $_" "WARN"
                }
                $this._workbook = $null
            }

            # Quit and release Excel app
            if ($null -ne $this._excelApp) {
                if (-not $this._isAttached) {
                    try {
                        $this._excelApp.Quit()
                    } catch {
                        # Write-PmcTuiLog "ExcelComReader: Error quitting Excel - $_" "WARN"
                    }
                }
                try {
                    [Marshal]::ReleaseComObject($this._excelApp) | Out-Null
                } catch {
                    # Write-PmcTuiLog "ExcelComReader: Error releasing Excel COM object - $_" "WARN"
                }
                $this._excelApp = $null
            }

            $this.IsOpen = $false

            # CRITICAL FIX ES-C5: Aggressive COM cleanup to ensure all resources are released
            [System.GC]::Collect()
            [System.GC]::WaitForPendingFinalizers()
            [System.GC]::Collect()

            # Write-PmcTuiLog "ExcelComReader: Closed" "INFO"
        }
    }
}

﻿# ExcelMappingService.ps1 - Service for managing Excel import profiles and mappings
#
# Provides CRUD operations for Excel mapping profiles
# Each profile contains field mappings (Excel cell -> Project property)
#
# Usage:
#   $service = [ExcelMappingService]::GetInstance()
#   $profile = $service.CreateProfile("My Profile", "Description")
#   $service.AddMapping($profileId, @{ display_name="Name"; excel_cell="A1"; project_property="name" })

using namespace System
using namespace System.Collections.Generic

Set-StrictMode -Version Latest

class ExcelMappingService {
    # === Singleton Instance ===
    static hidden [ExcelMappingService]$_instance = $null
    static hidden [object]$_instanceLock = [object]::new()

    # === Configuration ===
    hidden [string]$_profilesFile
    hidden [string]$_activeProfileId = $null

    # === In-memory cache ===
    hidden [hashtable]$_profilesCache = @{}
    hidden [datetime]$_cacheLoadTime = [datetime]::MinValue

    # === Event Callbacks ===
    [scriptblock]$OnProfileAdded = {}
    [scriptblock]$OnProfileUpdated = {}
    [scriptblock]$OnProfileDeleted = {}
    [scriptblock]$OnProfilesChanged = {}

    # === Singleton Access ===
    static [ExcelMappingService] GetInstance() {
        if ([ExcelMappingService]::_instance -eq $null) {
            [System.Threading.Monitor]::Enter([ExcelMappingService]::_instanceLock)
            try {
                if ([ExcelMappingService]::_instance -eq $null) {
                    [ExcelMappingService]::_instance = [ExcelMappingService]::new()
                }
            } finally {
                [System.Threading.Monitor]::Exit([ExcelMappingService]::_instanceLock)
            }
        }
        return [ExcelMappingService]::_instance
    }

    # === Constructor (Private - use GetInstance) ===
    ExcelMappingService() {
        # Determine profiles file location
        # FIXED: Point to the actual location found in user's home directory
        $this._profilesFile = "/home/teej/_tui/praxis-main/simpletaskpro/Data/excel-mappings.json"

        # Load profiles
        $this.LoadProfiles()
    }

    # === Profile Management ===
    hidden [void] LoadProfiles() {
        # Write-PmcTuiLog "ExcelMappingService.LoadProfiles: START - file=$($this._profilesFile)" "DEBUG"
        if (Test-Path $this._profilesFile) {
            # CRITICAL FIX ES-C3: Robust JSON parsing with null validation
            try {
                $jsonContent = Get-Content $this._profilesFile -Raw -ErrorAction Stop
                # Write-PmcTuiLog "ExcelMappingService.LoadProfiles: Read $($jsonContent.Length) chars from file" "DEBUG"
                $json = $jsonContent | ConvertFrom-Json -ErrorAction Stop
                # Write-PmcTuiLog "ExcelMappingService.LoadProfiles: JSON parsed successfully" "DEBUG"

                if ($null -eq $json) {
                    throw "JSON deserialization returned null"
                }

                # Write-PmcTuiLog "ExcelMappingService.LoadProfiles: Checking for active_profile_id property" "DEBUG"
                if (-not $json.PSObject.Properties['active_profile_id']) {
                    throw "JSON missing 'active_profile_id' property"
                }
                $this._profilesCache = @{}
                $this._activeProfileId = $json.active_profile_id
                # Write-PmcTuiLog "ExcelMappingService.LoadProfiles: active_profile_id=$($this._activeProfileId)" "DEBUG"

                # Write-PmcTuiLog "ExcelMappingService.LoadProfiles: Found $($json.profiles.Count) profiles" "DEBUG"
                foreach ($profile in $json.profiles) {
                    # Write-PmcTuiLog "ExcelMappingService.LoadProfiles: Processing profile id=$($profile.id) name=$($profile.name)" "DEBUG"

                    # Check for start_cell
                    if (-not $profile.PSObject.Properties['start_cell']) {
                        throw "Profile '$($profile.id)' missing 'start_cell' property"
                    }

                    $mappings = @()
                    # Check if mappings property exists and is not null - JSON deserialization can omit empty arrays
                    if ($profile.PSObject.Properties['mappings'] -and $null -ne $profile.mappings) {
                        # Write-PmcTuiLog "ExcelMappingService.LoadProfiles: Profile has $($profile.mappings.Count) mappings" "DEBUG"
                        foreach ($mapping in $profile.mappings) {
                            # ES-M4 FIX: Type validation before casting JSON booleans
                            $requiredValue = $false
                            if ($mapping.PSObject.Properties['required']) {
                                try {
                                    $requiredValue = [bool]$mapping.required
                                } catch {
                                    # Write-PmcTuiLog "Invalid 'required' value for mapping $($mapping.id), defaulting to false: $_" "WARN"
                                }
                            }

                            $includeInExportValue = $true
                            if ($mapping.PSObject.Properties['include_in_export']) {
                                try {
                                    $includeInExportValue = [bool]$mapping.include_in_export
                                } catch {
                                    # Write-PmcTuiLog "Invalid 'include_in_export' value for mapping $($mapping.id), defaulting to true: $_" "WARN"
                                }
                            }

                            $sortOrderValue = 0
                            if ($mapping.PSObject.Properties['sort_order']) {
                                try {
                                    $sortOrderValue = [int]$mapping.sort_order
                                } catch {
                                    # Write-PmcTuiLog "Invalid 'sort_order' value for mapping $($mapping.id), defaulting to 0: $_" "WARN"
                                }
                            }

                            # Force type conversion for boolean and int values from JSON
                            $mappings += @{
                                id = $mapping.id
                                display_name = $mapping.display_name
                                excel_cell = $mapping.excel_cell
                                project_property = $mapping.project_property
                                required = $requiredValue
                                data_type = $mapping.data_type
                                include_in_export = $includeInExportValue
                                sort_order = $sortOrderValue
                            }
                        }
                    }

                    # Parse datetime with error handling
                    try {
                        $created = [datetime]::Parse($profile.created)
                    } catch {
                        # Write-PmcTuiLog "Failed to parse created date for profile $($profile.id), using current time: $_" "WARN"
                        $created = [datetime]::Now
                    }

                    try {
                        $modified = [datetime]::Parse($profile.modified)
                    } catch {
                        # Write-PmcTuiLog "Failed to parse modified date for profile $($profile.id), using current time: $_" "WARN"
                        $modified = [datetime]::Now
                    }

                    $this._profilesCache[$profile.id] = @{
                        id = $profile.id
                        name = $profile.name
                        description = $profile.description
                        start_cell = $profile.start_cell
                        mappings = $mappings
                        created = $created
                        modified = $modified
                    }
                    # Write-PmcTuiLog "ExcelMappingService.LoadProfiles: Cached profile id=$($profile.id) with $($mappings.Count) mappings" "DEBUG"
                }
                $this._cacheLoadTime = [datetime]::Now
                # Write-PmcTuiLog "ExcelMappingService.LoadProfiles: SUCCESS - loaded $($this._profilesCache.Count) profiles" "DEBUG"
            } catch {
                # Write-PmcTuiLog "Failed to load Excel profiles: $_" "ERROR"
                # Write-PmcTuiLog "ExcelMappingService.LoadProfiles: STACK TRACE: $($_.ScriptStackTrace)" "ERROR"
                $this._profilesCache = @{}
            }
        } else {
            # Write-PmcTuiLog "ExcelMappingService.LoadProfiles: File not found: $($this._profilesFile)" "WARN"
        }
    }

    hidden [void] SaveProfiles() {
        try {
            $profiles = $this._profilesCache.Values | ForEach-Object {
                $mappings = @()
                foreach ($mapping in $_.mappings) {
                    $mappings += @{
                        id = $mapping.id
                        display_name = $mapping.display_name
                        excel_cell = $mapping.excel_cell
                        project_property = $mapping.project_property
                        required = $mapping.required
                        data_type = $mapping.data_type
                        include_in_export = $mapping.include_in_export
                        sort_order = $mapping.sort_order
                    }
                }

                @{
                    id = $_.id
                    name = $_.name
                    description = $_.description
                    start_cell = $_.start_cell
                    mappings = $mappings
                    created = $_.created.ToString("o")
                    modified = $_.modified.ToString("o")
                }
            }

            $metadata = @{
                schema_version = 1
                active_profile_id = $this._activeProfileId
                profiles = $profiles
            }

            # ES-H6 FIX: Atomic save with proper cleanup of temp file on failure
            $tempFile = "$($this._profilesFile).tmp"
            $metadata | ConvertTo-Json -Depth 10 | Set-Content -Path $tempFile -Encoding utf8

            try {
                if (Test-Path $this._profilesFile) {
                    Copy-Item $this._profilesFile "$($this._profilesFile).bak" -Force
                }

                Move-Item -Path $tempFile -Destination $this._profilesFile -Force
            } catch {
                # Clean up orphaned temp file if move fails
                if (Test-Path $tempFile) {
                    try {
                        Remove-Item $tempFile -Force -ErrorAction SilentlyContinue
                    } catch {
                        # Write-PmcTuiLog "Failed to clean up temp file $tempFile : $_" "WARNING"
                    }
                }
                throw
            }

        } catch {
            # Write-PmcTuiLog "Failed to save Excel profiles: $_" "ERROR"
            # Write-PmcTuiLog "Stack trace: $($_.ScriptStackTrace)" "ERROR"
            throw "Failed to save profiles: $($_.Exception.Message)"
        }
    }

    # === Profile CRUD Operations ===

    [array] GetAllProfiles() {
        return @($this._profilesCache.Values | Sort-Object -Property name)
    }

    [object] GetProfile([string]$profileId) {
        if ($this._profilesCache.ContainsKey($profileId)) {
            return $this._profilesCache[$profileId]
        }
        return $null
    }

    [object] GetActiveProfile() {
        if ($null -ne $this._activeProfileId -and $this._profilesCache.ContainsKey($this._activeProfileId)) {
            return $this._profilesCache[$this._activeProfileId]
        }
        return $null
    }

    [void] SetActiveProfile([string]$profileId) {
        if (-not $this._profilesCache.ContainsKey($profileId)) {
            throw "Profile not found: $profileId"
        }

        $this._activeProfileId = $profileId
        $this.SaveProfiles()

        if ($this.OnProfilesChanged) {
            & $this.OnProfilesChanged
        }
    }

    [object] CreateProfile([string]$name, [string]$description) {
        return $this.CreateProfile($name, $description, "A1")
    }

    [object] CreateProfile([string]$name, [string]$description, [string]$startCell) {
        # ES-H3 FIX: Check for duplicate name with proper count validation
        $existing = @($this._profilesCache.Values | Where-Object { $_['name'] -eq $name })
        if ($existing.Count -gt 0) {
            throw "Profile with name '$name' already exists"
        }

        $profileId = [guid]::NewGuid().ToString()

        $profile = @{
            id = $profileId
            name = $name
            description = $description
            start_cell = $startCell
            mappings = @()
            created = [datetime]::Now
            modified = [datetime]::Now
        }

        $this._profilesCache[$profileId] = $profile

        # Set as active if this is the first profile
        if ($this._profilesCache.Count -eq 1) {
            $this._activeProfileId = $profileId
        }

        $this.SaveProfiles()

        if ($this.OnProfileAdded) {
            & $this.OnProfileAdded $profile
        }
        if ($this.OnProfilesChanged) {
            & $this.OnProfilesChanged
        }

        return $profile
    }

    [void] UpdateProfile([string]$profileId, [hashtable]$changes) {
        if (-not $this._profilesCache.ContainsKey($profileId)) {
            throw "Profile not found: $profileId"
        }

        $profile = $this._profilesCache[$profileId]

        # Check for duplicate name if name is being changed
        if ($changes.ContainsKey('name') -and $changes.name -ne $profile['name']) {
            # ES-M7 FIX: Validate Where-Object returns expected result count
            $existing = @($this._profilesCache.Values | Where-Object { $_['name'] -eq $changes.name -and $_['id'] -ne $profileId })
            if ($existing.Count -gt 0) {
                throw "Profile with name '$($changes.name)' already exists"
            }
        }

        if ($changes.ContainsKey('name')) { $profile.name = $changes.name }
        if ($changes.ContainsKey('description')) { $profile.description = $changes.description }
        if ($changes.ContainsKey('start_cell')) { $profile.start_cell = $changes.start_cell }

        $profile.modified = [datetime]::Now

        $this.SaveProfiles()

        if ($this.OnProfileUpdated) {
            & $this.OnProfileUpdated $profile
        }
        if ($this.OnProfilesChanged) {
            & $this.OnProfilesChanged
        }
    }

    [void] DeleteProfile([string]$profileId) {
        if (-not $this._profilesCache.ContainsKey($profileId)) {
            throw "Profile not found: $profileId"
        }

        $profile = $this._profilesCache[$profileId]
        $this._profilesCache.Remove($profileId)

        # Clear active profile if it was deleted
        if ($this._activeProfileId -eq $profileId) {
            # Set to first remaining profile, or null
            $remaining = $this._profilesCache.Keys
            $this._activeProfileId = $(if ($remaining.Count -gt 0) { $remaining[0] } else { $null })
        }

        $this.SaveProfiles()

        if ($this.OnProfileDeleted) {
            & $this.OnProfileDeleted $profile
        }
        if ($this.OnProfilesChanged) {
            & $this.OnProfilesChanged
        }
    }

    # === Mapping CRUD Operations ===

    [object] AddMapping([string]$profileId, [hashtable]$mappingData) {
        if (-not $this._profilesCache.ContainsKey($profileId)) {
            throw "Profile not found: $profileId"
        }

        $profile = $this._profilesCache[$profileId]
        $mappingId = [guid]::NewGuid().ToString()

        # Determine sort order
        $sortOrder = $(if ($mappingData.ContainsKey('sort_order')) {
            $mappingData.sort_order
        } else {
            $profile.mappings.Count + 1
        })

        $mapping = @{
            id = $mappingId
            display_name = $mappingData.display_name
            excel_cell = $mappingData.excel_cell
            project_property = $mappingData.project_property
            required = $(if ($mappingData.ContainsKey('required')) { $mappingData.required } else { $false })
            data_type = $(if ($mappingData.ContainsKey('data_type')) { $mappingData.data_type } else { "string" })
            include_in_export = $(if ($mappingData.ContainsKey('include_in_export')) { $mappingData.include_in_export } else { $true })
            sort_order = $sortOrder
        }

        $profile.mappings += $mapping
        $profile.modified = [datetime]::Now

        $this.SaveProfiles()

        if ($this.OnProfileUpdated) {
            & $this.OnProfileUpdated $profile
        }
        if ($this.OnProfilesChanged) {
            & $this.OnProfilesChanged
        }

        return $mapping
    }

    [void] UpdateMapping([string]$profileId, [string]$mappingId, [hashtable]$changes) {
        if (-not $this._profilesCache.ContainsKey($profileId)) {
            throw "Profile not found: $profileId"
        }

        $profile = $this._profilesCache[$profileId]
        # ES-M7 FIX: Validate Where-Object returns exactly one result
        $matchingMappings = @($profile.mappings | Where-Object { $_.id -eq $mappingId })
        if ($matchingMappings.Count -eq 0) {
            throw "Mapping not found: $mappingId"
        }
        if ($matchingMappings.Count -gt 1) {
            # Write-PmcTuiLog "WARNING: Multiple mappings found with ID $mappingId in profile $profileId. Using first match." "WARN"
        }
        $mapping = $matchingMappings[0]

        if ($changes.ContainsKey('display_name')) { $mapping.display_name = $changes.display_name }
        if ($changes.ContainsKey('excel_cell')) { $mapping.excel_cell = $changes.excel_cell }
        if ($changes.ContainsKey('project_property')) { $mapping.project_property = $changes.project_property }
        if ($changes.ContainsKey('required')) { $mapping.required = $changes.required }
        if ($changes.ContainsKey('data_type')) { $mapping.data_type = $changes.data_type }
        if ($changes.ContainsKey('include_in_export')) { $mapping.include_in_export = $changes.include_in_export }
        if ($changes.ContainsKey('sort_order')) { $mapping.sort_order = $changes.sort_order }

        $profile.modified = [datetime]::Now

        $this.SaveProfiles()

        if ($this.OnProfileUpdated) {
            & $this.OnProfileUpdated $profile
        }
        if ($this.OnProfilesChanged) {
            & $this.OnProfilesChanged
        }
    }

    [void] DeleteMapping([string]$profileId, [string]$mappingId) {
        if (-not $this._profilesCache.ContainsKey($profileId)) {
            throw "Profile not found: $profileId"
        }

        $profile = $this._profilesCache[$profileId]
        # @() wrapper ensures array type even with 0 or 1 results
        $profile.mappings = @($profile.mappings | Where-Object { $_.id -ne $mappingId })
        $profile.modified = [datetime]::Now

        $this.SaveProfiles()

        if ($this.OnProfileUpdated) {
            & $this.OnProfileUpdated $profile
        }
        if ($this.OnProfilesChanged) {
            & $this.OnProfilesChanged
        }
    }

    [array] GetMappings([string]$profileId) {
        if (-not $this._profilesCache.ContainsKey($profileId)) {
            return @()
        }

        $profile = $this._profilesCache[$profileId]
        return @($profile.mappings | Sort-Object -Property sort_order)
    }
}

﻿# MenuRegistry.ps1 - Dynamic menu registration system
#
# Allows screens to register their own menu items instead of hardcoding
# menu structure in TaskListScreen.
#
# Usage in screen classes:
#   static [void] RegisterMenuItems([MenuRegistry]$registry) {
#       $registry.AddMenuItem('Tasks', 'Today View', 'Y', {
#           . "$PSScriptRoot/TodayViewScreen.ps1"
#           $global:PmcApp.PushScreen([TodayViewScreen]::new())
#       })
#   }

using namespace System
using namespace System.Collections.Generic

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Dynamic menu registration system for PMC screens

.DESCRIPTION
MenuRegistry allows screens to register their own menu items dynamically.
This decouples menu structure from TaskListScreen and allows screens to
control their own menu presence.

Menu structure:
- Tasks: Task views and actions
- Projects: Project management screens
- Time: Time tracking screens
- Tools: Utilities and helpers
- Options: Settings and configuration
- Help: Help and about screens

.EXAMPLE
$registry = [MenuRegistry]::new()
$registry.AddMenuItem('Tasks', 'Today', 'Y', { Show-TodayView })
$menuItems = $registry.GetMenuItems('Tasks')
#>
class MenuRegistry {
    # Singleton instance
    static [MenuRegistry]$_instance = $null

    # Menu structure: @{ 'Tasks' = @( @{ Label='Today'; Hotkey='Y'; Action={...} }, ... ) }
    [hashtable]$_menuItems = @{
        'Tasks' = [List[hashtable]]::new()
        'Projects' = [List[hashtable]]::new()
        'Time' = [List[hashtable]]::new()
        'Tools' = [List[hashtable]]::new()
        'Options' = [List[hashtable]]::new()
        'Help' = [List[hashtable]]::new()
    }

    # === Singleton Pattern ===

    <#
    .SYNOPSIS
    Get the singleton instance of MenuRegistry

    .OUTPUTS
    MenuRegistry singleton instance
    #>
    static [MenuRegistry] GetInstance() {
        if ($null -eq [MenuRegistry]::_instance) {
            [MenuRegistry]::_instance = [MenuRegistry]::new()
        }
        return [MenuRegistry]::_instance
    }

    # Private constructor for singleton
    hidden MenuRegistry() {
        # Initialize menu structure
    }

    # === Registration ===

    <#
    .SYNOPSIS
    Register a menu item

    .PARAMETER menuName
    Menu to add item to ('Tasks', 'Projects', 'Time', 'Tools', 'Options', 'Help')

    .PARAMETER label
    Display label for menu item

    .PARAMETER hotkey
    Keyboard shortcut (single character or [char]0 for separator)

    .PARAMETER action
    Scriptblock to execute when item is selected

    .PARAMETER order
    Optional sort order (lower numbers appear first, default 100)
    #>
    [void] AddMenuItem([string]$menuName, [string]$label, [char]$hotkey, [scriptblock]$action) {
        $this.AddMenuItem($menuName, $label, $hotkey, $action, 100)
    }

    [void] AddMenuItem([string]$menuName, [string]$label, [char]$hotkey, [scriptblock]$action, [int]$order) {
        if (-not $this._menuItems.ContainsKey($menuName)) {
            throw "Invalid menu name: $menuName. Valid: Tasks, Projects, Time, Tools, Options, Help"
        }

        $item = @{
            Label = $label
            Hotkey = $hotkey
            Action = $action
            Order = $order
        }

        $this._menuItems[$menuName].Add($item)
    }

    <#
    .SYNOPSIS
    Add a menu separator

    .PARAMETER menuName
    Menu to add separator to
    #>
    [void] AddSeparator([string]$menuName) {
        $this.AddMenuItem($menuName, "", [char]0, $null, 100)
    }

    [void] AddSeparator([string]$menuName, [int]$order) {
        $this.AddMenuItem($menuName, "", [char]0, $null, $order)
    }

    # === Retrieval ===

    <#
    .SYNOPSIS
    Get all menu items for a specific menu, sorted by order

    .PARAMETER menuName
    Menu name ('Tasks', 'Projects', etc.)

    .OUTPUTS
    Array of menu item hashtables sorted by Order
    #>
    [array] GetMenuItems([string]$menuName) {
        if (-not $this._menuItems.ContainsKey($menuName)) {
            return @()
        }

        # Sort by Order, then by Label
        return $this._menuItems[$menuName] | Sort-Object -Property Order, Label
    }

    <#
    .SYNOPSIS
    Get all registered menus with items

    .OUTPUTS
    Hashtable of menu names to sorted item arrays
    #>
    [hashtable] GetAllMenus() {
        $result = @{}
        foreach ($menuName in $this._menuItems.Keys) {
            $items = $this.GetMenuItems($menuName)
            if ($items.Count -gt 0) {
                $result[$menuName] = $items
            }
        }
        return $result
    }

    <#
    .SYNOPSIS
    Clear all registered menu items (for testing/reset)
    #>
    [void] Clear() {
        foreach ($menuName in $this._menuItems.Keys) {
            $this._menuItems[$menuName].Clear()
        }
    }

    # === Manifest-Based Discovery ===

    <#
    .SYNOPSIS
    Load menu items from manifest file

    .PARAMETER manifestPath
    Path to MenuItems.psd1 manifest file

    .PARAMETER container
    ServiceContainer instance for resolving screen dependencies

    .DESCRIPTION
    Loads menu item definitions from manifest and registers them.
    Uses the DI container to resolve screens when menu items are clicked.
    This avoids parsing all screen files at startup, fixing type resolution issues.
    #>
    [void] LoadFromManifest([string]$manifestPath, [object]$container) {
        if (-not (Test-Path $manifestPath)) {
            if ($global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] MenuRegistry: Manifest not found at '$manifestPath'"
            }
            # Add-Content -Path "/tmp/pmc-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] [MenuRegistry] ERROR: Menu manifest not found at '$manifestPath'"
            return
        }

        try {
            # Load manifest
            $manifest = Import-PowerShellDataFile -Path $manifestPath

            if ($global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] MenuRegistry: Loaded manifest with $($manifest.Count) entries"
            }

            $screensDir = Split-Path $manifestPath -Parent

            # Register each menu item
            foreach ($entry in $manifest.GetEnumerator()) {
                $screenName = $entry.Key
                $config = $entry.Value

                $menu = $config.Menu
                $label = $config.Label
                $hotkey = $config.Hotkey
                $order = $config.Order
                $screenFile = $config.ScreenFile
                $viewMode = $(if ($config.ContainsKey('ViewMode')) { $config.ViewMode } else { $null })  # Optional, for TaskListScreen variants

                # Register screen factory in container if not already registered
                $screenTypeName = $screenFile -replace '\.ps1$', ''
                $screenPath = Join-Path $screensDir $screenFile

                if (-not $container.IsRegistered($screenName)) {
                    # Capture variables in closure for the factory
                    $factoryScreenPath = $screenPath
                    $factoryScreenTypeName = $screenTypeName
                    $factoryViewMode = $viewMode

                    # Register screen factory in container (non-singleton, creates new instance each time)
                    $container.Register($screenName, {
                        param($c)
                        # Dot-source screen file to load class
                        . $factoryScreenPath
                        # CRITICAL FIX: Use -ArgumentList parameter for reliable argument passing
                        # Positional arguments don't work correctly with New-Object in closures
                        if ($factoryViewMode) {
                            return New-Object $factoryScreenTypeName -ArgumentList $c, $factoryViewMode
                        } else {
                            return New-Object $factoryScreenTypeName -ArgumentList $c
                        }
                    }.GetNewClosure(), $false)  # Non-singleton: create new instance each time

                    Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] MenuRegistry: Registered '$label' in '$menu' menu (hotkey=$hotkey order=$order)"
                }

                # Build scriptblock that uses container to resolve screen
                $scriptblock = [scriptblock]::Create(@"
try {
    `$screen = `$global:PmcContainer.Resolve('$screenName')
    if (`$screen) {
        `$global:PmcApp.PushScreen(`$screen)
    } else {
        if (`$global:PmcTuiLogFile) {
            Add-Content -Path `$global:PmcTuiLogFile -Value "[`$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] MenuRegistry: Failed to resolve screen '$screenName'"
        }
    }
} catch {
    if (`$global:PmcTuiLogFile) {
        Add-Content -Path `$global:PmcTuiLogFile -Value "[`$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] MenuRegistry: Error launching '$screenName': `$_"
        Add-Content -Path `$global:PmcTuiLogFile -Value "[`$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] Stack trace: `n`$(`$_.ScriptStackTrace)"
    }
    # Show error to user
    if (`$global:PmcApp -and `$global:PmcApp.CurrentScreen) {
        `$global:PmcApp.CurrentScreen.ShowError("Error launching '$label': `$_")
    }
}
"@)

                # Register the menu item
                $this.AddMenuItem($menu, $label, $hotkey, $scriptblock, $order)

                if ($global:PmcTuiLogFile) {
                    Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] MenuRegistry: Registered '$label' in '$menu' menu (hotkey=$hotkey order=$order)"
                }
            }

        } catch {
            $errorMsg = "MenuRegistry: Error loading manifest: $($_.Exception.Message)"
            if ($global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [ERROR] $errorMsg"
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] Stack trace: $($_.ScriptStackTrace)"
            }
            # Add-Content -Path "/tmp/pmc-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] [MenuRegistry] ERROR: $errorMsg"
        }
    }

    <#
    .SYNOPSIS
    DEPRECATED: Old discovery method - kept for reference but not used

    .DESCRIPTION
    This method is deprecated in favor of LoadFromManifest().
    It caused type resolution issues due to parse-time type checking in PowerShell.
    #>
    [void] DiscoverScreens([string[]]$screenPaths) {
        # DEPRECATED: Use LoadFromManifest() instead
        # This method caused issues:
        # - Slow startup (loads all 40+ screen files)
        # - Type resolution errors (bracket notation requires types at parse time)
        # - Circular dependencies between screens

        if ($global:PmcTuiLogFile) {
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] [WARN] MenuRegistry.DiscoverScreens() is deprecated - use LoadFromManifest() instead"
        }
    }
}

# Export for module usage
if ($MyInvocation.MyCommand.Path) {
    Export-ModuleMember -Variable MenuRegistry
}

﻿# PreferencesService.ps1 - User preferences persistence
#
# M-CFG-2: Preferences Persistence
# Saves and loads user preferences like sort order, view modes, column widths,
# theme settings, and other user-specific configuration.
#
# Usage:
#   $prefs = [PreferencesService]::GetInstance()
#   $prefs.SetPreference('defaultViewMode', 'active')
#   $viewMode = $prefs.GetPreference('defaultViewMode', 'all')
#   $prefs.SavePreferences()

using namespace System
using namespace System.IO
using namespace System.Collections.Generic

Set-StrictMode -Version Latest

<#
.SYNOPSIS
User preferences persistence service

.DESCRIPTION
Singleton service that manages user preferences for PMC TUI.
Preferences are stored in JSON format in the user's config directory.

Supported preferences:
- defaultViewMode: Default task list view (all, active, blocked, etc.)
- defaultSortColumn: Default column to sort by
- defaultSortAscending: Default sort direction
- defaultPriority: Default priority for new tasks
- showCompleted: Show completed tasks by default
- columnWidths: Custom column widths
- theme: Selected theme name
- useSymbols: Use Unicode symbols vs text alternatives
- dateFormat: Preferred date format
- timeFormat: Preferred time format

.EXAMPLE
$prefs = [PreferencesService]::GetInstance()
$prefs.SetPreference('defaultViewMode', 'active')
$prefs.SavePreferences()
#>
class PreferencesService {
    # Singleton instance
    static [PreferencesService]$_instance = $null

    # Preferences storage
    hidden [hashtable]$_preferences = @{}

    # File path for preferences
    hidden [string]$_preferencesPath = ""

    # Dirty flag for unsaved changes
    hidden [bool]$_isDirty = $false

    # Default preferences
    hidden [hashtable]$_defaults = @{
        # View preferences
        defaultViewMode = 'active'
        defaultSortColumn = 'due'
        defaultSortAscending = $true
        showCompleted = $false

        # Task defaults
        defaultPriority = 'medium'
        defaultStatus = 'pending'

        # UI preferences
        useSymbols = $true
        dateFormat = 'yyyy-MM-dd'
        timeFormat = 'HH:mm'
        theme = 'default'

        # Column widths (null means auto)
        columnWidths = @{
            title = $null
            due = 10
            priority = 10
            status = 12
            project = 20
            tags = 15
        }

        # Performance preferences
        enableVirtualScrolling = $true
        maxVisibleRows = 1000
        searchDebounceMs = 150
        cacheRefreshIntervalMs = 500
        showEditorStatistics = $false

        # Accessibility
        screenReaderMode = $false
        highContrastMode = $false
        largeFont = $false

        # Auto-save preferences
        autoSaveEnabled = $true
        autoSaveIntervalSeconds = 300
    }

    # === Singleton Pattern ===

    <#
    .SYNOPSIS
    Get the singleton instance of PreferencesService

    .OUTPUTS
    PreferencesService singleton instance
    #>
    static [PreferencesService] GetInstance() {
        if ($null -eq [PreferencesService]::_instance) {
            [PreferencesService]::_instance = [PreferencesService]::new()
        }
        return [PreferencesService]::_instance
    }

    # Private constructor for singleton
    hidden PreferencesService() {
        $this._InitializePreferencesPath()
        $this._LoadPreferences()
    }

    # === Initialization ===

    <#
    .SYNOPSIS
    Initialize preferences file path

    .DESCRIPTION
    Determines where to store preferences based on environment and platform
    #>
    hidden [void] _InitializePreferencesPath() {
        # PORTABILITY: Use local directory for self-contained deployment
        # Priority: ENV > Get-PmcConfigPath > Local .pmc-data directory
        $configPath = $(if ($env:PMC_CONFIG_PATH) {
            # Explicit override via environment variable
            $env:PMC_CONFIG_PATH
        } elseif (Get-Command Get-PmcConfigPath -ErrorAction SilentlyContinue) {
            # Use PMC module's config path if available
            Get-PmcConfigPath
        } else {
            # Default: Use .pmc-data directory relative to module root (self-contained)
            $moduleRoot = Split-Path (Split-Path $PSScriptRoot -Parent) -Parent
            Join-Path $moduleRoot ".pmc-data"
        })

        # Ensure directory exists
        if (-not (Test-Path $configPath)) {
            New-Item -ItemType Directory -Path $configPath -Force | Out-Null
        }

        $this._preferencesPath = Join-Path $configPath "preferences.json"

        if ((Get-Variable -Name 'PmcTuiLogFile' -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcTuiLogFile) {
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] PreferencesService: Preferences path: $($this._preferencesPath)"
        }
    }

    <#
    .SYNOPSIS
    Load preferences from file

    .DESCRIPTION
    Loads preferences from JSON file, validates them, and merges with defaults
    #>
    hidden [void] _LoadPreferences() {
        try {
            if (Test-Path $this._preferencesPath) {
                $json = Get-Content -Path $this._preferencesPath -Raw
                $loaded = $json | ConvertFrom-Json -AsHashtable

                if ((Get-Variable -Name 'PmcTuiLogFile' -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcTuiLogFile) {
                    Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] PreferencesService: Loaded preferences from file"
                }

                # M-CFG-6: Config Validation - validate loaded preferences
                $validated = $this._ValidatePreferences($loaded)

                # Merge with defaults (defaults for missing keys)
                $this._preferences = $this._MergeWithDefaults($validated)
                $this._isDirty = $false
            } else {
                if ((Get-Variable -Name 'PmcTuiLogFile' -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcTuiLogFile) {
                    Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] PreferencesService: No preferences file found, using defaults"
                }

                # Use defaults
                $this._preferences = $this._defaults.Clone()
                $this._isDirty = $true  # Mark dirty to save defaults
            }
        } catch {
            if ((Get-Variable -Name 'PmcTuiLogFile' -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] PreferencesService: Error loading preferences: $_"
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] PreferencesService: Using defaults"
            }

            # On error, use defaults
            $this._preferences = $this._defaults.Clone()
            $this._isDirty = $true
        }
    }

    # === Validation ===

    <#
    .SYNOPSIS
    Validate preferences object

    .PARAMETER prefs
    Preferences hashtable to validate

    .OUTPUTS
    Validated preferences hashtable

    .DESCRIPTION
    M-CFG-6: Config Validation
    Validates each preference value, removes invalid ones, uses defaults for invalid values
    #>
    hidden [hashtable] _ValidatePreferences([hashtable]$prefs) {
        $validated = @{}

        # Validate each preference
        foreach ($key in $prefs.Keys) {
            $value = $prefs[$key]

            switch ($key) {
                'defaultViewMode' {
                    $validModes = @('all', 'active', 'completed', 'blocked', 'overdue', 'today', 'tomorrow', 'week', 'nextactions', 'noduedate', 'month', 'agenda', 'upcoming')
                    if ($value -in $validModes) {
                        $validated[$key] = $value
                    }
                }
                'defaultSortColumn' {
                    $validColumns = @('title', 'due', 'priority', 'status', 'project', 'created', 'modified')
                    if ($value -in $validColumns) {
                        $validated[$key] = $value
                    }
                }
                'defaultPriority' {
                    $validPriorities = @('high', 'medium', 'low', 'none')
                    if ($value -in $validPriorities) {
                        $validated[$key] = $value
                    }
                }
                'defaultStatus' {
                    $validStatuses = @('pending', 'active', 'completed', 'blocked', 'cancelled', 'deferred')
                    if ($value -in $validStatuses) {
                        $validated[$key] = $value
                    }
                }
                'defaultSortAscending' {
                    if ($value -is [bool]) {
                        $validated[$key] = $value
                    }
                }
                'showCompleted' {
                    if ($value -is [bool]) {
                        $validated[$key] = $value
                    }
                }
                'useSymbols' {
                    if ($value -is [bool]) {
                        $validated[$key] = $value
                    }
                }
                'enableVirtualScrolling' {
                    if ($value -is [bool]) {
                        $validated[$key] = $value
                    }
                }
                'maxVisibleRows' {
                    if ($value -is [int] -and $value -gt 0 -and $value -le 10000) {
                        $validated[$key] = $value
                    }
                }
                'searchDebounceMs' {
                    if ($value -is [int] -and $value -ge 0 -and $value -le 5000) {
                        $validated[$key] = $value
                    }
                }
                'cacheRefreshIntervalMs' {
                    if ($value -is [int] -and $value -ge 0 -and $value -le 10000) {
                        $validated[$key] = $value
                    }
                }
                'showEditorStatistics' {
                    if ($value -is [bool]) {
                        $validated[$key] = $value
                    }
                }
                'autoSaveIntervalSeconds' {
                    if ($value -is [int] -and $value -ge 30 -and $value -le 3600) {
                        $validated[$key] = $value
                    }
                }
                default {
                    # For unknown keys or complex types (columnWidths, etc), just pass through
                    $validated[$key] = $value
                }
            }
        }

        return $validated
    }

    <#
    .SYNOPSIS
    Merge preferences with defaults

    .PARAMETER prefs
    User preferences hashtable

    .OUTPUTS
    Merged hashtable with defaults filled in for missing keys
    #>
    hidden [hashtable] _MergeWithDefaults([hashtable]$prefs) {
        $merged = $this._defaults.Clone()

        foreach ($key in $prefs.Keys) {
            $merged[$key] = $prefs[$key]
        }

        return $merged
    }

    # === Public API ===

    <#
    .SYNOPSIS
    Get a preference value

    .PARAMETER key
    Preference key

    .PARAMETER defaultValue
    Default value if key doesn't exist (optional, uses system default if not provided)

    .OUTPUTS
    Preference value or default
    #>
    [object] GetPreference([string]$key) {
        if ($this._preferences.ContainsKey($key)) {
            return $this._preferences[$key]
        }

        if ($this._defaults.ContainsKey($key)) {
            return $this._defaults[$key]
        }

        return $null
    }

    [object] GetPreference([string]$key, [object]$defaultValue) {
        if ($this._preferences.ContainsKey($key)) {
            return $this._preferences[$key]
        }

        return $defaultValue
    }

    <#
    .SYNOPSIS
    Set a preference value

    .PARAMETER key
    Preference key

    .PARAMETER value
    Preference value

    .DESCRIPTION
    Sets a preference and marks preferences as dirty (needing save)
    #>
    [void] SetPreference([string]$key, [object]$value) {
        $this._preferences[$key] = $value
        $this._isDirty = $true

        if ((Get-Variable -Name 'PmcTuiLogFile' -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcTuiLogFile) {
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] PreferencesService: Set preference '$key' = '$value'"
        }
    }

    <#
    .SYNOPSIS
    Get all preferences

    .OUTPUTS
    Hashtable of all current preferences
    #>
    [hashtable] GetAllPreferences() {
        return $this._preferences.Clone()
    }

    <#
    .SYNOPSIS
    Reset all preferences to defaults

    .DESCRIPTION
    M-CFG-4: Reset to Defaults functionality
    #>
    [void] ResetToDefaults() {
        $this._preferences = $this._defaults.Clone()
        $this._isDirty = $true

        if ((Get-Variable -Name 'PmcTuiLogFile' -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcTuiLogFile) {
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] PreferencesService: Reset all preferences to defaults"
        }
    }

    <#
    .SYNOPSIS
    Reset a specific preference to its default

    .PARAMETER key
    Preference key to reset

    .DESCRIPTION
    M-CFG-4: Reset to Defaults functionality
    #>
    [void] ResetPreference([string]$key) {
        if ($this._defaults.ContainsKey($key)) {
            $this._preferences[$key] = $this._defaults[$key]
            $this._isDirty = $true

            if ((Get-Variable -Name 'PmcTuiLogFile' -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] PreferencesService: Reset preference '$key' to default"
            }
        }
    }

    <#
    .SYNOPSIS
    Save preferences to file

    .DESCRIPTION
    Saves current preferences to JSON file
    #>
    [void] SavePreferences() {
        try {
            # Convert to JSON with nice formatting
            $json = $this._preferences | ConvertTo-Json -Depth 10

            # Save to file
            $json | Set-Content -Path $this._preferencesPath -Force

            $this._isDirty = $false

            if ((Get-Variable -Name 'PmcTuiLogFile' -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] PreferencesService: Saved preferences to $($this._preferencesPath)"
            }
        } catch {
            if ((Get-Variable -Name 'PmcTuiLogFile' -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcTuiLogFile) {
                Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss.fff')] PreferencesService: Error saving preferences: $_"
            }
            throw
        }
    }

    <#
    .SYNOPSIS
    Check if preferences have unsaved changes

    .OUTPUTS
    Boolean indicating if preferences are dirty
    #>
    [bool] IsDirty() {
        return $this._isDirty
    }

    <#
    .SYNOPSIS
    Get the preferences file path

    .OUTPUTS
    String path to preferences file
    #>
    [string] GetPreferencesPath() {
        return $this._preferencesPath
    }
}

# Export for module usage
if ($MyInvocation.InvocationName -ne '.') {
    try {
        Export-ModuleMember -Variable PreferencesService
    } catch {
        # Ignore Export-ModuleMember errors when not in a module context
    }
}

﻿# TaskStore.ps1 - Centralized Observable Data Store for PMC TUI
#
# SINGLETON pattern - provides centralized data access with:
# - Load data once from Get-PmcAllData
# - In-memory caching (tasks, projects, time logs)
# - CRUD operations (Add, Update, Delete, Get)
# - Event-driven updates (OnTaskAdded, OnTaskUpdated, OnTaskDeleted, etc.)
# - Auto-persistence via Set-PmcAllData
# - Thread-safe operations
# - Validation and error handling
# - Rollback on save failure
#
# Usage:
#   $store = [TaskStore]::GetInstance()
#
#   # CRUD operations
#   $task = $store.GetTask($id)
#   $allTasks = $store.GetAllTasks()
#   $store.AddTask($task)        # Fires OnTaskAdded, persists
#   $store.UpdateTask($id, $changes)  # Fires OnTaskUpdated, persists
#   $store.DeleteTask($id)       # Fires OnTaskDeleted, persists
#
#   # Subscribe to changes
#   $store.OnTaskAdded = { param($task) $this.RefreshUI() }
#   $store.OnTaskUpdated = { param($task) $this.RefreshUI() }
#   $store.OnTaskDeleted = { param($id) $this.RefreshUI() }
#
# The store automatically persists changes to disk and provides
# rollback capabilities if persistence fails.

using namespace System
using namespace System.Collections.Generic
using namespace System.Threading

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Centralized observable data store for PMC data with event-driven updates

.DESCRIPTION
TaskStore provides:
- Singleton instance for centralized data access
- In-memory caching of tasks, projects, time logs
- CRUD operations with automatic persistence
- Event callbacks for data changes (OnTaskAdded, OnTaskUpdated, etc.)
- Validation before persistence
- Rollback on save failure
- Thread-safe operations with locking
- Query methods (filtering, searching)
- Batch operations for performance

.EXAMPLE
$store = [TaskStore]::GetInstance()
$store.AddTask(@{ text='Buy milk'; project='personal'; priority=3 })
#>
class TaskStore {
    # === Singleton Instance ===
    static hidden [TaskStore]$_instance = $null
    static hidden [object]$_instanceLock = [object]::new()

    # === Data Storage ===
    hidden [hashtable]$_data = @{
        tasks    = @()
        projects = @()
        timelogs = @()
        settings = @{}
        metadata = @{
            lastLoaded = $null
            lastSaved  = $null
            version    = "1.0"
        }
    }

    # === Backup for Rollback ===
    hidden [hashtable]$_dataBackup = $null

    # === Thread Safety ===
    hidden [object]$_dataLock = [object]::new()

    # === Event Callbacks - Tasks ===
    [scriptblock]$OnTaskAdded = {}
    [scriptblock]$OnTaskUpdated = {}
    [scriptblock]$OnTaskDeleted = {}
    [scriptblock]$OnTasksChanged = {}  # Fired after any task change

    # === Event Callbacks - Projects ===
    [scriptblock]$OnProjectAdded = {}
    [scriptblock]$OnProjectUpdated = {}
    [scriptblock]$OnProjectDeleted = {}
    [scriptblock]$OnProjectsChanged = {}

    # === Event Callbacks - Time Logs ===
    [scriptblock]$OnTimeLogAdded = {}
    [scriptblock]$OnTimeLogUpdated = {}
    [scriptblock]$OnTimeLogDeleted = {}
    [scriptblock]$OnTimeLogsChanged = {}

    # === Event Callbacks - Global ===
    [scriptblock]$OnDataChanged = {}   # Fired after any data change
    [scriptblock]$OnLoadError = {}     # Fired when load fails
    [scriptblock]$OnSaveError = {}     # Fired when save fails

    # === State Flags ===
    [bool]$IsLoaded = $false
    [bool]$IsSaving = $false
    [string]$LastError = ""
    [bool]$AutoSave = $true  # CRITICAL: Enable by default to prevent data loss
    [bool]$HasPendingChanges = $false  # True when changes need to be saved

    # === Cached Statistics ===
    hidden [hashtable]$_cachedStats = $null
    hidden [bool]$_statsNeedUpdate = $true

    # === Validation Rules ===
    hidden [hashtable]$_validationRules = @{
        task    = @{
            required = @('text')
            types    = @{
                id        = 'string'
                text      = 'string'
                details   = 'string'
                project   = 'string'
                priority  = 'int'
                status    = 'string'
                due       = 'datetime'
                tags      = 'array'
                completed = 'bool'
                created   = 'datetime'
                parent_id = 'string'
            }
        }
        project = @{
            required = @('name')
            types    = @{
                id              = 'string'
                name            = 'string'
                description     = 'string'
                created         = 'datetime'
                status          = 'string'
                tags            = 'array'
                ID1             = 'string'
                ID2             = 'string'
                ProjFolder      = 'string'
                CAAName         = 'string'
                RequestName     = 'string'
                T2020           = 'string'
                AssignedDate    = 'datetime'
                DueDate         = 'datetime'
                BFDate          = 'datetime'
                RequestDate     = 'datetime'
                AuditType       = 'string'
                AuditorName     = 'string'
                AuditorPhone    = 'string'
                AuditorTL       = 'string'
                AuditorTLPhone  = 'string'
                AuditCase       = 'string'
                CASCase         = 'string'
                AuditStartDate  = 'datetime'
                TPName          = 'string'
                TPNum           = 'string'
                Address         = 'string'
                City            = 'string'
                Province        = 'string'
                PostalCode      = 'string'
                Country         = 'string'
                AuditPeriodFrom = 'datetime'
                AuditPeriodTo   = 'datetime'
            }
        }
        timelog = @{
            required = @('date', 'minutes')
            types    = @{
                date     = 'datetime'
                task     = 'string'
                project  = 'string'
                timecode = 'string'
                id1      = 'string'
                id2      = 'string'
                minutes  = 'int'
                notes    = 'string'
                created  = 'datetime'
            }
        }
    }

    # === Constructor (Private) ===
    TaskStore() {
        # Private constructor for singleton
        $this._InitializeStore()
    }

    # === Singleton Pattern ===

    <#
    .SYNOPSIS
    Get the singleton instance of TaskStore

    .OUTPUTS
    TaskStore singleton instance
    #>
    static [TaskStore] GetInstance() {
        if ($null -eq [TaskStore]::_instance) {
            [Monitor]::Enter([TaskStore]::_instanceLock)
            try {
                if ($null -eq [TaskStore]::_instance) {
                    [TaskStore]::_instance = [TaskStore]::new()
                    if (-not [TaskStore]::_instance.LoadData()) {
                        # FAIL FAST: Do not allow app to start with empty/corrupted state.
                        $err = [TaskStore]::_instance.LastError
                        [TaskStore]::_instance = $null # Reset instance so retry is possible
                        throw "CRITICAL: TaskStore failed to load data. Aborting to prevent data loss. Error: $err"
                    }
                }
            }
            finally {
                [Monitor]::Exit([TaskStore]::_instanceLock)
            }
        }

        return [TaskStore]::_instance
    }

    <#
    .SYNOPSIS
    Reset the singleton instance (for testing)
    #>
    static [void] ResetInstance() {
        [Monitor]::Enter([TaskStore]::_instanceLock)
        try {
            [TaskStore]::_instance = $null
        }
        finally {
            [Monitor]::Exit([TaskStore]::_instanceLock)
        }
    }

    # === Data Loading ===

    <#
    .SYNOPSIS
    Initialize the store
    #>
    hidden [void] _InitializeStore() {
        $this._data.tasks = [System.Collections.ArrayList]::new()
        $this._data.projects = [System.Collections.ArrayList]::new()
        $this._data.timelogs = [System.Collections.ArrayList]::new()
        $this.IsLoaded = $false
    }

    <#
    .SYNOPSIS
    Load data from Get-PmcAllData

    .OUTPUTS
    True if load succeeded, False otherwise
    #>
    [bool] LoadData() {
        # Write-PmcTuiLog "TaskStore.LoadData: Starting" "DEBUG"
        [Monitor]::Enter($this._dataLock)
        try {
            try {
                # FIX: Call Get-PmcData via explicit module invocation
                # Write-PmcTuiLog "TaskStore.LoadData: Calling Get-PmcData" "DEBUG"
                $pmcModule = Get-Module -Name 'Pmc.Strict'
                $pmcData = & $pmcModule { Get-PmcData }
                # Write-PmcTuiLog "TaskStore.LoadData: Get-PmcData returned - tasks=$($pmcData.tasks.Count) projects=$($pmcData.projects.Count) timelogs=$($pmcData.timelogs.Count)" "DEBUG"

                if ($null -eq $pmcData) {
                    $this.LastError = "Get-PmcData returned null"
                    # Write-PmcTuiLog "TaskStore.LoadData: ERROR - Get-PmcData returned null" "ERROR"
                    $this._InvokeCallback($this.OnLoadError, $this.LastError)
                    return $false
                }

                # Extract data sections
                if ($pmcData.tasks) {
                    $this._data.tasks = [System.Collections.ArrayList]::new()
                    foreach ($task in $pmcData.tasks) {
                        # Convert PSCustomObject to hashtable if needed
                        if ($task -isnot [hashtable]) {
                            $taskHash = @{}
                            foreach ($prop in $task.PSObject.Properties) {
                                $taskHash[$prop.Name] = $prop.Value
                            }
                            [void]$this._data.tasks.Add($taskHash)
                        }
                        else {
                            [void]$this._data.tasks.Add($task)
                        }
                    }
                    # Write-PmcTuiLog "TaskStore.LoadData: Loaded $($this._data.tasks.Count) tasks" "DEBUG"
                }
                else {
                    $this._data.tasks = [System.Collections.ArrayList]::new()
                    # Write-PmcTuiLog "TaskStore.LoadData: No tasks in data, initialized empty list" "DEBUG"
                }

                if ($pmcData.projects) {
                    $this._data.projects = [System.Collections.ArrayList]::new()
                    foreach ($project in $pmcData.projects) {
                        # Convert PSCustomObject to hashtable if needed
                        if ($project -isnot [hashtable]) {
                            $projectHash = @{}
                            foreach ($prop in $project.PSObject.Properties) {
                                $projectHash[$prop.Name] = $prop.Value
                            }
                            [void]$this._data.projects.Add($projectHash)
                        }
                        else {
                            [void]$this._data.projects.Add($project)
                        }
                    }
                    # Write-PmcTuiLog "TaskStore.LoadData: Loaded $($this._data.projects.Count) projects" "DEBUG"
                    if ($this._data.projects.Count -gt 0) {
                        $firstProj = $this._data.projects[0]
                        # Write-PmcTuiLog "TaskStore.LoadData: First project name='$(Get-SafeProperty $firstProj 'name')' ID1='$(Get-SafeProperty $firstProj 'ID1')'" "DEBUG"
                    }
                }
                else {
                    $this._data.projects = [System.Collections.ArrayList]::new()
                    # Write-PmcTuiLog "TaskStore.LoadData: No projects in data, initialized empty list" "DEBUG"
                }

                if ($pmcData.timelogs) {
                    $this._data.timelogs = [System.Collections.ArrayList]::new()
                    foreach ($log in $pmcData.timelogs) {
                        # Convert PSCustomObject to hashtable if needed
                        if ($log -isnot [hashtable]) {
                            $logHash = @{}
                            foreach ($prop in $log.PSObject.Properties) {
                                $logHash[$prop.Name] = $prop.Value
                            }
                            [void]$this._data.timelogs.Add($logHash)
                        }
                        else {
                            [void]$this._data.timelogs.Add($log)
                        }
                    }
                }
                else {
                    $this._data.timelogs = [System.Collections.ArrayList]::new()
                }

                if ($pmcData.settings) {
                    $this._data.settings = $pmcData.settings
                }
                else {
                    $this._data.settings = @{}
                }

                $this._data.metadata.lastLoaded = Get-Date
                $this.IsLoaded = $true
                $this.LastError = ""

                return $true
            }
            catch {
                $this.LastError = "Failed to load data: $($_.Exception.Message)"
                # Write-PmcTuiLog "TaskStore.LoadData: EXCEPTION: $($_.Exception.Message)" "ERROR"
                # Write-PmcTuiLog "TaskStore.LoadData: Stack trace: $($_.ScriptStackTrace)" "ERROR"
                $this._InvokeCallback($this.OnLoadError, $this.LastError)
                return $false
            }
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    <#
    .SYNOPSIS
    Reload data from disk (discards in-memory changes)

    .OUTPUTS
    True if reload succeeded, False otherwise
    #>
    [bool] ReloadData() {
        return $this.LoadData()
    }

    # === Data Persistence ===

    <#
    .SYNOPSIS
    Save all data to disk via Set-PmcAllData

    .OUTPUTS
    True if save succeeded, False otherwise
    #>
    [bool] SaveData() {
        # CRITICAL FIX #3: Acquire lock BEFORE checking IsSaving to prevent race condition
        [Monitor]::Enter($this._dataLock)
        try {
            # Check IsSaving INSIDE the lock to ensure thread safety
            if ($this.IsSaving) {
                $this.LastError = "Save already in progress"
                # Write-PmcTuiLog "TaskStore.SaveData: Already saving, returning false" "ERROR"
                return $false
            }

            $this.IsSaving = $true
            # Write-PmcTuiLog "TaskStore.SaveData: START - tasks=$($this._data.tasks.Count) projects=$($this._data.projects.Count) timelogs=$($this._data.timelogs.Count)" "INFO"

            try {
                # PHASE 1: Create in-memory backup for rollback
                # Write-PmcTuiLog "TaskStore.SaveData: Creating in-memory backup" "DEBUG"
                $this._CreateBackup()

                # PHASE 2: Create persistent timestamped backup before destructive operation
                # Write-PmcTuiLog "TaskStore.SaveData: Creating persistent backup" "DEBUG"
                $this._CreatePersistentBackup()

                # PHASE 3: Build data structure for Save-PmcData
                # Write-PmcTuiLog "TaskStore.SaveData: Building data structure" "DEBUG"
                $dataToSave = @{
                    tasks    = $this._data.tasks.ToArray()
                    projects = $this._data.projects.ToArray()
                    timelogs = $this._data.timelogs.ToArray()
                    settings = $this._data.settings
                }

                # Write-PmcTuiLog "TaskStore.SaveData: Data structure built - tasks=$($dataToSave.tasks.Count) projects=$($dataToSave.projects.Count) timelogs=$($dataToSave.timelogs.Count)" "INFO"

                # Log first task/project for verification
                if ($dataToSave.tasks.Count -gt 0) {
                    $firstTask = $dataToSave.tasks[0]
                    # Write-PmcTuiLog "TaskStore.SaveData: First task - text='$(Get-SafeProperty $firstTask 'text')' id='$(Get-SafeProperty $firstTask 'id')'" "DEBUG"
                }
                if ($dataToSave.projects.Count -gt 0) {
                    $firstProject = $dataToSave.projects[0]
                    # Write-PmcTuiLog "TaskStore.SaveData: First project - name='$(Get-SafeProperty $firstProject 'name')' ID1='$(Get-SafeProperty $firstProject 'ID1')'" "DEBUG"
                }

                # PHASE 4: Call Save-PmcData via explicit module invocation
                # Write-PmcTuiLog "TaskStore.SaveData: Calling Save-PmcData" "INFO"
                $pmcModule = Get-Module -Name 'Pmc.Strict'
                if ($null -eq $pmcModule) {
                    $this.LastError = "Pmc.Strict module not loaded - cannot save"
                    # Write-PmcTuiLog "TaskStore.SaveData: FATAL - Pmc.Strict module not found" "ERROR"
                    return $false
                }

                & $pmcModule { param($data) Save-PmcData -Data $data } $dataToSave
                # Write-PmcTuiLog "TaskStore.SaveData: Save-PmcData completed successfully" "INFO"

                # PHASE 5: Verify save success
                # Write-PmcTuiLog "TaskStore.SaveData: Verifying save" "DEBUG"
                $this._VerifySave($dataToSave)

                # PHASE 6: Update metadata
                $this._data.metadata.lastSaved = Get-Date
                $this.LastError = ""
                $this.HasPendingChanges = $false

                # Clear in-memory backup on successful save
                $this._dataBackup = $null

                # Write-PmcTuiLog "TaskStore.SaveData: SUCCESS - Data saved and verified" "INFO"
                return $true
            }
            catch {
                $this.LastError = "Failed to save data: $($_.Exception.Message)"
                # Write-PmcTuiLog "TaskStore.SaveData: EXCEPTION: $($_.Exception.Message)" "ERROR"
                # Write-PmcTuiLog "TaskStore.SaveData: Exception type: $($_.Exception.GetType().FullName)" "ERROR"
                # Write-PmcTuiLog "TaskStore.SaveData: Stack trace: $($_.ScriptStackTrace)" "ERROR"

                # Log actionable error guidance
                # Write-PmcTuiLog "TaskStore.SaveData: ERROR GUIDANCE:" "ERROR"
                # Write-PmcTuiLog "  1. Check log for detailed error above" "ERROR"
                # Write-PmcTuiLog "  2. Verify disk space available" "ERROR"
                # Write-PmcTuiLog "  3. Check file permissions on tasks.json" "ERROR"
                # Write-PmcTuiLog "  4. Look for backup files: tasks.json.backup.*" "ERROR"

                $this._InvokeCallback($this.OnSaveError, $this.LastError)

                # Rollback to in-memory backup
                # Write-PmcTuiLog "TaskStore.SaveData: Rolling back to in-memory backup" "INFO"
                $this._Rollback()

                return $false
            }
        }
        finally {
            $this.IsSaving = $false
            [Monitor]::Exit($this._dataLock)
            # Write-PmcTuiLog "TaskStore.SaveData: END" "DEBUG"
        }
    }

    <#
    .SYNOPSIS
    Create backup of current data for rollback
    #>
    hidden [void] _CreateBackup() {
        # Create shallow copy of arrays - deep copy not needed for backup/rollback
        $tasksCopy = [System.Collections.ArrayList]::new()
        foreach ($task in $this._data.tasks) { $tasksCopy.Add($task) | Out-Null }

        $projectsCopy = [System.Collections.ArrayList]::new()
        foreach ($project in $this._data.projects) { $projectsCopy.Add($project) | Out-Null }

        $timelogsCopy = [System.Collections.ArrayList]::new()
        foreach ($log in $this._data.timelogs) { $timelogsCopy.Add($log) | Out-Null }

        $this._dataBackup = @{
            tasks    = $tasksCopy
            projects = $projectsCopy
            timelogs = $timelogsCopy
            settings = $this._data.settings
        }
    }

    <#
    .SYNOPSIS
    Rollback to backup data
    #>
    hidden [void] _Rollback() {
        if ($null -ne $this._dataBackup) {
            $this._data.tasks = $this._dataBackup.tasks
            $this._data.projects = $this._dataBackup.projects
            $this._data.timelogs = $this._dataBackup.timelogs
            $this._data.settings = $this._dataBackup.settings
            $this._dataBackup = $null
        }
    }

    <#
    .SYNOPSIS
    Create persistent timestamped backup before save (separate from in-memory backup)

    .DESCRIPTION
    Creates a backup file like tasks.json.backup.20231215-143527
    Keeps last 5 backups, deletes older ones
    This protects against corruption from save failures
    #>
    hidden [void] _CreatePersistentBackup() {
        try {
            # Get tasks.json path
            $pmcModule = Get-Module -Name 'Pmc.Strict'
            if ($null -eq $pmcModule) {
                # Write-PmcTuiLog "_CreatePersistentBackup: Pmc.Strict module not found, skipping backup" "WARNING"
                return
            }

            $tasksFile = & $pmcModule { Get-PmcTaskFilePath }
            if (-not (Test-Path $tasksFile)) {
                # Write-PmcTuiLog "_CreatePersistentBackup: tasks.json does not exist yet, skipping backup" "DEBUG"
                return
            }

            # Create timestamped backup
            $timestamp = Get-Date -Format 'yyyyMMdd-HHmmss'
            $backupFile = "$tasksFile.backup.$timestamp"

            Copy-Item -Path $tasksFile -Destination $backupFile -Force
            # Write-PmcTuiLog "_CreatePersistentBackup: Created backup at $backupFile" "INFO"

            # Rotate backups - keep only last 5
            $backupDir = Split-Path $tasksFile -Parent
            $backupPattern = "$(Split-Path $tasksFile -Leaf).backup.*"
            $backups = Get-ChildItem -Path $backupDir -Filter $backupPattern | 
                Sort-Object LastWriteTime -Descending

            if ($backups.Count -gt 5) {
                $toDelete = $backups | Select-Object -Skip 5
                foreach ($old in $toDelete) {
                    Remove-Item $old.FullName -Force
                    # Write-PmcTuiLog "_CreatePersistentBackup: Deleted old backup $($old.Name)" "DEBUG"
                }
            }
        }
        catch {
            # Backup creation is non-critical, log but continue
            # Write-PmcTuiLog "_CreatePersistentBackup: Failed to create backup: $_" "WARNING"
        }
    }

    <#
    .SYNOPSIS
    Verify save succeeded by re-loading data from disk

    .DESCRIPTION
    Attempts to load tasks.json and verify task count matches
    This catches save failures early
    #>
    hidden [void] _VerifySave([hashtable]$savedData) {
        try {
            $pmcModule = Get-Module -Name 'Pmc.Strict'
            if ($null -eq $pmcModule) {
                # Write-PmcTuiLog "_VerifySave: Pmc.Strict module not found, cannot verify" "WARNING"
                return
            }

            # Reload data from disk
            $reloadedData = & $pmcModule { Get-PmcData }

            if ($null -eq $reloadedData) {
                # Write-PmcTuiLog "_VerifySave: Reloaded data is null!" "ERROR"
                throw "Save verification failed - reloaded data is null"
            }

            # Compare counts
            $savedTasks = $savedData.tasks.Count
            $reloadedTasks = if ($reloadedData.tasks) { @($reloadedData.tasks).Count } else { 0 }

            $savedProjects = $savedData.projects.Count  
            $reloadedProjects = if ($reloadedData.projects) { @($reloadedData.projects).Count } else { 0 }

            if ($savedTasks -ne $reloadedTasks) {
                # Write-PmcTuiLog "_VerifySave: Task count mismatch! Saved=$savedTasks Reloaded=$reloadedTasks" "ERROR"
                throw "Save verification failed - task count mismatch (saved:$savedTasks vs reloaded:$reloadedTasks)"
            }

            if ($savedProjects -ne $reloadedProjects) {
                # Write-PmcTuiLog "_VerifySave: Project count mismatch! Saved=$savedProjects Reloaded=$reloadedProjects" "ERROR"
                throw "Save verification failed - project count mismatch (saved:$savedProjects vs reloaded:$reloadedProjects)"
            }

            # Write-PmcTuiLog "_VerifySave: Verification passed - tasks=$reloadedTasks projects=$reloadedProjects" "DEBUG"
        }
        catch {
            # Write-PmcTuiLog "_VerifySave: Verification failed: $_" "ERROR"
            throw
        }
    }


