    <#
    .SYNOPSIS
    Add a new project

    .PARAMETER project
    Project hashtable (must include 'name' field minimum)

    .OUTPUTS
    True if add succeeded, False otherwise
    #>
    [bool] AddProject([hashtable]$project) {
        # Write-PmcTuiLog "TaskStore.AddProject: START - name='$($project.name)'" "DEBUG"
        [Monitor]::Enter($this._dataLock)
        try {
            # Validate project
            # Write-PmcTuiLog "TaskStore.AddProject: Calling _ValidateEntity..." "DEBUG"
            $validationErrors = $this._ValidateEntity($project, 'project')
            if ($validationErrors.Count -gt 0) {
                $errorMsg = "Project validation failed: $($validationErrors -join ', ')"
                $this.LastError = $errorMsg
                # Write-PmcTuiLog "TaskStore.AddProject: FAILED - $errorMsg" "ERROR"
                return $false
            }
            # Write-PmcTuiLog "TaskStore.AddProject: Validation passed" "DEBUG"

            # Check for duplicate name
            $existing = $this._data.projects | Where-Object { $_.name -eq $project.name }
            if ($existing) {
                $errorMsg = "Project already exists: $($project.name)"
                $this.LastError = $errorMsg
                # Write-PmcTuiLog "TaskStore.AddProject: FAILED - $errorMsg" "ERROR"
                return $false
            }
            # Write-PmcTuiLog "TaskStore.AddProject: No duplicate found" "DEBUG"

            # Create backup BEFORE any modifications
            $this._CreateBackup()

            # Add timestamps
            $now = Get-Date
            if (-not $project.ContainsKey('created')) {
                $project.created = $now
            }
            $project.modified = $now

            # Add to collection
            # Write-PmcTuiLog "TaskStore.AddProject: Adding to collection..." "DEBUG"
            $this._data.projects.Add($project)

            # Mark pending changes
            $this.HasPendingChanges = $true

            # Persist only if AutoSave is enabled
            if ($this.AutoSave) {
                # Write-PmcTuiLog "TaskStore.AddProject: AutoSave enabled, calling SaveData..." "DEBUG"
                if (-not $this.SaveData()) {
                    # Write-PmcTuiLog "TaskStore.AddProject: FAILED - SaveData returned false" "ERROR"
                    return $false
                }
                # Write-PmcTuiLog "TaskStore.AddProject: SaveData succeeded" "DEBUG"
            }

            # Fire events
            $this._InvokeCallback($this.OnProjectAdded, $project)
            $this._InvokeCallback($this.OnProjectsChanged, $this._data.projects.ToArray())
            $this._InvokeCallback($this.OnDataChanged, $null)

            # Write-PmcTuiLog "TaskStore.AddProject: SUCCESS" "DEBUG"
            return $true
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    <#
    .SYNOPSIS
    Update an existing project

    .PARAMETER name
    Project name

    .PARAMETER changes
    Hashtable of fields to update

    .OUTPUTS
    True if update succeeded, False otherwise
    #>
    [bool] UpdateProject([string]$name, [hashtable]$changes) {
        [Monitor]::Enter($this._dataLock)
        try {
            # Write-PmcTuiLog "UpdateProject: START - name='$name' changes=$($changes.Count)" "DEBUG"

            # Find project
            $project = $this._data.projects | Where-Object { $_.name -eq $name } | Select-Object -First 1

            if ($null -eq $project) {
                $this.LastError = "Project not found: $name"
                # Write-PmcTuiLog "UpdateProject: Project not found: $name" "ERROR"
                return $false
            }

            # Write-PmcTuiLog "UpdateProject: Found project, type=$($project.GetType().Name) BEFORE ID1='$(Get-SafeProperty $project 'ID1')'" "DEBUG"

            # Create backup BEFORE any modifications
            $this._CreateBackup()

            # Apply changes - handle both hashtable and PSCustomObject
            foreach ($key in $changes.Keys) {
                if ($key -eq 'ID1') {
                    # Write-PmcTuiLog "UpdateProject: Updating ID1 from '$(Get-SafeProperty $project 'ID1')' to '$($changes[$key])'" "DEBUG"
                }

                # Check if hashtable or PSCustomObject
                if ($project -is [hashtable]) {
                    # Hashtable: direct assignment
                    $project[$key] = $changes[$key]
                    if ($key -eq 'ID1') {
                        # Write-PmcTuiLog "UpdateProject: Hashtable assignment - project['ID1']='$($project[$key])'" "DEBUG"
                    }
                }
                else {
                    # PSCustomObject: use PSObject.Properties or Add-Member
                    if ($project.PSObject.Properties.Name -contains $key) {
                        $project.$key = $changes[$key]
                    }
                    else {
                        Add-Member -InputObject $project -MemberType NoteProperty -Name $key -Value $changes[$key] -Force
                    }
                }
            }

            # Write-PmcTuiLog "UpdateProject: AFTER applying changes - ID1='$(Get-SafeProperty $project 'ID1')'" "DEBUG"

            # Update modified timestamp
            if ($project -is [hashtable]) {
                $project['modified'] = Get-Date
            }
            else {
                if ($project.PSObject.Properties.Name -contains 'modified') {
                    $project.modified = Get-Date
                }
                else {
                    Add-Member -InputObject $project -MemberType NoteProperty -Name 'modified' -Value (Get-Date) -Force
                }
            }

            # Validate updated project
            $validationErrors = $this._ValidateEntity($project, 'project')
            if ($validationErrors.Count -gt 0) {
                $this.LastError = "Project validation failed: $($validationErrors -join ', ')"
                # Write-PmcTuiLog "UpdateProject: Validation FAILED - $($validationErrors -join ', ')" "ERROR"
                $this._Rollback()
                return $false
            }

            # Mark pending changes
            $this.HasPendingChanges = $true

            # Verify the change is in the projects array BEFORE SaveData
            $verifyProject = $this._data.projects | Where-Object { $_.name -eq $name } | Select-Object -First 1
            if ($verifyProject) {
                # Write-PmcTuiLog "UpdateProject: VERIFY before SaveData - project in array has ID1='$(Get-SafeProperty $verifyProject 'ID1')'" "DEBUG"
            }

            # Persist only if AutoSave is enabled
            if ($this.AutoSave) {
                # Write-PmcTuiLog "UpdateProject: AutoSave is enabled, calling SaveData" "DEBUG"
                if (-not $this.SaveData()) {
                    # Write-PmcTuiLog "UpdateProject: SaveData FAILED" "ERROR"
                    return $false
                }
                # Write-PmcTuiLog "UpdateProject: SaveData succeeded" "DEBUG"
            }
            else {
                # Write-PmcTuiLog "UpdateProject: AutoSave is DISABLED, skipping SaveData" "WARNING"
            }

            # Fire events
            $this._InvokeCallback($this.OnProjectUpdated, $project)
            $this._InvokeCallback($this.OnProjectsChanged, $this._data.projects.ToArray())
            $this._InvokeCallback($this.OnDataChanged, $null)

            return $true
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    <#
    .SYNOPSIS
    Delete a project by name

    .PARAMETER name
    Project name

    .OUTPUTS
    True if delete succeeded, False otherwise
    #>
    [bool] DeleteProject([string]$name) {
        [Monitor]::Enter($this._dataLock)
        try {
            # Find project index
            $index = -1
            for ($i = 0; $i -lt $this._data.projects.Count; $i++) {
                if ($this._data.projects[$i].name -eq $name) {
                    $index = $i
                    break
                }
            }

            if ($index -eq -1) {
                $this.LastError = "Project not found: $name"
                return $false
            }

            # Create backup BEFORE any modifications
            $this._CreateBackup()

            # Remove project
            $this._data.projects.RemoveAt($index)

            # Mark pending changes
            $this.HasPendingChanges = $true

            # Persist only if AutoSave is enabled
            if ($this.AutoSave) {
                if (-not $this.SaveData()) {
                    return $false
                }
            }

            # Fire events
            $this._InvokeCallback($this.OnProjectDeleted, $name)
            $this._InvokeCallback($this.OnProjectsChanged, $this._data.projects.ToArray())
            $this._InvokeCallback($this.OnDataChanged, $null)

            return $true
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    # === Time Log CRUD Operations ===

    <#
    .SYNOPSIS
    Get all time logs

    .OUTPUTS
    Array of time log hashtables
    #>
    [array] GetAllTimeLogs() {
        [Monitor]::Enter($this._dataLock)
        try {
            return $this._data.timelogs.ToArray()
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    <#
    .SYNOPSIS
    Get time logs for a specific task

    .PARAMETER taskId
    Task ID

    .OUTPUTS
    Array of time log hashtables
    #>
    [array] GetTimeLogsForTask([string]$taskId) {
        [Monitor]::Enter($this._dataLock)
        try {
            $logs = $this._data.timelogs | Where-Object { $_.taskId -eq $taskId }
            return $(if ($logs) { $logs } else { @() })
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    <#
    .SYNOPSIS
    Add a new time log

    .PARAMETER timelog
    Time log hashtable (must include 'taskId' and 'duration')

    .OUTPUTS
    True if add succeeded, False otherwise
    #>
    [bool] AddTimeLog([hashtable]$timelog) {
        [Monitor]::Enter($this._dataLock)
        try {
            if ($global:PmcTuiLogFile -and $global:PmcTuiLogLevel -ge 3) {
                # Write-PmcTuiLog "TaskStore.AddTimeLog: CALLED with timelog keys: $($timelog.Keys -join ', ')" "DEBUG"
                foreach ($key in $timelog.Keys) {
                    $val = $timelog[$key]
                    $valType = if ($null -eq $val) { 'null' } else { $val.GetType().Name }
                    # Write-PmcTuiLog "TaskStore.AddTimeLog:   $key = $val (type: $valType)" "DEBUG"
                }
            }
            # Validate time log
            $validationErrors = $this._ValidateEntity($timelog, 'timelog')
            if ($validationErrors.Count -gt 0) {
                $this.LastError = "Time log validation failed: $($validationErrors -join ', ')"
                # Write-PmcTuiLog "TaskStore.AddTimeLog: VALIDATION FAILED: $($validationErrors -join ', ')" "ERROR"
                return $false
            }
            # Write-PmcTuiLog "TaskStore.AddTimeLog: Validation passed" "DEBUG"

            # Create backup BEFORE any modifications
            $this._CreateBackup()

            # Generate ID if not present
            if (-not $timelog.ContainsKey('id') -or [string]::IsNullOrEmpty($timelog.id)) {
                $timelog.id = [Guid]::NewGuid().ToString()
            }

            # Add timestamp if not present
            if (-not $timelog.ContainsKey('timestamp')) {
                $timelog.timestamp = Get-Date
            }

            # Add to collection
            $this._data.timelogs.Add($timelog)

            # Mark pending changes
            $this.HasPendingChanges = $true

            # Persist only if AutoSave is enabled
            if ($this.AutoSave) {
                if (-not $this.SaveData()) {
                    return $false
                }
            }

            # Fire events
            $this._InvokeCallback($this.OnTimeLogAdded, $timelog)
            $this._InvokeCallback($this.OnTimeLogsChanged, $this._data.timelogs.ToArray())
            $this._InvokeCallback($this.OnDataChanged, $null)

            return $true
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    <#
    .SYNOPSIS
    Delete a time log by ID

    .PARAMETER id
    Time log ID

    .OUTPUTS
    True if delete succeeded, False otherwise
    #>
    [bool] DeleteTimeLog([string]$id) {
        [Monitor]::Enter($this._dataLock)
        try {
            # Find time log index
            $index = -1
            for ($i = 0; $i -lt $this._data.timelogs.Count; $i++) {
                if ($this._data.timelogs[$i].id -eq $id) {
                    $index = $i
                    break
                }
            }

            if ($index -eq -1) {
                $this.LastError = "Time log not found: $id"
                return $false
            }

            # Create backup BEFORE any modifications
            $this._CreateBackup()

            # Remove time log
            $this._data.timelogs.RemoveAt($index)

            # Mark pending changes
            $this.HasPendingChanges = $true

            # Persist only if AutoSave is enabled
            if ($this.AutoSave) {
                if (-not $this.SaveData()) {
                    return $false
                }
            }

            # Fire events
            $this._InvokeCallback($this.OnTimeLogDeleted, $id)
            $this._InvokeCallback($this.OnTimeLogsChanged, $this._data.timelogs.ToArray())
            $this._InvokeCallback($this.OnDataChanged, $null)

            return $true
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    <#
    .SYNOPSIS
    Update a time log entry

    .PARAMETER id
    Time log ID

    .PARAMETER changes
    Hashtable of fields to update

    .OUTPUTS
    True if update succeeded, False otherwise
    #>
    [bool] UpdateTimeLog([string]$id, [hashtable]$changes) {
        [Monitor]::Enter($this._dataLock)
        try {
            # Find time log
            $timelog = $null
            foreach ($log in $this._data.timelogs) {
                if ($log.id -eq $id) {
                    $timelog = $log
                    break
                }
            }

            if (-not $timelog) {
                $this.LastError = "Time log not found: $id"
                return $false
            }

            # Create backup BEFORE any modifications
            $this._CreateBackup()

            # Apply changes
            foreach ($key in $changes.Keys) {
                if ($key -ne 'id') {
                    # Don't allow ID changes
                    $timelog[$key] = $changes[$key]
                }
            }

            # Add modified timestamp
            $timelog.modified = [DateTime]::Now

            # Validate
            $validationErrors = $this._ValidateEntity($timelog, 'timelog')
            if ($validationErrors.Count -gt 0) {
                $this.LastError = "Validation failed: $($validationErrors -join ', ')"
                return $false
            }

            # Mark pending changes
            $this.HasPendingChanges = $true

            # Persist only if AutoSave is enabled
            if ($this.AutoSave) {
                if (-not $this.SaveData()) {
                    return $false
                }
            }

            # Fire events
            $this._InvokeCallback($this.OnTimeLogUpdated, $timelog)
            $this._InvokeCallback($this.OnTimeLogsChanged, $this._data.timelogs.ToArray())
            $this._InvokeCallback($this.OnDataChanged, $null)

            return $true
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    # === Query Methods ===

    <#
    .SYNOPSIS
    Get tasks by project

    .PARAMETER projectName
    Project name

    .OUTPUTS
    Array of task hashtables
    #>
    [array] GetTasksByProject([string]$projectName) {
        [Monitor]::Enter($this._dataLock)
        try {
            $tasks = $this._data.tasks | Where-Object { $_.project -eq $projectName }
            return $(if ($tasks) { $tasks } else { @() })
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    <#
    .SYNOPSIS
    Search tasks by text

    .PARAMETER searchText
    Search query

    .OUTPUTS
    Array of task hashtables
    #>
    [array] SearchTasks([string]$searchText) {
        [Monitor]::Enter($this._dataLock)
        try {
            $searchLower = $searchText.ToLower()
            $tasks = $this._data.tasks | Where-Object {
                $null -ne $_.text -and $_.text.ToLower().Contains($searchLower)
            }
            return $(if ($tasks) { $tasks } else { @() })
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    <#
    .SYNOPSIS
    Get tasks by priority range

    .PARAMETER minPriority
    Minimum priority (inclusive)

    .PARAMETER maxPriority
    Maximum priority (inclusive)

    .OUTPUTS
    Array of task hashtables
    #>
    [array] GetTasksByPriority([int]$minPriority, [int]$maxPriority) {
        [Monitor]::Enter($this._dataLock)
        try {
            $tasks = $this._data.tasks | Where-Object {
                $_.priority -ge $minPriority -and $_.priority -le $maxPriority
            }
            return $(if ($tasks) { $tasks } else { @() })
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    # === Validation ===

    <#
    .SYNOPSIS
    Validate an entity against rules

    .PARAMETER entity
    Entity hashtable to validate

    .PARAMETER entityType
    Entity type ('task', 'project', 'timelog')

    .OUTPUTS
    Array of validation error messages (empty if valid)
    #>
    hidden [string[]] _ValidateEntity($entity, [string]$entityType) {
        $errors = @()

        if (-not $this._validationRules.ContainsKey($entityType)) {
            $errors += "Unknown entity type: $entityType"
            return $errors
        }

        $rules = $this._validationRules[$entityType]

        # Check required fields
        foreach ($field in $rules.required) {
            $value = Get-SafeProperty $entity $field
            if ([string]::IsNullOrEmpty($value)) {
                $errors += "Required field missing: $field"
            }
        }

        # Check field types
        foreach ($field in $rules.types.Keys) {
            $hasField = Test-SafeProperty $entity $field
            if ($hasField) {
                $value = Get-SafeProperty $entity $field
                if ($field -eq 'tags') {
                    # Add-Content -Path "$($env:TEMP)\pmc-edit-debug.log" -Value "$(Get-Date -Format 'HH:mm:ss.fff') _ValidateEntity: Checking tags - value type=$($value.GetType().FullName) isArray=$($value -is [array]) value=$value"
                }
                if ($null -ne $value) {
                    $expectedType = $rules.types[$field]

                    $isValid = switch ($expectedType) {
                        'string' { $value -is [string] }
                        'int' {
                            # Accept both Int32 and Int64
                            $result = ($value -is [int]) -or ($value -is [int64]) -or ($value -is [int32])
                            if ($field -eq 'priority') {
                                # Add-Content -Path "$($env:TEMP)\pmc-edit-debug.log" -Value "$(Get-Date -Format 'HH:mm:ss.fff') TaskStore: Validating priority - value=$value type=$($value.GetType().FullName) isInt=$result"
                            }
                            $result
                        }
                        'bool' { $value -is [bool] }
                        'datetime' { $value -is [DateTime] }
                        'array' { $value -is [array] }
                        default { $true }
                    }

                    if (-not $isValid) {
                        $errors += "Field '$field' has invalid type (expected $expectedType, got $($value.GetType().FullName))"
                    }
                }
            }
        }

        return $errors
    }

    # === Helper Methods ===

    <#
    .SYNOPSIS
    Invoke callback safely
    #>
    hidden [void] _InvokeCallback([scriptblock]$callback, $arg) {
        if ($null -ne $callback -and $callback -ne {}) {
            try {
                if ($null -ne $arg) {
                    # Use Invoke-Command with -ArgumentList to pass single arg without array wrapping
                    Invoke-Command -ScriptBlock $callback -ArgumentList (, $arg)
                }
                else {
                    & $callback
                }
            }
            catch {
                # Log callback errors but DON'T rethrow - callbacks must never crash the app
                $this.LastError = "Callback failed: $($_.Exception.Message)"
                if (Get-Command Write-PmcTuiLog -ErrorAction SilentlyContinue) {
                    # Write-PmcTuiLog "TaskStore callback error: $($_.Exception.Message)" "ERROR"
                    # Write-PmcTuiLog "Callback code: $($callback.ToString())" "ERROR"
                    # Write-PmcTuiLog "Stack trace: $($_.ScriptStackTrace)" "ERROR"
                }
                # DON'T rethrow - background operations must not crash
            }
        }
    }

    # === Batch Operations ===

    <#
    .SYNOPSIS
    Add multiple tasks in a single transaction

    .PARAMETER tasks
    Array of task hashtables

    .OUTPUTS
    Number of tasks successfully added
    #>
    [int] AddTasks([hashtable[]]$tasks) {
        [Monitor]::Enter($this._dataLock)
        try {
            $addedCount = 0
            $this._CreateBackup()

            foreach ($task in $tasks) {
                # Validate task
                $validationErrors = $this._ValidateEntity($task, 'task')
                if ($validationErrors.Count -gt 0) {
                    continue  # Skip invalid tasks
                }

                # Generate ID if not present
                if (-not $task.ContainsKey('id') -or [string]::IsNullOrEmpty($task.id)) {
                    $task.id = [Guid]::NewGuid().ToString()
                }

                # Add timestamps
                $now = Get-Date
                if (-not $task.ContainsKey('created')) {
                    $task.created = $now
                }
                $task.modified = $now

                # Add to collection
                $this._data.tasks.Add($task)
                $addedCount++
            }

            # Persist once for all tasks
            if ($addedCount -gt 0) {
                if ($this.SaveData()) {
                    # Fire events once
                    $this._InvokeCallback($this.OnTasksChanged, $this._data.tasks.ToArray())
                    $this._InvokeCallback($this.OnDataChanged, $null)
                }
                else {
                    return 0
                }
            }

            return $addedCount
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    <#
    .SYNOPSIS
    Get data statistics

    .OUTPUTS
    Hashtable with data statistics
    #>
    [hashtable] GetStatistics() {
        [Monitor]::Enter($this._dataLock)
        try {
            # Return cached stats if available and not dirty
            if (-not $this._statsNeedUpdate -and $null -ne $this._cachedStats) {
                return $this._cachedStats
            }

            # Compute statistics (only when cache is dirty)
            $completedCount = 0
            $pendingCount = 0
            foreach ($task in $this._data.tasks) {
                if (Get-SafeProperty $task 'completed') {
                    $completedCount++
                }
                else {
                    $pendingCount++
                }
            }

            $this._cachedStats = @{
                taskCount          = $this._data.tasks.Count
                projectCount       = $this._data.projects.Count
                timeLogCount       = $this._data.timelogs.Count
                completedTaskCount = $completedCount
                pendingTaskCount   = $pendingCount
                lastLoaded         = $this._data.metadata.lastLoaded
                lastSaved          = $this._data.metadata.lastSaved
            }

            $this._statsNeedUpdate = $false
            return $this._cachedStats
        }
        finally {
            [Monitor]::Exit($this._dataLock)
        }
    }

    <#
    .SYNOPSIS
    Flush pending changes to disk

    .DESCRIPTION
    When AutoSave is disabled, changes accumulate in memory.
    Call this method to persist all pending changes to disk.

    .OUTPUTS
    True if flush succeeded, False otherwise
    #>
    [bool] Flush() {
        if (-not $this.HasPendingChanges) {
            return $true  # Nothing to save
        }

        if ($this.SaveData()) {
            $this.HasPendingChanges = $false
            return $true
        }

        return $false
    }

    <#
    .SYNOPSIS
    Enable automatic saving after each operation
    #>
    [void] EnableAutoSave() {
        $this.AutoSave = $true
    }

    <#
    .SYNOPSIS
    Disable automatic saving (batch mode)
    #>
    [void] DisableAutoSave() {
        $this.AutoSave = $false
    }
}

﻿# NoteService.ps1 - Service for managing notes
#
# Provides CRUD operations for notes with file-based storage
# Notes are stored in notes/ subdirectory as .txt files
#
# Usage:
#   $service = [NoteService]::GetInstance()
#   $notes = $service.GetAllNotes()
#   $service.CreateNote("Meeting Notes")
#   $service.DeleteNote($noteId)

using namespace System
using namespace System.Collections.Generic

Set-StrictMode -Version Latest

class NoteService {
    # === Singleton Instance ===
    static hidden [NoteService]$_instance = $null
    static hidden [object]$_instanceLock = [object]::new()

    # === Configuration ===
    hidden [string]$_notesDir
    hidden [string]$_metadataFile

    # === In-memory cache ===
    hidden [hashtable]$_notesCache = @{}
    hidden [datetime]$_cacheLoadTime = [datetime]::MinValue

    # === Event Callbacks ===
    [scriptblock]$OnNoteAdded = {}
    [scriptblock]$OnNoteUpdated = {}
    [scriptblock]$OnNoteDeleted = {}
    [scriptblock]$OnNotesChanged = {}

    # === Singleton Access ===
    static [NoteService] GetInstance() {
        if ([NoteService]::_instance -eq $null) {
            [System.Threading.Monitor]::Enter([NoteService]::_instanceLock)
            try {
                if ([NoteService]::_instance -eq $null) {
                    [NoteService]::_instance = [NoteService]::new()
                }
            } finally {
                [System.Threading.Monitor]::Exit([NoteService]::_instanceLock)
            }
        }
        return [NoteService]::_instance
    }

    # === Constructor (Private - use GetInstance) ===
    NoteService() {
        # Determine notes directory relative to PMC root
        $pmcRoot = Split-Path -Parent (Split-Path -Parent (Split-Path -Parent $PSScriptRoot))
        $this._notesDir = Join-Path $pmcRoot "notes"
        $this._metadataFile = Join-Path $this._notesDir "notes_metadata.json"

        # Ensure notes directory exists
        if (-not (Test-Path $this._notesDir)) {
            New-Item -ItemType Directory -Path $this._notesDir -Force | Out-Null
        }

        # Load metadata
        $this.LoadMetadata()
    }

    # === Metadata Management ===
    hidden [void] LoadMetadata() {
        if (Test-Path $this._metadataFile) {
            try {
                $json = Get-Content $this._metadataFile -Raw | ConvertFrom-Json -Depth 10
                foreach ($note in $json.notes) {
                    $this._notesCache[$note.id] = @{
                        id = $note.id
                        title = $note.title
                        file = $note.file
                        created = [datetime]::Parse($note.created)
                        modified = [datetime]::Parse($note.modified)
                        tags = $note.tags
                        word_count = $note.word_count
                        line_count = $note.line_count
                        owner_type = $(if ($note.PSObject.Properties['owner_type']) { $note.owner_type } else { "global" })
                        owner_id = $(if ($note.PSObject.Properties['owner_id']) { $note.owner_id } else { $null })
                    }
                }
                $this._cacheLoadTime = [datetime]::Now
            } catch {
                Write-Warning "Failed to load notes metadata: $_"
                $this._notesCache = @{}
            }
        }
    }

    hidden [void] SaveMetadata() {
        try {
            $notes = $this._notesCache.Values | ForEach-Object {
                @{
                    id = $_.id
                    title = $_.title
                    file = $_.file
                    created = $_.created.ToString("o")
                    modified = $_.modified.ToString("o")
                    tags = $_.tags
                    word_count = $_.word_count
                    line_count = $_.line_count
                    owner_type = $(if ($_.ContainsKey('owner_type')) { $_.owner_type } else { "global" })
                    owner_id = $(if ($_.ContainsKey('owner_id')) { $_.owner_id } else { $null })
                }
            }

            $metadata = @{
                schema_version = 1
                notes = $notes
            }

            # Atomic save: write to temp file, then rename
            $tempFile = "$($this._metadataFile).tmp"
            $metadata | ConvertTo-Json -Depth 10 | Set-Content -Path $tempFile -Encoding utf8

            # Backup existing file
            if (Test-Path $this._metadataFile) {
                Copy-Item $this._metadataFile "$($this._metadataFile).bak" -Force
            }

            # Rename temp to actual
            Move-Item -Path $tempFile -Destination $this._metadataFile -Force

        } catch {
            Write-Warning "Failed to save notes metadata: $_"
            throw
        }
    }

    # === CRUD Operations ===

    [array] GetAllNotes() {
        $notes = @($this._notesCache.Values | Sort-Object -Property modified -Descending)
        return $notes
    }

    [array] GetNotesByOwner([string]$ownerType, [string]$ownerId) {
        $notes = @($this._notesCache.Values | Where-Object {
            $_.owner_type -eq $ownerType -and $_.owner_id -eq $ownerId
        } | Sort-Object -Property modified -Descending)
        return $notes
    }

    [object] GetNote([string]$noteId) {
        if ($this._notesCache.ContainsKey($noteId)) {
            return $this._notesCache[$noteId]
        }
        return $null
    }

    [object] CreateNote([string]$title) {
        return $this.CreateNote($title, @())
    }

    [object] CreateNote([string]$title, [array]$tags) {
        return $this.CreateNote($title, $tags, "global", $null)
    }

    [object] CreateNote([string]$title, [array]$tags, [string]$ownerType, [string]$ownerId) {
        # Generate unique ID
        $noteId = [guid]::NewGuid().ToString()

        # Create note file
        $fileName = "$noteId.txt"
        $filePath = Join-Path $this._notesDir $fileName

        # Create initial empty note
        Set-Content -Path $filePath -Value "" -Encoding utf8

        # Create metadata entry
        $note = @{
            id = $noteId
            title = $title
            file = $filePath
            created = [datetime]::Now
            modified = [datetime]::Now
            tags = $tags
            owner_type = $ownerType
            owner_id = $ownerId
            word_count = 0
            line_count = 0
        }

        # Add to cache
        $this._notesCache[$noteId] = $note

        # Persist metadata
        $this.SaveMetadata()

        # Fire event
        if ($this.OnNoteAdded) {
            & $this.OnNoteAdded $note
        }
        if ($this.OnNotesChanged) {
            & $this.OnNotesChanged
        }

        return $note
    }

    [void] UpdateNoteMetadata([string]$noteId, [hashtable]$changes) {
        if (-not $this._notesCache.ContainsKey($noteId)) {
            throw "Note not found: $noteId"
        }

        $note = $this._notesCache[$noteId]

        # Apply changes
        foreach ($key in $changes.Keys) {
            if ($note.ContainsKey($key)) {
                $note[$key] = $changes[$key]
            }
        }

        # Update modified timestamp
        $note.modified = [datetime]::Now

        # Persist
        $this.SaveMetadata()

        # Fire event
        if ($this.OnNoteUpdated) {
            & $this.OnNoteUpdated $note
        }
        if ($this.OnNotesChanged) {
            & $this.OnNotesChanged
        }
    }

    [void] UpdateNoteStats([string]$noteId, [int]$wordCount, [int]$lineCount) {
        $this.UpdateNoteMetadata($noteId, @{
            word_count = $wordCount
            line_count = $lineCount
        })
    }

    [void] DeleteNote([string]$noteId) {
        if (-not $this._notesCache.ContainsKey($noteId)) {
            throw "Note not found: $noteId"
        }

        $note = $this._notesCache[$noteId]

        # Delete file
        if (Test-Path $note.file) {
            try {
                Remove-Item $note.file -Force -ErrorAction Stop
            } catch {
                # Write-PmcTuiLog "Failed to delete note file $($note.file): $_" "ERROR"
                throw "Failed to delete note file: $($_.Exception.Message)"
            }
        }

        # Remove from cache
        $this._notesCache.Remove($noteId)

        # Persist
        $this.SaveMetadata()

        # Fire event
        if ($this.OnNoteDeleted) {
            & $this.OnNoteDeleted $noteId
        }
        if ($this.OnNotesChanged) {
            & $this.OnNotesChanged
        }
    }

    [string] LoadNoteContent([string]$noteId) {
        if (-not $this._notesCache.ContainsKey($noteId)) {
            throw "Note not found: $noteId"
        }

        $note = $this._notesCache[$noteId]

        if (Test-Path $note.file) {
            return Get-Content -Path $note.file -Raw
        }

        return ""
    }

    [void] SaveNoteContent([string]$noteId, [string]$content) {
        if (-not $this._notesCache.ContainsKey($noteId)) {
            throw "Note not found: $noteId"
        }

        $note = $this._notesCache[$noteId]

        # Atomic save: write to temp file, then rename
        $tempFile = "$($note.file).tmp"

        try {
            # PowerShell 7+ requires lowercase encoding names
            Set-Content -Path $tempFile -Value $content -Encoding utf8

            # Backup existing file
            if (Test-Path $note.file) {
                Copy-Item $note.file "$($note.file).bak" -Force
            }

            # Rename temp to actual
            Move-Item -Path $tempFile -Destination $note.file -Force

            # Calculate stats
            if ([string]::IsNullOrEmpty($content)) {
                $lineCount = 0
                $wordCount = 0
            } else {
                $lineArray = @($content -split "`n")
                $lineCount = $lineArray.Count
                $wordArray = @($content -split '\s+' | Where-Object { -not [string]::IsNullOrWhiteSpace($_) })
                $wordCount = $(if ($wordArray) { $wordArray.Count } else { 0 })
            }

            # Update metadata
            $this.UpdateNoteStats($noteId, $wordCount, $lineCount)

        } catch {
            # Clean up temp file if it exists
            if (Test-Path $tempFile) {
                Remove-Item $tempFile -Force -ErrorAction SilentlyContinue
            }
            throw
        }
    }
}

# FileNoteService.ps1 - Filesystem-based note service
#
# Human-readable filenames (title = filename), optional per-note .meta sidecar.
# Much simpler than legacy NoteService - no monolithic metadata manifest.
#
# Storage structure:
#   notes/
#     Meeting Notes.txt      <- Note content
#     Meeting Notes.meta     <- Optional: tags, owner (only if needed)
#     Project Ideas.txt
#
# Usage:
#   $service = [FileNoteService]::GetInstance()
#   $notes = $service.GetAllNotes()
#   $service.CreateNote("My New Note")

using namespace System
using namespace System.IO
using namespace System.Collections.Generic

Set-StrictMode -Version Latest

class FileNoteService {
    # === Singleton ===
    static hidden [FileNoteService]$_instance = $null
    static hidden [object]$_instanceLock = [object]::new()

    # === Configuration ===
    hidden [string]$_notesDir
    hidden [hashtable]$_metaCache = @{}  # In-memory cache for .meta files

    # === Events ===
    [scriptblock]$OnNotesChanged = $null
    [scriptblock]$OnNoteAdded = $null

    # === Singleton Access ===
    static [FileNoteService] GetInstance() {
        if ($null -eq [FileNoteService]::_instance) {
            [System.Threading.Monitor]::Enter([FileNoteService]::_instanceLock)
            try {
                if ($null -eq [FileNoteService]::_instance) {
                    [FileNoteService]::_instance = [FileNoteService]::new()
                }
            } finally {
                [System.Threading.Monitor]::Exit([FileNoteService]::_instanceLock)
            }
        }
        return [FileNoteService]::_instance
    }

    # === Constructor ===
    FileNoteService() {
        # Determine notes directory relative to PMC root
        $pmcRoot = Split-Path -Parent (Split-Path -Parent (Split-Path -Parent $PSScriptRoot))
        $this._notesDir = Join-Path $pmcRoot "notes"

        # Ensure directory exists
        if (-not (Test-Path $this._notesDir)) {
            New-Item -ItemType Directory -Path $this._notesDir -Force | Out-Null
        }
    }

    # === CRUD Operations ===

    <#
    .SYNOPSIS
    Get all notes by scanning the directory
    #>
    [array] GetAllNotes() {
        $notes = @()

        $files = Get-ChildItem -Path $this._notesDir -Filter "*.txt" -File -ErrorAction SilentlyContinue

        foreach ($file in $files) {
            $noteId = $file.BaseName  # Filename without extension = ID and title
            $meta = $this._LoadMetadata($noteId)

            $notes += @{
                id = $noteId
                title = $noteId
                file = $file.FullName
                created = $file.CreationTime
                modified = $file.LastWriteTime
                tags = $(if ($meta['tags']) { $meta['tags'] } else { @() })
                owner_type = $(if ($meta['owner_type']) { $meta['owner_type'] } else { "global" })
                owner_id = $(if ($meta['owner_id']) { $meta['owner_id'] } else { "" })
                word_count = 0  # Compute on demand if needed
                line_count = 0
            }
        }

        # Sort by modified descending (most recent first)
        return $notes | Sort-Object -Property modified -Descending
    }

    <#
    .SYNOPSIS
    Get notes filtered by owner
    #>
    [array] GetNotesByOwner([string]$ownerType, [string]$ownerId) {
        return $this.GetAllNotes() | Where-Object {
            $_.owner_type -eq $ownerType -and $_.owner_id -eq $ownerId
        }
    }

    <#
    .SYNOPSIS
    Get a single note by ID (title)
    #>
    [object] GetNote([string]$noteId) {
        $filePath = Join-Path $this._notesDir "$noteId.txt"
        if (-not (Test-Path $filePath)) {
            return $null
        }

        $file = Get-Item $filePath
        $meta = $this._LoadMetadata($noteId)

        return @{
            id = $noteId
            title = $noteId
            file = $file.FullName
            created = $file.CreationTime
            modified = $file.LastWriteTime
            tags = $(if ($meta['tags']) { $meta['tags'] } else { @() })
            owner_type = $(if ($meta['owner_type']) { $meta['owner_type'] } else { "global" })
            owner_id = $(if ($meta['owner_id']) { $meta['owner_id'] } else { "" })
        }
    }

    <#
    .SYNOPSIS
    Create a new note with the given title
    #>
    [object] CreateNote([string]$title) {
        return $this.CreateNote($title, @(), "global", $null)
    }

    [object] CreateNote([string]$title, [array]$tags) {
        return $this.CreateNote($title, $tags, "global", $null)
    }

    [object] CreateNote([string]$title, [array]$tags, [string]$ownerType, [string]$ownerId) {
        # Sanitize title for filename
        $safeName = $this._SanitizeFilename($title)

        # Handle duplicates by appending number
        $baseName = $safeName
        $counter = 1
        while (Test-Path (Join-Path $this._notesDir "$safeName.txt")) {
            $safeName = "$baseName ($counter)"
            $counter++
        }

        $filePath = Join-Path $this._notesDir "$safeName.txt"

        # Create empty file with atomic write
        $this._AtomicWrite($filePath, "")

        # Save metadata if we have tags or owner
        if ($tags.Count -gt 0 -or ($ownerType -ne "global" -and $ownerId)) {
            $this._SaveMetadata($safeName, @{
                tags = $tags
                owner_type = $ownerType
                owner_id = $ownerId
            })
        }

        $note = $this.GetNote($safeName)

        # Fire events
        if ($this.OnNoteAdded) {
            & $this.OnNoteAdded $note
        }
        if ($this.OnNotesChanged) {
            & $this.OnNotesChanged
        }

        return $note
    }

    <#
    .SYNOPSIS
    Load note content from file
    #>
    [string] LoadNoteContent([string]$noteId) {
        $filePath = Join-Path $this._notesDir "$noteId.txt"

        if (-not (Test-Path $filePath)) {
            throw "Note not found: $noteId"
        }

        return Get-Content -Path $filePath -Raw -ErrorAction SilentlyContinue
    }

    <#
    .SYNOPSIS
    Save note content with atomic write and fsync
    #>
    [void] SaveNoteContent([string]$noteId, [string]$content) {
        $filePath = Join-Path $this._notesDir "$noteId.txt"

        if (-not (Test-Path $filePath)) {
            throw "Note not found: $noteId"
        }

        $this._AtomicWrite($filePath, $content)

        # Note: We do NOT rewrite any manifest here - that's the whole point!
        # Modified time is updated by the filesystem automatically.
    }

    <#
    .SYNOPSIS
    Update note metadata (tags, owner)
    #>
    [void] UpdateNoteMetadata([string]$noteId, [hashtable]$changes) {
        $filePath = Join-Path $this._notesDir "$noteId.txt"
        if (-not (Test-Path $filePath)) {
            throw "Note not found: $noteId"
        }

        # Handle title change = file rename
        if ($changes.ContainsKey('title') -and $changes.title -ne $noteId) {
            $newName = $this._SanitizeFilename($changes.title)
            $this.RenameNote($noteId, $newName)
            $noteId = $newName
        }

        # Load existing metadata
        $meta = $this._LoadMetadata($noteId)
        if (-not $meta) { $meta = @{} }

        # Apply changes
        if ($changes.ContainsKey('tags')) { $meta.tags = $changes.tags }
        if ($changes.ContainsKey('owner_type')) { $meta.owner_type = $changes.owner_type }
        if ($changes.ContainsKey('owner_id')) { $meta.owner_id = $changes.owner_id }

        # Save metadata (only if we have anything to store)
        if ($meta.tags -or ($meta.owner_type -and $meta.owner_type -ne "global") -or $meta.owner_id) {
            $this._SaveMetadata($noteId, $meta)
        }

        if ($this.OnNotesChanged) {
            & $this.OnNotesChanged
        }
    }

    <#
    .SYNOPSIS
    Rename a note (changes both file and title)
    #>
    [void] RenameNote([string]$oldName, [string]$newName) {
        $oldPath = Join-Path $this._notesDir "$oldName.txt"
        $newPath = Join-Path $this._notesDir "$newName.txt"

        if (-not (Test-Path $oldPath)) {
            throw "Note not found: $oldName"
        }

        if (Test-Path $newPath) {
            throw "A note with name '$newName' already exists"
        }

        # Rename content file
        Rename-Item $oldPath $newPath

        # Rename metadata file if exists
        $oldMeta = Join-Path $this._notesDir "$oldName.meta"
        $newMeta = Join-Path $this._notesDir "$newName.meta"
        if (Test-Path $oldMeta) {
            Rename-Item $oldMeta $newMeta
        }

        # Update cache
        if ($this._metaCache.ContainsKey($oldName)) {
            $this._metaCache[$newName] = $this._metaCache[$oldName]
            $this._metaCache.Remove($oldName)
        }
    }

    <#
    .SYNOPSIS
    Delete a note and its metadata
    #>
    [void] DeleteNote([string]$noteId) {
        $filePath = Join-Path $this._notesDir "$noteId.txt"

        if (-not (Test-Path $filePath)) {
            throw "Note not found: $noteId"
        }

        # Delete content file
        Remove-Item $filePath -Force

        # Delete metadata file if exists
        $metaPath = Join-Path $this._notesDir "$noteId.meta"
        if (Test-Path $metaPath) {
            Remove-Item $metaPath -Force
        }

        # Clear from cache
        $this._metaCache.Remove($noteId)

        if ($this.OnNotesChanged) {
            & $this.OnNotesChanged
        }
    }

    # === Private Helpers ===

    <#
    .SYNOPSIS
    Atomic write with fsync - write to temp, flush, rename
    #>
    hidden [void] _AtomicWrite([string]$targetPath, [string]$content) {
        $tempPath = "$targetPath.tmp"

        try {
            # Write to temp file
            [System.IO.File]::WriteAllText($tempPath, $content)

            # Fsync - ensure data hits disk
            $stream = [System.IO.File]::Open($tempPath, [System.IO.FileMode]::Open, [System.IO.FileAccess]::Read, [System.IO.FileShare]::None)
            $stream.Flush()
            $stream.Close()

            # Atomic rename
            Move-Item -Path $tempPath -Destination $targetPath -Force

        } catch {
            # Cleanup temp file on error
            if (Test-Path $tempPath) {
                Remove-Item $tempPath -Force -ErrorAction SilentlyContinue
            }
            throw
        }
    }

    <#
    .SYNOPSIS
    Load per-note metadata from .meta sidecar file
    #>
    hidden [hashtable] _LoadMetadata([string]$noteId) {
        # Check cache first
        if ($this._metaCache.ContainsKey($noteId)) {
            return $this._metaCache[$noteId]
        }

        $metaPath = Join-Path $this._notesDir "$noteId.meta"

        if (Test-Path $metaPath) {
            try {
                $json = Get-Content $metaPath -Raw | ConvertFrom-Json
                $meta = @{
                    tags = $(if ($json.tags) { @($json.tags) } else { @() })
                    owner_type = $(if ($json.owner_type) { $json.owner_type } else { "global" })
                    owner_id = $(if ($json.owner_id) { $json.owner_id } else { "" })
                }
                $this._metaCache[$noteId] = $meta
                return $meta
            } catch {
                Write-Warning "Failed to load metadata for $noteId $_"
            }
        }

        return @{}
    }

    <#
    .SYNOPSIS
    Save per-note metadata to .meta sidecar file
    #>
    hidden [void] _SaveMetadata([string]$noteId, [hashtable]$meta) {
        $metaPath = Join-Path $this._notesDir "$noteId.meta"

        $data = @{
            tags = $(if ($meta.tags) { $meta.tags } else { @() })
            owner_type = $(if ($meta.owner_type) { $meta.owner_type } else { "global" })
            owner_id = $(if ($meta.owner_id) { $meta.owner_id } else { "" })
        }

        $json = $data | ConvertTo-Json -Compress
        $this._AtomicWrite($metaPath, $json)

        # Update cache
        $this._metaCache[$noteId] = $meta
    }

    <#
    .SYNOPSIS
    Sanitize a string for use as a filename
    #>
    hidden [string] _SanitizeFilename([string]$name) {
        # Remove invalid characters
        $invalid = [System.IO.Path]::GetInvalidFileNameChars()
        $safeName = $name

        foreach ($char in $invalid) {
            $safeName = $safeName.Replace([string]$char, '')
        }

        # Trim whitespace and dots
        $safeName = $safeName.Trim().TrimEnd('.')

        # Ensure not empty
        if ([string]::IsNullOrWhiteSpace($safeName)) {
            $safeName = "Untitled"
        }

        # Limit length (Windows has 260 char path limit)
        if ($safeName.Length -gt 200) {
            $safeName = $safeName.Substring(0, 200)
        }

        return $safeName
    }

    <#
    .SYNOPSIS
    Update word/line stats (computed on demand, not stored)
    #>
    [void] UpdateNoteStats([string]$noteId, [int]$wordCount, [int]$lineCount) {
        # In filesystem mode, we don't store stats - they're derived from content
        # This method exists for API compatibility but is a no-op
    }
}

# PmcThemeEngine.ps1 - Core theme system with gradient support
#
# Handles all color resolution for the TUI:
# - Solid colors (single RGB value)
# - Multi-stop gradients (horizontal/vertical transitions)
# - Aggressive caching for performance
# - JSON-based theme configuration

using namespace System.Collections.Generic

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Theme engine singleton - handles all color/gradient computation and caching

.DESCRIPTION
Properties format in config.json:
  Solid:    { "Type": "Solid", "Color": "#ff8833" }
  Gradient: { "Type": "Gradient", "Direction": "Horizontal",
              "Stops": [{"Position": 0.0, "Color": "#ff8833"}, ...] }

Property names: "Background.Field", "Foreground.FieldFocused", etc.
#>
class PmcThemeEngine {
    hidden static [PmcThemeEngine]$_instance = $null

    # Loaded theme properties from config.json
    hidden [hashtable]$_properties = @{}
    hidden [hashtable]$_palette = @{}

    # Cache: key = "PropertyName_Width_Generation", value = string[] of ANSI sequences
    hidden [hashtable]$_gradientCache = @{}
    hidden [hashtable]$_solidCache = @{}

    # Int Caches
    hidden [hashtable]$_solidIntCache = @{}
    hidden [hashtable]$_gradientIntCache = @{}

    hidden [int]$_cacheGeneration = 0

    # Singleton access
    static [PmcThemeEngine] GetInstance() {
        if ($null -eq [PmcThemeEngine]::_instance) {
            [PmcThemeEngine]::_instance = [PmcThemeEngine]::new()
        }
        return [PmcThemeEngine]::_instance
    }

    PmcThemeEngine() {
        # Private constructor
    }

    # Configure engine with full state (called by PmcThemeManager)
    [void] Configure([hashtable]$properties, [hashtable]$palette) {
        $this._properties = $properties
        $this._palette = $palette
        $this.InvalidateCache()

        # Targeted diagnostic: log when Configure is called (only if debug enabled)
        if ((Test-Path variable:global:PmcDebug) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
            $propList = ($properties.Keys | Sort-Object) -join ', '
            Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [PmcThemeEngine] Configure: $($properties.Count) properties: $propList"
        }
    }

    # Public Primitive: Get ANSI from Hex (for Manager)
    [string] GetAnsiFromHex([string]$hex, [bool]$background) {
        if ([string]::IsNullOrEmpty($hex)) { return '' }
        return $this._GetSolidAnsiCached($hex, $background)
    }

    # Public Primitive: Get Int from Hex (for Manager)
    [int] GetIntFromHex([string]$hex) {
        if ([string]::IsNullOrEmpty($hex)) { return -1 }
        return $this._GetSolidIntCached($hex)
    }

    # Get background ANSI - handles solid or gradient
    [string] GetBackgroundAnsi([string]$propertyName, [int]$width, [int]$charIndex) {
        if (-not $this._properties.ContainsKey($propertyName)) {
            throw "Theme Property Missing: '$propertyName'"
        }

        $prop = $this._properties[$propertyName]

        if ($prop.Type -eq 'Solid') {
            return $this._GetSolidAnsiCached($prop.Color, $true)
        }
        elseif ($prop.Type -eq 'Gradient') {
            $gradient = $this._GetGradientArrayCached($propertyName, $prop, $width, $true)
            if ($charIndex -ge 0 -and $charIndex -lt $gradient.Count) {
                return $gradient[$charIndex]
            }
            return ''
        }

        return ''
    }

    # Get foreground ANSI - usually solid
    [string] GetForegroundAnsi([string]$propertyName) {
        if (-not $this._properties.ContainsKey($propertyName)) {
            throw "Theme Property Missing: '$propertyName'"
        }

        $prop = $this._properties[$propertyName]

        if ($prop.Type -eq 'Solid') {
            $ansi = $this._GetSolidAnsiCached($prop.Color, $false)
            return $ansi
        }

        return ''
    }

    # Get integer color value (Generic - for Solid colors)
    # Returns packed RGB int (0x00RRGGBB)
    [int] GetThemeColorInt([string]$propertyName) {
        if (-not $this._properties.ContainsKey($propertyName)) {
            throw "Theme Property Missing: '$propertyName'"
        }

        $prop = $this._properties[$propertyName]
        $hex = ""

        if ($prop.Type -eq 'Solid') {
            $hex = $prop.Color
        }
        elseif ($prop.Type -eq 'Gradient') {
            # Use first color for gradient fallback
            # Use ContainsKey to avoid strict mode errors on missing keys
            if ($prop.ContainsKey('Start') -and $prop.Start) {
                $hex = $prop.Start
            }
            elseif ($prop.ContainsKey('Stops') -and $prop.Stops -and $prop.Stops.Count -gt 0) {
                $hex = $prop.Stops[0].Color
            }
        }

        if ([string]::IsNullOrEmpty($hex)) { return 0 }

        return $this._ColorToInt($hex)
    }

    # Get gradient info for a property (returns null if solid)
    # Returns @{ Start = [int]; End = [int] } for gradient, or $null for solid
    [object] GetGradientInfo([string]$propertyName) {
        if (-not $this._properties.ContainsKey($propertyName)) {
            return $null
        }

        $prop = $this._properties[$propertyName]

        if ($prop.Type -eq 'Gradient') {
            $startHex = $prop.Start
            $endHex = $prop.End
            if ($startHex -and $endHex) {
                return @{
                    Start = $this._ColorToInt($startHex)
                    End = $this._ColorToInt($endHex)
                }
            }
        }

        return $null
    }

    # === INT API (For Hybrid Engine) ===

    # Get foreground Packed Int - usually solid
    [int] GetForegroundInt([string]$propertyName) {
        if (-not $this._properties.ContainsKey($propertyName)) {
            throw "Theme Property Missing: '$propertyName'"
        }

        $prop = $this._properties[$propertyName]

        if ($prop.Type -eq 'Solid') {
            return $this._GetSolidIntCached($prop.Color)
        }

        # Fallback for gradients acting as foregrounds (use generic)
        return $this.GetThemeColorInt($propertyName)
    }

    # Get background Packed Int
    [int] GetBackgroundInt([string]$propertyName, [int]$width, [int]$charIndex) {
        if (-not $this._properties.ContainsKey($propertyName)) {
            throw "Theme Property Missing: '$propertyName'"
        }

        $prop = $this._properties[$propertyName]

        if ($prop.Type -eq 'Solid') {
            return $this._GetSolidIntCached($prop.Color)
        }
        elseif ($prop.Type -eq 'Gradient') {
            # Gradient support for Ints
            $gradient = $this._GetGradientIntArrayCached($propertyName, $prop, $width)
            if ($charIndex -ge 0 -and $charIndex -lt $gradient.Count) {
                return $gradient[$charIndex]
            }
            return -1
        }

        return -1
    }

    # Cached solid color ANSI
    hidden [string] _GetSolidAnsiCached([string]$color, [bool]$background) {
        $cacheKey = "${color}_${background}_$($this._cacheGeneration)"

        if ($this._solidCache.ContainsKey($cacheKey)) {
            return $this._solidCache[$cacheKey]
        }

        $ansi = $this._ColorToAnsi($color, $background)
        $this._solidCache[$cacheKey] = $ansi
        return $ansi
    }

    # Cached solid color Int
    hidden [int] _GetSolidIntCached([string]$color) {
        $cacheKey = "${color}_$($this._cacheGeneration)"

        if ($this._solidIntCache.ContainsKey($cacheKey)) {
            return $this._solidIntCache[$cacheKey]
        }

        $intColor = $this._ColorToInt($color)
        $this._solidIntCache[$cacheKey] = $intColor
        return $intColor
    }

    # Cached gradient array
    hidden [string[]] _GetGradientArrayCached([string]$propertyName, [hashtable]$gradient, [int]$width, [bool]$background) {
        $cacheKey = "${propertyName}_${width}_${background}_$($this._cacheGeneration)"

        if ($this._gradientCache.ContainsKey($cacheKey)) {
            return $this._gradientCache[$cacheKey]
        }

        $array = $this._ComputeGradient($gradient, $width, $background)
        $this._gradientCache[$cacheKey] = $array
        return $array
    }

    # Cached gradient Int array
    hidden [int[]] _GetGradientIntArrayCached([string]$propertyName, [hashtable]$gradient, [int]$width) {
        $cacheKey = "${propertyName}_${width}_INT_$($this._cacheGeneration)"

        if ($this._gradientIntCache.ContainsKey($cacheKey)) {
            return $this._gradientIntCache[$cacheKey]
        }

        $array = $this._ComputeGradientInt($gradient, $width)
        $this._gradientIntCache[$cacheKey] = $array
        return $array
    }

    # Compute gradient as array of ANSI sequences
    hidden [string[]] _ComputeGradient([hashtable]$gradient, [int]$length, [bool]$background) {
        $result = [List[string]]::new($length)

        # Support both Stops array and simple Start/End
        $stops = $null
        if ($gradient.Stops) {
            $stops = $gradient.Stops | Sort-Object Position
        }
        elseif ($gradient.Start -and $gradient.End) {
            $stops = @(
                @{ Position = 0.0; Color = $gradient.Start }
                @{ Position = 1.0; Color = $gradient.End }
            )
        }
        else {
            # Fallback - return empty
            return @()
        }

        for ($i = 0; $i -lt $length; $i++) {
            $ratio = $(if ($length -eq 1) { 0.0 } else { $i / ($length - 1) })
            $color = $this._GetColorAtRatio($stops, $ratio)
            $result.Add($this._ColorToAnsi($color, $background))
        }

        return $result.ToArray()
    }

    # Compute gradient as array of Ints
    hidden [int[]] _ComputeGradientInt([hashtable]$gradient, [int]$length) {
        $result = [List[int]]::new($length)

        # Support both Stops array and simple Start/End
        $stops = $null
        if ($gradient.Stops) {
            $stops = $gradient.Stops | Sort-Object Position
        }
        elseif ($gradient.Start -and $gradient.End) {
            $stops = @(
                @{ Position = 0.0; Color = $gradient.Start }
                @{ Position = 1.0; Color = $gradient.End }
            )
        }
        else {
            # Fallback - return empty
            return @()
        }

        for ($i = 0; $i -lt $length; $i++) {
            $ratio = $(if ($length -eq 1) { 0.0 } else { $i / ($length - 1) })
            $color = $this._GetColorAtRatio($stops, $ratio)
            $result.Add($this._ColorToInt($color))
        }

        return $result.ToArray()
    }

    hidden [string] _GetColorAtRatio([array]$stops, [double]$ratio) {
        # Find surrounding stops
        $beforeStop = $stops[0]
        $afterStop = $stops[-1]

        for ($s = 0; $s -lt $stops.Count - 1; $s++) {
            if ($ratio -ge $stops[$s].Position -and $ratio -le $stops[$s + 1].Position) {
                $beforeStop = $stops[$s]
                $afterStop = $stops[$s + 1]
                break
            }
        }

        # Local interpolation between the two stops
        $localRatio = $(if ($afterStop.Position -eq $beforeStop.Position) {
                0.0
            }
            else {
                ($ratio - $beforeStop.Position) / ($afterStop.Position - $beforeStop.Position)
            })

        return $this._InterpolateColor($beforeStop.Color, $afterStop.Color, $localRatio)
    }

    # Linear color interpolation
    hidden [string] _InterpolateColor([string]$start, [string]$end, [double]$ratio) {
        $startHex = $start.TrimStart('#')
        $endHex = $end.TrimStart('#')

        $startR = [Convert]::ToInt32($startHex.Substring(0, 2), 16)
        $startG = [Convert]::ToInt32($startHex.Substring(2, 2), 16)
        $startB = [Convert]::ToInt32($startHex.Substring(4, 2), 16)

        $endR = [Convert]::ToInt32($endHex.Substring(0, 2), 16)
        $endG = [Convert]::ToInt32($endHex.Substring(2, 2), 16)
        $endB = [Convert]::ToInt32($endHex.Substring(4, 2), 16)

        $r = [int]($startR + ($endR - $startR) * $ratio)
        $g = [int]($startG + ($endG - $startG) * $ratio)
        $b = [int]($startB + ($endB - $startB) * $ratio)

        return "#{0:X2}{1:X2}{2:X2}" -f $r, $g, $b
    }

    # Convert hex color to ANSI escape sequence
    hidden [string] _ColorToAnsi([string]$hex, [bool]$background) {
        $hex = $hex.TrimStart('#')

        if ($hex.Length -ne 6) {
            return ''
        }

        try {
            $r = [Convert]::ToInt32($hex.Substring(0, 2), 16)
            $g = [Convert]::ToInt32($hex.Substring(2, 2), 16)
            $b = [Convert]::ToInt32($hex.Substring(4, 2), 16)

            if ($background) {
                return "`e[48;2;${r};${g};${b}m"
            }
            else {
                return "`e[38;2;${r};${g};${b}m"
            }
        }
        catch {
            # DEBUG
            # Add-Content -Path "/tmp/pmc-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcThemeEngine] ANSI CONVERSION FAILED: '$hex' - $_"
            return ''
        }
    }

    # Convert hex color to Packed Int
    hidden [int] _ColorToInt([string]$hex) {
        $hex = $hex.TrimStart('#')
        if ($hex.Length -ne 6) { return -1 }

        try {
            $r = [Convert]::ToInt32($hex.Substring(0, 2), 16)
            $g = [Convert]::ToInt32($hex.Substring(2, 2), 16)
            $b = [Convert]::ToInt32($hex.Substring(4, 2), 16)

            # Pack RGB: (R << 16) | (G << 8) | B
            return ($r -shl 16) -bor ($g -shl 8) -bor $b
        }
        catch {
            return -1
        }
    }

    # Clear all caches (call on theme reload)
    [void] InvalidateCache() {
        $this._cacheGeneration++
        $this._gradientCache.Clear()
        $this._solidCache.Clear()
        $this._solidIntCache.Clear()
        $this._gradientIntCache.Clear()
    }
}

# PmcThemeManager - Unified theme system bridging PMC and SpeedTUI
# Handles PMC's sophisticated palette derivation + SpeedTUI's theme manager

using namespace System.Collections.Generic

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Unified theme manager bridging PMC's theme system with SpeedTUI

.DESCRIPTION
PmcThemeManager provides a single interface for theme management:
- Wraps PMC's existing theme system (single hex → full palette)
- Integrates with SpeedTUI's theme manager (if available)
- Provides unified color role API
- Handles theme switching and synchronization
- Singleton pattern for global access

.EXAMPLE
$theme = [PmcThemeManager]::GetInstance()
$color = $theme.GetColor('Primary')
$ansi = $theme.GetAnsiSequence('Primary', $false)
#>
class PmcThemeManager {
    # === Singleton Instance ===
    hidden static [PmcThemeManager]$_instance = $null

    # === PMC Theme Data ===
    [hashtable]$PmcTheme           # From Get-PmcState -Section 'Display' -Key 'Theme'
    [hashtable]$StyleTokens        # From Get-PmcState -Section 'Display' -Key 'Styles'
    [hashtable]$ColorPalette       # From Get-PmcColorPalette()

    # === SpeedTUI Integration ===
    [object]$SpeedTUITheme = $null # SpeedTUI ThemeManager (if available)

    # === Cached Data ===
    hidden [hashtable]$_colorCache = @{}
    hidden [hashtable]$_ansiCache = @{}

    # === Singleton Constructor ===
    hidden PmcThemeManager() {
        $this._Initialize()
    }

    <#
    .SYNOPSIS
    Get singleton instance of PmcThemeManager
    #>
    static [PmcThemeManager] GetInstance() {
        if ($null -eq [PmcThemeManager]::_instance) {
            [PmcThemeManager]::_instance = [PmcThemeManager]::new()
        }
        return [PmcThemeManager]::_instance
    }

    # === Initialization ===

    <#
    .SYNOPSIS
    Initialize theme system by loading PMC theme state
    #>
    hidden [void] _Initialize() {
        try {
            if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
                Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [PmcThemeManager] _Initialize started"
            }

            # Load PMC theme from state (for StyleTokens)
            $displayState = Get-PmcState -Section 'Display'
            if ($displayState) {
                $this.PmcTheme = $displayState.Theme
                $this.StyleTokens = $displayState.Styles
            }

            # Load color palette
            $this.ColorPalette = Get-PmcColorPalette

            # Initialize caches
            $this._colorCache = @{}
            $this._ansiCache = @{}

            # ONE PATH: Load theme from file
            $theme = Get-ActiveTheme
            if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
                Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [PmcThemeManager] Active theme name: '$($theme.Name)' Hex: '$($theme.Hex)' Props: $($theme.Properties.Count)"
            }

            if ($theme -and $theme.Properties) {
                [PmcThemeEngine]::GetInstance().Configure($theme.Properties, $this.ColorPalette)
            } else {
                # No theme file found - this should not happen
                if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
                    Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [PmcThemeManager] ERROR: No theme loaded"
                }
            }
        } catch {
            if ($null -ne (Get-Variable -Name PmcDebug -Scope Global -ErrorAction SilentlyContinue) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
                Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [PmcThemeManager] _Initialize ERROR: $_"
            }
        }
    }


    <#
    .SYNOPSIS
    Try to initialize SpeedTUI theme manager if available
    #>
    hidden [void] _InitializeSpeedTUITheme() {
        # SpeedTUI integration would go here if needed
        # For now, PMC theme is primary source of truth
        $this.SpeedTUITheme = $null
    }


    # === Public API ===

    <#
    .SYNOPSIS
    Get hex color string for a specific role

    .PARAMETER role
    Color role: Primary, Border, Text, Muted, Error, Warning, Success, Bright, Header, etc.

    .OUTPUTS
    Hex color string (e.g., "#33aaff") or empty string if not found

    .EXAMPLE
    $color = $theme.GetColor('Primary')  # Returns "#33aaff"
    #>
    [string] GetColor([string]$role) {
        # Check cache first
        if ($this._colorCache.ContainsKey($role)) {
            return $this._colorCache[$role]
        }

        $color = $this._ResolveColor($role)
        $this._colorCache[$role] = $color
        return $color
    }

    <#
    .SYNOPSIS
    Resolve color from theme system
    #>
    hidden [string] _ResolveColor([string]$role) {
        # Try style tokens first (includes Fg property)
        if ($this.StyleTokens -and $this.StyleTokens.ContainsKey($role)) {
            $style = $this.StyleTokens[$role]
            if ($style.Fg) {
                return $style.Fg
            }
        }

        # Try color palette (RGB object → hex)
        if ($this.ColorPalette -and $this.ColorPalette.ContainsKey($role)) {
            $rgb = $this.ColorPalette[$role]
            if ($rgb.R -ne $null -and $rgb.G -ne $null -and $rgb.B -ne $null) {
                return ("#{0:X2}{1:X2}{2:X2}" -f $rgb.R, $rgb.G, $rgb.B)
            }
        }

        # STRICT THEME ENFORCEMENT: No fallbacks.
        throw "Theme Property Missing: '$role'"
    }

    <#
    .SYNOPSIS
    Get ANSI escape sequence for a color role

    .PARAMETER role
    Color role

    .PARAMETER background
    If true, returns background color sequence; otherwise foreground

    .OUTPUTS
    ANSI escape sequence (e.g., "`e[38;2;51;170;255m")

    .EXAMPLE
    $ansi = $theme.GetAnsiSequence('Primary', $false)  # Foreground
    $ansiBg = $theme.GetAnsiSequence('Primary', $true)  # Background
    #>
    [string] GetAnsiSequence([string]$role, [bool]$background = $false) {
        $cacheKey = "${role}_${background}"

        # Check cache
        if ($this._ansiCache.ContainsKey($cacheKey)) {
            return $this._ansiCache[$cacheKey]
        }

        # Get hex color
        $hex = $this.GetColor($role)
        if ([string]::IsNullOrEmpty($hex)) {
            return ''
        }

        # Convert to ANSI
        $ansi = $this._HexToAnsi($hex, $background)
        $this._ansiCache[$cacheKey] = $ansi
        return $ansi
    }

    <#
    .SYNOPSIS
    Convert hex color to ANSI sequence
    #>
    hidden [string] _HexToAnsi([string]$hex, [bool]$background) {
        # Parse hex
        $hex = $hex.TrimStart('#')
        if ($hex.Length -ne 6) { return '' }

        try {
            $r = [Convert]::ToInt32($hex.Substring(0, 2), 16)
            $g = [Convert]::ToInt32($hex.Substring(2, 2), 16)
            $b = [Convert]::ToInt32($hex.Substring(4, 2), 16)

            if ($background) {
                return "`e[48;2;${r};${g};${b}m"
            } else {
                return "`e[38;2;${r};${g};${b}m"
            }
        } catch {
            return ''
        }
    }

    <#
    .SYNOPSIS
    Get style object with foreground, background, and formatting

    .PARAMETER role
    Style role from StyleTokens (Title, Header, Body, Editing, Selected, etc.)

    .OUTPUTS
    Hashtable with Fg, Bg, Bold properties

    .EXAMPLE
    $style = $theme.GetStyle('Selected')
    # Returns @{ Bg = '#33aaff'; Fg = 'White' }
    #>
    [hashtable] GetStyle([string]$role) {
        if ($this.StyleTokens -and $this.StyleTokens.ContainsKey($role)) {
            return $this.StyleTokens[$role]
        }

        # Fallback: construct basic style from color
        return @{
            Fg = $this.GetColor($role)
        }
    }

    <#
    .SYNOPSIS
    Get complete theme hashtable with ANSI sequences for dialogs/widgets

    .DESCRIPTION
    Returns a standard hashtable with common theme elements as ANSI sequences.
    Useful for passing to dialogs, widgets, and other components that need
    multiple theme colors.

    .OUTPUTS
    Hashtable with ANSI sequences for common theme roles

    .EXAMPLE
    $theme = $themeManager.GetTheme()
    # Returns @{
    #   Primary = "`e[38;2;51;170;255m"
    #   PrimaryBg = "`e[48;2;51;170;255m"
    #   Text = "`e[38;2;204;204;204m"
    #   ...
    # }
    #>
    [hashtable] GetTheme() {
        return @{
            # Primary colors
            Primary = $this.GetAnsiSequence('Primary', $false)
            PrimaryBg = $this.GetAnsiSequence('Primary', $true)

            # Dialog colors (common pattern from TimeListScreen)
            DialogBg = $this.GetAnsiSequence('Surface', $true)
            DialogFg = $this.GetAnsiSequence('OnSurface', $false)
            DialogBorder = $this.GetAnsiSequence('Outline', $false)

            # Text hierarchy
            Header = $this.GetAnsiSequence('Header', $false)
            Title = $this.GetAnsiSequence('Title', $false)
            Text = $this.GetAnsiSequence('Text', $false)
            Body = $this.GetAnsiSequence('Body', $false)
            Muted = $this.GetAnsiSequence('Muted', $false)
            Label = $this.GetAnsiSequence('Label', $false)

            # Semantic colors
            Highlight = $this.GetAnsiSequence('Highlight', $false)
            Error = $this.GetAnsiSequence('Error', $false)
            Warning = $this.GetAnsiSequence('Warning', $false)
            Success = $this.GetAnsiSequence('Success', $false)
            Info = $this.GetAnsiSequence('Info', $false)

            # UI elements
            Border = $this.GetAnsiSequence('Border', $false)
            Status = $this.GetAnsiSequence('Status', $false)

            # Special
            Bright = $this.GetAnsiSequence('Bright', $false)
            Reset = "`e[0m"
        }
    }

    # === Theme Management ===

    <#
    .SYNOPSIS
    Reload theme from PMC state system

    .DESCRIPTION
    Call this after theme changes to refresh cached data
    #>
    [void] Reload() {
        # Clear caches
        $this._colorCache.Clear()
        $this._ansiCache.Clear()

        # Reload from state
        $this._Initialize()
    }

    <#
    .SYNOPSIS
    Set theme hex color and regenerate palette

    .PARAMETER hex
    New theme hex color (e.g., "#33aaff")

    .DESCRIPTION
    Updates PMC theme, saves config, and regenerates palette
    #>
    [void] SetTheme([string]$hex) {
        if ([string]::IsNullOrWhiteSpace($hex)) {
            throw "Theme hex cannot be empty"
        }

        # Normalize hex format
        if (-not $hex.StartsWith('#')) {
            $hex = '#' + $hex
        }

        try {
            # Update config
            $cfg = Get-PmcConfig
            if (-not $cfg.Display) { $cfg.Display = @{} }
            if (-not $cfg.Display.Theme) { $cfg.Display.Theme = @{} }
            $cfg.Display.Theme.Hex = $hex
            Save-PmcConfig $cfg

            # Force theme re-initialization
            Initialize-PmcThemeSystem -Force

            # Reload this manager
            $this.Reload()

            # CRITICAL FIX: Notify PmcThemeEngine of theme change
            # The engine caches theme properties and needs to reload
            try {
                $engine = [PmcThemeEngine]::GetInstance()
                if ($engine) {
                    $engine.InvalidateCache()
                }
            } catch {
                # PmcThemeEngine may not be available in all contexts
            }
        } catch {
            throw "Failed to set theme: $_"
        }
    }

    <#
    .SYNOPSIS
    Get current theme hex color

    .OUTPUTS
    Hex color string (e.g., "#33aaff")
    #>
    [string] GetCurrentThemeHex() {
        if ($this.PmcTheme -and $this.PmcTheme.Hex) {
            return $this.PmcTheme.Hex
        }
        return '#33aaff'
    }

    # === Utility Methods ===

    <#
    .SYNOPSIS
    Get RGB components from hex color

    .PARAMETER hex
    Hex color string (with or without #)

    .OUTPUTS
    Hashtable with R, G, B properties (0-255)
    #>
    [hashtable] HexToRgb([string]$hex) {
        $hex = $hex.TrimStart('#')
        if ($hex.Length -ne 6) {
            return @{ R = 0; G = 0; B = 0 }
        }

        try {
            return @{
                R = [Convert]::ToInt32($hex.Substring(0, 2), 16)
                G = [Convert]::ToInt32($hex.Substring(2, 2), 16)
                B = [Convert]::ToInt32($hex.Substring(4, 2), 16)
            }
        } catch {
            return @{ R = 0; G = 0; B = 0 }
        }
    }
}

﻿using namespace System
using namespace System.Collections.Generic
using namespace System.Text
using namespace System.Globalization

# DatePicker.ps1 - Production-ready date picker widget for PMC TUI
# Supports both text input (smart date parsing) and calendar mode (visual month grid)
#
# Usage:
#   $picker = [DatePicker]::new()
#   $picker.SetPosition(10, 5)
#   $picker.SetSize(35, 14)
#   $picker.SetDate([DateTime]::Today)
#
#   # Render
#   $ansiOutput = $picker.Render()
#
#   # Handle input
#   $key = [Console]::ReadKey($true)
#   $handled = $picker.HandleInput($key)
#
#   # Get result
#   if ($picker.IsConfirmed) {
#       $selected = $picker.GetSelectedDate()
#   }

Set-StrictMode -Version Latest

# Load PmcWidget base class if not already loaded
if (-not ([System.Management.Automation.PSTypeName]'PmcWidget').Type) {
    . "$PSScriptRoot/PmcWidget.ps1"
}

<#
.SYNOPSIS
Production-ready DatePicker widget with text and calendar modes

.DESCRIPTION
Features:
- Text mode: Smart date parsing (today, tomorrow, next friday, +7, eom, ISO dates)
- Calendar mode: Visual month grid with arrow navigation
- Full keyboard navigation
- Theme integration
- Event callbacks for date changes, confirmation, cancellation
- Automatic bounds clamping and validation

.EXAMPLE
$picker = [DatePicker]::new()
$picker.SetPosition(10, 5)
$picker.SetSize(35, 14)
$picker.SetDate([DateTime]::Today)
$ansiOutput = $picker.Render()
#>
class DatePicker : PmcWidget {
    # === Public Properties ===
    [bool]$IsConfirmed = $false      # True when user presses Enter
    [bool]$IsCancelled = $false      # True when user presses Esc

    # === Event Callbacks ===
    [scriptblock]$OnDateChanged = {}  # Called when date changes: param($newDate)
    [scriptblock]$OnConfirmed = {}    # Called when Enter pressed: param($finalDate)
    [scriptblock]$OnCancelled = {}    # Called when Esc pressed

    # === Private State ===
    hidden [DateTime]$_selectedDate = [DateTime]::Today
    hidden [DateTime]$_calendarMonth = [DateTime]::Today  # Month being displayed in calendar
    hidden [bool]$_isCalendarMode = $false                # False = text mode, True = calendar mode
    hidden [string]$_textInput = ""                       # Text mode input buffer
    hidden [string]$_errorMessage = ""                    # Error message to display
    hidden [int]$_cursorPosition = 0                      # Text cursor position

    # === Constructor ===
    DatePicker() : base("DatePicker") {
        $this.Width = 35
        $this.Height = 14
        $this._selectedDate = [DateTime]::Today
        $this._calendarMonth = [DateTime]::Today
        $this._textInput = $this._selectedDate.ToString("yyyy-MM-dd")
        $this.CanFocus = $true
        $this._isCalendarMode = $true  # ALWAYS start in calendar mode
    }

    # === Public API Methods ===

    <#
    .SYNOPSIS
    Set the currently selected date

    .PARAMETER date
    DateTime to set as selected
    #>
    [void] SetDate([DateTime]$date) {
        $this._selectedDate = $date
        $this._calendarMonth = $date
        $this._textInput = $date.ToString("yyyy-MM-dd")
        $this._errorMessage = ""
        $this._InvokeCallback($this.OnDateChanged, $date)
    }

    <#
    .SYNOPSIS
    Get the currently selected date

    .OUTPUTS
    DateTime object
    #>
    [DateTime] GetSelectedDate() {
        return $this._selectedDate
    }

    <#
    .SYNOPSIS
    Handle keyboard input

    .PARAMETER keyInfo
    ConsoleKeyInfo from [Console]::ReadKey($true)

    .OUTPUTS
    True if input was handled, False otherwise
    #>
    [bool] HandleInput([ConsoleKeyInfo]$keyInfo) {
        # Global keys
        if ($keyInfo.Key -eq 'Enter') {
            $this.IsConfirmed = $true
            $this._InvokeCallback($this.OnConfirmed, $this._selectedDate)
            return $true
        }

        if ($keyInfo.Key -eq 'Escape') {
            $this.IsCancelled = $true
            $this._InvokeCallback($this.OnCancelled, $null)
            return $true
        }

        # Always handle as calendar mode
        return $this._HandleCalendarInput($keyInfo)
    }

    # === Layout System ===

    [void] RegisterLayout([object]$engine) {
        ([PmcWidget]$this).RegisterLayout($engine)
        # Regions removed - using direct WriteAt in RenderToEngine for reliability
    }

    <#
    .SYNOPSIS
    Render date picker to engine
    #>
    [void] RenderToEngine([object]$engine) {
        # DEBUG: Conditional logging for rendering issues
        if ($global:PmcTuiLogFile -and $global:PmcTuiLogLevel -ge 3) {
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] [DatePicker.RenderToEngine] CALLED X=$($this.X) Y=$($this.Y) W=$($this.Width) H=$($this.Height)"
        }

        # Clamp to bounds
        $this._ClampToBounds($engine)

        # Get background color first (needed for Fill and DrawBox)
        $bg = $this.GetThemedBgInt('Background.Row', $this.Width, 0)

        # Ensure Popup is drawn ABOVE everything else
        if ($engine.PSObject.Methods['BeginLayer']) {
            $engine.BeginLayer(100)
        }

        $fg = $this.GetThemedInt('Foreground.Row')
        $borderFg = $this.GetThemedInt('Border.Widget')
        $primaryFg = $this.GetThemedInt('Foreground.Title')
        $mutedFg = $this.GetThemedInt('Foreground.Muted')
        $errorFg = $this.GetThemedInt('Foreground.Error')
        $successFg = $this.GetThemedInt('Foreground.Success')
        $highlightBg = $this.GetThemedBgInt('Background.RowSelected', 1, 0)
        $highlightFg = $this.GetThemedInt('Foreground.RowSelected')

        # DEBUG: Log colors
        if ($global:PmcTuiLogFile -and $global:PmcTuiLogLevel -ge 3) {
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] [DatePicker.RenderToEngine] Colors: bg=$bg borderFg=$borderFg"
        }

        # Draw Box (Panel Background)
        $engine.Fill($this.X, $this.Y, $this.Width, $this.Height, ' ', $fg, $bg)
        $engine.DrawBox($this.X, $this.Y, $this.Width, $this.Height, $borderFg, $bg)

        # Title
        $title = "Select Date"
        $pad = [Math]::Max(0, [Math]::Floor(($this.Width - 4 - $title.Length) / 2))
        $engine.WriteAt($this.X + 2 + $pad, $this.Y + 1, $title, $primaryFg, $bg)

        # Status
        $currentValue = "Current: " + $this._selectedDate.ToString("yyyy-MM-dd ddd")
        $engine.WriteAt($this.X + 2, $this.Y + 2, $this.PadText($currentValue, $this.Width - 4, 'left'), $fg, $bg)

        # Calendar Header
        $monthYear = $this._calendarMonth.ToString("MMMM yyyy")
        $pad = [Math]::Max(0, [Math]::Floor(($this.Width - 4 - $monthYear.Length) / 2))
        $engine.WriteAt($this.X + 2 + $pad, $this.Y + 4, $monthYear, $primaryFg, $bg)

        # Day Names
        $dayNames = "Su Mo Tu We Th Fr Sa"
        $pad = [Math]::Max(0, [Math]::Floor(($this.Width - 4 - $dayNames.Length) / 2))
        $engine.WriteAt($this.X + 2 + $pad, $this.Y + 5, $dayNames, $primaryFg, $bg)

        # Grid
        $firstDay = [DateTime]::new($this._calendarMonth.Year, $this._calendarMonth.Month, 1)
        $daysInMonth = [DateTime]::DaysInMonth($this._calendarMonth.Year, $this._calendarMonth.Month)
        $startDayOfWeek = [int]$firstDay.DayOfWeek
        $today = [DateTime]::Today

        for ($week = 0; $week -lt 6; $week++) {
            $rowY = $this.Y + 6 + $week
            $padGrid = [Math]::Max(0, [Math]::Floor(($this.Width - 4 - 20) / 2))
            $startX = $this.X + 2 + $padGrid

            for ($dow = 0; $dow -lt 7; $dow++) {
                $dayNum = ($week * 7 + $dow) - $startDayOfWeek + 1
                $cellX = $startX + ($dow * 3)

                if ($dayNum -ge 1 -and $dayNum -le $daysInMonth) {
                    $thisDate = [DateTime]::new($this._calendarMonth.Year, $this._calendarMonth.Month, $dayNum)
                    $dayStr = $dayNum.ToString().PadLeft(2)

                    $isSelected = ($thisDate.Date -eq $this._selectedDate.Date)
                    $isToday = ($thisDate.Date -eq $today)

                    $cBg = $bg
                    $cFg = $fg

                    if ($isSelected) {
                        $cBg = $highlightBg
                        $cFg = $highlightFg
                    }
                    elseif ($isToday) {
                        $cFg = $primaryFg
                    }

                    $engine.WriteAt($cellX, $rowY, $dayStr, $cFg, $cBg)
                }
            }
        }

        # Help
        $helpText = "Enter: Select"
        $engine.WriteAt($this.X + 2, $this.Y + $this.Height - 3, $this.PadText($helpText, $this.Width - 4, 'left'), $mutedFg, $bg)

        # Error
        if ($this._errorMessage) {
            $engine.WriteAt($this.X + 2, $this.Y + $this.Height - 1, $this.PadText($this._errorMessage, $this.Width - 4, 'left'), $errorFg, $bg)
        }

        # End layer elevation
        if ($engine.PSObject.Methods['EndLayer']) {
            $engine.EndLayer()
        }

        # DEBUG: Log completion
        if ($global:PmcTuiLogFile -and $global:PmcTuiLogLevel -ge 3) {
            Add-Content -Path $global:PmcTuiLogFile -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] [DatePicker.RenderToEngine] COMPLETE"
        }
    }

    <#
    .SYNOPSIS
    Ensure widget stays within screen bounds (Engine Viewport)
    #>
    hidden [void] _ClampToBounds([object]$engine) {
        # Check Engine bounds first (Authoritative Viewport)
        if ($engine -and $engine.PSObject.Properties['Width']) {
            $termWidth = $engine.Width
            $termHeight = $engine.Height
        }
        else {
            # Fallback to console (but verify console availability)
            try {
                $termWidth = [Console]::WindowWidth
                $termHeight = [Console]::WindowHeight
            }
            catch {
                $termWidth = 80
                $termHeight = 24
            }
        }

        # Clamp X
        if ($this.X + $this.Width -ge $termWidth) {
            $this.X = [Math]::Max(0, $termWidth - $this.Width)
        }

        # Clamp Y
        if ($this.Y + $this.Height -ge $termHeight) {
            $this.Y = [Math]::Max(0, $termHeight - $this.Height)
        }
    }

    <#
    .SYNOPSIS
    Render the date picker widget (Legacy)
    #>
    [string] Render() {
        return ""
    }

    # === Private Helper Methods ===

    hidden [void] _ToggleMode() {
        $this._isCalendarMode = -not $this._isCalendarMode
        $this._errorMessage = ""

        if ($this._isCalendarMode) {
            $parsed = $this._ParseTextInput()
            if ($parsed) {
                $this._selectedDate = $parsed
                $this._calendarMonth = $parsed
            }
        }
        else {
            $this._textInput = $this._selectedDate.ToString("yyyy-MM-dd")
            $this._cursorPosition = $this._textInput.Length
        }
    }

    hidden [bool] _HandleCalendarInput([ConsoleKeyInfo]$keyInfo) {
        $changed = $false

        switch ($keyInfo.Key) {
            'LeftArrow' {
                $this._selectedDate = $this._selectedDate.AddDays(-1)
                $changed = $true
            }
            'RightArrow' {
                $this._selectedDate = $this._selectedDate.AddDays(1)
                $changed = $true
            }
            'UpArrow' {
                $this._selectedDate = $this._selectedDate.AddDays(-7)
                $changed = $true
            }
            'DownArrow' {
                $this._selectedDate = $this._selectedDate.AddDays(7)
                $changed = $true
            }
            'PageUp' {
                $this._selectedDate = $this._selectedDate.AddMonths(-1)
                $this._calendarMonth = $this._selectedDate
                $changed = $true
            }
            'PageDown' {
                $this._selectedDate = $this._selectedDate.AddMonths(1)
                $this._calendarMonth = $this._selectedDate
                $changed = $true
            }
            'Home' {
                $this._selectedDate = [DateTime]::new($this._selectedDate.Year, $this._selectedDate.Month, 1)
                $changed = $true
            }
            'End' {
                $daysInMonth = [DateTime]::DaysInMonth($this._selectedDate.Year, $this._selectedDate.Month)
                $this._selectedDate = [DateTime]::new($this._selectedDate.Year, $this._selectedDate.Month, $daysInMonth)
                $changed = $true
            }
        }

        if ($changed) {
            if ($this._selectedDate.Month -ne $this._calendarMonth.Month -or
                $this._selectedDate.Year -ne $this._calendarMonth.Year) {
                $this._calendarMonth = $this._selectedDate
            }
            $this._InvokeCallback($this.OnDateChanged, $this._selectedDate)
        }

        return $changed
    }

    hidden [object] _ParseTextInput() {
        $input = $this._textInput.Trim().ToLower()

        if ([string]::IsNullOrWhiteSpace($input)) {
            $this._errorMessage = "Empty input"
            return $null
        }

        try {
            if ($input -eq 'today') { return [DateTime]::Today }
            if ($input -eq 'tomorrow') { return [DateTime]::Today.AddDays(1) }

            if ($input -match '^([+-]?\d+)$') {
                $days = [int]$Matches[1]
                return [DateTime]::Today.AddDays($days)
            }

            if ($input -eq 'eom') {
                $today = [DateTime]::Today
                $daysInMonth = [DateTime]::DaysInMonth($today.Year, $today.Month)
                return [DateTime]::new($today.Year, $today.Month, $daysInMonth)
            }

            if ($input -match '^next\s+(\w+)') {
                $dayName = $Matches[1]
                $targetDay = $this._ParseDayOfWeek($dayName)
                if ($targetDay -ne $null) {
                    $today = [DateTime]::Today
                    $daysUntil = (([int]$targetDay - [int]$today.DayOfWeek + 7) % 7)
                    if ($daysUntil -eq 0) { $daysUntil = 7 }
                    return $today.AddDays($daysUntil)
                }
            }

            $targetDay = $this._ParseDayOfWeek($input)
            if ($targetDay -ne $null) {
                $today = [DateTime]::Today
                $daysUntil = (([int]$targetDay - [int]$today.DayOfWeek + 7) % 7)
                if ($daysUntil -eq 0) { $daysUntil = 7 }
                return $today.AddDays($daysUntil)
            }

            if ($input -match '^\d{4}-\d{2}-\d{2}$') {
                $parsed = [DateTime]::ParseExact($input, 'yyyy-MM-dd', [CultureInfo]::InvariantCulture)
                return $parsed
            }

            $parsed = [DateTime]::Parse($input, [CultureInfo]::InvariantCulture)
            return $parsed
        }
        catch {
            $this._errorMessage = "Invalid date: $input"
            return $null
        }

        $this._errorMessage = "Unrecognized format: $input"
        return $null
    }

    hidden [object] _ParseDayOfWeek([string]$name) {
        $name = $name.ToLower()
        switch -Regex ($name) {
            '^su(n|nday)?$' { return [DayOfWeek]::Sunday }
            '^mo(n|nday)?$' { return [DayOfWeek]::Monday }
            '^tu(e|es|esday)?$' { return [DayOfWeek]::Tuesday }
            '^we(d|dnesday)?$' { return [DayOfWeek]::Wednesday }
            '^th(u|ursday)?$' { return [DayOfWeek]::Thursday }
            '^fr(i|iday)?$' { return [DayOfWeek]::Friday }
            '^sa(t|turday)?$' { return [DayOfWeek]::Saturday }
        }
        return $null
    }

    hidden [void] _InvokeCallback([scriptblock]$callback, $arg) {
        if ($callback -and $callback -ne {}) {
            try {
                if ($arg -ne $null) {
                    & $callback $arg
                }
                else {
                    & $callback
                }
            }
            catch {
                # Silently ignore callback errors
            }
        }
    }
}

﻿using namespace System.Collections.Generic
using namespace System.Text

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Dialog system for PMC TUI

.DESCRIPTION
Provides reusable dialog components:
- ConfirmDialog: Yes/No confirmation
- TextInputDialog: Single line text input
- MessageDialog: Display message with OK button
#>

class PmcDialog {
    [string]$Title
    [string]$Message
    [int]$Width
    [int]$Height
    [bool]$IsComplete = $false
    [bool]$Result = $false
    [string]$TextResult = ''

    PmcDialog([string]$title, [string]$message) {
        $this.Title = $title
        $this.Message = $message
        $this.Width = [Math]::Max(60, [Math]::Max($title.Length + 10, $message.Length + 10))
        $this.Height = 10
    }

    [string] Render([int]$termWidth, [int]$termHeight, [hashtable]$theme) {
        # Legacy render stub
        return ""
    }

    <#
    .SYNOPSIS
    Render directly to engine (new high-performance path)
    #>
    [void] RenderToEngine([object]$engine) {
        # Calculate centered position if not set (or if we want to force center)
        # But Widgets usually have X/Y set. 
        # Dialogs often need re-centering on resize.
        # Let's assume layout manager or caller sets X/Y.

        # Colors (Ints)
        # Colors (Themed)
        $bg = $this.GetThemedColorInt('Background.Widget')
        $fg = $this.GetThemedColorInt('Foreground.Primary')
        $borderFg = $this.GetThemedColorInt('Border.Widget')
        $highlightFg = $this.GetThemedColorInt('Foreground.Title')

        # Shadow (Offset 2,1)
        $shadowBg = [HybridRenderEngine]::_PackRGB(0, 0, 0)
        $engine.Fill($this.X + 2, $this.Y + 1, $this.Width, $this.Height, ' ', -1, $shadowBg)

        # Main Box
        $engine.Fill($this.X, $this.Y, $this.Width, $this.Height, ' ', $fg, $bg)
        $engine.DrawBox($this.X, $this.Y, $this.Width, $this.Height, $borderFg, $bg)

        # Title
        $titleX = $this.X + [Math]::Floor(($this.Width - $this.Title.Length) / 2)
        $engine.WriteAt($titleX, $this.Y + 1, $this.Title, $highlightFg, $bg)

        # Message
        if ($this.Message) {
            $msgX = $this.X + [Math]::Floor(($this.Width - $this.Message.Length) / 2)
            $engine.WriteAt($msgX, $this.Y + 3, $this.Message, $fg, $bg)
        }
    }

    [bool] HandleInput([ConsoleKeyInfo]$keyInfo) {
        # Override in derived classes
        return $false
    }
}

class ConfirmDialog : PmcDialog {
    [int]$SelectedButton = 0  # 0 = Yes, 1 = No

    ConfirmDialog([string]$title, [string]$message) : base($title, $message) {
    }

    [void] RenderToEngine([object]$engine) {
        ([PmcDialog]$this).RenderToEngine($engine)

        $bg = $this.GetThemedColorInt('Background.Widget')
        $fg = $this.GetThemedColorInt('Foreground.Primary')
        $highlightBg = $this.GetThemedColorInt('Background.RowSelected')

        # Buttons
        $yesText = " Yes "
        $noText = " No "
        $gap = 4
        $totalW = $yesText.Length + $noText.Length + $gap

        $btnX = $this.X + [Math]::Floor(($this.Width - $totalW) / 2)
        $btnY = $this.Y + 6

        # Yes
        $yesBg = if ($this.SelectedButton -eq 0) { $highlightBg } else { $bg }
        $engine.WriteAt($btnX, $btnY, $yesText, $fg, $yesBg)

        # No
        $noBg = if ($this.SelectedButton -eq 1) { $highlightBg } else { $bg }
        $engine.WriteAt($btnX + $yesText.Length + $gap, $btnY, $noText, $fg, $noBg)
    }

    [string] Render([int]$termWidth, [int]$termHeight, [hashtable]$theme) { return "" }

    [bool] HandleInput([ConsoleKeyInfo]$keyInfo) {
        switch ($keyInfo.Key) {
            'LeftArrow' {
                $this.SelectedButton = 0
                return $true
            }
            'RightArrow' {
                $this.SelectedButton = 1
                return $true
            }
            'Tab' {
                $this.SelectedButton = 1 - $this.SelectedButton
                return $true
            }
            'Enter' {
                $this.Result = ($this.SelectedButton -eq 0)
                $this.IsComplete = $true
                return $true
            }
            'Escape' {
                $this.Result = $false
                $this.IsComplete = $true
                return $true
            }
            'Y' {
                $this.Result = $true
                $this.IsComplete = $true
                return $true
            }
            'N' {
                $this.Result = $false
                $this.IsComplete = $true
                return $true
            }
        }
        return $false
    }
}

class TextInputDialog : PmcDialog {
    [string]$InputBuffer = ''
    [string]$Prompt

    TextInputDialog([string]$title, [string]$prompt, [string]$defaultValue) : base($title, $prompt) {
        $this.Prompt = $prompt
        $this.InputBuffer = $defaultValue
    }

    [void] RenderToEngine([object]$engine) {
        ([PmcDialog]$this).RenderToEngine($engine)

        $bg = $this.GetThemedColorInt('Background.Widget')
        $fg = $this.GetThemedColorInt('Foreground.Primary')
        $inputBg = $this.GetThemedColorInt('Background.Field')
        $cursorFg = $this.GetThemedColorInt('Foreground.Muted')

        $inputWidth = $this.Width - 8
        $inputX = $this.X + 4
        $inputY = $this.Y + 5

        $display = $this.InputBuffer
        if ($display.Length -gt $inputWidth - 2) {
            $display = $display.Substring($display.Length - $inputWidth + 2)
        }

        $engine.Fill($inputX, $inputY, $inputWidth, 1, ' ', $fg, $inputBg)
        $engine.WriteAt($inputX + 1, $inputY, $display, $fg, $inputBg)
        $engine.WriteAt($inputX + 1 + $display.Length, $inputY, "_", $cursorFg, $inputBg)

        # Hint
        $hint = "Enter: OK | Esc: Cancel"
        $hintX = $this.X + [Math]::Floor(($this.Width - $hint.Length) / 2)
        $engine.WriteAt($hintX, $this.Y + 7, $hint, $cursorFg, $bg)
    }

    [string] Render([int]$termWidth, [int]$termHeight, [hashtable]$theme) { return "" }

    [bool] HandleInput([ConsoleKeyInfo]$keyInfo) {
        switch ($keyInfo.Key) {
            'Enter' {
                $this.TextResult = $this.InputBuffer
                $this.Result = $true
                $this.IsComplete = $true
                return $true
            }
            'Escape' {
                $this.Result = $false
                $this.IsComplete = $true
                return $true
            }
            'Backspace' {
                if ($this.InputBuffer.Length > 0) {
                    $this.InputBuffer = $this.InputBuffer.Substring(0, $this.InputBuffer.Length - 1)
                }
                return $true
            }
            default {
                if ($keyInfo.KeyChar -ge 32 -and $keyInfo.KeyChar -le 126) {
                    $this.InputBuffer += $keyInfo.KeyChar
                }
                return $true
            }
        }
        return $false
    }
}

class MessageDialog : PmcDialog {
    MessageDialog([string]$title, [string]$message) : base($title, $message) {
    }

    [void] RenderToEngine([object]$engine) {
        ([PmcDialog]$this).RenderToEngine($engine)

        $bg = $this.GetThemedColorInt('Background.Widget')
        $fg = $this.GetThemedColorInt('Foreground.Primary')
        $highlightBg = $this.GetThemedColorInt('Background.RowSelected')

        $ok = " OK "
        $okX = $this.X + [Math]::Floor(($this.Width - $ok.Length) / 2)

        $engine.WriteAt($okX, $this.Y + 6, $ok, $fg, $highlightBg)
    }

    [string] Render([int]$termWidth, [int]$termHeight, [hashtable]$theme) { return "" }

    [bool] HandleInput([ConsoleKeyInfo]$keyInfo) {
        switch ($keyInfo.Key) {
            'Enter' {
                $this.Result = $true
                $this.IsComplete = $true
                return $true
            }
            'Escape' {
                $this.Result = $true
                $this.IsComplete = $true
                return $true
            }
        }
        return $false
    }
}

<#
.SYNOPSIS
Multi-field project form dialog
#>
class ProjectFormDialog : PmcDialog {
    [hashtable]$Fields = @{
        name        = ''
        description = ''
        path        = ''
        aliases     = ''
    }
    [string]$CurrentField = 'name'
    [array]$FieldOrder = @('name', 'description', 'path', 'aliases')
    [hashtable]$FieldLabels = @{
        name        = 'Name'
        description = 'Description'
        path        = 'Path'
        aliases     = 'Aliases'
    }
    [bool]$IsEditMode = $false
    [string]$OriginalName = ''

    # Constructor for new project
    ProjectFormDialog() : base("Add Project", "Enter project details") {
        $this.Width = 70
        $this.Height = 16
        $this.IsEditMode = $false
    }

    # Constructor for editing existing project
    ProjectFormDialog([hashtable]$existingProject) : base("Edit Project", "Modify project details") {
        $this.Width = 70
        $this.Height = 16
        $this.IsEditMode = $true
        $this.OriginalName = $existingProject.name

        # Populate fields from existing project
        $this.Fields.name = $existingProject.name
        $this.Fields.description = $(if ($existingProject.description) { $existingProject.description } else { '' })
        $this.Fields.path = $(if ($existingProject.path) { $existingProject.path } else { '' })
        $this.Fields.aliases = $(if ($existingProject.aliases -and $existingProject.aliases.Count -gt 0) {
                $existingProject.aliases -join ', '
            }
            else {
                ''
            })
    }

    [void] RenderToEngine([object]$engine) {
        # Base renders Frame + Title
        ([PmcDialog]$this).RenderToEngine($engine)

        $bg = $this.GetThemedColorInt('Background.Widget')
        $fg = $this.GetThemedColorInt('Foreground.Primary')
        $highlight = $this.GetThemedColorInt('Foreground.RowSelected')
        $accentBg = $this.GetThemedColorInt('Background.RowSelected')
        $muted = $this.GetThemedColorInt('Foreground.Muted')

        # Override base BG for form? Or match it. Base uses 45,55,72. 
        # This form used 30,30,30. Let's use Base colors for consistency if possible, 
        # or stick to its own palette. 
        # Let's stick to its own palette for now to minimize visual change risk.
        # But we need to redraw base box with these colors if we want consistency.
        # Let's re-draw background/border using Form colors.
        $engine.Fill($this.X, $this.Y, $this.Width, $this.Height, ' ', $fg, $bg)
        $engine.DrawBox($this.X, $this.Y, $this.Width, $this.Height, $fg, $bg)
        $engine.WriteAt($this.X + 2, $this.Y + 1, $this.Title, $highlight, $bg)

        $fieldY = $this.Y + 3
        $labelX = $this.X + 4
        $inputX = $this.X + 18
        $inputWidth = $this.Width - 22

        foreach ($fieldName in $this.FieldOrder) {
            $isCurrent = ($fieldName -eq $this.CurrentField)
            $label = $this.FieldLabels[$fieldName]
            $value = $this.Fields[$fieldName]

            # Label
            $lFg = if ($isCurrent) { $highlight } else { $muted }
            $engine.WriteAt($labelX, $fieldY, $label.PadRight(12), $lFg, $bg)

            # Input
            $iBg = if ($isCurrent) { $accentBg } else { $bg }
            $iFg = $fg

            $disp = $value
            if ($disp.Length -gt $inputWidth) { $disp = $disp.Substring(0, $inputWidth - 3) + "..." }

            $engine.Fill($inputX, $fieldY, $inputWidth, 1, ' ', $iFg, $iBg)
            $engine.WriteAt($inputX, $fieldY, $disp, $iFg, $iBg)

            if ($isCurrent) {
                $cursorX = $inputX + [Math]::Min($value.Length, $inputWidth - 1)
                $engine.WriteAt($cursorX, $fieldY, "_", $highlight, $accentBg)
            }

            $fieldY += 2
        }

        # Instructions
        $inst = "Tab: Next | Enter: Save | Esc: Cancel"
        $instX = $this.X + [Math]::Floor(($this.Width - $inst.Length) / 2)
        $engine.WriteAt($instX, $this.Y + $this.Height - 3, $inst, $muted, $bg)
    }

    [string] Render([int]$termWidth, [int]$termHeight, [hashtable]$theme) { return "" }

    # REMOVED: UpdateFieldDisplay() - Dead code that was never called
    # This method used [Console]::Write() which bypassed the render engine layer system.
    # If field-by-field updates are needed in the future, they should use the render engine
    # with BeginLayer([ZIndex]::Dialog) instead of direct console writes.

    # File picker integration
    [string]$FilePicker = ''  # Signal to caller to show file picker

    [bool] HandleInput([ConsoleKeyInfo]$keyInfo) {
        switch ($keyInfo.Key) {
            'Enter' {
                # Check if we're on path field - open file picker
                if ($this.CurrentField -eq 'path') {
                    $this.FilePicker = 'show'
                    return $true
                }

                # Otherwise validate and save
                if ([string]::IsNullOrWhiteSpace($this.Fields.name)) {
                    # Name is required - don't close
                    return $true
                }
                $this.Result = $true
                $this.IsComplete = $true
                return $true
            }
            'Escape' {
                $this.Result = $false
                $this.IsComplete = $true
                return $true
            }
            'Tab' {
                # Move to next field - NEEDS FULL REDRAW
                $currentIndex = $this.FieldOrder.IndexOf($this.CurrentField)
                if ($keyInfo.Modifiers -eq [ConsoleModifiers]::Shift) {
                    # Shift+Tab: previous field
                    $currentIndex--
                    if ($currentIndex -lt 0) {
                        $currentIndex = $this.FieldOrder.Count - 1
                    }
                }
                else {
                    # Tab: next field
                    $currentIndex++
                    if ($currentIndex -ge $this.FieldOrder.Count) {
                        $currentIndex = 0
                    }
                }
                $this.CurrentField = $this.FieldOrder[$currentIndex]
                return $true  # Caller must re-render
            }
            'UpArrow' {
                # Move to previous field - NEEDS FULL REDRAW
                $currentIndex = $this.FieldOrder.IndexOf($this.CurrentField)
                $currentIndex--
                if ($currentIndex -lt 0) {
                    $currentIndex = $this.FieldOrder.Count - 1
                }
                $this.CurrentField = $this.FieldOrder[$currentIndex]
                return $true  # Caller must re-render
            }
            'DownArrow' {
                # Move to next field - NEEDS FULL REDRAW
                $currentIndex = $this.FieldOrder.IndexOf($this.CurrentField)
                $currentIndex++
                if ($currentIndex -ge $this.FieldOrder.Count) {
                    $currentIndex = 0
                }
                $this.CurrentField = $this.FieldOrder[$currentIndex]
                return $true  # Caller must re-render
            }
            'Backspace' {
                # Delete character from current field - UPDATE CURRENT FIELD ONLY
                if ($this.Fields[$this.CurrentField].Length -gt 0) {
                    $this.Fields[$this.CurrentField] = $this.Fields[$this.CurrentField].Substring(0, $this.Fields[$this.CurrentField].Length - 1)
                }
                return $false  # No full redraw needed
            }
            default {
                # Add character to current field - UPDATE CURRENT FIELD ONLY
                if ($keyInfo.KeyChar -ge 32 -and $keyInfo.KeyChar -le 126) {
                    $this.Fields[$this.CurrentField] += $keyInfo.KeyChar
                }
                return $false  # No full redraw needed
            }
        }
        return $false
    }

    # Get the project hashtable result
    [hashtable] GetProject() {
        # Parse aliases from comma-separated string
        $aliasesArray = @()
        if (-not [string]::IsNullOrWhiteSpace($this.Fields.aliases)) {
            $aliasesArray = @($this.Fields.aliases -split ',' | ForEach-Object { $_.Trim() } | Where-Object { $_ })
        }

        return @{
            name        = $this.Fields.name
            description = $this.Fields.description
            path        = $this.Fields.path
            aliases     = $aliasesArray
        }
    }
}

using namespace System
using namespace System.Collections.Generic

<#
.SYNOPSIS
Modern File/Directory Picker Widget for PmcTUI
Fully integrated with SpeedTUI render engine, proper Z-layering, theme support
#>

if (-not ([System.Management.Automation.PSTypeName]'PmcWidget').Type) {
    . "$PSScriptRoot/PmcWidget.ps1"
}

class PmcFilePicker : PmcWidget {
    # === Public Properties ===
    [string]$CurrentPath = ''
    [array]$Items = @()
    [int]$SelectedIndex = 0
    [int]$ScrollOffset = 0
    [bool]$DirectoriesOnly = $true
    [string]$SelectedPath = ''
    [bool]$IsComplete = $false
    [bool]$Result = $false

    # === Event Callbacks ===
    [scriptblock]$OnConfirmed = {}       # Called when selection confirmed: param($path)
    [scriptblock]$OnCancelled = {}       # Called when cancelled

    # === Private State ===
    hidden [int]$_minWidth = 50
    hidden [int]$_minHeight = 15
    hidden [int]$_contentPadding = 2
    hidden [int]$_headerHeight = 3      # Title + separator + blank
    hidden [int]$_footerHeight = 2      # Blank + footer text

    PmcFilePicker([string]$startPath, [bool]$directoriesOnly) {
        $this.DirectoriesOnly = $directoriesOnly
        $this.Width = 70
        $this.Height = 22

        # Set start path
        if ([string]::IsNullOrWhiteSpace($startPath) -or -not (Test-Path $startPath)) {
            $this.CurrentPath = [Environment]::GetFolderPath('UserProfile')
        } else {
            $this.CurrentPath = $startPath
        }

        $this._LoadItems()
    }

    # === Item Management ===

    hidden [void] _LoadItems() {
        $this.Items = @()
        $this.SelectedIndex = 0
        $this.ScrollOffset = 0

        try {
            # Add parent directory (..)
            $parent = Split-Path -Parent $this.CurrentPath
            if ($parent -and $parent -ne $this.CurrentPath) {
                $this.Items += @{ Name = '..'; Path = $parent; IsDirectory = $true }
            }

            # Get directories
            $dirs = @(Get-ChildItem -Path $this.CurrentPath -Directory -ErrorAction SilentlyContinue | Sort-Object Name)
            foreach ($dir in $dirs) {
                $this.Items += @{ Name = $dir.Name; Path = $dir.FullName; IsDirectory = $true }
            }

            # Get files if not DirectoriesOnly
            if (-not $this.DirectoriesOnly) {
                $files = @(Get-ChildItem -Path $this.CurrentPath -File -ErrorAction SilentlyContinue | Sort-Object Name)
                foreach ($file in $files) {
                    $this.Items += @{ Name = $file.Name; Path = $file.FullName; IsDirectory = $false }
                }
            }
        } catch {
            # Fall back to home directory
            $this.CurrentPath = [Environment]::GetFolderPath('UserProfile')
            $this._LoadItems()
        }
    }

    # === Layout ===

    [void] RegisterLayout([object]$engine) {
        ([PmcWidget]$this).RegisterLayout($engine)

        # Ensure minimum dimensions
        if ($this.Width -lt $this._minWidth) { $this.Width = $this._minWidth }
        if ($this.Height -lt $this._minHeight) { $this.Height = $this._minHeight }

        # Center on screen if not positioned
        if ($this.X -le 0) {
            $this.X = [Math]::Max(1, [Math]::Floor(($engine.Width - $this.Width) / 2))
        }
        if ($this.Y -le 0) {
            $this.Y = [Math]::Max(1, [Math]::Floor(($engine.Height - $this.Height) / 2))
        }

        # Define regions
        $contentHeight = $this.Height - $this._headerHeight - $this._footerHeight
        $engine.DefineRegion("$($this.RegionID)_Header", $this.X + 1, $this.Y + 1, $this.Width - 2, 1)
        $engine.DefineRegion("$($this.RegionID)_Path", $this.X + 1, $this.Y + 2, $this.Width - 2, 1)
        $engine.DefineRegion("$($this.RegionID)_Items", $this.X + 1, $this.Y + 3, $this.Width - 2, $contentHeight)
        $engine.DefineRegion("$($this.RegionID)_Footer", $this.X + 1, $this.Y + $this.Height - 1, $this.Width - 2, 1)
    }

    # === Rendering ===

    [void] RenderToEngine([object]$engine) {
        $this.RegisterLayout($engine)

        # Add-Content -Path "/tmp/pmc-filepicker-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] === FilePicker.RenderToEngine START === X=$($this.X) Y=$($this.Y) Width=$($this.Width) Height=$($this.Height) engineWidth=$($engine.Width) engineHeight=$($engine.Height)"

        # Begin Z-layer (above content, below dialogs)
        if ($engine.PSObject.Methods['BeginLayer']) {
            # Add-Content -Path "/tmp/pmc-filepicker-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] BeginLayer(20) called"
            $engine.BeginLayer(20)
        }
        else {
            # Add-Content -Path "/tmp/pmc-filepicker-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] WARNING: BeginLayer method not available"
        }

        # Theme colors
        $primaryBg = $this.GetThemedColorInt('Background.Primary')
        $primaryFg = $this.GetThemedColorInt('Foreground.Field')
        $borderFg = $this.GetThemedColorInt('Border.Widget')
        $titleFg = $this.GetThemedColorInt('Foreground.Title')
        $selectedBg = $this.GetThemedColorInt('Background.RowSelected')
        $selectedFg = $this.GetThemedColorInt('Foreground.RowSelected')
        $mutedFg = $this.GetThemedColorInt('Foreground.Muted')

        # Draw background and border
        $engine.Fill($this.X, $this.Y, $this.Width, $this.Height, ' ', $primaryFg, $primaryBg)

        # Draw top and bottom border
        $engine.Fill($this.X, $this.Y, $this.Width, 1, '─', $borderFg, $primaryBg)
        $engine.Fill($this.X, $this.Y + $this.Height - 1, $this.Width, 1, '─', $borderFg, $primaryBg)

        # Draw left and right borders
        for ($i = $this.Y; $i -lt ($this.Y + $this.Height); $i++) {
            $engine.WriteAt($this.X, $i, '│', $borderFg, $primaryBg)
            $engine.WriteAt($this.X + $this.Width - 1, $i, '│', $borderFg, $primaryBg)
        }

        # Draw corners
        $engine.WriteAt($this.X, $this.Y, '┌', $borderFg, $primaryBg)
        $engine.WriteAt($this.X + $this.Width - 1, $this.Y, '┐', $borderFg, $primaryBg)
        $engine.WriteAt($this.X, $this.Y + $this.Height - 1, '└', $borderFg, $primaryBg)
        $engine.WriteAt($this.X + $this.Width - 1, $this.Y + $this.Height - 1, '┘', $borderFg, $primaryBg)

        # Draw header
        $headerText = "📁 Select Directory"
        $headerPadded = $headerText.PadRight($this.Width - 2)
        $engine.WriteAt($this.X + 1, $this.Y + 1, $headerPadded.Substring(0, $this.Width - 2), $titleFg, $primaryBg)

        # Draw path
        $pathDisplay = $this.CurrentPath
        if ($pathDisplay.Length -gt ($this.Width - 4)) {
            $pathDisplay = "..." + $pathDisplay.Substring($pathDisplay.Length - ($this.Width - 7))
        }
        $pathPadded = $pathDisplay.PadRight($this.Width - 2)
        $engine.WriteAt($this.X + 1, $this.Y + 2, $pathPadded.Substring(0, $this.Width - 2), $mutedFg, $primaryBg)

        # Draw items
        $listY = $this.Y + 3
        $contentHeight = $this.Height - $this._headerHeight - $this._footerHeight

        # Ensure selection is visible
        if ($this.SelectedIndex -lt $this.ScrollOffset) {
            $this.ScrollOffset = $this.SelectedIndex
        }
        if ($this.SelectedIndex -ge ($this.ScrollOffset + $contentHeight)) {
            $this.ScrollOffset = $this.SelectedIndex - $contentHeight + 1
        }

        # Render items
        $row = 0
        $itemCount = $this.Items.Count
        for ($i = $this.ScrollOffset; $i -lt [Math]::Min($this.ScrollOffset + $contentHeight, $itemCount); $i++) {
            $item = $this.Items[$i]
            $isSelected = ($i -eq $this.SelectedIndex)
            $itemBg = if ($isSelected) { $selectedBg } else { $primaryBg }
            $itemFg = if ($isSelected) { $selectedFg } else { if ($item.IsDirectory) { $titleFg } else { $primaryFg } }

            # Item text
            $icon = if ($item.IsDirectory) { "📁" } else { "📄" }
            $displayName = $item.Name
            $maxNameLen = $this.Width - 6
            if ($displayName.Length -gt $maxNameLen) {
                $displayName = $displayName.Substring(0, $maxNameLen - 3) + "..."
            }

            $itemText = " $icon $displayName"
            $itemTextPadded = $itemText.PadRight($this.Width - 2)

            $engine.Fill($this.X + 1, $listY + $row, $this.Width - 2, 1, ' ', $itemFg, $itemBg)
            $engine.WriteAt($this.X + 1, $listY + $row, $itemTextPadded.Substring(0, $this.Width - 2), $itemFg, $itemBg)

            $row++
        }

        # Fill remaining rows
        while ($row -lt $contentHeight) {
            $engine.Fill($this.X + 1, $listY + $row, $this.Width - 2, 1, ' ', $primaryFg, $primaryBg)
            $row++
        }

        # Draw scroll indicators
        if ($this.ScrollOffset -gt 0) {
            $engine.WriteAt($this.X + $this.Width - 2, $listY, "▲", $mutedFg, $primaryBg)
        }
        if (($this.ScrollOffset + $contentHeight) -lt $itemCount) {
            $engine.WriteAt($this.X + $this.Width - 2, $listY + $contentHeight - 1, "▼", $mutedFg, $primaryBg)
        }

        # Draw footer
        $footerText = "↑↓: Navigate | Enter: Select | Space: Use Path | Esc: Cancel"
        if ($footerText.Length -gt ($this.Width - 2)) {
            $footerText = $footerText.Substring(0, $this.Width - 5) + "..."
        }
        $footerPadded = $footerText.PadRight($this.Width - 2)
        $engine.WriteAt($this.X + 1, $this.Y + $this.Height - 1, $footerPadded.Substring(0, $this.Width - 2), $mutedFg, $primaryBg)

        # End Z-layer
        if ($engine.PSObject.Methods['EndLayer']) {
            # Add-Content -Path "/tmp/pmc-filepicker-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] EndLayer() called"
            $engine.EndLayer()
        }
        # Add-Content -Path "/tmp/pmc-filepicker-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] === FilePicker.RenderToEngine END ==="
    }

    # === Input Handling ===

    [bool] HandleInput([ConsoleKeyInfo]$keyInfo) {
        switch ($keyInfo.Key) {
            'UpArrow' {
                if ($this.SelectedIndex -gt 0) {
                    $this.SelectedIndex--
                }
                return $true
            }
            'DownArrow' {
                if ($this.SelectedIndex -lt ($this.Items.Count - 1)) {
                    $this.SelectedIndex++
                }
                return $true
            }
            'PageUp' {
                $this.SelectedIndex = [Math]::Max(0, $this.SelectedIndex - 10)
                return $true
            }
            'PageDown' {
                $this.SelectedIndex = [Math]::Min($this.Items.Count - 1, $this.SelectedIndex + 10)
                return $true
            }
            'Home' {
                $this.SelectedIndex = 0
                return $true
            }
            'End' {
                if ($this.Items.Count -gt 0) {
                    $this.SelectedIndex = $this.Items.Count - 1
                }
                return $true
            }
            'Enter' {
                if ($this.Items.Count -eq 0) {
                    return $true
                }
                $selected = $this.Items[$this.SelectedIndex]
                if ($selected.IsDirectory) {
                    $this.CurrentPath = $selected.Path
                    $this._LoadItems()
                } else {
                    $this.SelectedPath = $selected.Path
                    $this.Result = $true
                    $this.IsComplete = $true
                    $this._InvokeCallback($this.OnConfirmed, $this.SelectedPath)
                }
                return $true
            }
            'Spacebar' {
                # Select current path
                $this.SelectedPath = $this.CurrentPath
                $this.Result = $true
                $this.IsComplete = $true
                $this._InvokeCallback($this.OnConfirmed, $this.SelectedPath)
                return $true
            }
            'Escape' {
                $this.Result = $false
                $this.IsComplete = $true
                $this._InvokeCallback($this.OnCancelled, $null)
                return $true
            }
        }
        return $false
    }

    # === Callbacks ===

    hidden [void] _InvokeCallback([scriptblock]$callback, [object]$arg) {
        if ($callback -and $callback -ne $null) {
            try {
                & $callback $arg
            } catch {
                # Callback error - log but don't crash
            }
        }
    }

    # === Legacy Render method (for compatibility) ===

    [string] Render() {
        return ""
    }
}

using namespace System.Collections.Generic
using namespace System.Text

# PmcMenuBar - Top-level navigation menu with dropdown support
# Full keyboard navigation, hotkeys, and dropdown menus

Set-StrictMode -Version Latest

<#
.SYNOPSIS
Menu item within a dropdown menu

.DESCRIPTION
Represents a single item in a dropdown menu with:
- Display label
- Hotkey character
- Action scriptblock
- Enabled/disabled state
- Separator flag
#>
class PmcMenuItem {
    [string]$Label = ""
    [char]$Hotkey = [char]0
    [scriptblock]$Action = $null
    [bool]$Enabled = $true
    [bool]$IsSeparator = $false
    [string]$Description = ""  # Optional hint text

    PmcMenuItem([string]$label, [char]$hotkey, [scriptblock]$action) {
        $this.Label = $label
        $this.Hotkey = $hotkey
        $this.Action = $action
    }

    # Constructor for separator
    static [PmcMenuItem] Separator() {
        $item = [PmcMenuItem]::new("", [char]0, $null)
        $item.IsSeparator = $true
        return $item
    }
}

<#
.SYNOPSIS
Top-level menu containing dropdown items

.DESCRIPTION
Represents a menu in the menu bar (e.g., "File", "Edit", "View")
#>
class PmcMenu {
    [string]$Title = ""
    [char]$Hotkey = [char]0
    [List[PmcMenuItem]]$Items

    PmcMenu([string]$title, [char]$hotkey) {
        $this.Title = $title
        $this.Hotkey = $hotkey
        $this.Items = [List[PmcMenuItem]]::new()
    }

    [PmcMenu] AddItem([string]$label, [char]$hotkey, [scriptblock]$action) {
        $this.Items.Add([PmcMenuItem]::new($label, $hotkey, $action))
        return $this
    }

    [PmcMenu] AddSeparator() {
        $this.Items.Add([PmcMenuItem]::Separator())
        return $this
    }
}

<#
.SYNOPSIS
Menu bar widget with dropdown menus

.DESCRIPTION
PmcMenuBar provides:
- Top-level horizontal menu bar
- Dropdown menus on activation
- Full keyboard navigation (arrows, hotkeys)
- Themed rendering
- Event callbacks

.EXAMPLE
$menuBar = [PmcMenuBar]::new()
$menuBar.AddMenu('File', 'F', @(
    [PmcMenuItem]::new('New', 'N', { Add-Content -Path "/tmp/pmc-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcMenuBar] New!" })
    [PmcMenuItem]::new('Open', 'O', { Add-Content -Path "/tmp/pmc-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] [PmcMenuBar] Open!" })
    [PmcMenuItem]::Separator()
    [PmcMenuItem]::new('Exit', 'X', { exit })
))
#>
class PmcMenuBar : PmcWidget {
    # === Properties ===
    [List[PmcMenu]]$Menus
    [int]$SelectedMenuIndex = -1        # Which menu is selected (hover)
    [int]$SelectedItemIndex = -1        # Which item in dropdown is selected
    [bool]$IsActive = $false            # Menu bar has focus
    [bool]$DropdownVisible = $false     # Dropdown is showing

    # === Events ===
    [scriptblock]$OnMenuItemSelected = $null

    # === Cached Render Data ===
    hidden [List[int]]$_menuXPositions = [List[int]]::new()  # X position of each menu

    # === Constructor ===
    PmcMenuBar() : base("MenuBar") {
        $this.Menus = [List[PmcMenu]]::new()
        $this.CanFocus = $true

        # Default size (will be set by layout manager)
        $this.Width = 80
        $this.Height = 1
    }

    # === Menu Management ===

    <#
    .SYNOPSIS
    Add a menu to the menu bar
    #>
    [PmcMenuBar] AddMenu([string]$title, [char]$hotkey, [array]$items) {
        $menu = [PmcMenu]::new($title, $hotkey)
        foreach ($item in $items) {
            $menu.Items.Add($item)
        }
        $this.Menus.Add($menu)
        return $this
    }

    # === Navigation ===

    <#
    .SYNOPSIS
    Activate the menu bar (show first menu or highlight bar)
    #>
    [void] Activate() {
        $this.IsActive = $true
        if ($this.Menus.Count -gt 0) {
            $this.SelectedMenuIndex = 0
        }
        $this.Invalidate()
    }

    <#
    .SYNOPSIS
    Deactivate the menu bar
    #>
    [void] Deactivate() {
        $this.IsActive = $false
        $this.DropdownVisible = $false
        $this.SelectedMenuIndex = -1
        $this.SelectedItemIndex = -1
        $this.Invalidate()
    }

    <#
    .SYNOPSIS
    Show dropdown for currently selected menu
    #>
    [void] ShowDropdown() {
        if ($this.SelectedMenuIndex -ge 0 -and $this.SelectedMenuIndex -lt $this.Menus.Count) {
            $menu = $this.Menus[$this.SelectedMenuIndex]

            # Don't show dropdown if menu has no items
            if ($null -eq $menu.Items -or $menu.Items.Count -eq 0) {
                return
            }

            $this.DropdownVisible = $true
            $this.SelectedItemIndex = 0
            # Skip separators
            $this._SelectNextEnabledItem()
            $this.Invalidate()
        }
    }

    <#
    .SYNOPSIS
    Hide dropdown
    #>
    [void] HideDropdown() {
        $this.DropdownVisible = $false
        $this.SelectedItemIndex = -1
        $this.Invalidate()
    }

    <#
    .SYNOPSIS
    Move selection to next menu
    #>
    [void] SelectNextMenu() {
        if ($this.Menus.Count -eq 0) { return }
        $this.SelectedMenuIndex = ($this.SelectedMenuIndex + 1) % $this.Menus.Count
        $this.SelectedItemIndex = -1
        $this.Invalidate()
    }

    <#
    .SYNOPSIS
    Move selection to previous menu
    #>
    [void] SelectPreviousMenu() {
        if ($this.Menus.Count -eq 0) { return }
        $this.SelectedMenuIndex--
        if ($this.SelectedMenuIndex -lt 0) {
            $this.SelectedMenuIndex = $this.Menus.Count - 1
        }
        $this.SelectedItemIndex = -1
        $this.Invalidate()
    }

    <#
    .SYNOPSIS
    Move selection to next item in dropdown
    #>
    [void] SelectNextItem() {
        if (-not $this.DropdownVisible -or $this.SelectedMenuIndex -lt 0) { return }
        $menu = $this.Menus[$this.SelectedMenuIndex]
        if ($menu.Items.Count -eq 0) { return }

        $this.SelectedItemIndex = ($this.SelectedItemIndex + 1) % $menu.Items.Count
        $this._SelectNextEnabledItem()
        $this.Invalidate()
    }

    <#
    .SYNOPSIS
    Move selection to previous item in dropdown
    #>
    [void] SelectPreviousItem() {
        if (-not $this.DropdownVisible -or $this.SelectedMenuIndex -lt 0) { return }
        $menu = $this.Menus[$this.SelectedMenuIndex]
        if ($menu.Items.Count -eq 0) { return }

        $this.SelectedItemIndex--
        if ($this.SelectedItemIndex -lt 0) {
            $this.SelectedItemIndex = $menu.Items.Count - 1
        }
        $this._SelectPreviousEnabledItem()
        $this.Invalidate()
    }

    # Skip separators and disabled items
    hidden [void] _SelectNextEnabledItem() {
        if ($this.SelectedMenuIndex -lt 0) { return }
        $menu = $this.Menus[$this.SelectedMenuIndex]
        $attempts = 0
        while ($attempts -lt $menu.Items.Count) {
            $item = $menu.Items[$this.SelectedItemIndex]
            if (-not $item.IsSeparator -and $item.Enabled) { break }
            $this.SelectedItemIndex = ($this.SelectedItemIndex + 1) % $menu.Items.Count
            $attempts++
        }
    }

    hidden [void] _SelectPreviousEnabledItem() {
        if ($this.SelectedMenuIndex -lt 0) { return }
        $menu = $this.Menus[$this.SelectedMenuIndex]
        $attempts = 0
        while ($attempts -lt $menu.Items.Count) {
            $item = $menu.Items[$this.SelectedItemIndex]
            if (-not $item.IsSeparator -and $item.Enabled) { break }
            $this.SelectedItemIndex--
            if ($this.SelectedItemIndex -lt 0) {
                $this.SelectedItemIndex = $menu.Items.Count - 1
            }
            $attempts++
        }
    }

    <#
    .SYNOPSIS
    Execute currently selected menu item
    #>
    [bool] ExecuteSelectedItem() {
        if (-not $this.DropdownVisible -or $this.SelectedMenuIndex -lt 0 -or $this.SelectedItemIndex -lt 0) {
            return $false
        }

        $menu = $this.Menus[$this.SelectedMenuIndex]
        if ($this.SelectedItemIndex -ge $menu.Items.Count) {
            return $false
        }

        $item = $menu.Items[$this.SelectedItemIndex]
        if ($item.IsSeparator -or -not $item.Enabled) {
            return $false
        }

        # Close dropdown BEFORE executing action
        $this.HideDropdown()
        $this.Deactivate()

        # Execute action
        if ($item.Action) {
            try {
                & $item.Action
            } catch {
                # Log error so menu action failures are visible (only if debug enabled)
                if ((Test-Path variable:global:PmcDebug) -and $global:PmcDebug -and $global:PmcTuiLogFile) {
                    Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [MenuBar] Action failed for '$($item.Label)': $_"
                    Add-Content $global:PmcTuiLogFile "[$(Get-Date -F 'HH:mm:ss.fff')] [MenuBar] Stack: $($_.ScriptStackTrace)"
                }
            }
        }

        # Fire event
        if ($this.OnMenuItemSelected) {
            & $this.OnMenuItemSelected $this $menu $item
        }

        return $true
    }

    # === Layout System ===

    [void] RegisterLayout([object]$engine) {
        ([PmcWidget]$this).RegisterLayout($engine)
        $engine.DefineRegion("$($this.RegionID)_Main", $this.X, $this.Y, $this.Width, $this.Height)
    }

    # === Rendering ===

    [string] OnRender() {
        # Legacy support if needed, but we prefer RenderToEngine
        return "" 
    }

    <#
    .SYNOPSIS
    Render directly to engine (new high-performance path)
    #>
    [void] RenderToEngine([object]$engine) {
        $this.RegisterLayout($engine)

        # Colors (Ints)
        $bg = $this.GetThemedBgInt('Background.MenuBar', 1, 0)
        $fg = $this.GetThemedInt('Foreground.Row')
        $highlightBg = $this.GetThemedBgInt('Background.RowSelected', 1, 0)
        $highlightFg = $this.GetThemedInt('Foreground.RowSelected')

        # Fallback removed - strict theme enforcement

        # 1. Draw Main Bar Background
        $engine.Fill($this.X, $this.Y, $this.Width, 1, ' ', $fg, $bg)

        # 2. Draw Menu Titles
        $currentX = $this.X + 1
        $this._menuXPositions.Clear()

        for ($i = 0; $i -lt $this.Menus.Count; $i++) {
            $menu = $this.Menus[$i]
            $this._menuXPositions.Add($currentX - $this.X)

            $text = " $($menu.Title)"
            if ($menu.Hotkey -ne 0) { $text += "($($menu.Hotkey))" }
            $text += " "

            $itemBg = $bg
            $itemFg = $fg
            if ($i -eq $this.SelectedMenuIndex -and $this.IsActive) {
                $itemBg = $highlightBg
                $itemFg = $highlightFg
            }

            $engine.WriteAt($currentX, $this.Y, $text, $itemFg, $itemBg)
            $currentX += $text.Length
        }

        # 3. Draw Dropdown (if visible)
        if ($this.DropdownVisible -and $this.SelectedMenuIndex -ge 0) {
             $this._RenderDropdownToEngine($engine)
        }
    }

    hidden [void] _RenderDropdownToEngine([object]$engine) {
        if ($this.SelectedMenuIndex -lt 0 -or $this.SelectedMenuIndex -ge $this.Menus.Count) { return }
        $menu = $this.Menus[$this.SelectedMenuIndex]
        if ($menu.Items.Count -eq 0) { return }

        # Calculate width
        $maxWidth = 10
        foreach ($item in $menu.Items) {
            if (-not $item.IsSeparator) {
                $itemWidth = $item.Label.Length + 5
                if ($item.Hotkey -eq 0) { $itemWidth = $item.Label.Length + 1 }
                if ($itemWidth -gt $maxWidth) { $maxWidth = $itemWidth }
            }
        }
        $width = $maxWidth + 2
        $height = $menu.Items.Count + 2 # Borders

        $x = $this.X + $this._menuXPositions[$this.SelectedMenuIndex]
        $y = $this.Y + 1

        # Define Popup Region
        $regionId = "$($this.RegionID)_Dropdown"
        $engine.DefineRegion($regionId, $x, $y, $width, $height, 100) # Z=100

        # Colors
        $bg = $this.GetThemedBgInt('Background.MenuBar', 1, 0)
        $fg = $this.GetThemedInt('Foreground.Row')
        $borderFg = $this.GetThemedInt('Border.Widget')
        $highlightBg = $this.GetThemedBgInt('Background.RowSelected', 1, 0)
        $highlightFg = $this.GetThemedInt('Foreground.RowSelected')
        $mutedFg = $this.GetThemedInt('Foreground.Muted')

        # Fallback removed - strict theme enforcement

        # Draw Box in Region
        # We can use Fill/WriteToRegion
        $engine.Fill($x, $y, $width, $height, ' ', $fg, $bg)

        # Borders
        $engine.WriteToRegion($regionId, $this.BuildBoxBorder($width, 'top', 'single'), $borderFg, $bg)

        # Items
        for ($i = 0; $i -lt $menu.Items.Count; $i++) {
            $item = $menu.Items[$i]
            $itemY = $y + 1 + $i

            # Left/Right Border
            $engine.WriteAt($x, $itemY, $this.GetBoxChar('single_vertical'), $borderFg, $bg)
            $engine.WriteAt($x + $width - 1, $itemY, $this.GetBoxChar('single_vertical'), $borderFg, $bg)

            if ($item.IsSeparator) {
                # Separator
                $sep = $this.GetBoxChar('single_vertical') + ($this.GetBoxChar('single_horizontal') * ($width - 2)) + $this.GetBoxChar('single_vertical')
                $engine.WriteAt($x, $itemY, $sep, $borderFg, $bg)
            } else {
                $isSelected = ($i -eq $this.SelectedItemIndex)
                $iBg = $(if ($isSelected) { $highlightBg } else { $bg })
                $iFg = $(if ($isSelected) { $highlightFg } else { $fg })
                if (-not $item.Enabled) { $iFg = $mutedFg }

                $text = " $($item.Label)"
                if ($item.Hotkey -ne 0) { $text += " ($($item.Hotkey))" }

                # Pad
                $padLen = $width - 2 - $text.Length
                if ($padLen -gt 0) { $text += (" " * $padLen) }

                $engine.WriteAt($x + 1, $itemY, $text, $iFg, $iBg)
            }
        }

        # Bottom Border
        $engine.WriteAt($x, $y + $height - 1, $this.BuildBoxBorder($width, 'bottom', 'single'), $borderFg, $bg)
    }

    # === Input Handling ===

    [bool] HandleKeyPress([System.ConsoleKeyInfo]$keyInfo) {
        $key = $keyInfo.Key
        $char = $keyInfo.KeyChar


        # }

        # Handle Alt+hotkey even when not active (to activate menu)
        if ($keyInfo.Modifiers -band [ConsoleModifiers]::Alt) {
            # }
            if ($this._HandleMenuHotkey($char)) {
                # Menu hotkey handler already set SelectedMenuIndex and showed dropdown
                # Just ensure IsActive is set (don't call Activate() which resets index to 0)
                $this.IsActive = $true
                # }
                return $true
            }
            # }
        }

        # Normal menu operations require IsActive
        if (-not $this.IsActive) {
            return $false
        }

        # Dropdown navigation
        if ($this.DropdownVisible) {
            switch ($key) {
                'UpArrow' {
                    $this.SelectPreviousItem()
                    return $true
                }
                'DownArrow' {
                    $this.SelectNextItem()
                    return $true
                }
                'LeftArrow' {
                    $this.HideDropdown()
                    $this.SelectPreviousMenu()
                    $this.ShowDropdown()
                    return $true
                }
                'RightArrow' {
                    $this.HideDropdown()
                    $this.SelectNextMenu()
                    $this.ShowDropdown()
                    return $true
                }
                'Enter' {
                    $this.ExecuteSelectedItem()
                    # Always return true - we handled the key (even if no action executed)
                    return $true
                }
                'Escape' {
                    # }
                    $this.HideDropdown()
                    $this.Deactivate()
                    return $true
                }
                default {
                    # Check hotkeys
                    if ($this._HandleItemHotkey($char)) {
                        return $true
                    }
                }
            }
        } else {
            # Menu bar navigation
            switch ($key) {
                'LeftArrow' {
                    $this.SelectPreviousMenu()
                    return $true
                }
                'RightArrow' {
                    $this.SelectNextMenu()
                    return $true
                }
                'DownArrow' {
                    $this.ShowDropdown()
                    return $true
                }
                'Enter' {
                    $this.ShowDropdown()
                    return $true
                }
                'Escape' {
                    # }
                    $this.Deactivate()
                    return $true
                }
                default {
                    # Check menu hotkeys
                    if ($this._HandleMenuHotkey($char)) {
                        return $true
                    }
                }
            }
        }

        return $false
    }

    hidden [bool] _HandleMenuHotkey([char]$char) {
        if ($char -eq [char]0) { return $false }
        $charUpper = [char]::ToUpper($char)

        for ($i = 0; $i -lt $this.Menus.Count; $i++) {
            $menu = $this.Menus[$i]
            if ([char]::ToUpper($menu.Hotkey) -eq $charUpper) {
                $this.SelectedMenuIndex = $i
                $this.ShowDropdown()
                return $true
            }
        }
        return $false
    }

    hidden [bool] _HandleItemHotkey([char]$char) {
        if ($char -eq [char]0 -or $this.SelectedMenuIndex -lt 0) { return $false }
        $charUpper = [char]::ToUpper($char)

        $menu = $this.Menus[$this.SelectedMenuIndex]
        for ($i = 0; $i -lt $menu.Items.Count; $i++) {
            $item = $menu.Items[$i]
            if (-not $item.IsSeparator -and $item.Enabled -and [char]::ToUpper($item.Hotkey) -eq $charUpper) {
                $this.SelectedItemIndex = $i
                return $this.ExecuteSelectedItem()
            }
        }
        return $false
    }
}

# Classes exported automatically in PowerShell 5.1+

﻿using namespace System
using namespace System.Collections.Generic
using namespace System.Text

# ProjectPicker.ps1 - Project selection widget with fuzzy search and inline create
#
# Usage:
#   $picker = [ProjectPicker]::new()
#   $picker.SetPosition(10, 5)
#   $picker.SetSize(35, 12)
#   $picker.OnProjectSelected = { param($project) Add-Content -Path "/tmp/pmc-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] [ProjectPicker] Selected: $project" }
#
#   # Render
#   $picker.RenderToEngine($engine)
#
#   # Handle input
#   $key = [Console]::ReadKey($true)
#   $handled = $picker.HandleInput($key)
#
#   # Get result
#   if ($picker.IsConfirmed) {
#       $selected = $picker.GetSelectedProject()
#   }

Set-StrictMode -Off

# Load PmcWidget base class if not already loaded
if (-not ([System.Management.Automation.PSTypeName]'PmcWidget').Type) {
    . "$PSScriptRoot/PmcWidget.ps1"
}

<#
.SYNOPSIS
Project selection widget with fuzzy search and inline project creation

.DESCRIPTION
Features:
- Load projects from Get-PmcData
- Type-ahead fuzzy filtering (matches substrings and initials)
- Arrow key navigation through filtered list
- Enter to select project
- Alt+N to create new project inline (switches to TextInput mode)
- Recent projects shown at top
- Project count display
- OnProjectSelected event callback
- Visual list with scroll indicators
- Empty state handling

.EXAMPLE
$picker = [ProjectPicker]::new()
$picker.SetPosition(10, 5)
$picker.SetSize(35, 12)
$picker.RenderToEngine($engine)
#>
class ProjectPicker : PmcWidget {
    # === Public Properties ===
    [string]$Label = "Select Project"      # Widget title
    [bool]$ShowRecentFirst = $true         # Show recent projects at top

    # === Event Callbacks ===
    [scriptblock]$OnProjectSelected = {}   # Called when project selected: param($projectName)
    [scriptblock]$OnProjectCreated = {}    # Called when new project created: param($projectName)
    [scriptblock]$OnCancelled = {}         # Called when Esc pressed

    # === State Flags ===
    [bool]$IsConfirmed = $false            # True when project selected
    [bool]$IsCancelled = $false            # True when Esc pressed

    # === Private State ===
    hidden [string[]]$_allProjects = @()           # All available projects
    hidden [string[]]$_filteredProjects = @()      # Filtered project list
    hidden [string]$_searchText = ""               # Current search filter
    hidden [int]$_selectedIndex = 0                # Selected item index in filtered list
    hidden [int]$_scrollOffset = 0                 # Scroll offset for long lists
    hidden [bool]$_isCreateMode = $false           # True when creating new project
    hidden [string]$_createText = ""               # Text for new project name
    hidden [int]$_createCursorPos = 0              # Cursor position in create mode
    hidden [string]$_errorMessage = ""             # Error message to display
    hidden [DateTime]$_lastRefresh = [DateTime]::MinValue

    # === Constructor ===
    ProjectPicker() : base("ProjectPicker") {
        $this.Width = 35
        $this.Height = 12
        $this.CanFocus = $true
        $this._LoadProjects()
    }

    # === Public API Methods ===

    <#
    .SYNOPSIS
    Refresh project list from data source
    #>
    [void] RefreshProjects() {
        $this._LoadProjects()
        $this._ApplyDoFilter()
    }

    <#
    .SYNOPSIS
    Get the currently selected project name

    .OUTPUTS
    String project name or empty string if none selected
    #>
    [string] GetSelectedProject() {
        if ($this._filteredProjects.Count -eq 0) {
            return ""
        }

        if ($this._selectedIndex -ge 0 -and $this._selectedIndex -lt $this._filteredProjects.Count) {
            $selected = $this._filteredProjects[$this._selectedIndex]
            if ($selected -eq "(No Project)") { return "" }
            return $selected
        }

        return ""
    }

    <#
    .SYNOPSIS
    Set initial search filter text
    #>
    [void] SetSearchText([string]$text) {
        $this._searchText = $text
        $this._ApplyDoFilter()
    }

    <#
    .SYNOPSIS
    Set the selected project by name (without filtering)
    #>
    [void] SetSelectedProject([string]$projectName) {
        # Find the project in the full list
        for ($i = 0; $i -lt $this._filteredProjects.Count; $i++) {
            if ($this._filteredProjects[$i] -eq $projectName) {
                $this._selectedIndex = $i
                $this._AdjustScrollOffset()
                return
            }
        }
        # Not found - select first item
        $this._selectedIndex = 0
        $this._AdjustScrollOffset()
    }

    <#
    .SYNOPSIS
    Handle keyboard input
    #>
    [bool] HandleInput([ConsoleKeyInfo]$keyInfo) {
        # Check if we need to refresh projects (every 5 seconds)
        if (([DateTime]::Now - $this._lastRefresh).TotalSeconds -gt 5) {
            $this._LoadProjects()
            $this._ApplyDoFilter()
        }

        # Create mode has different input handling
        if ($this._isCreateMode) {
            return $this._HandleCreateModeInput($keyInfo)
        }

        # Enter - select current project
        if ($keyInfo.Key -eq 'Enter') {
            $selected = $this.GetSelectedProject()
            $this.IsConfirmed = $true
            # Callback with selected project (empty string if No Project)
            $this._InvokeCallback($this.OnProjectSelected, $selected)
            return $true
        }

        # Escape - cancel
        if ($keyInfo.Key -eq 'Escape') {
            $this.IsCancelled = $true
            $this._InvokeCallback($this.OnCancelled, $null)
            return $true
        }

        # Alt+N - create new project
        if ($keyInfo.Modifiers -band [ConsoleModifiers]::Alt -and $keyInfo.Key -eq 'N') {
            $this._EnterCreateMode()
            return $true
        }

        # Navigation
        if ($keyInfo.Key -eq 'UpArrow') {
            $this._MoveSelectionUp()
            return $true
        }

        if ($keyInfo.Key -eq 'DownArrow') {
            $this._MoveSelectionDown()
            return $true
        }

        if ($keyInfo.Key -eq 'PageUp') {
            $this._MoveSelectionUp()
            $this._MoveSelectionUp()
            $this._MoveSelectionUp()
            return $true
        }

        if ($keyInfo.Key -eq 'PageDown') {
            $this._MoveSelectionDown()
            $this._MoveSelectionDown()
            $this._MoveSelectionDown()
            return $true
        }

        if ($keyInfo.Key -eq 'Home') {
            $this._selectedIndex = 0
            $this._AdjustScrollOffset()
            return $true
        }

        if ($keyInfo.Key -eq 'End') {
            $this._selectedIndex = [Math]::Max(0, $this._filteredProjects.Count - 1)
            $this._AdjustScrollOffset()
            return $true
        }

        # Backspace - remove character from search
        if ($keyInfo.Key -eq 'Backspace') {
            if ($this._searchText.Length -gt 0) {
                $this._searchText = $this._searchText.Substring(0, $this._searchText.Length - 1)
                $this._ApplyDoFilter()
            }
            return $true
        }

        # Ctrl+U - clear search
        if ($keyInfo.Modifiers -band [ConsoleModifiers]::Control -and $keyInfo.Key -eq 'U') {
            $this._searchText = ""
            $this._ApplyDoFilter()
            return $true
        }

        # Regular character - add to search
        if ($keyInfo.KeyChar -ge 32 -and $keyInfo.KeyChar -le 126) {
            $this._searchText += $keyInfo.KeyChar
            $this._ApplyDoFilter()
            return $true
        }

        # Space
        if ($keyInfo.Key -eq 'Spacebar') {
            $this._searchText += ' '
            $this._ApplyDoFilter()
            return $true
        }

        return $false
    }

    # === Layout System ===

    [void] RegisterLayout([object]$engine) {
        ([PmcWidget]$this).RegisterLayout($engine)
        # Regions removed - using direct WriteAt in RenderToEngine
    }

    <#
    .SYNOPSIS
    Render project picker to engine
    #>
    [void] RenderToEngine([object]$engine) {
        # Clamp to bounds
        $this._ClampToBounds($engine)

        # Begin layer elevation (popup z-order)
        if ($engine.PSObject.Methods['BeginLayer']) {
            $engine.BeginLayer(100)
        }

        # Get themed colors
        $borderFg = $this.GetThemedInt('Border.Widget')
        $fg = $this.GetThemedInt('Foreground.Row')
        $bg = $this.GetThemedBgInt('Background.Row', $this.Width, 0)
        $primaryFg = $this.GetThemedInt('Foreground.Primary')
        $mutedFg = $this.GetThemedInt('Foreground.Muted')
        $highlightBg = $this.GetThemedBgInt('Background.RowSelected', $this.Width, 0)
        $highlightFg = $this.GetThemedInt('Foreground.RowSelected')
        $errorFg = $this.GetThemedInt('Foreground.Error')

        # Draw border box
        $engine.DrawBox($this.X, $this.Y, $this.Width, $this.Height, $borderFg, $bg)

        # Draw title
        $titleText = " $($this.Label) "
        $engine.WriteAt($this.X + 2, $this.Y, $titleText, $primaryFg, $bg)

        # Draw count (if not in create mode)
        if (-not $this._isCreateMode) {
            $countStr = "($($this._filteredProjects.Count))"
            $engine.WriteAt($this.X + $this.Width - $countStr.Length - 2, $this.Y + 1, $countStr, $mutedFg, $bg)
        }

        # Separator line under title
        $engine.Fill($this.X + 1, $this.Y + 2, $this.Width - 2, 1, [char]0x2500, $borderFg, $bg)

        if ($this._isCreateMode) {
            # Create Input at Y+3
            $inputY = $this.Y + 3

            # Input Prompt/border
            $engine.WriteAt($this.X, $inputY, [char]0x2502, $borderFg, $bg) # Left border
            $engine.WriteAt($this.X + $this.Width - 1, $inputY, [char]0x2502, $borderFg, $bg) # Right border

            # Input content
            $inputStr = $this._createText
            $innerWidth = $this.Width - 4

            if ([string]::IsNullOrEmpty($inputStr)) {
                $engine.WriteAt($this.X + 2, $inputY, "Enter project name...", $mutedFg, $bg)
            }
            else {
                # Simple rendering without complex scrolling for now
                $display = $inputStr
                if ($display.Length -gt $innerWidth) { $display = $display.Substring($display.Length - $innerWidth) }
                $engine.WriteAt($this.X + 2, $inputY, $display, $fg, $bg)
            }

            # Help at Y+5
            $helpText = "Enter=Create | Esc=Cancel"
            $engine.WriteAt($this.X + 2, $this.Y + 5, $this.PadText($helpText, $this.Width - 4, 'left'), $mutedFg, $bg)
        }
        else {
            # Count
            $countText = "($($this._filteredProjects.Count))"
            $countX = $this.X + $this.Width - $countText.Length - 2
            if ($countX -gt $this.X + 2) {
                $engine.WriteAt($countX, $this.Y + 1, $countText, $mutedFg, $bg)
            }

            # Search at Y+2
            $searchText = if ([string]::IsNullOrWhiteSpace($this._searchText)) { "Type to filter..." } else { $this._searchText }
            $searchFg = if ([string]::IsNullOrWhiteSpace($this._searchText)) { $mutedFg } else { $primaryFg }
            $engine.WriteAt($this.X + 2, $this.Y + 2, $this.PadText($searchText, $this.Width - 4, 'left'), $searchFg, $bg)

            # List at Y+3
            $listY = $this.Y + 3
            $listHeight = [Math]::Max(1, $this.Height - 5)
            $visibleProjects = @()
            if ($this._filteredProjects.Count -gt 0) {
                $endIndex = [Math]::Min($this._scrollOffset + $listHeight, $this._filteredProjects.Count)
                for ($i = $this._scrollOffset; $i -lt $endIndex; $i++) {
                    $visibleProjects += $this._filteredProjects[$i]
                }
            }

            for ($i = 0; $i -lt $listHeight; $i++) {
                $currentY = $listY + $i
                if ($currentY -ge $this.Y + $this.Height - 1) { break } # Safety

                if ($i -lt $visibleProjects.Count) {
                    $projectName = $visibleProjects[$i]
                    $isSelected = ($this._scrollOffset + $i) -eq $this._selectedIndex

                    $iBg = if ($isSelected) { $highlightBg } else { $bg }
                    $iFg = if ($isSelected) { $highlightFg } else { $fg }

                    $taskCount = $this._GetTaskCountForProject($projectName)
                    $displayName = if ($taskCount -ge 0) { "$projectName ($taskCount)" } else { $projectName }

                    # Manual fill for row background
                    $engine.Fill($this.X + 1, $currentY, $this.Width - 2, 1, ' ', $iFg, $iBg)
                    $engine.WriteAt($this.X + 2, $currentY, $this.TruncateText($displayName, $this.Width - 4), $iFg, $iBg)
                }
                else {
                    # Empty row
                    $engine.Fill($this.X + 1, $currentY, $this.Width - 2, 1, ' ', $fg, $bg)
                }
            }

            # Help
            $helpText = "Enter=Select | Alt+N=Create"
            $engine.WriteAt($this.X + 2, $this.Y + $this.Height - 2, $this.PadText($helpText, $this.Width - 4, 'left'), $mutedFg, $bg)
        }

        if ($this._errorMessage) {
            $engine.WriteAt($this.X + 2, $this.Y + $this.Height - 1, $this.PadText($this._errorMessage, $this.Width - 4, 'left'), $errorFg, $bg)
        }

        # End layer elevation
        if ($engine.PSObject.Methods['EndLayer']) {
            $engine.EndLayer()
        }
    }

    <#
    .SYNOPSIS
    Ensure widget stays within screen bounds (Engine Viewport)
    #>
    hidden [void] _ClampToBounds([object]$engine) {
        # Check Engine bounds first (Authoritative Viewport)
        if ($engine -and $engine.PSObject.Properties['Width']) {
            $termWidth = $engine.Width
            $termHeight = $engine.Height
        }
        else {
            # Fallback to console (but verify console availability)
            try {
                $termWidth = [Console]::WindowWidth
                $termHeight = [Console]::WindowHeight
            }
            catch {
                $termWidth = 80
                $termHeight = 24
            }
        }

        # Clamp X: ensure widget fits within right edge (0-indexed, so -1 for right boundary)
        if ($this.X + $this.Width -gt $termWidth) {
            $this.X = [Math]::Max(0, $termWidth - $this.Width)
        }

        # Clamp Y: ensure widget fits within bottom edge
        if ($this.Y + $this.Height -gt $termHeight) {
            $this.Y = [Math]::Max(0, $termHeight - $this.Height)
        }
    }

    # === Private Helper Methods ===

    <#
    .SYNOPSIS
    Load projects from PMC data
    #>
    hidden [void] _LoadProjects() {
        try {
            . "$PSScriptRoot/../services/TaskStore.ps1"
            $store = [TaskStore]::GetInstance()
            $projectsData = $store.GetAllProjects()

            # FIX: Always include "(No Project)" option at the beginning
            $projects = @("(No Project)")

            if ($null -ne $projectsData -and $projectsData.Count -gt 0) {
                foreach ($proj in $projectsData) {
                    if ($null -ne $proj.name -and -not [string]::IsNullOrWhiteSpace($proj.name)) {
                        $projects += $proj.name.ToString()
                    }
                }
            }

            $this._allProjects = $projects
            $this._lastRefresh = [DateTime]::Now
        }
        catch {
            # Failed to load projects - use "(No Project)" only
            $this._allProjects = @("(No Project)")
        }

        # Apply filter to refresh filtered list
        $this._ApplyDoFilter()
    }

    <#
    .SYNOPSIS
    Apply fuzzy search filter to project list
    #>
    hidden [void] _ApplyDoFilter() {
        if ([string]::IsNullOrWhiteSpace($this._searchText)) {
            $this._filteredProjects = $this._allProjects
        }
        else {
            $searchLower = $this._searchText.ToLower()
            $filtered = @()

            foreach ($project in $this._allProjects) {
                $projectLower = $project.ToLower()

                # Exact substring match
                if ($projectLower.Contains($searchLower)) {
                    $filtered += $project
                    continue
                }

                # Fuzzy match - initials or character sequence
                if ($this._FuzzyMatch($projectLower, $searchLower)) {
                    $filtered += $project
                }
            }

            $this._filteredProjects = $filtered
        }

        # Ensure filtered projects is initialized
        if ($null -eq $this._filteredProjects) {
            $this._filteredProjects = @()
        }

        # Reset selection if out of bounds
        if ($this._selectedIndex -ge $this._filteredProjects.Count) {
            $this._selectedIndex = [Math]::Max(0, $this._filteredProjects.Count - 1)
        }

        $this._AdjustScrollOffset()
    }

    <#
    .SYNOPSIS
    Fuzzy match algorithm - matches initials and subsequences

    .PARAMETER text
    Text to search in (lowercase)

    .PARAMETER pattern
    Pattern to match (lowercase)

    .OUTPUTS
    True if pattern matches text
    #>
    hidden [bool] _FuzzyMatch([string]$text, [string]$pattern) {
        $patternIdx = 0
        $textIdx = 0

        while ($patternIdx -lt $pattern.Length -and $textIdx -lt $text.Length) {
            if ($pattern[$patternIdx] -eq $text[$textIdx]) {
                $patternIdx++
            }
            $textIdx++
        }

        return $patternIdx -eq $pattern.Length
    }

    <#
    .SYNOPSIS
    Move selection up
    #>
    hidden [void] _MoveSelectionUp() {
        if ($this._selectedIndex -gt 0) {
            $this._selectedIndex--
            $this._AdjustScrollOffset()
        }
    }

    <#
    .SYNOPSIS
    Move selection down
    #>
    hidden [void] _MoveSelectionDown() {
        if ($this._selectedIndex -lt ($this._filteredProjects.Count - 1)) {
            $this._selectedIndex++
            $this._AdjustScrollOffset()
        }
    }

    <#
    .SYNOPSIS
    Adjust scroll offset to keep selection visible
    #>
    hidden [void] _AdjustScrollOffset() {
        $maxVisibleItems = $this.Height - 4

        # If selected item is above visible area, scroll up
        if ($this._selectedIndex -lt $this._scrollOffset) {
            $this._scrollOffset = $this._selectedIndex
        }

        # If selected item is below visible area, scroll down
        if ($this._selectedIndex -ge ($this._scrollOffset + $maxVisibleItems)) {
            $this._scrollOffset = $this._selectedIndex - $maxVisibleItems + 1
        }

        # Clamp scroll offset
        if ($this._scrollOffset -lt 0) {
            $this._scrollOffset = 0
        }

        $maxScroll = [Math]::Max(0, $this._filteredProjects.Count - $maxVisibleItems)
        if ($this._scrollOffset -gt $maxScroll) {
            $this._scrollOffset = $maxScroll
        }
    }

    <#
    .SYNOPSIS
    Enter create mode for new project
    #>
    hidden [void] _EnterCreateMode() {
        $this._isCreateMode = $true
        $this._createText = ""
        $this._createCursorPos = 0
        $this._errorMessage = ""
    }

    <#
    .SYNOPSIS
    Handle input in create mode

    .PARAMETER keyInfo
    ConsoleKeyInfo

    .OUTPUTS
    True if handled
    #>
    hidden [bool] _HandleCreateModeInput([ConsoleKeyInfo]$keyInfo) {
        # Enter - create project
        if ($keyInfo.Key -eq 'Enter') {
            $projectName = $this._createText.Trim()

            if ([string]::IsNullOrWhiteSpace($projectName)) {
                $this._errorMessage = "Project name cannot be empty"
                return $true
            }

            # Check for duplicates
            $exists = $false
            foreach ($existing in $this._allProjects) {
                if ($existing.ToLower() -eq $projectName.ToLower()) {
                    $exists = $true
                    break
                }
            }

            if ($exists) {
                $this._errorMessage = "Project already exists"
                return $true
            }

            # Create project in PMC data
            try {
                $data = Get-PmcData
                if ($null -eq $data.projects) {
                    $data | Add-Member -NotePropertyName 'projects' -NotePropertyValue @() -Force
                }

                $newProject = [PSCustomObject]@{
                    name        = $projectName
                    description = ""
                    aliases     = @()
                    created     = (Get-Date).ToString('yyyy-MM-dd HH:mm:ss')
                }

                $data.projects += $newProject
                Save-PmcData $data

                # Refresh and select new project
                $this._LoadProjects()
                $this._isCreateMode = $false
                $this._createText = ""
                $this._errorMessage = ""

                # Find and select the new project
                for ($i = 0; $i -lt $this._filteredProjects.Count; $i++) {
                    if ($this._filteredProjects[$i] -eq $projectName) {
                        $this._selectedIndex = $i
                        $this._AdjustScrollOffset()
                        break
                    }
                }

                $this.IsConfirmed = $true
                $this._InvokeCallback($this.OnProjectCreated, $projectName)
                $this._InvokeCallback($this.OnProjectSelected, $projectName)

                return $true
            }
            catch {
                $this._errorMessage = "Failed to create project"
                return $true
            }
        }

        # Escape - cancel create mode
        if ($keyInfo.Key -eq 'Escape') {
            $this._isCreateMode = $false
            $this._createText = ""
            $this._createCursorPos = 0
            $this._errorMessage = ""
            return $true
        }

        # Backspace
        if ($keyInfo.Key -eq 'Backspace') {
            if ($this._createCursorPos -gt 0) {
                $before = $this._createText.Substring(0, $this._createCursorPos - 1)
                $after = $(if ($this._createCursorPos -lt $this._createText.Length) {
                        $this._createText.Substring($this._createCursorPos)
                    }
                    else { "" })
                $this._createText = $before + $after
                $this._createCursorPos--
            }
            return $true
        }

        # Delete
        if ($keyInfo.Key -eq 'Delete') {
            if ($this._createCursorPos -lt $this._createText.Length) {
                $before = $this._createText.Substring(0, $this._createCursorPos)
                $after = $(if ($this._createCursorPos + 1 -lt $this._createText.Length) {
                        $this._createText.Substring($this._createCursorPos + 1)
                    }
                    else { "" })
                $this._createText = $before + $after
            }
            return $true
        }

        # Navigation
        if ($keyInfo.Key -eq 'LeftArrow') {
            if ($this._createCursorPos -gt 0) {
                $this._createCursorPos--
            }
            return $true
        }

        if ($keyInfo.Key -eq 'RightArrow') {
            if ($this._createCursorPos -lt $this._createText.Length) {
                $this._createCursorPos++
            }
            return $true
        }

        if ($keyInfo.Key -eq 'Home') {
            $this._createCursorPos = 0
            return $true
        }

        if ($keyInfo.Key -eq 'End') {
            $this._createCursorPos = $this._createText.Length
            return $true
        }

        # Regular character input
        if ($keyInfo.KeyChar -ge 32 -and $keyInfo.KeyChar -le 126) {
            $before = $this._createText.Substring(0, $this._createCursorPos)
            $after = $(if ($this._createCursorPos -lt $this._createText.Length) {
                    $this._createText.Substring($this._createCursorPos)
                }
                else { "" })
            $this._createText = $before + $keyInfo.KeyChar + $after
            $this._createCursorPos++
            return $true
        }

        # Space
        if ($keyInfo.Key -eq 'Spacebar') {
            $before = $this._createText.Substring(0, $this._createCursorPos)
            $after = $(if ($this._createCursorPos -lt $this._createText.Length) {
                    $this._createText.Substring($this._createCursorPos)
                }
                else { "" })
            $this._createText = $before + ' ' + $after
            $this._createCursorPos++
            return $true
        }

        return $false
    }

    <#
    .SYNOPSIS
    L-POL-8: Get task count for a project

    .PARAMETER projectName
    Name of the project

    .OUTPUTS
    Number of tasks in project, or -1 if count unavailable
    #>
    hidden [int] _GetTaskCountForProject([string]$projectName) {
        try {
            . "$PSScriptRoot/../services/TaskStore.ps1"
            $store = [TaskStore]::GetInstance()
            $allTasks = $store.GetAllTasks()

            if ($null -eq $allTasks) {
                return -1
            }

            $count = 0
            foreach ($task in $allTasks) {
                $taskProject = $task.project
                if ($taskProject -eq $projectName -and -not ($task.completed)) {
                    $count++
                }
            }

            return $count
        }
        catch {
            # Failed to get count - return -1 to skip displaying count
            return -1
        }
    }

    <#
    .SYNOPSIS
    Invoke a callback scriptblock safely

    .PARAMETER callback
    Scriptblock to invoke

    .PARAMETER args
    Arguments to pass
    #>
    hidden [void] _InvokeCallback([scriptblock]$callback, $argument) {
        if ($null -ne $callback) {
            try {
                if ($null -ne $argument) {
                    & $callback $argument
                }
                else {
                    & $callback
                }
            }
            catch {
                # Callback failed - log but don't crash widget
            }
        }
    }
}

using namespace System.Collections.Generic

# SimpleFilePicker.ps1 - Simple file/folder picker that works with SpeedTUI

Set-StrictMode -Version Latest

# Ensure PmcWidget is loaded
if (-not ([System.Management.Automation.PSTypeName]'PmcWidget').Type) {
    . "$PSScriptRoot/PmcWidget.ps1"
}

class SimpleFilePicker : PmcWidget {
    [string]$CurrentPath = ''
    [List[object]]$Items = $null
    [int]$SelectedIndex = 0
    [int]$ScrollOffset = 0
    [bool]$IsComplete = $false
    [bool]$Result = $false
    [string]$SelectedPath = ''
    [bool]$DirectoriesOnly = $false

    SimpleFilePicker([string]$startPath, [bool]$directoriesOnly) : base("SimpleFilePicker") {
        $this.Width = 60
        $this.Height = 20
        $this.DirectoriesOnly = $directoriesOnly
        $this.Items = [List[object]]::new()

        # Validate and set start path
        if ([string]::IsNullOrWhiteSpace($startPath) -or -not (Test-Path $startPath)) {
            $this.CurrentPath = [Environment]::GetFolderPath('UserProfile')
        }
        else {
            if (Test-Path -Path $startPath -PathType Leaf) {
                # If it's a file, use parent directory
                $this.CurrentPath = Split-Path -Parent $startPath
            }
            else {
                $this.CurrentPath = $startPath
            }
        }

        $this.LoadItems()
    }

    [void] LoadItems() {
        $this.Items.Clear()
        $this.SelectedIndex = 0
        $this.ScrollOffset = 0

        try {
            # Add parent directory
            $parent = Split-Path -Parent $this.CurrentPath
            if ($parent) {
                $this.Items.Add(@{
                        Name        = '..'
                        Path        = $parent
                        IsDirectory = $true
                    })
            }

            # Get directories
            $dirs = Get-ChildItem -Path $this.CurrentPath -Directory -ErrorAction SilentlyContinue | Sort-Object Name
            foreach ($dir in $dirs) {
                $this.Items.Add(@{
                        Name        = $dir.Name
                        Path        = $dir.FullName
                        IsDirectory = $true
                    })
            }

            # Get files if not directories-only
            if (-not $this.DirectoriesOnly) {
                $files = Get-ChildItem -Path $this.CurrentPath -File -ErrorAction SilentlyContinue | Sort-Object Name
                foreach ($file in $files) {
                    $this.Items.Add(@{
                            Name        = $file.Name
                            Path        = $file.FullName
                            IsDirectory = $false
                        })
                }
            }
        }
        catch {
            # On error, go to home
            $this.CurrentPath = [Environment]::GetFolderPath('UserProfile')
            $this.LoadItems()
        }
    }

    [bool] HandleInput([ConsoleKeyInfo]$key) {
        switch ($key.Key) {
            'UpArrow' {
                if ($this.SelectedIndex -gt 0) {
                    $this.SelectedIndex--
                    # Adjust scroll if needed
                    if ($this.SelectedIndex -lt $this.ScrollOffset) {
                        $this.ScrollOffset = $this.SelectedIndex
                    }
                }
                return $true
            }
            'DownArrow' {
                if ($this.SelectedIndex -lt ($this.Items.Count - 1)) {
                    $this.SelectedIndex++
                    # Adjust scroll if needed (show 15 items at a time - or Height-5)
                    $visibleItems = $this.Height - 5
                    if ($this.SelectedIndex -ge ($this.ScrollOffset + $visibleItems)) {
                        $this.ScrollOffset = $this.SelectedIndex - $visibleItems + 1
                    }
                }
                return $true
            }
            'Enter' {
                if ($this.Items.Count -eq 0) {
                    return $true
                }

                $selected = $this.Items[$this.SelectedIndex]
                if ($selected.IsDirectory) {
                    # Navigate into directory
                    $this.CurrentPath = $selected.Path
                    $this.LoadItems()
                }
                else {
                    # Select file
                    $this.SelectedPath = $selected.Path
                    $this.Result = $true
                    $this.IsComplete = $true
                }
                return $true
            }
            'Spacebar' {
                # Select current directory
                $this.SelectedPath = $this.CurrentPath
                $this.Result = $true
                $this.IsComplete = $true
                return $true
            }
            'Escape' {
                $this.Result = $false
                $this.IsComplete = $true
                return $true
            }
        }
        return $false
    }

    # === Layout System ===

    [void] RegisterLayout([object]$engine) {
        ([PmcWidget]$this).RegisterLayout($engine)

        $engine.DefineRegion("$($this.RegionID)_Title", $this.X + 2, $this.Y + 1, $this.Width - 4, 1)
        $engine.DefineRegion("$($this.RegionID)_Path", $this.X + 2, $this.Y + 2, $this.Width - 4, 1)

        $listHeight = [Math]::Max(1, $this.Height - 6)
        $engine.DefineRegion("$($this.RegionID)_List", $this.X + 2, $this.Y + 4, $this.Width - 4, $listHeight)

        $engine.DefineRegion("$($this.RegionID)_Help", $this.X + 2, $this.Y + $this.Height - 2, $this.Width - 4, 1)
    }

    <#
    .SYNOPSIS
    Render directly to engine (new high-performance path)
    #>
    [void] RenderToEngine([object]$engine) {
        $this.RegisterLayout($engine)

        # Colors (Ints)
        # Colors (Ints)
        $bg = $this.GetThemedBgInt('Background.Panel', 1, 0)
        $fg = $this.GetThemedInt('Foreground.Row')
        $highlightBg = $this.GetThemedBgInt('Background.RowSelected', 1, 0)
        $highlightFg = $this.GetThemedInt('Foreground.RowSelected')
        $dirColor = $this.GetThemedInt('Foreground.Title')
        $muted = $this.GetThemedInt('Foreground.Muted')

        # Draw Box
        $engine.Fill($this.X, $this.Y, $this.Width, $this.Height, ' ', $fg, $bg)
        $engine.DrawBox($this.X, $this.Y, $this.Width, $this.Height, $fg, $bg)

        # Title
        $title = if ($this.DirectoriesOnly) { "Select Folder" } else { "Select File" }
        $engine.WriteToRegion("$($this.RegionID)_Title", "=== $title ===", $fg, $bg)

        # Path
        $engine.WriteToRegion("$($this.RegionID)_Path", "Current: $($this.CurrentPath)", $fg, $bg)

        # List
        $listRegion = "$($this.RegionID)_List"
        $bounds = $engine.GetRegionBounds($listRegion)

        if ($bounds) {
            $visibleCount = $bounds.Height

            for ($i = 0; $i -lt $visibleCount; $i++) {
                $idx = $this.ScrollOffset + $i
                if ($idx -ge $this.Items.Count) { break }

                $item = $this.Items[$idx]
                $isSelected = ($idx -eq $this.SelectedIndex)

                $iBg = if ($isSelected) { $highlightBg } else { $bg }
                $iFg = if ($isSelected) { $highlightFg } else { if ($item.IsDirectory) { $dirColor } else { $fg } }

                $prefix = if ($item.IsDirectory) { "[DIR]" } else { "[FILE]" }
                $text = if ($isSelected) { "> $prefix $($item.Name)" } else { "  $prefix $($item.Name)" }

                $engine.Fill($bounds.X, $bounds.Y + $i, $bounds.Width, 1, ' ', $iFg, $iBg)
                $engine.WriteAt($bounds.X, $bounds.Y + $i, $text, $iFg, $iBg)
            }
        }

        # Help
        $help = "Up/Down: Navigate | Enter: Open | Space: Select | Esc: Cancel"
        $engine.WriteToRegion("$($this.RegionID)_Help", $help, $muted, $bg)
    }

    [string] Render() { return "" }
}

