﻿# TextAreaEditor.ps1 - Full-featured multiline text editor for PMC ConsoleUI
# Ported from Praxis FullNotesEditor with adaptations for PMC
# Features: Gap buffer, undo/redo, word navigation, auto-save, scrolling, selection, copy/paste, find/replace

Set-StrictMode -Version Latest

# Selection mode enum
enum SelectionMode {
    None
    Stream      # Normal selection (character-based)
    Block       # Rectangular/column selection
}

class TextAreaEditor : PmcWidget {
    # Widget position and size (Inherited from PmcWidget)
    # [int]$X = 0
    # [int]$Y = 0
    # [int]$Width = 80
    # [int]$Height = 24

    # The actual text content using gap buffer
    hidden [GapBuffer]$_gapBuffer

    # Line tracking for efficient operations
    hidden [System.Collections.ArrayList]$_lineStarts
    hidden [bool]$_lineIndexDirty = $true

    # Cursor position
    [int]$CursorX = 0
    [int]$CursorY = 0
    [int]$ScrollOffsetY = 0
    [int]$ScrollOffsetX = 0

    # Selection state
    [SelectionMode]$SelectionMode = [SelectionMode]::None
    [int]$SelectionAnchorX = 0  # Where selection started
    [int]$SelectionAnchorY = 0
    [int]$SelectionEndX = 0     # Where selection ends (current cursor)
    [int]$SelectionEndY = 0

    # Internal clipboard (app-only, performant)
    static [string]$_clipboard = ""

    # Undo/redo with full state tracking
    hidden [System.Collections.ArrayList]$_undoStack
    hidden [System.Collections.ArrayList]$_redoStack

    # Editor settings
    [string]$Style = ""
    [int]$TabWidth = 4
    [bool]$Modified = $false
    [bool]$EnableUndo = $false  # PERFORMANCE: Undo disabled by default for responsiveness
    [bool]$ShowStatistics = $false
    [bool]$ShowCursor = $false  # Controls cursor visibility (for focus)

    # File info
    [string]$FilePath = ""
    hidden [string]$_originalText = ""
    hidden [datetime]$_lastSaveTime = [datetime]::MinValue

    TextAreaEditor() : base("TextAreaEditor") {
        $this._gapBuffer = [GapBuffer]::new()
        $this._gapBuffer.Insert(0, "")  # Start with empty content
        $this._lineStarts = [System.Collections.ArrayList]::new()
        $this._undoStack = [System.Collections.ArrayList]::new()
        $this._redoStack = [System.Collections.ArrayList]::new()
        $this.BuildLineIndex()

        $this.Width = 80
        $this.Height = 24
        $this.CanFocus = $true
    }

    [void] SetBounds([int]$x, [int]$y, [int]$width, [int]$height) {
        $this.X = $x
        $this.Y = $y
        $this.Width = $width
        $this.Height = $height
    }

    [void] SetText([string]$text) {
        # Store original text for comparison
        $this._originalText = $text

        # Clear buffer and insert new text
        $this._gapBuffer.Delete(0, $this._gapBuffer.GetLength())
        if ([string]::IsNullOrEmpty($text)) {
            $this._gapBuffer.Insert(0, "")
        }
        else {
            $this._gapBuffer.Insert(0, $text)
        }

        $this.BuildLineIndex()
        $this.CursorX = 0
        $this.CursorY = 0
        $this.ScrollOffsetY = 0
        $this.ScrollOffsetX = 0
        $this.Modified = $false
        $this._undoStack.Clear()
        $this._redoStack.Clear()
        $this._lastSaveTime = [datetime]::Now
    }

    [string] GetText() {
        return $this._gapBuffer.GetText()
    }

    [object] GetStatistics() {
        # Performance optimization: Don't calculate if disabled
        if (-not $this.ShowStatistics) {
            return [PSCustomObject]@{
                Lines = 0
                Words = 0
                Chars = 0
            }
        }

        # Use optimized C# implementation from GapBuffer if available
        # This avoids allocating the full string and regex matching
        if ($this._gapBuffer.PSObject.Methods['GetContentStatistics']) {
            $stats = $this._gapBuffer.GetContentStatistics()
            return [PSCustomObject]@{
                Lines = $stats.Lines
                Words = $stats.Words
                Chars = $stats.Chars
            }
        }

        # Fallback (slow path)
        $text = $this.GetText()
        $lines = $this.GetLineCount()
        $words = @($text -split '\s+' | Where-Object { $_ }).Count
        $chars = $text.Length

        return [PSCustomObject]@{
            Lines = $lines
            Words = $words
            Chars = $chars
        }
    }

    # Build line index for efficient line operations
    hidden [void] BuildLineIndex() {
        $this._lineStarts.Clear()
        $this._lineStarts.Add(0) | Out-Null  # First line starts at position 0

        # Optimized: Use GapBuffer.FindAll to get all newlines at once
        $newlines = $this._gapBuffer.FindAll("`n")
        foreach ($index in $newlines) {
            $this._lineStarts.Add($index + 1) | Out-Null
        }

        $this._lineIndexDirty = $false
    }

    [int] GetLineCount() {
        if ($this._lineIndexDirty) {
            $this.BuildLineIndex()
        }
        return [Math]::Max(1, $this._lineStarts.Count)
    }

    [string] GetLine([int]$lineIndex) {
        if ($this._lineIndexDirty) {
            $this.BuildLineIndex()
        }

        if ($lineIndex -lt 0 -or $lineIndex -ge $this.GetLineCount()) {
            return ""
        }

        $lineStart = $this._lineStarts[$lineIndex]
        $lineEnd = $(if ($lineIndex + 1 -lt $this._lineStarts.Count) {
                $this._lineStarts[$lineIndex + 1] - 1
            }
            else {
                $this._gapBuffer.GetLength()
            })

        # Exclude the newline character
        if ($lineEnd -gt $lineStart -and $this._gapBuffer.GetChar($lineEnd - 1) -eq "`n") {
            $lineEnd--
        }

        $lineLength = [Math]::Max(0, $lineEnd - $lineStart)
        if ($lineLength -eq 0) {
            return ""
        }

        return $this._gapBuffer.GetText($lineStart, $lineLength)
    }

    # Get position in buffer from line/column
    hidden [int] GetPositionFromLineCol([int]$line, [int]$col) {
        if ($this._lineIndexDirty) {
            $this.BuildLineIndex()
        }

        if ($line -lt 0 -or $line -ge $this.GetLineCount()) {
            return -1
        }

        $lineStart = $this._lineStarts[$line]
        $lineText = $this.GetLine($line)
        $actualCol = [Math]::Min($col, $lineText.Length)

        return $lineStart + $actualCol
    }

    # === Layout System ===
    # RenderToEngine implementation (replaces Render)
    [void] RenderToEngine([object]$engine) {
        # Ensure bounds are valid
        if ($this.Width -le 0 -or $this.Height -le 0) {
            return
        }

        # Theme colors - use consistent theme for ALL text
        $textFg = $this.GetThemedInt('Foreground.Row')
        $textBg = $this.GetThemedBgInt('Background.Field', 1, 0)
        $selFg = $this.GetThemedInt('Foreground.RowSelected')
        $selBg = $this.GetThemedBgInt('Background.RowSelected', 1, 0)
        $cursorBg = $this.GetThemedBgInt('Background.RowSelected', 1, 0)

        # Fallback colors removed for strict theme enforcement
        # if ($textBg -eq -1) { $textBg = [HybridRenderEngine]::_PackRGB(30, 30, 30) }
        # if ($selBg -eq -1) { $selBg = [HybridRenderEngine]::_PackRGB(0, 80, 160) }
        # if ($cursorBg -eq -1) { $cursorBg = [HybridRenderEngine]::_PackRGB(200, 200, 200) }

        # Draw background
        for ($r = 0; $r -lt $this.Height; $r++) {
            $engine.WriteAt($this.X, $this.Y + $r, (" " * $this.Width), $textFg, $textBg)
        }

        # Normalize selection bounds
        $hasSelection = ($this.SelectionMode -ne [SelectionMode]::None)
        $selStartLine = 0; $selEndLine = 0; $selStartCol = 0; $selEndCol = 0
        if ($hasSelection) {
            $selStartLine = [Math]::Min($this.SelectionAnchorY, $this.SelectionEndY)
            $selEndLine = [Math]::Max($this.SelectionAnchorY, $this.SelectionEndY)
            if ($selStartLine -eq $selEndLine) {
                $selStartCol = [Math]::Min($this.SelectionAnchorX, $this.SelectionEndX)
                $selEndCol = [Math]::Max($this.SelectionAnchorX, $this.SelectionEndX)
            } else {
                if ($this.SelectionAnchorY -lt $this.SelectionEndY) {
                    $selStartCol = $this.SelectionAnchorX
                    $selEndCol = $this.SelectionEndX
                } else {
                    $selStartCol = $this.SelectionEndX
                    $selEndCol = $this.SelectionAnchorX
                }
            }
        }

        # Calculate visible range
        $startLine = $this.ScrollOffsetY
        $endLine = [Math]::Min($this.GetLineCount() - 1, $startLine + $this.Height - 1)

        # Draw text content with selection highlighting
        for ($i = 0; $i -le ($endLine - $startLine); $i++) {
            $lineIndex = $startLine + $i
            $lineText = $this.GetLine($lineIndex)
            $screenY = $this.Y + $i

            # Handle horizontal scrolling
            $visibleStart = $this.ScrollOffsetX
            $visibleEnd = [Math]::Min($lineText.Length, $visibleStart + $this.Width)

            if ($visibleStart -lt $lineText.Length) {
                $visibleText = $lineText.Substring($visibleStart, [Math]::Min($this.Width, $lineText.Length - $visibleStart))

                # Check if this line has selection
                if ($hasSelection -and $lineIndex -ge $selStartLine -and $lineIndex -le $selEndLine) {
                    # Determine selection range on this line
                    $lineSelStart = 0
                    $lineSelEnd = $lineText.Length

                    if ($this.SelectionMode -eq [SelectionMode]::Block) {
                        # Block Selection: Fixed columns for all lines
                        $blockStart = [Math]::Min($this.SelectionAnchorX, $this.SelectionEndX)
                        $blockEnd = [Math]::Max($this.SelectionAnchorX, $this.SelectionEndX)

                        $lineSelStart = $blockStart
                        $lineSelEnd = $blockEnd
                    }
                    else {
                        # Stream Selection
                        if ($lineIndex -eq $selStartLine) { $lineSelStart = $selStartCol }
                        if ($lineIndex -eq $selEndLine) { $lineSelEnd = $selEndCol }
                    }

                    # Adjust for scroll offset
                    $lineSelStart = [Math]::Max(0, $lineSelStart - $visibleStart)
                    $lineSelEnd = [Math]::Max(0, $lineSelEnd - $visibleStart)
                    $lineSelEnd = [Math]::Min($lineSelEnd, $this.Width)

                    # Draw text in three parts: before, selected, after
                    if ($lineSelStart -gt 0) {
                        $beforeText = $visibleText.Substring(0, [Math]::Min($lineSelStart, $visibleText.Length))
                        $engine.WriteAt($this.X, $screenY, $beforeText, $textFg, $textBg)
                    }
                    if ($lineSelEnd -gt $lineSelStart -and $lineSelStart -lt $visibleText.Length) {
                        $selText = $visibleText.Substring($lineSelStart, [Math]::Min($lineSelEnd - $lineSelStart, $visibleText.Length - $lineSelStart))
                        $engine.WriteAt($this.X + $lineSelStart, $screenY, $selText, $selFg, $selBg)
                    }
                    if ($lineSelEnd -lt $visibleText.Length) {
                        $afterText = $visibleText.Substring($lineSelEnd)
                        $engine.WriteAt($this.X + $lineSelEnd, $screenY, $afterText, $textFg, $textBg)
                    }
                } else {
                    # No selection on this line
                    $engine.WriteAt($this.X, $screenY, $visibleText, $textFg, $textBg)
                }
            }
        }

        # Draw cursor (inverted character at cursor position) - ONLY if ShowCursor is true
        if ($this.ShowCursor) {
            $cursorScreenY = $this.CursorY - $this.ScrollOffsetY
            $cursorScreenX = $this.CursorX - $this.ScrollOffsetX
            if ($cursorScreenY -ge 0 -and $cursorScreenY -lt $this.Height -and $cursorScreenX -ge 0 -and $cursorScreenX -lt $this.Width) {
                $cursorLine = $this.GetLine($this.CursorY)
                $cursorChar = " "  # Default to space for end-of-line cursor
                if ($this.CursorX -ge 0 -and $this.CursorX -lt $cursorLine.Length) {
                    # Get character at cursor position as string
                    $cursorChar = [string]$cursorLine.Substring($this.CursorX, 1)
                }
                # Draw cursor with high contrast (use selection foreground which is usually white/bright)
                $engine.WriteAt($this.X + $cursorScreenX, $this.Y + $cursorScreenY, $cursorChar, $selFg, $cursorBg)
            }
        }
    }

    hidden [bool] IsLineSelected([int]$line, [int]$startLine, [int]$endLine, [int]$startCol, [int]$endCol) {
        if ($this.SelectionMode -eq [SelectionMode]::None) {
            return $false
        }

        if ($line -lt $startLine -or $line -gt $endLine) {
            return $false
        }

        if ($this.SelectionMode -eq [SelectionMode]::Stream) {
            # Stream selection
            if ($line -eq $startLine -and $line -eq $endLine) {
                return $true # Simplified: assumes if line is in range, it has some selection
            }
            elseif ($line -eq $startLine) {
                return $true
            }
            elseif ($line -eq $endLine) {
                return $true
            }
            else {
                return $true
            }
        }
        elseif ($this.SelectionMode -eq [SelectionMode]::Block) {
            # Block selection
            return $true
        }

        return $false
    }

