    }

    [bool] HandleInput([System.ConsoleKeyInfo]$key) {
        $handled = $true
        $isShift = $key.Modifiers -band [System.ConsoleModifiers]::Shift
        $isCtrl = $key.Modifiers -band [System.ConsoleModifiers]::Control

        # Save state for undo before modifications
        if (-not ($key.Key -in @([System.ConsoleKey]::LeftArrow, [System.ConsoleKey]::RightArrow,
                    [System.ConsoleKey]::UpArrow, [System.ConsoleKey]::DownArrow,
                    [System.ConsoleKey]::Home, [System.ConsoleKey]::End))) {
            $this.SaveUndoState()
        }

        switch ($key.Key) {
            # Navigation with selection support
            ([System.ConsoleKey]::LeftArrow) {
                if ($isShift) {
                    # Start or extend selection
                    if ($this.SelectionMode -eq [SelectionMode]::None) {
                        $mode = $(if ($isCtrl) { [SelectionMode]::Block } else { [SelectionMode]::Stream })
                        $this.StartSelection($mode)
                    }
                    if ($isCtrl) { $this.MoveCursorWordLeft() } else { $this.MoveCursorLeft() }
                    $this.ExtendSelection()
                }
                else {
                    if ($this.SelectionMode -ne [SelectionMode]::None) { $this.ClearSelection() }
                    if ($isCtrl) { $this.MoveCursorWordLeft() } else { $this.MoveCursorLeft() }
                }
            }
            ([System.ConsoleKey]::RightArrow) {
                if ($isShift) {
                    if ($this.SelectionMode -eq [SelectionMode]::None) {
                        $mode = $(if ($isCtrl) { [SelectionMode]::Block } else { [SelectionMode]::Stream })
                        $this.StartSelection($mode)
                    }
                    if ($isCtrl) { $this.MoveCursorWordRight() } else { $this.MoveCursorRight() }
                    $this.ExtendSelection()
                }
                else {
                    if ($this.SelectionMode -ne [SelectionMode]::None) { $this.ClearSelection() }
                    if ($isCtrl) { $this.MoveCursorWordRight() } else { $this.MoveCursorRight() }
                }
            }
            ([System.ConsoleKey]::UpArrow) {
                if ($isShift) {
                    if ($this.SelectionMode -eq [SelectionMode]::None) {
                        $mode = $(if ($isCtrl) { [SelectionMode]::Block } else { [SelectionMode]::Stream })
                        $this.StartSelection($mode)
                    }
                    $this.MoveCursorUp()
                    $this.ExtendSelection()
                }
                else {
                    if ($this.SelectionMode -ne [SelectionMode]::None) { $this.ClearSelection() }
                    $this.MoveCursorUp()
                }
            }
            ([System.ConsoleKey]::DownArrow) {
                if ($isShift) {
                    if ($this.SelectionMode -eq [SelectionMode]::None) {
                        $mode = $(if ($isCtrl) { [SelectionMode]::Block } else { [SelectionMode]::Stream })
                        $this.StartSelection($mode)
                    }
                    $this.MoveCursorDown()
                    $this.ExtendSelection()
                }
                else {
                    if ($this.SelectionMode -ne [SelectionMode]::None) { $this.ClearSelection() }
                    $this.MoveCursorDown()
                }
            }
            ([System.ConsoleKey]::Home) {
                if ($key.Modifiers -band [System.ConsoleModifiers]::Control) {
                    $this.CursorX = 0
                    $this.CursorY = 0
                    $this.EnsureCursorVisible()
                }
                else {
                    $this.CursorX = 0
                    $this.EnsureCursorVisible()
                }
            }
            ([System.ConsoleKey]::End) {
                if ($key.Modifiers -band [System.ConsoleModifiers]::Control) {
                    $this.CursorY = $this.GetLineCount() - 1
                    $this.CursorX = $this.GetLine($this.CursorY).Length
                    $this.EnsureCursorVisible()
                }
                else {
                    $this.CursorX = $this.GetLine($this.CursorY).Length
                    $this.EnsureCursorVisible()
                }
            }
            ([System.ConsoleKey]::PageUp) {
                $this.CursorY = [Math]::Max(0, $this.CursorY - $this.Height)
                $this.EnsureCursorVisible()
            }
            ([System.ConsoleKey]::PageDown) {
                $this.CursorY = [Math]::Min($this.GetLineCount() - 1, $this.CursorY + $this.Height)
                $this.EnsureCursorVisible()
            }

            # Editing
            ([System.ConsoleKey]::Enter) { $this.InsertNewLine() }
            ([System.ConsoleKey]::Backspace) { $this.Backspace() }
            ([System.ConsoleKey]::Delete) { $this.Delete() }
            ([System.ConsoleKey]::Tab) { $this.InsertTab() }

            # Undo/Redo
            ([System.ConsoleKey]::Z) {
                if ($key.Modifiers -band [System.ConsoleModifiers]::Control) {
                    $this.Undo()
                }
                else {
                    $this.InsertChar($key.KeyChar)
                }
            }

            # Redo
            ([System.ConsoleKey]::Y) {
                if ($key.Modifiers -band [System.ConsoleModifiers]::Control) {
                    $this.Redo()
                }
                else {
                    $this.InsertChar($key.KeyChar)
                }
            }

            # Select All
            ([System.ConsoleKey]::A) {
                if ($isCtrl) {
                    $this.SelectAll()
                }
                else {
                    $this.InsertChar($key.KeyChar)
                }
            }

            # Copy
            ([System.ConsoleKey]::C) {
                if ($isCtrl) {
                    $this.Copy()
                }
                else {
                    $this.InsertChar($key.KeyChar)
                }
            }

            # Cut
            ([System.ConsoleKey]::X) {
                if ($isCtrl) {
                    $this.Cut()
                }
                else {
                    $this.InsertChar($key.KeyChar)
                }
            }

            # Paste
            ([System.ConsoleKey]::V) {
                if ($isCtrl) {
                    $this.Paste()
                }
                else {
                    $this.InsertChar($key.KeyChar)
                }
            }

            # Find
            ([System.ConsoleKey]::F) {
                if ($isCtrl) {
                    # Future feature: Implement find dialog with search highlighting
                    # Reserved: Ctrl+F keybinding for future find functionality
                    $handled = $false  # Let parent handle for now
                }
                else {
                    $this.InsertChar($key.KeyChar)
                }
            }

            # Replace
            ([System.ConsoleKey]::H) {
                if ($isCtrl) {
                    # Future feature: Implement find/replace dialog with preview
                    # Reserved: Ctrl+H keybinding for future replace functionality
                    $handled = $false  # Let parent handle for now
                }
                else {
                    $this.InsertChar($key.KeyChar)
                }
            }

            # Escape - clear selection
            ([System.ConsoleKey]::Escape) {
                if ($this.SelectionMode -ne [SelectionMode]::None) {
                    $this.ClearSelection()
                }
                else {
                    $handled = $false
                }
            }

            default {
                if ($key.KeyChar -and -not [char]::IsControl($key.KeyChar)) {
                    $this.InsertChar($key.KeyChar)
                }
                else {
                    $handled = $false
                }
            }
        }

        return $handled
    }

    # Cursor movement methods
    [void] MoveCursorLeft() {
        if ($this.CursorX -gt 0) {
            $this.CursorX--
        }
        elseif ($this.CursorY -gt 0) {
            $this.CursorY--
            $this.CursorX = $this.GetLine($this.CursorY).Length
        }
        $this.EnsureCursorVisible()
    }

    [void] MoveCursorRight() {
        $lineLength = $this.GetLine($this.CursorY).Length
        if ($this.CursorX -lt $lineLength) {
            $this.CursorX++
        }
        elseif ($this.CursorY -lt ($this.GetLineCount() - 1)) {
            $this.CursorY++
            $this.CursorX = 0
        }
        $this.EnsureCursorVisible()
    }

    [void] MoveCursorUp() {
        if ($this.CursorY -gt 0) {
            $this.CursorY--
            $lineLength = $this.GetLine($this.CursorY).Length
            $this.CursorX = [Math]::Min($this.CursorX, $lineLength)
        }
        $this.EnsureCursorVisible()
    }

    [void] MoveCursorDown() {
        if ($this.CursorY -lt ($this.GetLineCount() - 1)) {
            $this.CursorY++
            $lineLength = $this.GetLine($this.CursorY).Length
            $this.CursorX = [Math]::Min($this.CursorX, $lineLength)
        }
        $this.EnsureCursorVisible()
    }

    [void] MoveCursorWordLeft() {
        # Move to previous word boundary
        $line = $this.GetLine($this.CursorY)
        if ($this.CursorX -gt 0) {
            # Skip current word
            while ($this.CursorX -gt 0 -and $line[$this.CursorX - 1] -match '\w') {
                $this.CursorX--
            }
            # Skip whitespace
            while ($this.CursorX -gt 0 -and $line[$this.CursorX - 1] -match '\s') {
                $this.CursorX--
            }
            # Move to start of previous word
            while ($this.CursorX -gt 0 -and $line[$this.CursorX - 1] -match '\w') {
                $this.CursorX--
            }
        }
        elseif ($this.CursorY -gt 0) {
            $this.MoveCursorLeft()
        }
        $this.EnsureCursorVisible()
    }

    [void] MoveCursorWordRight() {
        # Move to next word boundary
        $line = $this.GetLine($this.CursorY)
        if ($this.CursorX -lt $line.Length) {
            # Skip current word
            while ($this.CursorX -lt $line.Length -and $line[$this.CursorX] -match '\w') {
                $this.CursorX++
            }
            # Skip whitespace
            while ($this.CursorX -lt $line.Length -and $line[$this.CursorX] -match '\s') {
                $this.CursorX++
            }
        }
        elseif ($this.CursorY -lt ($this.GetLineCount() - 1)) {
            $this.MoveCursorRight()
        }
        $this.EnsureCursorVisible()
    }

    [void] EnsureCursorVisible() {
        # Vertical scrolling
        if ($this.CursorY -lt $this.ScrollOffsetY) {
            $this.ScrollOffsetY = $this.CursorY
        }
        elseif ($this.CursorY -ge ($this.ScrollOffsetY + $this.Height)) {
            $this.ScrollOffsetY = $this.CursorY - $this.Height + 1
        }

        # Horizontal scrolling
        if ($this.CursorX -lt $this.ScrollOffsetX) {
            $this.ScrollOffsetX = $this.CursorX
        }
        elseif ($this.CursorX -ge ($this.ScrollOffsetX + $this.Width)) {
            $this.ScrollOffsetX = $this.CursorX - $this.Width + 1
        }
    }

    # Selection methods
    [void] StartSelection([SelectionMode]$mode) {
        $this.SelectionMode = $mode
        $this.SelectionAnchorX = $this.CursorX
        $this.SelectionAnchorY = $this.CursorY
        $this.SelectionEndX = $this.CursorX
        $this.SelectionEndY = $this.CursorY
    }

    [void] ExtendSelection() {
        $this.SelectionEndX = $this.CursorX
        $this.SelectionEndY = $this.CursorY
    }

    [void] ClearSelection() {
        $this.SelectionMode = [SelectionMode]::None
    }

    [void] SelectAll() {
        $this.SelectionMode = [SelectionMode]::Stream
        $this.SelectionAnchorX = 0
        $this.SelectionAnchorY = 0
        $this.SelectionEndY = $this.GetLineCount() - 1
        $this.SelectionEndX = $this.GetLine($this.SelectionEndY).Length
        $this.CursorX = $this.SelectionEndX
        $this.CursorY = $this.SelectionEndY
    }

    # Editing methods
    [void] InsertChar([char]$c) {
        if ($this.SelectionMode -ne [SelectionMode]::None) {
            $this.DeleteSelection()
        }

        $pos = $this.GetPositionFromLineCol($this.CursorY, $this.CursorX)
        $this._gapBuffer.Insert($pos, [string]$c)
        $this.CursorX++
        $this.Modified = $true
        $this._lineIndexDirty = $true
        $this.EnsureCursorVisible()
    }

    [void] InsertNewLine() {
        if ($this.SelectionMode -ne [SelectionMode]::None) {
            $this.DeleteSelection()
        }

        $pos = $this.GetPositionFromLineCol($this.CursorY, $this.CursorX)
        $this._gapBuffer.Insert($pos, "`n")
        $this.CursorY++
        $this.CursorX = 0
        $this.Modified = $true
        $this._lineIndexDirty = $true
        $this.EnsureCursorVisible()
    }

    [void] Backspace() {
        if ($this.SelectionMode -ne [SelectionMode]::None) {
            $this.DeleteSelection()
            return
        }

        $pos = $this.GetPositionFromLineCol($this.CursorY, $this.CursorX)
        if ($pos -gt 0) {
            $this._gapBuffer.Delete($pos - 1, 1)

            # Update cursor
            if ($this.CursorX -gt 0) {
                $this.CursorX--
            }
            elseif ($this.CursorY -gt 0) {
                $this.CursorY--
                $this._lineIndexDirty = $true # Force rebuild to get correct line length
                $this.CursorX = $this.GetLine($this.CursorY).Length
            }

            $this.Modified = $true
            $this._lineIndexDirty = $true
            $this.EnsureCursorVisible()
        }
    }

    [void] Delete() {
        if ($this.SelectionMode -ne [SelectionMode]::None) {
            $this.DeleteSelection()
            return
        }

        $pos = $this.GetPositionFromLineCol($this.CursorY, $this.CursorX)
        if ($pos -lt $this._gapBuffer.GetLength()) {
            $this._gapBuffer.Delete($pos, 1)
            $this.Modified = $true
            $this._lineIndexDirty = $true
        }
    }

    [void] InsertTab() {
        # Insert spaces for tab
        $spaces = " " * $this.TabWidth
        $this.InsertString($spaces)
    }

    [void] InsertString([string]$s) {
        if ($this.SelectionMode -ne [SelectionMode]::None) {
            $this.DeleteSelection()
        }

        $pos = $this.GetPositionFromLineCol($this.CursorY, $this.CursorX)
        $this._gapBuffer.Insert($pos, $s)
        $this.CursorX += $s.Length
        $this.Modified = $true
        $this._lineIndexDirty = $true
        $this.EnsureCursorVisible()
    }

    # Get text in selection range (efficient - no regex)
    hidden [string] GetSelectedText() {
        if ($this.SelectionMode -eq [SelectionMode]::None) { return "" }

        # Normalize anchor and end (anchor might be after end if selecting backwards)
        $startLine = [Math]::Min($this.SelectionAnchorY, $this.SelectionEndY)
        $endLine = [Math]::Max($this.SelectionAnchorY, $this.SelectionEndY)

        if ($startLine -eq $endLine) {
            $startCol = [Math]::Min($this.SelectionAnchorX, $this.SelectionEndX)
            $endCol = [Math]::Max($this.SelectionAnchorX, $this.SelectionEndX)
        } else {
            if ($this.SelectionAnchorY -lt $this.SelectionEndY) {
                $startCol = $this.SelectionAnchorX
                $endCol = $this.SelectionEndX
            } else {
                $startCol = $this.SelectionEndX
                $endCol = $this.SelectionAnchorX
            }
        }

        $startPos = $this.GetPositionFromLineCol($startLine, $startCol)
        $endPos = $this.GetPositionFromLineCol($endLine, $endCol)
        $length = $endPos - $startPos

        if ($length -le 0) { return "" }
        return $this._gapBuffer.GetText($startPos, $length)
    }

    [void] DeleteSelection() {
        if ($this.SelectionMode -eq [SelectionMode]::None) { return }

        # Get normalized positions
        $startLine = [Math]::Min($this.SelectionAnchorY, $this.SelectionEndY)
        $endLine = [Math]::Max($this.SelectionAnchorY, $this.SelectionEndY)

        if ($startLine -eq $endLine) {
            $startCol = [Math]::Min($this.SelectionAnchorX, $this.SelectionEndX)
            $endCol = [Math]::Max($this.SelectionAnchorX, $this.SelectionEndX)
        } else {
            if ($this.SelectionAnchorY -lt $this.SelectionEndY) {
                $startCol = $this.SelectionAnchorX
                $endCol = $this.SelectionEndX
            } else {
                $startCol = $this.SelectionEndX
                $endCol = $this.SelectionAnchorX
            }
        }

        $startPos = $this.GetPositionFromLineCol($startLine, $startCol)
        $endPos = $this.GetPositionFromLineCol($endLine, $endCol)
        $length = $endPos - $startPos

        if ($length -gt 0) {
            $this._gapBuffer.Delete($startPos, $length)
            $this._lineIndexDirty = $true
            $this.Modified = $true

            # Move cursor to start of deleted region
            $this.CursorY = $startLine
            $this.CursorX = $startCol
            $this.EnsureCursorVisible()
        }

        $this.ClearSelection()
    }

    # Clipboard operations (internal clipboard - no system clipboard for performance)
    [void] Copy() {
        $text = $this.GetSelectedText()
        if ($text) {
            [TextAreaEditor]::_clipboard = $text
        }
    }

    [void] Cut() {
        $this.Copy()
        $this.DeleteSelection()
    }

    [void] Paste() {
        if ([TextAreaEditor]::_clipboard) {
            if ($this.SelectionMode -ne [SelectionMode]::None) {
                $this.DeleteSelection()
            }
            $this.InsertString([TextAreaEditor]::_clipboard)
        }
    }

    # Undo/Redo (stubs)
    [void] SaveUndoState() { }
    [void] Undo() { }
    [void] Redo() { }
}

﻿using namespace System.Collections.Generic
using namespace System.Text

Set-StrictMode -Version Latest

# Ensure PmcWidget is loaded
if (-not ([System.Management.Automation.PSTypeName]'PmcWidget').Type) {
    . "$PSScriptRoot/PmcWidget.ps1"
}

<#
.SYNOPSIS
Dialog to show individual time entries within an aggregated group
#>
class TimeEntryDetailDialog : PmcWidget {
    [string]$Title
    [array]$Entries
    # [int]$Width  # Inherited
    # [int]$Height # Inherited
    [bool]$IsComplete = $false
    [int]$ScrollOffset = 0
    [int]$SelectedIndex = 0

    TimeEntryDetailDialog([string]$title, [array]$entries) : base("TimeEntryDetail") {
        $this.Title = $title
        $this.Entries = $entries
        $this.Width = 80
        $this.Height = [Math]::Min(25, $entries.Count + 7)
    }

    # === Layout System ===

    [void] RegisterLayout([object]$engine) {
        ([PmcWidget]$this).RegisterLayout($engine)
        # Regions removed - using direct WriteAt in RenderToEngine
    }

    <#
    .SYNOPSIS
    Render directly to engine (new high-performance path)
    #>
    [void] RenderToEngine([object]$engine) {
        $this.RegisterLayout($engine)

        # Colors
        # Use Panel background
        $bg = [HybridRenderEngine]::AnsiColorToInt($this.GetThemedBg('Background.Panel', 1, 0))
        $fg = [HybridRenderEngine]::AnsiColorToInt($this.GetThemedFg('Foreground.Row'))
        $borderFg = [HybridRenderEngine]::AnsiColorToInt($this.GetThemedFg('Border.Widget'))
        $highlightFg = [HybridRenderEngine]::AnsiColorToInt($this.GetThemedFg('Foreground.Title'))
        $selectedBg = [HybridRenderEngine]::AnsiColorToInt($this.GetThemedBg('Background.RowSelected', 1, 0))

        if ($bg -eq -1) { $bg = [HybridRenderEngine]::_PackRGB(45, 55, 72) }

        # Draw Box
        $engine.Fill($this.X, $this.Y, $this.Width, $this.Height, ' ', $fg, $bg)
        $engine.DrawBox($this.X, $this.Y, $this.Width, $this.Height, $borderFg, $bg)

        # Title
        $pad = [Math]::Max(0, [Math]::Floor(($this.Width - 4 - $this.Title.Length) / 2))
        $engine.WriteAt($this.X + 2 + $pad, $this.Y + 1, $this.Title, $highlightFg, $bg)

        # Header
        $headerText = " Task".PadRight(25) + " Hours  " + " Notes"
        # Manual clip if needed - header is fixed width usually
        $engine.WriteAt($this.X + 2, $this.Y + 2, $headerText, $highlightFg, $bg)

        # Separator 1
        $engine.WriteAt($this.X, $this.Y + 3, "├" + ("─" * ($this.Width - 2)) + "┤", $borderFg, $bg)

        # List Area
        $listX = $this.X + 2
        $listY = $this.Y + 4
        $listWidth = $this.Width - 4
        $listHeight = $this.Height - 6

        for ($i = 0; $i -lt $listHeight; $i++) {
            $entryIndex = $this.ScrollOffset + $i
            if ($entryIndex -ge $this.Entries.Count) { break }

            $entry = $this.Entries[$entryIndex]
            $isSelected = ($entryIndex -eq $this.SelectedIndex)

            $iBg = if ($isSelected) { $selectedBg } else { $bg }

            # Format
            $task = if ($entry.ContainsKey('task')) { $entry.task } else { '' }
            if ($task.Length -gt 24) { $task = $task.Substring(0, 21) + "..." }

            $minutes = if ($entry.ContainsKey('minutes')) { $entry.minutes } else { 0 }
            $hours = [Math]::Round($minutes / 60.0, 2)

            $notes = if ($entry.ContainsKey('notes')) { $entry.notes } else { '' }
            $maxNotesLen = $this.Width - 35
            if ($notes.Length -gt $maxNotesLen) { $notes = $notes.Substring(0, $maxNotesLen - 3) + "..." }

            $line = " $($task.PadRight(24)) $($hours.ToString('0.00').PadLeft(5))  $notes"

            # Ensure line fits
            if ($line.Length -gt $listWidth) { $line = $line.Substring(0, $listWidth) }

            $engine.Fill($listX, $listY + $i, $listWidth, 1, ' ', $fg, $iBg)
            $engine.WriteAt($listX, $listY + $i, $line, $fg, $iBg)
        }

        # Separator 2
        $engine.WriteAt($this.X, $this.Y + $this.Height - 3, "├" + ("─" * ($this.Width - 2)) + "┤", $borderFg, $bg)

        # Footer
        $footer = " ↑/↓: Navigate  Enter/Esc: Close"
        $engine.WriteAt($this.X + 2, $this.Y + $this.Height - 2, $footer, $fg, $bg)
    }



    [bool] HandleInput([ConsoleKeyInfo]$keyInfo) {
        switch ($keyInfo.Key) {
            'UpArrow' {
                if ($this.SelectedIndex -gt 0) {
                    $this.SelectedIndex--
                    # Adjust scroll if needed
                    if ($this.SelectedIndex -lt $this.ScrollOffset) {
                        $this.ScrollOffset = $this.SelectedIndex
                    }
                }
                return $true
            }
            'DownArrow' {
                if ($this.SelectedIndex -lt ($this.Entries.Count - 1)) {
                    $this.SelectedIndex++
                    # Adjust scroll if needed
                    $maxVisible = $this.Height - 6  # 6 rows for border/header/footer
                    if ($this.SelectedIndex -ge ($this.ScrollOffset + $maxVisible)) {
                        $this.ScrollOffset = $this.SelectedIndex - $maxVisible + 1
                    }
                }
                return $true
            }
            'Enter' {
                $this.IsComplete = $true
                return $true
            }
            'Escape' {
                $this.IsComplete = $true
                return $true
            }
        }
        return $false
    }
}

# PmcWidget - Base class for all PMC widgets
# Extends SpeedTUI Component with PMC-specific theme and layout integration

using namespace System.Collections.Generic
using namespace System.Text

Set-StrictMode -Version Latest

# SpeedTUI framework must be loaded before this file
# (loaded by PmcApplication.ps1 or Start-PmcTUI.ps1)
# This check ensures Component class is available
if (-not ([System.Management.Automation.PSTypeName]'Component').Type) {
    throw "SpeedTUI Component class not found. Ensure SpeedTUILoader.ps1 is loaded before PmcWidget.ps1"
}

# PmcThemeEngine must be loaded before this file
# (loaded by SpeedTUILoader.ps1)
if (-not ([System.Management.Automation.PSTypeName]'PmcThemeEngine').Type) {
    throw "PmcThemeEngine class not found. Ensure PmcThemeEngine.ps1 is loaded before PmcWidget.ps1"
}

# Ensure ZIndex is loaded (needed by most widgets)
if (-not ([System.Management.Automation.PSTypeName]'ZIndex').Type) {
    $zIndexPath = Join-Path $PSScriptRoot '../ZIndex.ps1'
    if (Test-Path $zIndexPath) {
        . $zIndexPath
    }
}

<#
.SYNOPSIS
Base class for all PMC widgets extending SpeedTUI Component

.DESCRIPTION
PmcWidget provides the foundation for all PMC UI components:
- Integration with PMC's theme system (single hex → full palette derivation)
- Layout constraint support (named regions, percentage-based positioning)
- Box drawing with full Unicode character set
- Performance optimizations (string caching, pre-computation)
- Unified event handling
- State management hooks

.EXAMPLE
# Example: Custom widget implementation
# class MyCustomWidget : PmcWidget {
#     [void] RenderToEngine([object]$engine) {
#         $this.DrawBox($this.X, $this.Y, 20, 5, $this.GetThemedInt('Border'), $this.GetThemedBgInt('Background', 20, 0))
#     }
# }
#>
class PmcWidget : Component {
    # === PMC-Specific Properties ===
    [string]$Name = ""                    # Widget name for debugging
    [bool]$Visible = $true                # Widget visibility (default: true)
    [hashtable]$LayoutConstraints = @{}   # Named region constraints
    [string]$RegionID = ""                # Engine Layout Region ID

    # === Theme Integration ===
    hidden [hashtable]$_pmcTheme = $null        # Cached PMC theme
    hidden [hashtable]$_pmcStyleTokens = $null  # Cached style tokens
    hidden [bool]$_themeInitialized = $false

    # === Box Drawing Characters ===
    hidden [hashtable]$_boxChars = @{
        # Single line
        'single_horizontal'   = '─'
        'single_vertical'     = '│'
        'single_topleft'      = '┌'
        'single_topright'     = '┐'
        'single_bottomleft'   = '└'
        'single_bottomright'  = '┘'
        'single_cross'        = '┼'
        'single_t_down'       = '┬'
        'single_t_up'         = '┴'
        'single_t_right'      = '├'
        'single_t_left'       = '┤'

        # Double line
        'double_horizontal'   = '═'
        'double_vertical'     = '║'
        'double_topleft'      = '╔'
        'double_topright'     = '╗'
        'double_bottomleft'   = '╚'
        'double_bottomright'  = '╝'
        'double_cross'        = '╬'
        'double_t_down'       = '╦'
        'double_t_up'         = '╩'
        'double_t_right'      = '╠'
        'double_t_left'       = '╣'

        # Heavy line
        'heavy_horizontal'    = '━'
        'heavy_vertical'      = '┃'
        'heavy_topleft'       = '┏'
        'heavy_topright'      = '┓'
        'heavy_bottomleft'    = '┗'
        'heavy_bottomright'   = '┛'

        # Rounded
        'rounded_topleft'     = '╭'
        'rounded_topright'    = '╮'
        'rounded_bottomleft'  = '╰'
        'rounded_bottomright' = '╯'
    }

    # === Constructor ===
    PmcWidget() : base() {
        $this.Name = $this.GetType().Name
        $this._EnsureThemeInitialized()
    }

    PmcWidget([string]$name) : base() {
        $this.Name = $name
        $this._EnsureThemeInitialized()
    }

    # === Layout System ===

    <#
    .SYNOPSIS
    Register layout regions with the engine.
    Override this to define complex grids or sub-regions.
    #>
    [void] RegisterLayout([object]$engine) {
        if ([string]::IsNullOrEmpty($this.RegionID)) {
            $this.RegionID = $this.Name + "_" + [Guid]::NewGuid().ToString().Substring(0, 8)
        }

        # Define base region for this widget
        # Z-Index default 0, parent relative if supported later
        if ($engine.PSObject.Methods['DefineRegion']) {
            $engine.DefineRegion($this.RegionID, $this.X, $this.Y, $this.Width, $this.Height)
        }
    }

    # === Rendering ===

    <#
    .SYNOPSIS
    Ensure PMC theme system is loaded
    #>
    hidden [void] _EnsureThemeInitialized() {
        if ($this._themeInitialized) { return }

        try {
            # Get PMC theme state
            $displayState = Get-PmcState -Section 'Display'
            if ($displayState) {
                $this._pmcTheme = $displayState.Theme
                $this._pmcStyleTokens = $displayState.Styles
            }

            # Fallback to defaults if state not available
            # FAIL FAST
            # if (-not $this._pmcTheme) {
            #    $this._pmcTheme = @{
            #        PaletteName = 'default'
            #        Hex = '#33aaff'
            #        TrueColor = $true
            #    }
            # }

            # FAIL FAST
            # if (-not $this._pmcStyleTokens) {
            #    $this._pmcStyleTokens = @{
            #        Title = @{ Fg = '#33aaff' }
            #        Body = @{ Fg = '#CCCCCC' }
            #        Border = @{ Fg = '#666666' }
            #    }
            # }

            $this._themeInitialized = $true
        }
        catch {
            # FAIL FAST
            throw

            # Fallback - widget still functional with defaults
            # if (Get-Command Write-PmcTuiLog -ErrorAction SilentlyContinue) {
            #     Write-PmcTuiLog "Theme initialization failed: $($_.Exception.Message)" "ERROR"
            #     Write-PmcTuiLog "Stack: $($_.ScriptStackTrace)"
            # }
            # $this._themeInitialized = $true
        }
    }

    <#
    .SYNOPSIS
    Get color for a specific role from PMC theme system

    .PARAMETER role
    Color role: Primary, Border, Text, Muted, Error, Warning, Success, Bright, Header, etc.

    .OUTPUTS
    String - background ANSI escape sequence

    .NOTES
    NEW THEME API - Replaces GetThemedAnsi()
    Delegates to PmcThemeEngine singleton for all color resolution
    Supports both solid colors and gradients automatically

    Property name format: "Background.Field", "Background.FieldFocused", etc.
    For solid colors: width/charIndex ignored, same ANSI for all positions
    For gradients: returns interpolated ANSI for specific character position
    #>
    [string] GetThemedBg([string]$propertyName, [int]$width, [int]$charIndex) {
        $engine = [PmcThemeEngine]::GetInstance()
        $result = $engine.GetBackgroundAnsi($propertyName, $width, $charIndex)
        # CRITICAL DEBUG: Log what theme engine returns
        return $result
    }

    <#
    .SYNOPSIS
    Get foreground ANSI color sequence from theme engine

    .PARAMETER propertyName
    Theme property name (e.g., "Foreground.Field", "Foreground.FieldFocused")

    .OUTPUTS
    String - foreground ANSI escape sequence

    .NOTES
    NEW THEME API - Replaces GetThemedAnsi()
    Foregrounds are typically solid colors
    #>
    [string] GetThemedFg([string]$propertyName) {
        $engine = [PmcThemeEngine]::GetInstance()
        $result = $engine.GetForegroundAnsi($propertyName)
        # CRITICAL DEBUG: Log what theme engine returns
        return $result
    }

    <#
    .SYNOPSIS
    Get integer color value for HybridRenderEngine
    #>
    [int] GetThemedColorInt([string]$propertyName) {
        $engine = [PmcThemeEngine]::GetInstance()
        return $engine.GetThemeColorInt($propertyName)
    }

    <#
    .SYNOPSIS
    Get packed RGB integer for a specific role (Hybrid Engine optimized)

    .PARAMETER role
    Color role or property name

    .PARAMETER background
    If true, gets background color (supports gradient/position if index provided)

    .OUTPUTS
    Int - Packed RGB integer or -1
    #>
    [int] GetThemedInt([string]$role) {
        $engine = [PmcThemeEngine]::GetInstance()
        return $engine.GetForegroundInt($role)
    }

    [int] GetThemedBgInt([string]$role, [int]$width, [int]$charIndex) {
        $engine = [PmcThemeEngine]::GetInstance()
        return $engine.GetBackgroundInt($role, $width, $charIndex)
    }

    <#
    .SYNOPSIS
    Get gradient info for a property (returns null if solid)
    #>
    [object] GetGradientInfo([string]$propertyName) {
        $engine = [PmcThemeEngine]::GetInstance()
        return $engine.GetGradientInfo($propertyName)
    }

    <#
    .SYNOPSIS
    Write themed text that automatically uses gradient if theme defines it

    .PARAMETER renderEngine
    The render engine (HybridRenderEngine)

    .PARAMETER x
    X coordinate

    .PARAMETER y
    Y coordinate

    .PARAMETER text
    Text to render

    .PARAMETER fgProp
    Foreground theme property (e.g., 'Foreground.Row')

    .PARAMETER bgProp
    Background theme property (e.g., 'Background.Row')
    #>
    [void] WriteThemedAt([object]$renderEngine, [int]$x, [int]$y, [string]$text, [string]$fgProp, [string]$bgProp) {
        $themeEngine = [PmcThemeEngine]::GetInstance()

        # Check if foreground is gradient
        $gradientInfo = $themeEngine.GetGradientInfo($fgProp)

        # DEBUG: Log to file
        # Add-Content -Path "/tmp/pmc-gradient-debug.log" -Value "[$(Get-Date -Format 'HH:mm:ss.fff')] WriteThemedAt: fgProp=$fgProp gradientInfo=$(if($gradientInfo){'GRADIENT'}else{'null'})"

        # Get background color (always solid for now)
        $bg = $themeEngine.GetThemeColorInt($bgProp)

        if ($gradientInfo) {
            # Use gradient overload
            # Add-Content -Path "/tmp/pmc-gradient-debug.log" -Value "  -> GRADIENT WriteAt: Start=$($gradientInfo.Start) End=$($gradientInfo.End)"
            $renderEngine.WriteAt($x, $y, $text, $gradientInfo.Start, $gradientInfo.End, $bg)
        }
        else {
            # Use solid color
            $fg = $themeEngine.GetThemeColorInt($fgProp)
            $renderEngine.WriteAt($x, $y, $text, $fg, $bg)
        }
    }

    <#
    .SYNOPSIS
    Write themed text to a specific region, automatically handling gradients

    .PARAMETER renderEngine
    The render engine (HybridRenderEngine)

    .PARAMETER regionId
    Target region ID

    .PARAMETER text
    Text to render

    .PARAMETER fgProp
    Foreground theme property

    .PARAMETER bgProp
    Background theme property
    #>
    [void] WriteThemedToRegion([object]$renderEngine, [string]$regionId, [string]$text, [string]$fgProp, [string]$bgProp) {
        $themeEngine = [PmcThemeEngine]::GetInstance()

        # Check if foreground is gradient
        $gradientInfo = $themeEngine.GetGradientInfo($fgProp)

        # Get background color (always solid for now)
        $bg = $themeEngine.GetThemeColorInt($bgProp)

        if ($gradientInfo) {
            # Use gradient overload
            $renderEngine.WriteToRegion($regionId, $text, $gradientInfo.Start, $gradientInfo.End, $bg)
        }
        else {
            # Use solid color
            $fg = $themeEngine.GetThemeColorInt($fgProp)
            $renderEngine.WriteToRegion($regionId, $text, $fg, $bg)
        }
    }


    # === Box Drawing Methods ===

    <#
    .SYNOPSIS
    Get box-drawing character by name

    .PARAMETER charName
    Character name (e.g., 'single_horizontal', 'double_topleft', 'rounded_topleft')

    .OUTPUTS
    Unicode box-drawing character
    #>
    [string] GetBoxChar([string]$charName) {
        if ($this._boxChars.ContainsKey($charName)) {
            return $this._boxChars[$charName]
        }
        return ''
    }

    <#
    .SYNOPSIS
    Build a horizontal line with specified style

    .PARAMETER width
    Width in characters

    .PARAMETER style
    Line style: 'single' (default), 'double', 'heavy'

    .OUTPUTS
    String containing repeated horizontal line character
    #>
    [string] BuildHorizontalLine([int]$width, [string]$style = 'single') {
        $char = $this.GetBoxChar("${style}_horizontal")
        if ([string]::IsNullOrEmpty($char)) {
            $char = '─'
        }
        return $char * $width
    }

    <#
    .SYNOPSIS
    Build a box border string (top, middle, or bottom line)

    .PARAMETER width
    Width in characters (total, including corners)

    .PARAMETER position
    'top', 'middle', 'bottom'

    .PARAMETER style
    Border style: 'single', 'double', 'heavy', 'rounded'

    .OUTPUTS
    String containing full border line with corners

    .EXAMPLE
    $topLine = $this.BuildBoxBorder(40, 'top', 'single')      # "┌──────────────────────────────────────┐"
    $bottomLine = $this.BuildBoxBorder(40, 'bottom', 'rounded')  # "╰──────────────────────────────────────╯"
    #>
    [string] BuildBoxBorder([int]$width, [string]$position, [string]$style = 'single') {
        if ($width -lt 2) { return '' }

        $leftChar = ''
        $rightChar = ''
        $horizChar = $this.GetBoxChar("${style}_horizontal")

        # Handle rounded style (special case - uses single for horizontal)
        if ($style -eq 'rounded') {
            $horizChar = $this.GetBoxChar('single_horizontal')
            switch ($position) {
                'top' {
                    $leftChar = $this.GetBoxChar('rounded_topleft')
                    $rightChar = $this.GetBoxChar('rounded_topright')
                }
                'bottom' {
                    $leftChar = $this.GetBoxChar('rounded_bottomleft')
                    $rightChar = $this.GetBoxChar('rounded_bottomright')
                }
                default {
                    $leftChar = $this.GetBoxChar('single_vertical')
                    $rightChar = $this.GetBoxChar('single_vertical')
                }
            }
        }
        else {
            switch ($position) {
                'top' {
                    $leftChar = $this.GetBoxChar("${style}_topleft")
                    $rightChar = $this.GetBoxChar("${style}_topright")
                }
                'bottom' {
                    $leftChar = $this.GetBoxChar("${style}_bottomleft")
                    $rightChar = $this.GetBoxChar("${style}_bottomright")
                }
                default {
                    $leftChar = $this.GetBoxChar("${style}_vertical")
                    $rightChar = $this.GetBoxChar("${style}_vertical")
                }
            }
        }

        $innerWidth = $width - 2
        return $leftChar + ($horizChar * $innerWidth) + $rightChar
    }

    # === Layout Constraint Methods ===

    <#
    .SYNOPSIS
    Apply layout constraints to calculate actual position and size

    .PARAMETER termWidth
    Terminal width in characters

    .PARAMETER termHeight
    Terminal height in characters

    .DESCRIPTION
    Supports percentage-based positioning and sizing:
    - X/Y: Can be integers or strings like "10%" or "CENTER"
    - Width/Height: Can be integers or strings like "50%" or "FILL"
    #>
    [void] ApplyLayoutConstraints([int]$termWidth, [int]$termHeight) {
        if (-not $this.LayoutConstraints -or $this.LayoutConstraints.Count -eq 0) {
            return
        }

        $newX = $this.X
        $newY = $this.Y
        $newWidth = $this.Width
        $newHeight = $this.Height

        # Process X constraint
        if ($this.LayoutConstraints.ContainsKey('X')) {
            $xConstraint = $this.LayoutConstraints['X']
            if ($xConstraint -is [string]) {
                if ($xConstraint -match '^(\d+)%$') {
                    $pct = [int]$Matches[1]
                    $newX = [Math]::Floor($termWidth * $pct / 100.0)
                }
                elseif ($xConstraint -eq 'CENTER') {
                    $newX = [Math]::Floor(($termWidth - $newWidth) / 2.0)
                }
            }
            else {
                $newX = [int]$xConstraint
            }
        }

        # Process Y constraint
        if ($this.LayoutConstraints.ContainsKey('Y')) {
            $yConstraint = $this.LayoutConstraints['Y']
            if ($yConstraint -is [string]) {
                if ($yConstraint -match '^(\d+)%$') {
                    $pct = [int]$Matches[1]
                    $newY = [Math]::Floor($termHeight * $pct / 100.0)
                }
                elseif ($yConstraint -match '^BOTTOM-(\d+)$') {
                    $offset = [int]$Matches[1]
                    $newY = $termHeight - $offset
                }
                elseif ($yConstraint -eq 'BOTTOM') {
                    $newY = $termHeight - 1
                }
            }
            else {
                $newY = [int]$yConstraint
            }
        }

        # Process Width constraint
        if ($this.LayoutConstraints.ContainsKey('Width')) {
            $widthConstraint = $this.LayoutConstraints['Width']
            if ($widthConstraint -is [string]) {
                if ($widthConstraint -match '^(\d+)%$') {
                    $pct = [int]$Matches[1]
                    $newWidth = [Math]::Floor($termWidth * $pct / 100.0)
                }
                elseif ($widthConstraint -eq 'FILL') {
                    $newWidth = $termWidth - $newX
                }
            }
            else {
                $newWidth = [int]$widthConstraint
            }
        }

        # Process Height constraint
        if ($this.LayoutConstraints.ContainsKey('Height')) {
            $heightConstraint = $this.LayoutConstraints['Height']
            if ($heightConstraint -is [string]) {
                if ($heightConstraint -match '^(\d+)%$') {
                    $pct = [int]$Matches[1]
                    $newHeight = [Math]::Floor($termHeight * $pct / 100.0)
                }
                elseif ($heightConstraint -eq 'FILL') {
                    $newHeight = $termHeight - $newY
                }
            }
            else {
                $newHeight = [int]$heightConstraint
            }
        }

        # Apply calculated bounds (uses SpeedTUI's methods)
        $this.SetPosition($newX, $newY)
        $this.SetSize($newWidth, $newHeight)
    }

    # === Terminal Resize Handling ===

    <#
    .SYNOPSIS
    Handle terminal resize events

    .DESCRIPTION
    Override from Component to add layout constraint recalculation
    #>
    [void] OnTerminalResize([int]$newWidth, [int]$newHeight) {
        # Recalculate constraints
        $this.ApplyLayoutConstraints($newWidth, $newHeight)

        # Call base implementation (handles children, invalidation)
        ([Component]$this).OnTerminalResize($newWidth, $newHeight)
    }

    # === Utility Methods ===

    <#
    .SYNOPSIS
    Build a VT100 cursor position sequence

    .PARAMETER x
    X coordinate (column, 0-based)

    .PARAMETER y
    Y coordinate (row, 0-based)

    .OUTPUTS
    ANSI escape sequence to move cursor
    #>
    [string] BuildMoveTo([int]$x, [int]$y) {
        # VT100 uses 1-based coordinates
        $col = $x + 1
        $row = $y + 1
        return "`e[${row};${col}H"
    }

    <#
    .SYNOPSIS
    Get cached spaces string (performance optimization)

    .PARAMETER count
    Number of spaces

    .OUTPUTS
    String of spaces
    #>
    [string] GetSpaces([int]$count) {
        if ($count -le 0) { return '' }
        return (" " * $count)
    }

    <#
    .SYNOPSIS
    Truncate text to fit width with ellipsis

    .PARAMETER text
    Text to truncate

    .PARAMETER maxWidth
    Maximum width

    .OUTPUTS
    Truncated text with ellipsis if needed
    #>
    [string] TruncateText([string]$text, [int]$maxWidth) {
        if ([string]::IsNullOrEmpty($text)) { return '' }

        # Use visible length for truncation decision
        $visibleLen = $this.GetVisibleLength($text)
        if ($visibleLen -le $maxWidth) { return $text }
        if ($maxWidth -le 1) { return '…' }

        # Truncation with ANSI codes is complex - strip codes, truncate, return
        # (We lose color formatting, but that's acceptable for truncated text)
        $stripped = $text -replace '\e\[[0-9;]*m', ''
        return $stripped.Substring(0, $maxWidth - 1) + '…'
    }

    <#
    .SYNOPSIS
    Get visible length of text (excluding ANSI escape codes)

    .PARAMETER text
    Text to measure

    .OUTPUTS
    Visible character count
    #>
    [int] GetVisibleLength([string]$text) {
        if ([string]::IsNullOrEmpty($text)) { return 0 }
        # Remove ANSI escape sequences: \e[...m or \e[..;..m etc.
        $stripped = $text -replace '\e\[[0-9;]*m', ''
        return $stripped.Length
    }

    <#
    .SYNOPSIS
    Pad text to specified width

    .PARAMETER text
    Text to pad (may contain ANSI escape codes)

    .PARAMETER width
    Target width in visible characters

    .PARAMETER align
    Alignment: 'left' (default), 'center', 'right'

    .OUTPUTS
    Padded text
    #>
    [string] PadText([string]$text, [int]$width, [string]$align = 'left') {
        if ([string]::IsNullOrEmpty($text)) { return (" " * $width) }

        # Use visible length instead of raw .Length to account for ANSI codes
        $visibleLen = $this.GetVisibleLength($text)
        if ($text.Length -lt 50) {
            # Write-PmcTuiLog "PadText: text='$text' rawLen=$($text.Length) visibleLen=$visibleLen width=$width align=$align"
        }

        if ($visibleLen -ge $width) {
            # Text is already wide enough - truncate if needed
            # For now, just return as-is (truncation is complex with ANSI codes)
            return $text
        }

        $padding = $width - $visibleLen
        $result = switch ($align) {
            'center' {
                $leftPad = [Math]::Floor($padding / 2.0)
                $rightPad = $padding - $leftPad
                (" " * $leftPad) + $text + (" " * $rightPad)
            }
            'right' { (" " * $padding) + $text }
            default { $text + (" " * $padding) }
        }
        return $result
    }

    <#
    .SYNOPSIS
    L-POL-1: Truncate help text to fit narrow terminals

    .PARAMETER text
    Help text to truncate

    .PARAMETER maxWidth
    Maximum width (defaults to terminal width - 10)

    .OUTPUTS
    Truncated string with ellipsis if needed
    #>
    [string] TruncateHelpText([string]$text, [int]$maxWidth = -1) {
        if ($maxWidth -lt 0) {
            # L-POL-1: Use centralized terminal service (cached, optimized)
            $termWidth = [Console]::WindowWidth
            $maxWidth = $termWidth - 10
        }

        if ($text.Length -le $maxWidth) {
            return $text
        }

        # Truncate with ellipsis
        return $text.Substring(0, $maxWidth - 3) + "..."
    }
}


# Classes and functions are exported automatically in PowerShell 5.1+

﻿# TabPanel.ps1 - Tabbed interface widget for organizing fields into logical groups
#
# Usage:
#   $tabPanel = [TabPanel]::new()
#   $tabPanel.AddTab('Identity', @(
#       @{Name='ID1'; Label='ID1'; Value='12345'}
#       @{Name='ID2'; Label='ID2'; Value='ABC-2024'}
#   ))
#   $tabPanel.AddTab('Request', @(...))
#
#   # Navigation
#   $tabPanel.NextTab()       # Tab key
#   $tabPanel.PrevTab()       # Shift+Tab
#   $tabPanel.SelectTab(2)    # Number keys 1-6
#   $tabPanel.NextField()     # Down arrow
#   $tabPanel.PrevField()     # Up arrow
#
#   # Rendering
#   $output = $tabPanel.Render()

using namespace System.Collections.Generic
using namespace System.Text

Set-StrictMode -Version Latest

# Load PmcWidget base class
if (-not ([System.Management.Automation.PSTypeName]'PmcWidget').Type) {
    . "$PSScriptRoot/PmcWidget.ps1"
}

<#
.SYNOPSIS
Tabbed interface widget for organizing many fields into logical groups

.DESCRIPTION
TabPanel provides a tab-based navigation interface for displaying and editing
grouped fields. Perfect for forms with many fields that need organization.

Features:
- Multiple tabs with labels
- Keyboard navigation (Tab/Shift+Tab, arrow keys, number keys)
- Inline field editing
- Theme integration
- Visual tab indicators (active, inactive)
- Field highlighting and selection
- Scrolling within tabs if needed

.EXAMPLE
$tabs = [TabPanel]::new()
$tabs.AddTab('General', @(
    @{Name='name'; Label='Name'; Value='John Doe'}
    @{Name='email'; Label='Email'; Value='john@example.com'}
))
$tabs.AddTab('Details', @(...))
$output = $tabs.Render()
#>
class TabPanel : PmcWidget {
    # === Tab Structure ===
    [List[hashtable]]$Tabs = [List[hashtable]]::new()
    [int]$CurrentTabIndex = 0
    [int]$SelectedFieldIndex = 0  # Field index within current tab

    # === Display Configuration ===
    [int]$TabBarHeight = 2        # Rows for tab bar
    [int]$ContentPadding = 2      # Padding inside content area
    [int]$LabelWidth = 22         # Width for field labels
    [bool]$ShowTabNumbers = $true # Show [1] [2] [3] on tabs

    # === Events ===
    [scriptblock]$OnTabChanged = {}      # Called when tab changes: param($tabIndex)
    [scriptblock]$OnFieldSelected = {}   # Called when field selected: param($field)
    [scriptblock]$OnFieldEdit = {}       # Called when field edited: param($field, $newValue)

    # === Constructor ===
    TabPanel() : base("TabPanel") {
        $this.Width = 80
        $this.Height = 25
        $this.CanFocus = $true
    }

    # === Tab Management ===

    <#
    .SYNOPSIS
    Add a new tab with fields

    .PARAMETER name
    Tab name/label

    .PARAMETER fields
    Array of field hashtables: @{Name=''; Label=''; Value=''; Type='text'}
    #>
    [void] AddTab([string]$name, [array]$fields) {
        $tab = @{
            Name         = $name
            Fields       = $fields
            ScrollOffset = 0
        }
        $this.Tabs.Add($tab)
    }
    [void] ClearTabs() { $this.Tabs.Clear(); $this.CurrentTabIndex = 0; $this.SelectedFieldIndex = 0 }

    <#
    .SYNOPSIS
    Get current tab
    #>
    [hashtable] GetCurrentTab() {
        if ($this.Tabs.Count -eq 0) {
            return $null
        }
        return $this.Tabs[$this.CurrentTabIndex]
    }

    <#
    .SYNOPSIS
    Get currently selected field
    #>
    [hashtable] GetCurrentField() {
        $tab = $this.GetCurrentTab()
        if ($null -eq $tab -or $tab.Fields.Count -eq 0) {
            return $null
        }

        if ($this.SelectedFieldIndex -ge 0 -and $this.SelectedFieldIndex -lt $tab.Fields.Count) {
            return $tab.Fields[$this.SelectedFieldIndex]
        }

        return $null
    }

    # === Navigation ===

    [void] NextTab() {
        if ($this.Tabs.Count -eq 0) { return }

        $oldIndex = $this.CurrentTabIndex
        $this.CurrentTabIndex = ($this.CurrentTabIndex + 1) % $this.Tabs.Count
        $this.SelectedFieldIndex = 0  # Reset to first field in new tab

        if ($oldIndex -ne $this.CurrentTabIndex) {
            $this._InvokeCallback($this.OnTabChanged, $this.CurrentTabIndex)
        }
    }

    [void] PrevTab() {
        if ($this.Tabs.Count -eq 0) { return }

        $oldIndex = $this.CurrentTabIndex
        $this.CurrentTabIndex--
        if ($this.CurrentTabIndex -lt 0) {
            $this.CurrentTabIndex = $this.Tabs.Count - 1
        }
        $this.SelectedFieldIndex = 0

        if ($oldIndex -ne $this.CurrentTabIndex) {
            $this._InvokeCallback($this.OnTabChanged, $this.CurrentTabIndex)
        }
    }

    [void] SelectTab([int]$index) {
        if ($index -ge 0 -and $index -lt $this.Tabs.Count) {
            $oldIndex = $this.CurrentTabIndex
            $this.CurrentTabIndex = $index
            $this.SelectedFieldIndex = 0

            if ($oldIndex -ne $this.CurrentTabIndex) {
                $this._InvokeCallback($this.OnTabChanged, $this.CurrentTabIndex)
            }
        }
    }

    [void] NextField() {
        $tab = $this.GetCurrentTab()
        if ($null -eq $tab -or $tab.Fields.Count -eq 0) { return }

        $this.SelectedFieldIndex++
        if ($this.SelectedFieldIndex -ge $tab.Fields.Count) {
            $this.SelectedFieldIndex = $tab.Fields.Count - 1
        }

        # Auto-scroll if needed
        $this._EnsureFieldVisible()

        $field = $this.GetCurrentField()
        if ($field) {
            $this._InvokeCallback($this.OnFieldSelected, $field)
        }
    }

    [void] PrevField() {
        $tab = $this.GetCurrentTab()
        if ($null -eq $tab -or $tab.Fields.Count -eq 0) { return }

        $this.SelectedFieldIndex--
        if ($this.SelectedFieldIndex -lt 0) {
            $this.SelectedFieldIndex = 0
        }

        # Auto-scroll if needed
        $this._EnsureFieldVisible()

        $field = $this.GetCurrentField()
        if ($field) {
            $this._InvokeCallback($this.OnFieldSelected, $field)
        }
    }

    hidden [void] _EnsureFieldVisible() {
        $tab = $this.GetCurrentTab()
        if ($null -eq $tab) { return }

        $visibleRows = $this.Height - $this.TabBarHeight - 4  # Tab bar + padding

        # If selected field is above visible area
        if ($this.SelectedFieldIndex -lt $tab.ScrollOffset) {
            $tab.ScrollOffset = $this.SelectedFieldIndex
        }

        # If selected field is below visible area
        if ($this.SelectedFieldIndex -ge ($tab.ScrollOffset + $visibleRows)) {
            $tab.ScrollOffset = $this.SelectedFieldIndex - $visibleRows + 1
        }
    }

    # === Input Handling ===

    [bool] HandleInput([ConsoleKeyInfo]$keyInfo) {
        if ($keyInfo.Key -eq 'Tab') {
            if ($keyInfo.Modifiers -band [ConsoleModifiers]::Shift) {
                $this.PrevTab()
            }
            else {
                $this.NextTab()
            }
            return $true
        }

        # Left/Right arrows cycle through tabs
        if ($keyInfo.Key -eq 'LeftArrow') {
            $this.PrevTab()
            return $true
        }

        if ($keyInfo.Key -eq 'RightArrow') {
            $this.NextTab()
            return $true
        }

        if ($keyInfo.Key -eq 'UpArrow') {
            $this.PrevField()
            return $true
        }

        if ($keyInfo.Key -eq 'DownArrow') {
            $this.NextField()
            return $true
        }

        if ($keyInfo.Key -eq 'PageDown') {
            # Jump 10 fields down
            for ($i = 0; $i -lt 10; $i++) {
                $this.NextField()
            }
            return $true
        }

        if ($keyInfo.Key -eq 'PageUp') {
            # Jump 10 fields up
            for ($i = 0; $i -lt 10; $i++) {
                $this.PrevField()
            }
            return $true
        }

        if ($keyInfo.Key -eq 'Home') {
            $this.SelectedFieldIndex = 0
            $tab = $this.GetCurrentTab()
            if ($tab) {
                $tab.ScrollOffset = 0
            }
            return $true
        }

        if ($keyInfo.Key -eq 'End') {
            $tab = $this.GetCurrentTab()
            if ($tab -and $tab.Fields.Count -gt 0) {
                $this.SelectedFieldIndex = $tab.Fields.Count - 1
                $this._EnsureFieldVisible()
            }
            return $true
        }

        # Number keys 1-9 to jump to tabs
        if ($this.ShowTabNumbers -and $keyInfo.KeyChar -match '[1-9]') {
            $tabNum = [int]$keyInfo.KeyChar - [int][char]'1'  # 0-based index
            $this.SelectTab($tabNum)
            return $true
        }

        return $false
    }

    # === Layout System ===

    [void] RegisterLayout([object]$engine) {
        ([PmcWidget]$this).RegisterLayout($engine)

        $engine.DefineRegion("$($this.RegionID)_Tabs", $this.X, $this.Y, $this.Width, 1)
        $engine.DefineRegion("$($this.RegionID)_Separator", $this.X, $this.Y + 1, $this.Width, 1)

        $contentHeight = $this.Height - 2
        $engine.DefineRegion("$($this.RegionID)_Content", $this.X, $this.Y + 2, $this.Width, $contentHeight)
    }


    # === Rendering ===

    [void] RenderToEngine([object]$engine) {
        $this.RegisterLayout($engine)

        # Use Z-layer 10 for tabs to ensure they render above other content
        if ($engine.PSObject.Methods['BeginLayer']) {
            $engine.BeginLayer(10)
        }

        if ($this.Tabs.Count -eq 0) {
            # Render empty state
            $fgSec = $this.GetThemedInt("Foreground.Secondary"); $engine.WriteAt($this.X, $this.Y, "No tabs defined", $fgSec, -1)
            if ($engine.PSObject.Methods['EndLayer']) {
                $engine.EndLayer()
            }
            return
        }

        # 1. Render Tab Bar
        $currentX = $this.X
        for ($i = 0; $i -lt $this.Tabs.Count; $i++) {
            $tab = $this.Tabs[$i]
            $isCurrent = ($i -eq $this.CurrentTabIndex)

            $label = $tab.Name
            if ($this.ShowTabNumbers) {
                $label = "[$($i+1)] $label"
            }

            # Add padding
            $label = " $label "

            if ($isCurrent) {
                # Active Tab
                $bg = $this.GetThemedInt("Background.Accent")
                $fg = $this.GetThemedInt("Foreground.Primary")
                $engine.WriteAt($currentX, $this.Y, $label, $fg, $bg)
            } else {
                # Inactive Tab
                $bg = $this.GetThemedInt("Background.Panel")
                $fg = $this.GetThemedInt("Foreground.Secondary")
                $engine.WriteAt($currentX, $this.Y, $label, $fg, $bg)
            }

            $currentX += $label.Length + 1
        }

        # 2. Render Separator
        $sepColor = $this.GetThemedInt("Foreground.Border")
        $line = [string]::new([char]0x2500, $this.Width); $engine.WriteAt($this.X, $this.Y + 1, $line, $sepColor, -1)

        # 3. Render Content (Fields)
        $tab = $this.GetCurrentTab()
        if ($tab) {
            $startY = $this.Y + 2
            $fields = $tab.Fields

            # Handle scrolling
            if (-not $tab.ContainsKey('ScrollOffset')) { $tab.ScrollOffset = 0 }

            $visibleRows = $this.Height - 2 - 1 # Height - TabBar - Separator

            for ($i = 0; $i -lt $visibleRows; $i++) {
                $fieldIndex = $tab.ScrollOffset + $i
                if ($fieldIndex -ge $fields.Count) { break }

                $field = $fields[$fieldIndex]
                $isSelected = ($fieldIndex -eq $this.SelectedFieldIndex)
                $rowY = $startY + $i

                # Render Label
                if ($isSelected) {
                    $labelBg = $this.GetThemedInt("Background.Selection"); $labelFg = $this.GetThemedInt("Foreground.Selection")
                } else {
                    $labelBg = $this.GetThemedInt("Background.Primary"); $labelFg = $this.GetThemedInt("Foreground.Secondary")
                }
                $engine.WriteAt($this.X + 2, $rowY, $field.Label.PadRight($this.LabelWidth), $labelFg, $labelBg)

                # Render Value
                $valueX = $this.X + 2 + $this.LabelWidth + 1
                $valueWidth = $this.Width - ($valueX - $this.X) - 2
                $displayValue = if ($field.Value) { $field.Value.ToString() } else { "" }

                if ($displayValue.Length -gt $valueWidth) {
                    $displayValue = $displayValue.Substring(0, $valueWidth - 3) + "..."
                }

                if ($isSelected) {
                    $bg = $this.GetThemedInt("Background.Selection")
                    $fg = $this.GetThemedInt("Foreground.Selection")
                    $engine.WriteAt($valueX, $rowY, $displayValue.PadRight($valueWidth), $fg, $bg)
                } else {
                    $fg = $this.GetThemedInt("Foreground.Primary")
                    $engine.WriteAt($valueX, $rowY, $displayValue, $fg, -1)
                }
            }

            # Scroll indicators if needed
            if ($tab.ScrollOffset -gt 0) {
                $fgAcc = $this.GetThemedInt("Foreground.Accent"); $engine.WriteAt($this.X + $this.Width - 1, $startY, "^", $fgAcc, -1)
            }
            if (($tab.ScrollOffset + $visibleRows) -lt $fields.Count) {
                $fgAcc2 = $this.GetThemedInt("Foreground.Accent"); $engine.WriteAt($this.X + $this.Width - 1, $startY + $visibleRows - 1, "v", $fgAcc2, -1)
            }
        }

        if ($engine.PSObject.Methods['EndLayer']) {
            $engine.EndLayer()
        }
    }

    # === Helper Methods ===

    hidden [void] _InvokeCallback([scriptblock]$callback, $arg) {
        if ($null -ne $callback -and $callback -ne {}) {
            try {
                if ($null -ne $arg) {
                    & $callback $arg
                }
                else {
                    & $callback
                }
            }
            catch {
                # Silently ignore callback errors
            }
        }
    }

    <#
    .SYNOPSIS
    Update a field value
    #>
    [void] UpdateFieldValue([string]$fieldName, $newValue) {
        $tab = $this.GetCurrentTab()
        if ($null -eq $tab) { return }

        foreach ($field in $tab.Fields) {
            if ($field.Name -eq $fieldName) {
                $oldValue = $field.Value
                $field.Value = $newValue
                $this._InvokeCallback($this.OnFieldEdit, @($field, $newValue))
                break
            }
        }
    }

    <#
    .SYNOPSIS
    Get all field values from all tabs as hashtable
    #>
    [hashtable] GetAllValues() {
        $values = @{}

        foreach ($tab in $this.Tabs) {
            foreach ($field in $tab.Fields) {
                $values[$field.Name] = $field.Value
            }
        }

        return $values
    }
}

