﻿# SpeedTUI Application Framework - Main application orchestrator

using namespace System.Collections.Generic

class Application {
    [string]$Title = "SpeedTUI Application"
    [Component]$RootComponent
    [RenderEngine]$RenderEngine
    [InputManager]$InputManager
    [DataStoreManager]$DataStoreManager
    [EnhancedThemeManager]$ThemeManager
    [object]$Logger

    # Application state
    hidden [bool]$_running = $false
    hidden [bool]$_initialized = $false
    hidden [System.Diagnostics.Stopwatch]$_frameTimer
    hidden [double]$_targetFrameTime = 16.67  # 60 FPS

    # Performance tracking
    hidden [int]$_frameCount = 0
    hidden [double]$_totalFrameTime = 0
    hidden [DateTime]$_startTime

    Application() {
        $this.Logger = Get-Logger
        $this.Logger.Info("Application", "Constructor", "Application created")

        # Initialize core services
        $this.RenderEngine = [OptimizedRenderEngine]::new()
        $this.InputManager = [InputManager]::new()
        $this.ThemeManager = Get-ThemeManager
        $this._frameTimer = [System.Diagnostics.Stopwatch]::new()
    }

    Application([string]$title) {
        $this.Title = $title
        $this.Logger = Get-Logger
        $this.Logger.Info("Application", "Constructor", "Application created with title", @{
            Title = $title
        })

        # Initialize core services
        $this.RenderEngine = [OptimizedRenderEngine]::new()
        $this.InputManager = [InputManager]::new()
        $this.ThemeManager = Get-ThemeManager
        $this._frameTimer = [System.Diagnostics.Stopwatch]::new()
    }

    # Set root component
    [Application] SetRoot([Component]$component) {
        [Guard]::NotNull($component, "component")

        $this.RootComponent = $component

        $this.Logger.Debug("Application", "SetRoot", "Root component set", @{
            ComponentType = $component.GetType().Name
            ComponentId = $component.Id
        })

        return $this
    }

    # Initialize application
    [void] Initialize() {
        if ($this._initialized) { return }

        $this.Logger.Info("Application", "Initialize", "Initializing application", @{
            Title = $this.Title
        })

        try {
            # Initialize render engine
            $this.RenderEngine.Initialize()

            # Initialize root component
            if ($null -eq $this.RootComponent) {
                throw [InvalidOperationException]::new("No root component set")
            }

            # Set root bounds to full terminal
            $this.RootComponent.SetBounds(0, 0, $this.RenderEngine._terminal.Width, $this.RenderEngine._terminal.Height)

            # Initialize component tree
            $this.RootComponent.Initialize($this.RenderEngine)

            # Start input manager
            $this.InputManager.Start()

            # Register focusable components
            $this.InputManager.GetFocusManager().RefreshFocusableList($this.RootComponent)

            # Set application title
            [Console]::Title = $this.Title

            $this._initialized = $true
            $this._startTime = [DateTime]::Now

            $this.Logger.Info("Application", "Initialize", "Application initialized successfully")

        } catch {
            $this.Logger.Fatal("Application", "Initialize", "Failed to initialize application", @{
                Error = $_.Exception.Message
                StackTrace = $_.ScriptStackTrace
            })
            throw
        }
    }

    # Main run loop
    [void] Run() {
        if (-not $this._initialized) {
            $this.Initialize()
        }

        $this._running = $true
        $this.Logger.Info("Application", "Run", "Starting main loop")

        try {
            $loopCount = 0
            while ($this._running) {
                $this._frameTimer.Restart()

                # Process input
                $this.ProcessInput()

                # Update data bindings
                $this.UpdateBindings()

                # Render frame
                $this.Render()

                # Frame timing
                $this.WaitForNextFrame()

                # Update performance metrics
                $this.UpdatePerformanceMetrics()

                # Safety check - stop after 30 seconds if no input
                $loopCount++
                if ($loopCount -gt 1800) {  # 60 FPS * 30 seconds
                    $this.Logger.Info("Application", "Run", "Auto-stopping after 30 seconds")
                    $this.Stop()
                }
            }
        } catch {
            $this.Logger.Error("Application", "Run", "Error in main loop", @{
                Error = $_.Exception.Message
                StackTrace = $_.ScriptStackTrace
            })
            throw
        } finally {
            $this.Cleanup()
        }
    }

    # Stop application
    [void] Stop() {
        $this.Logger.Info("Application", "Stop", "Stopping application")
        $this._running = $false
    }

    # Process input
    hidden [void] ProcessInput() {
        # Check for window resize
        $this.CheckWindowResize()

        # Process input - check for exit keys first
        try {
            if ([Console]::KeyAvailable) {
                $key = [Console]::ReadKey($true)

                # Ctrl+Q to quit (check before InputManager)
                if ($key.Key -eq [System.ConsoleKey]::Q -and
                    $key.Modifiers -eq [System.ConsoleModifiers]::Control) {
                    $this.Stop()
                    return
                }

                # Route input to component tree first
                # Component tree includes screens, widgets, and editors
                # If they don't handle it, fall through to global shortcuts
                $this.RootComponent.HandleKeyPress($key)
            }
        } catch {
            # KeyAvailable might not work in all environments - that's okay
            $this.Logger.Trace("Application", "ProcessInput", "KeyAvailable not supported", @{
                Error = $_.Exception.Message
            })
        }
    }

    # Check for terminal resize
    hidden [void] CheckWindowResize() {
        $currentWidth = [Console]::WindowWidth
        $currentHeight = [Console]::WindowHeight

        if ($currentWidth -ne $this.RenderEngine._terminal.Width -or 
            $currentHeight -ne $this.RenderEngine._terminal.Height) {

            $this.Logger.Info("Application", "CheckWindowResize", "Terminal resized", @{
                OldSize = "$($this.RenderEngine._terminal.Width)x$($this.RenderEngine._terminal.Height)"
                NewSize = "${currentWidth}x${currentHeight}"
            })

            # Update terminal dimensions
            $this.RenderEngine._terminal.UpdateDimensions()

            # Recreate render buffer
            $this.RenderEngine._buffer = [RenderBuffer]::new($currentWidth, $currentHeight)

            # Update root component bounds
            $this.RootComponent.SetBounds(0, 0, $currentWidth, $currentHeight)

            # Refresh focusable components
            $this.InputManager.GetFocusManager().RefreshFocusableList($this.RootComponent)
        }
    }

    # Update data bindings
    hidden [void] UpdateBindings() {
        # This is where reactive data updates would be processed
        # For now, it's a placeholder for future implementation
    }

    # Render frame
    hidden [void] Render() {
        $timer = $this.Logger.MeasurePerformance("Application", "RenderFrame")

        try {
            $this.RenderEngine.BeginFrame()

            # Render component tree
            $this.RootComponent.Render()

            # Show FPS counter if debug mode
            if ($this.Logger.GlobalLevel -le [LogLevel]::Debug) {
                $this.RenderDebugInfo()
            }

            $this.RenderEngine.EndFrame()

        } finally {
            $timer.Dispose()
        }
    }

    # Render debug information
    hidden [void] RenderDebugInfo() {
        $fps = $(if ($this._frameCount -gt 0) {
            [Math]::Round(1000.0 / ($this._totalFrameTime / $this._frameCount), 1)
        } else { 0 })

        $uptime = [DateTime]::Now - $this._startTime
        $uptimeStr = "{0:D2}:{1:D2}:{2:D2}" -f $uptime.Hours, $uptime.Minutes, $uptime.Seconds

        $debugInfo = "FPS: $fps | Frame: $($this._frameCount) | Uptime: $uptimeStr"

        # Draw in top-right corner
        $x = $this.RenderEngine._terminal.Width - $debugInfo.Length - 2
        $y = 0

        if ($x -gt 0) {
            $this.RenderEngine._terminal.WriteAt($x, $y, $debugInfo, [Colors]::BrightBlack, "")
        }
    }

    # Wait for next frame
    hidden [void] WaitForNextFrame() {
        $elapsed = $this._frameTimer.Elapsed.TotalMilliseconds
        $sleepTime = $this._targetFrameTime - $elapsed

        if ($sleepTime -gt 0) {
            [System.Threading.Thread]::Sleep([int]$sleepTime)
        }
    }

    # Update performance metrics
    hidden [void] UpdatePerformanceMetrics() {
        $frameTime = $this._frameTimer.Elapsed.TotalMilliseconds
        $this._frameCount++
        $this._totalFrameTime += $frameTime

        # Log performance every 60 frames
        if ($this._frameCount % 60 -eq 0) {
            $avgFrameTime = $this._totalFrameTime / 60
            $fps = 1000.0 / $avgFrameTime

            $this.Logger.Debug("Application", "Performance", "Frame statistics", @{
                AverageFrameMs = [Math]::Round($avgFrameTime, 2)
                FPS = [Math]::Round($fps, 1)
                TotalFrames = $this._frameCount
            })

            # Reset for next batch
            $this._totalFrameTime = 0
        }
    }

    # Cleanup
    hidden [void] Cleanup() {
        $this.Logger.Info("Application", "Cleanup", "Cleaning up application")

        try {
            # Stop input manager
            $this.InputManager.Stop()

            # Cleanup render engine
            $this.RenderEngine.Cleanup()

            # Log final statistics
            $totalUptime = [DateTime]::Now - $this._startTime
            $this.Logger.Info("Application", "Cleanup", "Application statistics", @{
                TotalFrames = $this._frameCount
                TotalUptime = $totalUptime.ToString()
                Title = $this.Title
            })

            # Dispose logger
            $this.Logger.Dispose()

        } catch {
            Write-Error "Cleanup failed: $_"
        }
    }
}

# Application builder for fluent API
class ApplicationBuilder {
    hidden [Application]$_app

    ApplicationBuilder() {
        $this._app = [Application]::new()
    }

    ApplicationBuilder([string]$title) {
        $this._app = [Application]::new($title)
    }

    [ApplicationBuilder] Title([string]$title) {
        $this._app.Title = $title
        return $this
    }

    [ApplicationBuilder] Root([Component]$component) {
        $this._app.SetRoot($component)
        return $this
    }

    [ApplicationBuilder] Theme([string]$themeName) {
        $this._app.ThemeManager.SetTheme($themeName)
        return $this
    }

    [ApplicationBuilder] LogLevel([LogLevel]$level) {
        $this._app.Logger.GlobalLevel = $level
        return $this
    }

    [ApplicationBuilder] LogToConsole() {
        $this._app.Logger.EnableConsole = $true
        return $this
    }

    [ApplicationBuilder] Configure([scriptblock]$configBlock) {
        & $configBlock $this._app
        return $this
    }

    [Application] Build() {
        return $this._app
    }

    [void] Run() {
        $this._app.Run()
    }
}

# Helper function for creating applications
function New-SpeedTUIApp {
    param(
        [string]$Title = "SpeedTUI Application",
        [scriptblock]$AppDefinition
    )

    $builder = [ApplicationBuilder]::new($Title)

    if ($AppDefinition) {
        $result = & $AppDefinition $builder
        # Ensure we return the builder, not whatever the scriptblock returned
        if ($result -is [ApplicationBuilder]) {
            return $result
        }
    }

    return $builder
}

﻿# SpeedTUI Data Store - Reactive data management with change notifications

using namespace System.Collections.Generic
using namespace System.ComponentModel

class DataChangeEventArgs : EventArgs {
    [string]$PropertyName
    [object]$OldValue
    [object]$NewValue
    [string]$Path
    hidden [object]$_logger

    DataChangeEventArgs([string]$propertyName, [object]$oldValue, [object]$newValue, [string]$path) {
        $this._logger = Get-Logger

        $this._logger.Trace("DataChangeEventArgs", "Constructor", "Creating data change event args", @{
            PropertyName = $propertyName
            HasOldValue = $null -ne $oldValue
            HasNewValue = $null -ne $newValue
            Path = $path
        })

        try {
            $this.PropertyName = $propertyName
            $this.OldValue = $oldValue
            $this.NewValue = $newValue
            $this.Path = $path

            $this._logger.Trace("DataChangeEventArgs", "Constructor", "Event args created successfully")
        } catch {
            $this._logger.Error("DataChangeEventArgs", "Constructor", "Failed to create event args", @{
                Exception = $_.Exception.Message
            })
            throw
        }
    }
}

class ObservableObject {
    hidden [Dictionary[string, object]]$_properties
    hidden [Dictionary[string, List[scriptblock]]]$_propertyWatchers
    hidden [object]$_logger

    # Property changed event (simplified)
    hidden [List[scriptblock]]$_propertyChangedHandlers

    ObservableObject() {
        $this._logger = Get-Logger

        $this._logger.Trace("ObservableObject", "Constructor", "Creating observable object")

        try {
            $this._properties = [Dictionary[string, object]]::new()
            $this._propertyWatchers = [Dictionary[string, List[scriptblock]]]::new()
            $this._propertyChangedHandlers = [List[scriptblock]]::new()

            $this._logger.Debug("ObservableObject", "Constructor", "Observable object created successfully")
        } catch {
            $this._logger.Error("ObservableObject", "Constructor", "Failed to create observable object", @{
                Exception = $_.Exception.Message
                StackTrace = $_.ScriptStackTrace
            })
            throw
        }
    }

    # Get property value
    [object] Get([string]$propertyName) {
        $this._logger.Trace("ObservableObject", "Get", "Getting property value", @{
            PropertyName = $propertyName
        })

        try {
            [Guard]::NotNullOrEmpty($propertyName, "propertyName")

            if ($this._properties.ContainsKey($propertyName)) {
                $value = $this._properties[$propertyName]

                $this._logger.Trace("ObservableObject", "Get", "Property value retrieved", @{
                    PropertyName = $propertyName
                    HasValue = $null -ne $value
                    ValueType = $(if ($null -ne $value) { $value.GetType().Name } else { "null" })
                })

                return $value
            }

            $this._logger.Trace("ObservableObject", "Get", "Property not found, returning null", @{
                PropertyName = $propertyName
                AvailableProperties = ($this._properties.Keys -join ", ")
            })

            return $null
        } catch {
            $this._logger.Error("ObservableObject", "Get", "Failed to get property value", @{
                PropertyName = $propertyName
                Exception = $_.Exception.Message
            })
            throw
        }
    }

    # Set property value
    [void] Set([string]$propertyName, [object]$value) {
        $this._logger.Trace("ObservableObject", "Set", "Setting property value", @{
            PropertyName = $propertyName
            HasValue = $null -ne $value
            ValueType = $(if ($null -ne $value) { $value.GetType().Name } else { "null" })
        })

        try {
            [Guard]::NotNullOrEmpty($propertyName, "propertyName")

            $oldValue = $this.Get($propertyName)

            # Check if value actually changed
            if ([object]::Equals($oldValue, $value)) {
                $this._logger.Trace("ObservableObject", "Set", "Value unchanged, skipping notification", @{
                    PropertyName = $propertyName
                })
                return
            }

            # Set new value
            $this._properties[$propertyName] = $value

            # Log change
            $this._logger.Debug("ObservableObject", "Set", "Property changed", @{
                Property = $propertyName
                OldValue = $oldValue
                NewValue = $value
                OldType = $(if ($null -ne $oldValue) { $oldValue.GetType().Name } else { "null" })
                NewType = $(if ($null -ne $value) { $value.GetType().Name } else { "null" })
            })

            # Notify property changed
            $this.OnPropertyChanged($propertyName, $oldValue, $value)

            $this._logger.Trace("ObservableObject", "Set", "Property set successfully")
        } catch {
            $this._logger.Error("ObservableObject", "Set", "Failed to set property value", @{
                PropertyName = $propertyName
                Exception = $_.Exception.Message
                StackTrace = $_.ScriptStackTrace
            })
            throw
        }
    }

    # Watch for property changes
    [void] Watch([string]$propertyName, [scriptblock]$handler) {
        $this._logger.Trace("ObservableObject", "Watch", "Adding property watcher", @{
            PropertyName = $propertyName
            HasHandler = $null -ne $handler
        })

        try {
            [Guard]::NotNullOrEmpty($propertyName, "propertyName")
            [Guard]::NotNull($handler, "handler")

            if (-not $this._propertyWatchers.ContainsKey($propertyName)) {
                $this._propertyWatchers[$propertyName] = [List[scriptblock]]::new()
                $this._logger.Debug("ObservableObject", "Watch", "Created new watcher list", @{
                    PropertyName = $propertyName
                })
            }

            $this._propertyWatchers[$propertyName].Add($handler)

            $this._logger.Debug("ObservableObject", "Watch", "Watcher added successfully", @{
                Property = $propertyName
                WatcherCount = $this._propertyWatchers[$propertyName].Count
                TotalWatchedProperties = $this._propertyWatchers.Count
            })
        } catch {
            $this._logger.Error("ObservableObject", "Watch", "Failed to add watcher", @{
                PropertyName = $propertyName
                Exception = $_.Exception.Message
            })
            throw
        }
    }

    # Remove watcher
    [void] Unwatch([string]$propertyName, [scriptblock]$handler) {
        $this._logger.Trace("ObservableObject", "Unwatch", "Removing property watcher", @{
            PropertyName = $propertyName
            HasHandler = $null -ne $handler
        })

        try {
            [Guard]::NotNullOrEmpty($propertyName, "propertyName")
            [Guard]::NotNull($handler, "handler")

            if ($this._propertyWatchers.ContainsKey($propertyName)) {
                $removed = $this._propertyWatchers[$propertyName].Remove($handler)

                $this._logger.Debug("ObservableObject", "Unwatch", "Watcher removal attempt", @{
                    PropertyName = $propertyName
                    WasRemoved = $removed
                    RemainingWatchers = $this._propertyWatchers[$propertyName].Count
                })

                # Clean up empty watcher lists
                if ($this._propertyWatchers[$propertyName].Count -eq 0) {
                    $this._propertyWatchers.Remove($propertyName)
                    $this._logger.Trace("ObservableObject", "Unwatch", "Removed empty watcher list")
                }
            } else {
                $this._logger.Warn("ObservableObject", "Unwatch", "No watchers found for property", @{
                    PropertyName = $propertyName
                    WatchedProperties = ($this._propertyWatchers.Keys -join ", ")
                })
            }
        } catch {
            $this._logger.Error("ObservableObject", "Unwatch", "Failed to remove watcher", @{
                PropertyName = $propertyName
                Exception = $_.Exception.Message
            })
            throw
        }
    }

    # Property changed notification
    hidden [void] OnPropertyChanged([string]$propertyName, [object]$oldValue, [object]$newValue) {
        $this._logger.Trace("ObservableObject", "OnPropertyChanged", "Notifying property change", @{
            PropertyName = $propertyName
            PropertyWatchers = $(if ($this._propertyWatchers.ContainsKey($propertyName)) { $this._propertyWatchers[$propertyName].Count } else { 0 })
            GlobalHandlers = $this._propertyChangedHandlers.Count
        })

        try {
            $watchersNotified = 0
            $watchersFailed = 0

            # Notify watchers
            if ($this._propertyWatchers.ContainsKey($propertyName)) {
                foreach ($watcher in $this._propertyWatchers[$propertyName]) {
                    try {
                        & $watcher $propertyName $oldValue $newValue
                        $watchersNotified++
                    } catch {
                        $watchersFailed++
                        $this._logger.Error("ObservableObject", "OnPropertyChanged", "Watcher execution failed", @{
                            Property = $propertyName
                            Exception = $_.Exception.Message
                            StackTrace = $_.ScriptStackTrace
                        })
                    }
                }
            }

            $handlersNotified = 0
            $handlersFailed = 0

            # Raise PropertyChanged event
            foreach ($handler in $this._propertyChangedHandlers) {
                try {
                    & $handler $propertyName $oldValue $newValue
                    $handlersNotified++
                } catch {
                    $handlersFailed++
                    $this._logger.Error("ObservableObject", "OnPropertyChanged", "PropertyChanged handler execution failed", @{
                        Property = $propertyName
                        Exception = $_.Exception.Message
                        StackTrace = $_.ScriptStackTrace
                    })
                }
            }

            $this._logger.Debug("ObservableObject", "OnPropertyChanged", "Property change notification completed", @{
                PropertyName = $propertyName
                WatchersNotified = $watchersNotified
                WatchersFailed = $watchersFailed
                HandlersNotified = $handlersNotified
                HandlersFailed = $handlersFailed
            })
        } catch {
            $this._logger.Error("ObservableObject", "OnPropertyChanged", "Property change notification failed", @{
                PropertyName = $propertyName
                Exception = $_.Exception.Message
            })
            throw
        }
    }

    # Get all property names
    [string[]] GetPropertyNames() {
        $this._logger.Trace("ObservableObject", "GetPropertyNames", "Getting all property names", @{
            PropertyCount = $this._properties.Count
        })

        try {
            $keys = $this._properties.Keys

            $this._logger.Trace("ObservableObject", "GetPropertyNames", "Property names retrieved", @{
                PropertyCount = $keys.Count
                PropertyNames = ($keys -join ", ")
            })

            return $keys
        } catch {
            $this._logger.Error("ObservableObject", "GetPropertyNames", "Failed to get property names", @{
                Exception = $_.Exception.Message
            })
            throw
        }
    }

    # Check if property exists
    [bool] HasProperty([string]$propertyName) {
        $this._logger.Trace("ObservableObject", "HasProperty", "Checking if property exists", @{
            PropertyName = $propertyName
        })

        try {
            $exists = $this._properties.ContainsKey($propertyName)

            $this._logger.Trace("ObservableObject", "HasProperty", "Property existence check completed", @{
                PropertyName = $propertyName
                Exists = $exists
            })

            return $exists
        } catch {
            $this._logger.Error("ObservableObject", "HasProperty", "Failed to check property existence", @{
                PropertyName = $propertyName
                Exception = $_.Exception.Message
            })
            throw
        }
    }
}

class DataStore : ObservableObject {
    hidden [Dictionary[string, ObservableObject]]$_collections
    hidden [List[scriptblock]]$_globalWatchers
    hidden [string]$_name

    DataStore([string]$name) {
        $this._logger = Get-Logger

        $this._logger.Trace("DataStore", "Constructor", "Creating data store", @{
            Name = $name
        })

        try {
            [Guard]::NotNullOrEmpty($name, "name")

            $this._name = $name
            $this._collections = [Dictionary[string, ObservableObject]]::new()
            $this._globalWatchers = [List[scriptblock]]::new()

            $this._logger.Info("DataStore", "Constructor", "DataStore created successfully", @{
                Name = $name
            })
        } catch {
            $this._logger.Error("DataStore", "Constructor", "Failed to create data store", @{
                Name = $name
                Exception = $_.Exception.Message
                StackTrace = $_.ScriptStackTrace
            })
            throw
        }
    }

    # Create or get a collection
    [ObservableObject] Collection([string]$name) {
        $this._logger.Trace("DataStore", "Collection", "Getting or creating collection", @{
            Store = $this._name
            CollectionName = $name
            ExistingCollections = $this._collections.Count
        })

        try {
            [Guard]::NotNullOrEmpty($name, "name")

            if (-not $this._collections.ContainsKey($name)) {
                $this._logger.Debug("DataStore", "Collection", "Creating new collection", @{
                    Store = $this._name
                    CollectionName = $name
                })

                $collection = [ObservableObject]::new()
                $this._collections[$name] = $collection

                # Set up collection watcher to propagate changes
                $store = $this
                $collection.Watch("*", {
                    param($prop, $old, $new)
                    $store.NotifyGlobalWatchers("$name.$prop", $old, $new)
                })

                $this._logger.Debug("DataStore", "Collection", "Collection created successfully", @{
                    Store = $this._name
                    Collection = $name
                    TotalCollections = $this._collections.Count
                })
            } else {
                $this._logger.Trace("DataStore", "Collection", "Returning existing collection", @{
                    Store = $this._name
                    CollectionName = $name
                })
            }

            return $this._collections[$name]
        } catch {
            $this._logger.Error("DataStore", "Collection", "Failed to get or create collection", @{
                Store = $this._name
                CollectionName = $name
                Exception = $_.Exception.Message
                StackTrace = $_.ScriptStackTrace
            })
            throw
        }
    }

    # Get value using path notation (e.g., "users.currentUser.name")
    [object] GetPath([string]$path) {
        $this._logger.Trace("DataStore", "GetPath", "Getting value by path", @{
            Store = $this._name
            Path = $path
        })

        try {
            [Guard]::NotNullOrEmpty($path, "path")

            $parts = $path.Split('.')

            if ($parts.Count -eq 0) { 
                $this._logger.Warn("DataStore", "GetPath", "Empty path parts")
                return $null 
            }

            # Get collection
            $collection = $this.Collection($parts[0])

            if ($parts.Count -eq 1) {
                $this._logger.Trace("DataStore", "GetPath", "Returning collection directly")
                return $collection
            }

            # Navigate path
            $current = $collection
            for ($i = 1; $i -lt $parts.Count; $i++) {
                $propName = $parts[$i]
                $value = $current.Get($propName)

                if ($i -eq $parts.Count - 1) {
                    $this._logger.Debug("DataStore", "GetPath", "Path value retrieved", @{
                        Path = $path
                        HasValue = $null -ne $value
                        ValueType = $(if ($null -ne $value) { $value.GetType().Name } else { "null" })
                    })
                    return $value
                }

                if ($value -is [ObservableObject]) {
                    $current = $value
                } else {
                    $this._logger.Debug("DataStore", "GetPath", "Path navigation stopped - non-observable object", @{
                        Path = $path
                        StoppedAt = $propName
                    })
                    return $null
                }
            }

            return $null
        } catch {
            $this._logger.Error("DataStore", "GetPath", "Failed to get path value", @{
                Store = $this._name
                Path = $path
                Exception = $_.Exception.Message
            })
            throw
        }
    }

    # Set value using path notation
    [void] SetPath([string]$path, [object]$value) {
        [Guard]::NotNullOrEmpty($path, "path")

        $parts = $path.Split('.')

        if ($parts.Count -eq 0) { return }

        # Get collection
        $collection = $this.Collection($parts[0])

        if ($parts.Count -eq 1) {
            throw [ArgumentException]::new("Cannot set collection directly")
        }

        # Navigate to parent
        $current = $collection
        for ($i = 1; $i -lt $parts.Count - 1; $i++) {
            $propName = $parts[$i]
            $obj = $current.Get($propName)

            if ($null -eq $obj) {
                # Create intermediate objects
                $obj = [ObservableObject]::new()
                $current.Set($propName, $obj)
            }

            if ($obj -is [ObservableObject]) {
                $current = $obj
            } else {
                throw [InvalidOperationException]::new("Cannot navigate through non-observable object at: $propName")
            }
        }

        # Set final property
        $finalProp = $parts[$parts.Count - 1]
        $current.Set($finalProp, $value)

        $this._logger.Debug("DataStore", "SetPath", "Value set via path", @{
            Store = $this._name
            Path = $path
            Value = $value
        })
    }

    # Watch for any changes in the store
    [void] WatchAll([scriptblock]$handler) {
        [Guard]::NotNull($handler, "handler")

        $this._globalWatchers.Add($handler)

        $this._logger.Debug("DataStore", "WatchAll", "Global watcher added", @{
            Store = $this._name
            WatcherCount = $this._globalWatchers.Count
        })
    }

    # Watch specific path
    [void] WatchPath([string]$path, [scriptblock]$handler) {
        [Guard]::NotNullOrEmpty($path, "path")
        [Guard]::NotNull($handler, "handler")

        $this.WatchAll({
            param($changedPath, $old, $new)
            if ($changedPath -eq $path -or $changedPath.StartsWith("$path.")) {
                & $handler $changedPath $old $new
            }
        })
    }

    # Notify global watchers
    hidden [void] NotifyGlobalWatchers([string]$path, [object]$oldValue, [object]$newValue) {
        foreach ($watcher in $this._globalWatchers) {
            try {
                & $watcher $path $oldValue $newValue
            } catch {
                $this._logger.Error("DataStore", "NotifyGlobalWatchers", "Global watcher failed", @{
                    Store = $this._name
                    Path = $path
                    Error = $_.Exception.Message
                })
            }
        }
    }

    # Create snapshot of store data
    [hashtable] CreateSnapshot() {
        $snapshot = @{}

        foreach ($kvp in $this._collections.GetEnumerator()) {
            $collectionData = @{}
            $collection = $kvp.Value

            foreach ($prop in $collection.GetPropertyNames()) {
                $collectionData[$prop] = $collection.Get($prop)
            }

            $snapshot[$kvp.Key] = $collectionData
        }

        return $snapshot
    }

    # Restore from snapshot
    [void] RestoreSnapshot([hashtable]$snapshot) {
        [Guard]::NotNull($snapshot, "snapshot")

        $this._logger.Info("DataStore", "RestoreSnapshot", "Restoring from snapshot", @{
            Store = $this._name
            Collections = $snapshot.Keys -join ", "
        })

        foreach ($kvp in $snapshot.GetEnumerator()) {
            $collection = $this.Collection($kvp.Key)
            $data = $kvp.Value

            if ($data -is [hashtable]) {
                foreach ($prop in $data.GetEnumerator()) {
                    $collection.Set($prop.Key, $prop.Value)
                }
            }
        }
    }
}

# Global data store manager
class DataStoreManager {
    static [Dictionary[string, DataStore]]$_stores = [Dictionary[string, DataStore]]::new()
    static [object]$_logger = [object]::GetInstance()

    static [DataStore] GetStore([string]$name) {
        [DataStoreManager]::_logger.Trace("DataStoreManager", "GetStore", "Getting data store", @{
            Name = $name
            ExistingStores = [DataStoreManager]::_stores.Count
        })

        try {
            [Guard]::NotNullOrEmpty($name, "name")

            if (-not [DataStoreManager]::_stores.ContainsKey($name)) {
                [DataStoreManager]::_logger.Debug("DataStoreManager", "GetStore", "Creating new store", @{
                    StoreName = $name
                })

                $store = [DataStore]::new($name)
                [DataStoreManager]::_stores[$name] = $store

                [DataStoreManager]::_logger.Info("DataStoreManager", "GetStore", "Store created successfully", @{
                    StoreName = $name
                    TotalStores = [DataStoreManager]::_stores.Count
                })
            } else {
                [DataStoreManager]::_logger.Trace("DataStoreManager", "GetStore", "Returning existing store")
            }

            return [DataStoreManager]::_stores[$name]
        } catch {
            [DataStoreManager]::_logger.Error("DataStoreManager", "GetStore", "Failed to get store", @{
                Name = $name
                Exception = $_.Exception.Message
            })
            throw
        }
    }

    static [void] RemoveStore([string]$name) {
        [Guard]::NotNullOrEmpty($name, "name")

        if ([DataStoreManager]::_stores.Remove($name)) {
            [DataStoreManager]::_logger.Info("DataStoreManager", "RemoveStore", "Store removed", @{
                StoreName = $name
            })
        }
    }

    static [string[]] GetStoreNames() {
        return [DataStoreManager]::_stores.Keys
    }
}

﻿# SpeedTUI Enhanced Application - Flicker-free screen-based application with RenderEngine integration
# Implements EVOLUTION.md vision while maintaining compatibility with existing screen architecture

using namespace System.Collections.Generic

# Load dependencies
. "$PSScriptRoot/OptimizedRenderEngine.ps1"  # Use optimized engine for flicker-free rendering!
. "$PSScriptRoot/SimplifiedTerminal.ps1"  # Use simplified terminal for speed!
. "$PSScriptRoot/Logger.ps1"

<#
.SYNOPSIS
Enhanced SpeedTUI Application for screen-based applications with integrated RenderEngine

.DESCRIPTION
This class bridges the gap between the existing screen-based architecture and the new
RenderEngine system. It eliminates flickering by:
- Using RenderEngine for all screen output instead of Clear-Host + Write-Host
- Implementing true differential rendering
- Maintaining backward compatibility with existing screens
- Following EVOLUTION.md vision of simple APIs with hidden performance optimizations

.EXAMPLE
# Replace the main loop in SpeedTUI.ps1 with:
$app = [EnhancedApplication]::new()
$app.Run([DashboardScreen]::new())
#>
class EnhancedApplication {
    # Core systems (hidden performance layer)
    hidden [OptimizedRenderEngine]$_renderEngine
    hidden [SimplifiedTerminal]$_terminal  
    hidden [object]$_logger
    hidden [object]$_performanceMonitor

    # Application state 
    hidden [object]$_currentScreen
    hidden [bool]$_running = $false
    hidden [bool]$_initialized = $false
    hidden [bool]$_needsRefresh = $false

    # Performance tracking (transparent to developer)
    hidden [int]$_frameCount = 0
    hidden [double]$_totalFrameTime = 0
    hidden [DateTime]$_startTime = [DateTime]::MinValue

    EnhancedApplication() {
        $this._logger = Get-Logger
        $this._logger.Info("SpeedTUI", "EnhancedApplication", "Creating enhanced application with RenderEngine integration")

        try {
            # Initialize core systems
            $this._terminal = [SimplifiedTerminal]::GetInstance()
            $this._renderEngine = [OptimizedRenderEngine]::new()

            # Get performance monitor if available
            if (Get-Command "Get-PerformanceMonitor" -ErrorAction SilentlyContinue) {
                $this._performanceMonitor = Get-PerformanceMonitor
            }

            $this._logger.Info("SpeedTUI", "EnhancedApplication", "Enhanced application created successfully")
        } catch {
            $this._logger.Error("SpeedTUI", "EnhancedApplication", "Failed to create application", @{
                Exception = $_.Exception.Message
                StackTrace = $_.ScriptStackTrace
            })
            throw
        }
    }

    <#
    .SYNOPSIS
    Initialize application systems
    #>
    [void] Initialize() {
        if ($this._initialized) { return }

        $this._logger.Debug("SpeedTUI", "EnhancedApplication", "Initializing application systems")

        try {
            # Initialize terminal and render engine
            $this._terminal.Initialize()
            $this._renderEngine.Initialize()

            # CLEAR SCREEN PROPERLY - like @praxis VT100 engine
            # Send clear screen + home position to eliminate startup messages
            Write-Host "`e[2J`e[H" -NoNewline
            [Console]::SetCursorPosition(0, 0)

            # Update render engine dimensions
            $this._renderEngine.UpdateDimensions()

            $this._initialized = $true
            $this._startTime = [DateTime]::Now

            $this._logger.Info("SpeedTUI", "EnhancedApplication", "Application initialized", @{
                TerminalSize = "$($this._terminal.Width)x$($this._terminal.Height)"
            })
        } catch {
            $this._logger.Error("SpeedTUI", "EnhancedApplication", "Failed to initialize", @{
                Exception = $_.Exception.Message
            })
            throw
        }
    }

    <#
    .SYNOPSIS
    Run application with flicker-free rendering

    .PARAMETER startScreen
    Initial screen to display
    #>
    [void] Run([object]$startScreen) {
        $this._logger.Info("SpeedTUI", "EnhancedApplication", "Starting enhanced application", @{
            StartScreen = $startScreen.GetType().Name
        })

        try {
            $this.Initialize()
            $this._currentScreen = $startScreen
            $this._running = $true
            $refreshNeeded = $true

            # Enhanced main loop with RenderEngine integration
            while ($this._running) {
                $frameStart = [DateTime]::Now

                try {
                    # Render using RenderEngine (eliminates flickering)
                    if ($refreshNeeded) {
                        $this._needsRefresh = $true
                        $this.RenderScreenFlickerFree()
                        $refreshNeeded = $false
                        $this._needsRefresh = $false
                    }

                    # Handle input with proper delegation
                    if ([Console]::KeyAvailable) {
                        $inputResult = $this.HandleInputEnhanced()

                        # Process input results
                        switch ($inputResult) {
                            "EXIT" {
                                $this._running = $false
                                break
                            }
                            "REFRESH" {
                                $refreshNeeded = $true
                                break
                            }
                            "SWITCH_TIMETRACKING" {
                                $this._currentScreen = [TimeTrackingScreen]::new()
                                $refreshNeeded = $true
                                break  
                            }
                            "SWITCH_DASHBOARD" {
                                $this._currentScreen = [DashboardScreen]::new()
                                $refreshNeeded = $true
                                break
                            }
                            "SWITCH_MONITORING" {
                                $this._currentScreen = [MonitoringScreen]::new()
                                $refreshNeeded = $true
                                break
                            }
                            default {
                                # Any other result triggers refresh
                                if ($inputResult) {
                                    $refreshNeeded = $true
                                }
                            }
                        }
                    }

                    # Performance tracking (hidden from developer)
                    $frameTime = ([DateTime]::Now - $frameStart).TotalMilliseconds
                    $this._totalFrameTime += $frameTime
                    $this._frameCount++

                    # Log performance occasionally
                    if ($this._frameCount % 100 -eq 0) {
                        # Performance tracking disabled for speed
                    }

                    # Frame rate limiting (60 FPS)
                    Start-Sleep -Milliseconds 16

                } catch {
                    $this._logger.Error("SpeedTUI", "EnhancedApplication", "Error in main loop", @{
                        Exception = $_.Exception.Message
                        FrameCount = $this._frameCount
                    })

                    # Attempt recovery
                    $refreshNeeded = $true
                    Start-Sleep -Milliseconds 100
                }
            }

        } catch {
            $this._logger.Fatal("SpeedTUI", "EnhancedApplication", "Critical application error", @{
                Exception = $_.Exception.Message
                StackTrace = $_.ScriptStackTrace
            })
            throw
        } finally {
            $this.Cleanup()
        }
    }

    <#
    .SYNOPSIS
    Render current screen using RenderEngine (eliminates flickering)
    #>
    hidden [void] RenderScreenFlickerFree() {
        # Rendering screen flicker-free

        try {
            # Begin frame (prepares for differential rendering)
            $this._renderEngine.BeginFrame()

            # Request clear for screen changes
            if ($this._needsRefresh) {
                $this._renderEngine.RequestClear()
            }

            # Get screen content the traditional way (backward compatibility)
            $screenContent = $this._currentScreen.Render()

            # Render to RenderEngine buffer instead of terminal directly
            if ($screenContent -and $screenContent.Count -gt 0) {
                $lineCount = [Math]::Min($screenContent.Count, $this._terminal.Height)

                for ($i = 0; $i -lt $lineCount; $i++) {
                    if ($screenContent[$i]) {
                        # Write to render buffer using simplified API
                        $this._renderEngine.WriteAt(0, $i, $screenContent[$i])
                    }
                }
            }

            # End frame - this does the differential rendering magic!
            # Only changed characters are written to terminal
            $this._renderEngine.EndFrame()

            # Screen rendered successfully

        } catch {
            $this._logger.Error("SpeedTUI", "EnhancedApplication", "Failed to render screen", @{
                ScreenType = $this._currentScreen.GetType().Name
                Exception = $_.Exception.Message
            })

            # Fallback to traditional rendering if RenderEngine fails
            try {
                Clear-Host
                $screenContent = $this._currentScreen.Render()
                if ($screenContent) {
                    foreach ($line in $screenContent) {
                        Write-Host $line
                    }
                }
            } catch {
                $this._logger.Error("SpeedTUI", "EnhancedApplication", "Fallback rendering also failed", @{
                    Exception = $_.Exception.Message
                })
            }
        }
    }

    <#
    .SYNOPSIS
    Handle input with enhanced delegation and global key handling
    #>
    hidden [string] HandleInputEnhanced() {
        $key = [Console]::ReadKey($true)
        $keyString = $key.Key.ToString()

        # Add modifiers
        if ($key.Modifiers -band [ConsoleModifiers]::Control) {
            $keyString = "Ctrl+$keyString"
        }
        if ($key.Modifiers -band [ConsoleModifiers]::Alt) {
            $keyString = "Alt+$keyString"
        }
        if ($key.Modifiers -band [ConsoleModifiers]::Shift) {
            $keyString = "Shift+$keyString"
        }

        $this._logger.Trace("SpeedTUI", "EnhancedApplication", "Key pressed", @{
            Key = $keyString
            KeyChar = [int]$key.KeyChar
            CurrentScreen = $this._currentScreen.GetType().Name
        })

        # Global application keys (simple API)
        switch ($keyString) {
            "Escape" { 
                return "EXIT" 
            }
            "F5" { 
                return "REFRESH" 
            }
            "Ctrl+Q" { 
                return "EXIT" 
            }
        }

        # Delegate to current screen with proper error handling
        try {
            # Maintain backward compatibility with existing screens
            if ($this._currentScreen -is [DashboardScreen]) {
                $result = $this._currentScreen.HandleInput($keyString)

                # Map DashboardScreen results to application actions
                switch ($result) {
                    "EXIT" { return "EXIT" }
                    "REFRESH" { return "REFRESH" }
                    "TIMETRACKING" { return "SWITCH_TIMETRACKING" }
                    "MONITORING" { return "SWITCH_MONITORING" }
                    default { return $result }
                }
            }
            elseif ($this._currentScreen -is [TimeTrackingScreen]) {
                $result = $this._currentScreen.HandleInput($keyString)

                # Map TimeTrackingScreen results
                switch ($result) {
                    "EXIT" { return "EXIT" }
                    "BACK" { return "SWITCH_DASHBOARD" }
                    "REFRESH" { return "REFRESH" }
                    default { return $result }
                }
            }
            elseif ($this._currentScreen -is [MonitoringScreen]) {
                $result = $this._currentScreen.HandleInput($keyString)

                switch ($result) {
                    "EXIT" { return "EXIT" }
                    "BACK" { return "SWITCH_DASHBOARD" }
                    "REFRESH" { return "REFRESH" }
                    default { return $result }
                }
            }
            else {
                # Generic screen handling
                if ($this._currentScreen.PSObject.Methods["HandleInput"]) {
                    return $this._currentScreen.HandleInput($keyString)
                }
            }

        } catch {
            $this._logger.Error("SpeedTUI", "EnhancedApplication", "Error handling input", @{
                Key = $keyString
                ScreenType = $this._currentScreen.GetType().Name
                Exception = $_.Exception.Message
            })
            return "REFRESH"  # Refresh on error
        }

        return $null
    }

    <#
    .SYNOPSIS
    Clean up application resources
    #>
    [void] Cleanup() {
        $this._logger.Info("SpeedTUI", "EnhancedApplication", "Cleaning up enhanced application")

        try {
            # Cleanup render engine
            if ($this._renderEngine) {
                $this._renderEngine.Cleanup()
            }

            # Log final performance stats (transparent to developer)
            if ($this._frameCount -gt 0) {
                $totalUptime = [DateTime]::Now - $this._startTime
                $avgFrameTime = $this._totalFrameTime / $this._frameCount

                $this._logger.Info("SpeedTUI", "EnhancedApplication", "Final performance statistics", @{
                    TotalFrames = $this._frameCount
                    TotalUptime = $totalUptime.ToString("hh\:mm\:ss")
                    AverageFrameTime = [Math]::Round($avgFrameTime, 2)
                    AverageFPS = [Math]::Round(1000.0 / $avgFrameTime, 1)
                    RenderEngineUsed = $true
                })
            }

            $this._logger.Info("SpeedTUI", "EnhancedApplication", "Enhanced application cleanup completed")
        } catch {
            $this._logger.Error("SpeedTUI", "EnhancedApplication", "Error during cleanup", @{
                Exception = $_.Exception.Message
            })
        }
    }

    # === Simple API Methods (EVOLUTION.md: Simple by default) ===

    <#
    .SYNOPSIS
    Get current screen (simple API)
    #>
    [object] GetCurrentScreen() {
        return $this._currentScreen
    }

    <#
    .SYNOPSIS
    Switch to a different screen (simple API)
    #>
    [void] SwitchToScreen([string]$screenType) {
        switch ($screenType) {
            "Dashboard" { $this._currentScreen = [DashboardScreen]::new() }
            "TimeTracking" { $this._currentScreen = [TimeTrackingScreen]::new() }  
            "Monitoring" { $this._currentScreen = [MonitoringScreen]::new() }
            default { 
                $this._logger.Warn("SpeedTUI", "EnhancedApplication", "Unknown screen type", @{
                    ScreenType = $screenType
                })
            }
        }
    }

    <#
    .SYNOPSIS
    Stop application (simple API)
    #>
    [void] Stop() {
        $this._running = $false
        $this._logger.Info("SpeedTUI", "EnhancedApplication", "Application stop requested")
    }

    # === Advanced API Methods (EVOLUTION.md: Progressive disclosure) ===

    <#
    .SYNOPSIS
    Get performance statistics (advanced feature)
    #>
    [hashtable] GetPerformanceStats() {
        $uptime = $(if ($this._startTime -ne [DateTime]::MinValue) {
            [DateTime]::Now - $this._startTime
        } else {
            [TimeSpan]::Zero
        })

        return @{
            FrameCount = $this._frameCount
            TotalFrameTime = $this._totalFrameTime
            AverageFrameTime = $(if ($this._frameCount -gt 0) { $this._totalFrameTime / $this._frameCount } else { 0 })
            AverageFPS = $(if ($this._frameCount -gt 0) { 1000.0 / ($this._totalFrameTime / $this._frameCount) } else { 0 })
            Uptime = $uptime.ToString("hh\:mm\:ss")
            RenderEngineIntegrated = $true
            FlickerFreeRendering = $true
        }
    }

    <#
    .SYNOPSIS
    Get render engine for power users (advanced API)
    #>
    [OptimizedRenderEngine] GetRenderEngine() {
        return $this._renderEngine
    }

    <#
    .SYNOPSIS
    Enable detailed logging (developer helper)
    #>
    [void] EnableDebugMode() {
        if ($this._logger) {
            $this._logger.GlobalLevel = [LogLevel]::Trace
            $this._logger.Info("SpeedTUI", "EnhancedApplication", "Debug mode enabled - detailed logging active")
        }
    }

    <#
    .SYNOPSIS
    Show performance overlay (debugging helper)
    #>
    [void] ShowPerformanceOverlay([bool]$show = $true) {
        # This would show FPS and performance info on screen
        # Implementation would add a debug region to the render engine
        $this._logger.Debug("SpeedTUI", "EnhancedApplication", "Performance overlay", @{
            Enabled = $show
        })
    }
}

﻿# SpeedTUI Enhanced Terminal - Optimized terminal control with transparent performance improvements
# This enhances the existing Terminal class with performance optimizations while maintaining the same API

# Load the performance core
. "$PSScriptRoot/Internal/PerformanceCore.ps1"

<#
.SYNOPSIS
Enhanced Terminal class with transparent performance optimizations

.DESCRIPTION
This class extends the existing Terminal functionality with performance optimizations
from the internal performance core. The API remains exactly the same for developers,
but performance is significantly improved through caching and pooling.

.EXAMPLE
# Developer code remains exactly the same:
$terminal = [EnhancedTerminal]::GetInstance()
$terminal.Initialize()
$terminal.WriteAt(10, 5, "Hello World")
$terminal.EndFrame()
#>
class EnhancedTerminal : Terminal {
    # Additional performance tracking
    hidden [System.Collections.Generic.Dictionary[string, string]]$_sequenceCache
    hidden [string]$_previousBuffer = ""
    hidden [bool]$_enableDifferentialRendering = $true

    # Constructor
    EnhancedTerminal() : base() {
        $this._sequenceCache = [System.Collections.Generic.Dictionary[string, string]]::new()
        $this._logger.Info("EnhancedTerminal", "Constructor", "Enhanced Terminal initialized with performance optimizations")
    }

    # Enhanced cursor movement with caching
    [void] MoveCursor([int]$x, [int]$y) {
        [Guard]::NonNegative($x, "x")
        [Guard]::NonNegative($y, "y")
        [Guard]::InRange($x, 0, $this.Width - 1, "x")
        [Guard]::InRange($y, 0, $this.Height - 1, "y")

        # Use optimized VT100 movement
        $moveSequence = [InternalVT100]::MoveTo($x, $y)
        $this._renderBatch.Append($moveSequence)
    }

    # Enhanced WriteAt with performance optimizations
    [void] WriteAt([int]$x, [int]$y, [string]$text) {
        [Guard]::NotNull($text, "text")

        if ($x -lt 0 -or $y -lt 0 -or $y -ge $this.Height) { return }

        # Clip text to terminal width
        $maxLength = $this.Width - $x
        if ($maxLength -le 0) { return }

        if ($text.Length -gt $maxLength) {
            $text = $text.Substring(0, $maxLength)
        }

        $this.MoveCursor($x, $y)
        $this._renderBatch.Append($text)
    }

    # Enhanced WriteAt with colors using optimized sequences
    [void] WriteAt([int]$x, [int]$y, [string]$text, [string]$foreground, [string]$background) {
        [Guard]::NotNull($text, "text")

        if ($x -lt 0 -or $y -lt 0 -or $y -ge $this.Height) { return }

        # Clip text to terminal width
        $maxLength = $this.Width - $x
        if ($maxLength -le 0) { return }

        if ($text.Length -gt $maxLength) {
            $text = $text.Substring(0, $maxLength)
        }

        $this.MoveCursor($x, $y)

        # Apply colors if provided
        if ($foreground) {
            $this._renderBatch.Append($foreground)
        }
        if ($background) {
            $this._renderBatch.Append($background)
        }

        $this._renderBatch.Append($text)

        # Reset colors using cached sequence
        if ($foreground -or $background) {
            $this._renderBatch.Append([InternalStringCache]::GetAnsiSequence("reset"))
        }
    }

    # Enhanced RGB color support
    [void] WriteAtRGB([int]$x, [int]$y, [string]$text, [int]$fgR, [int]$fgG, [int]$fgB) {
        $fgColor = [InternalVT100]::RGB($fgR, $fgG, $fgB)
        $this.WriteAt($x, $y, $text, $fgColor, "")
    }

    # Enhanced RGB color with background support
    [void] WriteAtRGB([int]$x, [int]$y, [string]$text, [int]$fgR, [int]$fgG, [int]$fgB, [int]$bgR, [int]$bgG, [int]$bgB) {
        $fgColor = [InternalVT100]::RGB($fgR, $fgG, $fgB)
        $bgColor = [InternalVT100]::RGBBackground($bgR, $bgG, $bgB)
        $this.WriteAt($x, $y, $text, $fgColor, $bgColor)
    }

    # Enhanced clear operations using optimized sequences
    [void] Clear() {
        $this._renderBatch.Append([InternalStringCache]::GetAnsiSequence("clear"))
        $this._renderBatch.Append([InternalStringCache]::GetAnsiSequence("home"))
    }

    [void] ClearLine([int]$y) {
        [Guard]::InRange($y, 0, $this.Height - 1, "y")
        $this.MoveCursor(0, $y)
        $this._renderBatch.Append([InternalStringCache]::GetAnsiSequence("clearline"))
    }

    # Enhanced region clearing with optimized spaces
    [void] ClearRegion([int]$x, [int]$y, [int]$width, [int]$height) {
        [Guard]::NonNegative($x, "x")
        [Guard]::NonNegative($y, "y")
        [Guard]::Positive($width, "width")
        [Guard]::Positive($height, "height")

        # Use optimized spaces
        $spaces = [InternalStringCache]::GetSpaces($width)
        for ($row = 0; $row -lt $height; $row++) {
            $currentY = $y + $row
            if ($currentY -ge $this.Height) { break }
            $this.WriteAt($x, $currentY, $spaces)
        }
    }

    # Enhanced box drawing with cached characters
    [void] DrawBox([int]$x, [int]$y, [int]$width, [int]$height, [string]$style) {
        [Guard]::NonNegative($x, "x")
        [Guard]::NonNegative($y, "y")
        [Guard]::InRange($width, 2, $this.Width - $x, "width")
        [Guard]::InRange($height, 2, $this.Height - $y, "height")

        # Use cached box drawing characters
        $chars = switch ($style) {
            "Double" { @{
TL = $(if ($null -ne ([InternalStringCache]::GetBoxDrawing("doubletopleft"))) { [InternalStringCache]::GetBoxDrawing("doubletopleft") } else { "╔" })
TR = $(if ($null -ne ([InternalStringCache]::GetBoxDrawing("doubletopright"))) { [InternalStringCache]::GetBoxDrawing("doubletopright") } else { "╗" })
BL = $(if ($null -ne ([InternalStringCache]::GetBoxDrawing("doublebottomleft"))) { [InternalStringCache]::GetBoxDrawing("doublebottomleft") } else { "╚" })
BR = $(if ($null -ne ([InternalStringCache]::GetBoxDrawing("doublebottomright"))) { [InternalStringCache]::GetBoxDrawing("doublebottomright") } else { "╝" })
H = $(if ($null -ne ([InternalStringCache]::GetBoxDrawing("doublehorizontal"))) { [InternalStringCache]::GetBoxDrawing("doublehorizontal") } else { "═" })
V = $(if ($null -ne ([InternalStringCache]::GetBoxDrawing("doublevertical"))) { [InternalStringCache]::GetBoxDrawing("doublevertical") } else { "║" })
            }}
            "Rounded" { @{
TL = $(if ($null -ne ([InternalStringCache]::GetBoxDrawing("roundedtopleft"))) { [InternalStringCache]::GetBoxDrawing("roundedtopleft") } else { "╭" })
TR = $(if ($null -ne ([InternalStringCache]::GetBoxDrawing("roundedtopright"))) { [InternalStringCache]::GetBoxDrawing("roundedtopright") } else { "╮" })
BL = $(if ($null -ne ([InternalStringCache]::GetBoxDrawing("roundedbottomleft"))) { [InternalStringCache]::GetBoxDrawing("roundedbottomleft") } else { "╰" })
BR = $(if ($null -ne ([InternalStringCache]::GetBoxDrawing("roundedbottomright"))) { [InternalStringCache]::GetBoxDrawing("roundedbottomright") } else { "╯" })
                H = [InternalStringCache]::GetBoxDrawing("horizontal")
                V = [InternalStringCache]::GetBoxDrawing("vertical")
            }}
            default { @{
                TL = [InternalStringCache]::GetBoxDrawing("topleft")
                TR = [InternalStringCache]::GetBoxDrawing("topright")
                BL = [InternalStringCache]::GetBoxDrawing("bottomleft")
                BR = [InternalStringCache]::GetBoxDrawing("bottomright")
                H = [InternalStringCache]::GetBoxDrawing("horizontal")
                V = [InternalStringCache]::GetBoxDrawing("vertical")
            }}
        }

        # Use optimized horizontal line generation
        $horizontalLine = $(if ($chars.H -eq "─") {
            [InternalStringCache]::GetSpaces($width - 2) -replace ' ', '─'
        } else {
            $chars.H * ($width - 2)
        })

        # Top border
        $this.WriteAt($x, $y, $chars.TL)
        if ($width -gt 2) {
            $this.WriteAt($x + 1, $y, $horizontalLine)
        }
        $this.WriteAt($x + $width - 1, $y, $chars.TR)

        # Side borders
        for ($row = 1; $row -lt $height - 1; $row++) {
            $this.WriteAt($x, $y + $row, $chars.V)
            $this.WriteAt($x + $width - 1, $y + $row, $chars.V)
        }

        # Bottom border
        if ($height -gt 1) {
            $this.WriteAt($x, $y + $height - 1, $chars.BL)
            if ($width -gt 2) {
                $this.WriteAt($x + 1, $y + $height - 1, $horizontalLine)
            }
            $this.WriteAt($x + $width - 1, $y + $height - 1, $chars.BR)
        }
    }

    # Enhanced cursor control
    [void] HideCursor() {
        if ($this.CursorVisible) {
            [Console]::Write([InternalStringCache]::GetAnsiSequence("hidecursor"))
            $this.CursorVisible = $false
        }
    }

    [void] ShowCursor() {
        if (-not $this.CursorVisible) {
            [Console]::Write([InternalStringCache]::GetAnsiSequence("showcursor"))
            $this.CursorVisible = $true
        }
    }

    # Enhanced frame handling with differential rendering
    [void] BeginFrame() {
        $this._frameTimer.Restart()

        # Use pooled StringBuilder for better performance
        if ($this._renderBatch) {
            Return-PooledStringBuilder $this._renderBatch
        }
        $this._renderBatch = Get-PooledStringBuilder 4096

        # Only set cursor position if we need to
        if (-not $this._enableDifferentialRendering) {
            [Console]::SetCursorPosition(0, 0)
        }
    }

    [void] EndFrame() {
        # Get the current frame content
        $currentBuffer = $this._renderBatch.ToString()

        # Only write if content has changed (differential rendering)
        if ($this._enableDifferentialRendering -and $currentBuffer -eq $this._previousBuffer) {
            # No changes, skip the write
        } else {
            # Write batch to terminal
            if ($this._renderBatch.Length -gt 0) {
                try {
                    [Console]::Write($currentBuffer)
                    $this._previousBuffer = $currentBuffer
                } catch {
                    $this._logger.Error("EnhancedTerminal", "EndFrame", "Failed to write to console", @{
                        Error = $_.Exception.Message
                        BatchSize = $this._renderBatch.Length
                    })
                }
            }
        }

        # Return StringBuilder to pool
        Return-PooledStringBuilder $this._renderBatch
        $this._renderBatch = $null

        # Track performance
        $this._frameTimer.Stop()
        $this._lastFrameTime = $this._frameTimer.Elapsed.TotalMilliseconds
        $this._frameCount++

        # Reduced performance logging frequency
        if ($this._frameCount % 300 -eq 0) {  # Every 5 seconds at 60fps
            $perfStats = Get-PerformanceStats
            $this._logger.Debug("EnhancedTerminal", "Performance", "Frame timing and optimization stats", @{
                LastFrameMs = [Math]::Round($this._lastFrameTime, 2)
                FPS = [Math]::Round(1000.0 / $this._lastFrameTime, 1)
                StringBuilderReuseRate = $perfStats.StringBuilderPool.ReuseRate
                ColorCacheSize = $perfStats.ColorCacheSize
            })
        }
    }

    # Enable/disable differential rendering
    [void] SetDifferentialRendering([bool]$enabled) {
        $this._enableDifferentialRendering = $enabled
        if (-not $enabled) {
            $this._previousBuffer = ""
        }
    }

    # Get enhanced performance statistics
    [hashtable] GetPerformanceStats() {
        $baseStats = @{
            FPS = $this.GetFPS()
            FrameCount = $this._frameCount
            LastFrameTime = $this._lastFrameTime
            DifferentialRendering = $this._enableDifferentialRendering
        }

        $perfStats = Get-PerformanceStats
        return $baseStats + $perfStats
    }
}

