﻿# SpeedTUI Input Manager - Centralized input handling with focus management

using namespace System.Collections.Generic
using namespace System.Threading
using namespace System.Collections.Concurrent

class KeyBinding {
    [System.ConsoleKey]$Key
    [System.ConsoleModifiers]$Modifiers
    [string]$Action
    [scriptblock]$Handler
    [string]$Description
    hidden [object]$_logger

    KeyBinding([System.ConsoleKey]$key, [System.ConsoleModifiers]$modifiers, [string]$action, [scriptblock]$handler) {
        $this._logger = Get-Logger
        $this._logger.Trace("KeyBinding", "Constructor", "Creating key binding", @{
            Key = $key
            Modifiers = $modifiers
            Action = $action
        })

        $this.Key = $key
        $this.Modifiers = $modifiers
        $this.Action = $action
        $this.Handler = $handler
        $this.Description = ""
    }

    [bool] Matches([System.ConsoleKeyInfo]$keyInfo) {
        $this._logger.Trace("KeyBinding", "Matches", "Checking key match", @{
            BindingKey = $this.Key
            BindingModifiers = $this.Modifiers
            InputKey = $keyInfo.Key
            InputModifiers = $keyInfo.Modifiers
        })

        $matches = $keyInfo.Key -eq $this.Key -and $keyInfo.Modifiers -eq $this.Modifiers
        $this._logger.Trace("KeyBinding", "Matches", "Match result", @{ Matches = $matches })
        return $matches
    }

    [string] ToString() {
        $modStr = ""
        if ($this.Modifiers -band [System.ConsoleModifiers]::Control) { $modStr += "Ctrl+" }
        if ($this.Modifiers -band [System.ConsoleModifiers]::Alt) { $modStr += "Alt+" }
        if ($this.Modifiers -band [System.ConsoleModifiers]::Shift) { $modStr += "Shift+" }
        return "$modStr$($this.Key)"
    }
}

class FocusManager {
    hidden [List[Component]]$_focusableComponents
    hidden [Component]$_currentFocus
    hidden [object]$_logger

    FocusManager() {
        $this._logger = Get-Logger
        $this._logger.Trace("FocusManager", "Constructor", "Creating focus manager")

        $this._focusableComponents = [List[Component]]::new()
        $this._logger.Debug("FocusManager", "Constructor", "FocusManager created successfully")
    }

    [void] RegisterComponent([Component]$component) {
        $this._logger.Trace("FocusManager", "RegisterComponent", "Registering component", @{
            ComponentId = $(if ($component) { $component.Id } else { "null" })
            CanFocus = $(if ($component) { $component.CanFocus } else { $false })
        })

        try {
            [Guard]::NotNull($component, "component")

            if ($component.CanFocus -and -not $this._focusableComponents.Contains($component)) {
                $this._focusableComponents.Add($component)
                $this._logger.Debug("FocusManager", "RegisterComponent", "Component registered", @{
                    ComponentId = $component.Id
                    TotalFocusableComponents = $this._focusableComponents.Count
                })
            } else {
                $this._logger.Trace("FocusManager", "RegisterComponent", "Component not registered", @{
                    ComponentId = $component.Id
                    Reason = $(if (-not $component.CanFocus) { "CannotFocus" } else { "AlreadyRegistered" })
                })
            }

            # Sort by TabIndex
            $this._focusableComponents.Sort({
                param($a, $b)
                $a.TabIndex.CompareTo($b.TabIndex)
            })

        } catch {
            $this._logger.Error("FocusManager", "RegisterComponent", "Failed to register component", @{
                Exception = $_.Exception.Message
            })
            throw
        }
    }

    [void] UnregisterComponent([Component]$component) {
        [Guard]::NotNull($component, "component")

        if ($this._focusableComponents.Remove($component)) {
            if ($this._currentFocus -eq $component) {
                $this.FocusNext()
            }

            $this._logger.Debug("FocusManager", "UnregisterComponent", "Component unregistered", @{
                ComponentId = $component.Id
            })
        }
    }

    [void] SetFocus([Component]$component) {
        [Guard]::NotNull($component, "component")

        if (-not $component.CanFocus -or -not $component.Visible) {
            $this._logger.Warn("FocusManager", "SetFocus", "Cannot focus component", @{
                ComponentId = $component.Id
                CanFocus = $component.CanFocus
                Visible = $component.Visible
            })
            return
        }

        if ($this._currentFocus -ne $component) {
            # Blur current
            if ($null -ne $this._currentFocus) {
                $this._currentFocus.Blur()
            }

            # Focus new
            $this._currentFocus = $component
            $component.Focus()

            $this._logger.Debug("FocusManager", "SetFocus", "Focus changed", @{
                ComponentId = $component.Id
                ComponentType = $component.GetType().Name
            })
        }
    }

    [void] FocusNext() {
        if ($this._focusableComponents.Count -eq 0) { return }

        $currentIndex = -1
        if ($null -ne $this._currentFocus) {
            $currentIndex = $this._focusableComponents.IndexOf($this._currentFocus)
        }

        $nextIndex = ($currentIndex + 1) % $this._focusableComponents.Count
        $attempts = 0

        # Find next visible component
        while ($attempts -lt $this._focusableComponents.Count) {
            $next = $this._focusableComponents[$nextIndex]
            if ($next.Visible) {
                $this.SetFocus($next)
                break
            }
            $nextIndex = ($nextIndex + 1) % $this._focusableComponents.Count
            $attempts++
        }
    }

    [void] FocusPrevious() {
        if ($this._focusableComponents.Count -eq 0) { return }

        $currentIndex = -1
        if ($null -ne $this._currentFocus) {
            $currentIndex = $this._focusableComponents.IndexOf($this._currentFocus)
        }

        $prevIndex = $(if ($currentIndex -le 0) { 
            $this._focusableComponents.Count - 1 
        } else { 
            $currentIndex - 1 
        })

        $attempts = 0

        # Find previous visible component
        while ($attempts -lt $this._focusableComponents.Count) {
            $prev = $this._focusableComponents[$prevIndex]
            if ($prev.Visible) {
                $this.SetFocus($prev)
                break
            }
            $prevIndex = $(if ($prevIndex -le 0) { 
                $this._focusableComponents.Count - 1 
            } else { 
                $prevIndex - 1 
            })
            $attempts++
        }
    }

    [Component] GetFocusedComponent() {
        return $this._currentFocus
    }

    [void] RefreshFocusableList([Component]$root) {
        [Guard]::NotNull($root, "root")

        $this._focusableComponents.Clear()
        $focusable = $root.GetFocusableComponents()

        foreach ($component in $focusable) {
            $this.RegisterComponent($component)
        }

        $this._logger.Debug("FocusManager", "RefreshFocusableList", "Focusable list refreshed", @{
            TotalComponents = $this._focusableComponents.Count
        })

        # Focus first component if nothing focused
        if ($null -eq $this._currentFocus -and $this._focusableComponents.Count -gt 0) {
            $this.SetFocus($this._focusableComponents[0])
        }
    }
}

class InputManager {
    hidden [Dictionary[string, KeyBinding]]$_globalBindings
    hidden [Dictionary[string, Dictionary[string, KeyBinding]]]$_contextBindings
    hidden [string]$_currentContext = "default"
    hidden [FocusManager]$_focusManager
    hidden [object]$_logger
    hidden [bool]$_running = $false
    hidden [Thread]$_inputThread
    hidden [BlockingCollection[System.ConsoleKeyInfo]]$_keyQueue

    InputManager() {
        $this._globalBindings = [Dictionary[string, KeyBinding]]::new()
        $this._contextBindings = [Dictionary[string, Dictionary[string, KeyBinding]]]::new()
        $this._focusManager = [FocusManager]::new()
        $this._keyQueue = [BlockingCollection[System.ConsoleKeyInfo]]::new()
        $this._logger = Get-Logger

        $this._logger.Info("InputManager", "Constructor", "InputManager created")

        # Register default bindings
        $this.RegisterDefaultBindings()
    }

    [void] RegisterDefaultBindings() {
        # Tab navigation
        $this.RegisterGlobalBinding([System.ConsoleKey]::Tab, 0, "FocusNext", {
            $this._focusManager.FocusNext()
        })

        $this.RegisterGlobalBinding([System.ConsoleKey]::Tab, [System.ConsoleModifiers]::Shift, "FocusPrevious", {
            $this._focusManager.FocusPrevious()
        })

        # Escape
        $this.RegisterGlobalBinding([System.ConsoleKey]::Escape, 0, "Cancel", {})

        $this._logger.Debug("InputManager", "RegisterDefaultBindings", "Default bindings registered")
    }

    [void] RegisterGlobalBinding([System.ConsoleKey]$key, [System.ConsoleModifiers]$modifiers, [string]$action, [scriptblock]$handler) {
        [Guard]::NotNullOrEmpty($action, "action")
        [Guard]::NotNull($handler, "handler")

        $binding = [KeyBinding]::new($key, $modifiers, $action, $handler)
        $bindingKey = "$($key)_$($modifiers)"
        $this._globalBindings[$bindingKey] = $binding

        $this._logger.Debug("InputManager", "RegisterGlobalBinding", "Global binding registered", @{
            Key = $binding.ToString()
            Action = $action
        })
    }

    [void] RegisterContextBinding([string]$context, [System.ConsoleKey]$key, [System.ConsoleModifiers]$modifiers, [string]$action, [scriptblock]$handler) {
        [Guard]::NotNullOrEmpty($context, "context")
        [Guard]::NotNullOrEmpty($action, "action")
        [Guard]::NotNull($handler, "handler")

        if (-not $this._contextBindings.ContainsKey($context)) {
            $this._contextBindings[$context] = [Dictionary[string, KeyBinding]]::new()
        }

        $binding = [KeyBinding]::new($key, $modifiers, $action, $handler)
        $bindingKey = "$($key)_$($modifiers)"
        $this._contextBindings[$context][$bindingKey] = $binding

        $this._logger.Debug("InputManager", "RegisterContextBinding", "Context binding registered", @{
            Context = $context
            Key = $binding.ToString()
            Action = $action
        })
    }

    [void] SetContext([string]$context) {
        [Guard]::NotNullOrEmpty($context, "context")

        if ($this._currentContext -ne $context) {
            $this._logger.Debug("InputManager", "SetContext", "Context changed", @{
                OldContext = $this._currentContext
                NewContext = $context
            })
            $this._currentContext = $context
        }
    }

    [void] Start() {
        if ($this._running) { return }

        $this._running = $true

        # Don't use background thread for now - will check keys synchronously

        $this._logger.Info("InputManager", "Start", "Input manager started")
    }

    [void] Stop() {
        $this._running = $false

        if ($null -ne $this._inputThread) {
            $this._inputThread.Join(1000)
            $this._inputThread = $null
        }

        $this._logger.Info("InputManager", "Stop", "Input manager stopped")
    }

    [bool] ProcessInput([Component]$rootComponent) {
        [Guard]::NotNull($rootComponent, "rootComponent")

        # Check for available keys directly
        if (-not [Console]::KeyAvailable) {
            return $false
        }

        $keyInfo = [Console]::ReadKey($true)

        $this._logger.Trace("InputManager", "ProcessInput", "Key received", @{
            Key = $keyInfo.Key
            Char = $keyInfo.KeyChar
            Modifiers = $keyInfo.Modifiers
        })

        # Let focused component handle first
        $focused = $this._focusManager.GetFocusedComponent()
        if ($null -ne $focused -and $focused.HandleKeyPress($keyInfo)) {
            return $true
        }

        # Check context bindings
        $bindingKey = "$($keyInfo.Key)_$($keyInfo.Modifiers)"

        if ($this._contextBindings.ContainsKey($this._currentContext)) {
            $contextBindings = $this._contextBindings[$this._currentContext]
            if ($contextBindings.ContainsKey($bindingKey)) {
                $binding = $contextBindings[$bindingKey]
                $this._logger.Debug("InputManager", "ProcessInput", "Context binding triggered", @{
                    Context = $this._currentContext
                    Action = $binding.Action
                })
                & $binding.Handler
                return $true
            }
        }

        # Check global bindings
        if ($this._globalBindings.ContainsKey($bindingKey)) {
            $binding = $this._globalBindings[$bindingKey]
            $this._logger.Debug("InputManager", "ProcessInput", "Global binding triggered", @{
                Action = $binding.Action
            })
            & $binding.Handler
            return $true
        }

        # Let root component handle unhandled keys
        return $rootComponent.HandleKeyPress($keyInfo)
    }

    [FocusManager] GetFocusManager() {
        return $this._focusManager
    }

    [List[KeyBinding]] GetActiveBindings() {
        $bindings = [List[KeyBinding]]::new()

        # Add context bindings
        if ($this._contextBindings.ContainsKey($this._currentContext)) {
            foreach ($binding in $this._contextBindings[$this._currentContext].Values) {
                $bindings.Add($binding)
            }
        }

        # Add global bindings
        foreach ($binding in $this._globalBindings.Values) {
            $bindings.Add($binding)
        }

        return $bindings
    }
}

﻿# SpeedTUI Logger - Granular, module-specific logging with extensive null checks
using namespace System.Collections.Concurrent
using namespace System.IO

enum LogLevel {
    Trace = 0
    Debug = 1
    Info = 2
    Warn = 3
    Error = 4
    Fatal = 5
    None = 99
}

class LogEntry {
    [DateTime]$Timestamp
    [LogLevel]$Level
    [string]$Module
    [string]$Component
    [string]$Message
    [hashtable]$Context
    [string]$StackTrace

    [string] ToString() {
        $ctx = $(if ($this.Context -and $this.Context.Count -gt 0) {
                " | " + ($this.Context.GetEnumerator() | ForEach-Object { "$($_.Key)=$($_.Value)" }) -join ", "
            }
            else { "" })

        return "$($this.Timestamp.ToString('yyyy-MM-dd HH:mm:ss.fff')) [$($this.Level)] [$($this.Module)][$($this.Component)] $($this.Message)$ctx"
    }
}

class Logger {
    # Singleton instance
    static [object]$Instance = $null

    # Configuration
    [ConcurrentDictionary[string, LogLevel]]$ModuleLevels
    [ConcurrentDictionary[string, LogLevel]]$ComponentLevels
    [LogLevel]$GlobalLevel = [LogLevel]::Debug
    [bool]$EnableConsole = $false
    [bool]$EnableFile = $true
    [string]$LogDirectory
    [string]$LogFilePath

    # Performance
    hidden [StreamWriter]$_fileWriter
    hidden [ConcurrentQueue[LogEntry]]$_logQueue
    hidden [System.Threading.Timer]$_flushTimer
    hidden [object]$_writeLock = [object]::new()

    # Statistics
    [ConcurrentDictionary[string, int]]$LogCounts
    [ConcurrentDictionary[string, TimeSpan]]$PerformanceMetrics

    hidden Logger() {
        $this.ModuleLevels = [ConcurrentDictionary[string, LogLevel]]::new()
        $this.ComponentLevels = [ConcurrentDictionary[string, LogLevel]]::new()
        $this.LogCounts = [ConcurrentDictionary[string, int]]::new()
        $this.PerformanceMetrics = [ConcurrentDictionary[string, TimeSpan]]::new()
        $this._logQueue = [ConcurrentQueue[LogEntry]]::new()

        # Set up log directory
        $this.LogDirectory = Join-Path (Join-Path $PSScriptRoot "..") "Logs"
        if (-not (Test-Path $this.LogDirectory)) {
            New-Item -ItemType Directory -Path $this.LogDirectory -Force | Out-Null
        }

        # Create log file with timestamp
        $timestamp = [DateTime]::Now.ToString("yyyyMMdd_HHmmss")
        $this.LogFilePath = Join-Path $this.LogDirectory "speedtui_$timestamp.log"

        # Initialize file writer
        $this.InitializeFileWriter()

        # No automatic timer - will flush synchronously for now
    }

    static [object] GetInstance() {
        if ($null -eq [Logger]::Instance) {
            [Logger]::Instance = [Logger]::new()
        }
        return [Logger]::Instance
    }

    hidden [void] InitializeFileWriter() {
        try {
            $this._fileWriter = [StreamWriter]::new($this.LogFilePath, $true)
            $this._fileWriter.AutoFlush = $false
        }
        catch {
            Write-Warning "Failed to initialize log file writer: $_"
            $this.EnableFile = $false
        }
    }

    # Module-level configuration
    [void] SetModuleLevel([string]$module, [LogLevel]$level) {
        if ([string]::IsNullOrWhiteSpace($module)) { return }
        $this.ModuleLevels[$module] = $level
    }

    # Component-level configuration (more granular)
    [void] SetComponentLevel([string]$module, [string]$component, [LogLevel]$level) {
        if ([string]::IsNullOrWhiteSpace($module) -or [string]::IsNullOrWhiteSpace($component)) { return }
        $key = "$module.$component"
        $this.ComponentLevels[$key] = $level
    }

    # Check if logging is enabled for given context
    hidden [bool] ShouldLog([LogLevel]$level, [string]$module, [string]$component) {
        if ($level -lt $this.GlobalLevel) { return $false }

        # Check component-specific level first (most specific)
        if (-not [string]::IsNullOrWhiteSpace($component)) {
            $componentKey = "$module.$component"
            $componentLevel = $null
            if ($this.ComponentLevels.ContainsKey($componentKey)) {
                $componentLevel = $this.ComponentLevels[$componentKey]
                return $level -ge $componentLevel
            }
        }

        # Check module-specific level
        if (-not [string]::IsNullOrWhiteSpace($module)) {
            $moduleLevel = $null
            if ($this.ModuleLevels.ContainsKey($module)) {
                $moduleLevel = $this.ModuleLevels[$module]
                return $level -ge $moduleLevel
            }
        }

        # Fall back to global level
        return $true
    }

    # Core logging method with extensive null checks
    [void] Log([LogLevel]$level, [string]$module, [string]$component, [string]$message, [hashtable]$context) {
        # Null checks
        if ($null -eq $module) { $module = "Unknown" }
        if ($null -eq $component) { $component = "Unknown" }
        if ($null -eq $message) { $message = "" }
        if ($null -eq $context) { $context = @{} }

        # Check if we should log
        if (-not $this.ShouldLog($level, $module, $component)) { return }

        # Create log entry
        $entry = [LogEntry]@{
            Timestamp  = [DateTime]::Now
            Level      = $level
            Module     = $module
            Component  = $component
            Message    = $message
            Context    = $context
            StackTrace = $(if ($level -ge [LogLevel]::Error) { (Get-PSCallStack | Out-String) } else { "" })
        }

        # Queue for async write
        $this._logQueue.Enqueue($entry)

        # PERFORMANCE FIX: Only flush immediately for ERROR/FATAL, batch everything else
        # This prevents 6000+ disk writes/sec in render loops
        if ($level -ge [LogLevel]::Error) {
            $this.FlushQueue()
        }
        # Flush periodically when queue gets large (100 entries)
        elseif ($this._logQueue.Count -ge 100) {
            $this.FlushQueue()
        }

        # Update statistics
        $statKey = "$module.$component.$level"
        [void]$this.LogCounts.AddOrUpdate($statKey, 1, { param($k, $v) $v + 1 })

        # Console output for errors/warnings in debug mode
        if ($this.EnableConsole -and $level -ge [LogLevel]::Warn) {
            $this.WriteToConsole($entry)
        }
    }

    # Convenience methods for each log level
    [void] Trace([string]$module, [string]$component, [string]$message) {
        $this.Log([LogLevel]::Trace, $module, $component, $message, @{})
    }

    [void] Trace([string]$module, [string]$component, [string]$message, [hashtable]$context) {
        $this.Log([LogLevel]::Trace, $module, $component, $message, $context)
    }

    [void] Debug([string]$module, [string]$component, [string]$message) {
        $this.Log([LogLevel]::Debug, $module, $component, $message, @{})
    }

    [void] Debug([string]$module, [string]$component, [string]$message, [hashtable]$context) {
        $this.Log([LogLevel]::Debug, $module, $component, $message, $context)
    }

    [void] Info([string]$module, [string]$component, [string]$message) {
        $this.Log([LogLevel]::Info, $module, $component, $message, @{})
    }

    [void] Info([string]$module, [string]$component, [string]$message, [hashtable]$context) {
        $this.Log([LogLevel]::Info, $module, $component, $message, $context)
    }

    [void] Warn([string]$module, [string]$component, [string]$message) {
        $this.Log([LogLevel]::Warn, $module, $component, $message, @{})
    }

    [void] Warn([string]$module, [string]$component, [string]$message, [hashtable]$context) {
        $this.Log([LogLevel]::Warn, $module, $component, $message, $context)
    }

    [void] Error([string]$module, [string]$component, [string]$message) {
        $this.Log([LogLevel]::Error, $module, $component, $message, @{})
    }

    [void] Error([string]$module, [string]$component, [string]$message, [hashtable]$context) {
        $this.Log([LogLevel]::Error, $module, $component, $message, $context)
    }

    [void] Fatal([string]$module, [string]$component, [string]$message) {
        $this.Log([LogLevel]::Fatal, $module, $component, $message, @{})
    }

    [void] Fatal([string]$module, [string]$component, [string]$message, [hashtable]$context) {
        $this.Log([LogLevel]::Fatal, $module, $component, $message, $context)
    }

    # Performance tracking
    [IDisposable] MeasurePerformance([string]$module, [string]$operation) {
        return [PerformanceTimer]::new($this, $module, $operation)
    }

    # Flush queue to file
    hidden [void] FlushQueue() {
        if (-not $this.EnableFile -or $null -eq $this._fileWriter) { return }

        $entries = [System.Collections.Generic.List[LogEntry]]::new()
        $entry = $null

        # Dequeue all pending entries
        while ($this._logQueue.TryDequeue([ref]$entry)) {
            $entries.Add($entry)
        }

        if ($entries.Count -eq 0) { return }

        # Write to file
        try {
            [System.Threading.Monitor]::Enter($this._writeLock)
            foreach ($e in $entries) {
                $this._fileWriter.WriteLine($e.ToString())
            }
            $this._fileWriter.Flush()
        }
        catch {
            Write-Warning "Failed to write to log file: $_"
        }
        finally {
            [System.Threading.Monitor]::Exit($this._writeLock)
        }
    }

    # Console output with color coding
    hidden [void] WriteToConsole([LogEntry]$entry) {
        $color = switch ($entry.Level) {
            Trace { "DarkGray" }
            Debug { "Gray" }
            Info { "White" }
            Warn { "Yellow" }
            Error { "Red" }
            Fatal { "DarkRed" }
            default { "White" }
        }

        Write-Host $entry.ToString() -ForegroundColor $color
    }

    # Get statistics
    [hashtable] GetStatistics() {
        return @{
            LogCounts          = $this.LogCounts.ToArray()
            PerformanceMetrics = $this.PerformanceMetrics.ToArray()
            QueueSize          = $this._logQueue.Count
            LogFilePath        = $this.LogFilePath
        }
    }

    # Cleanup
    [void] Dispose() {
        $this.FlushQueue()

        if ($null -ne $this._fileWriter) {
            $this._fileWriter.Close()
            $this._fileWriter.Dispose()
            $this._fileWriter = $null
        }
    }
}

# Performance measurement helper
class PerformanceTimer : System.IDisposable {
    hidden [object]$_logger
    hidden [string]$_module
    hidden [string]$_operation
    hidden [System.Diagnostics.Stopwatch]$_stopwatch

    PerformanceTimer([object]$logger, [string]$module, [string]$operation) {
        $this._logger = $logger
        $this._module = $module
        $this._operation = $operation
        $this._stopwatch = [System.Diagnostics.Stopwatch]::StartNew()

        $logger.Trace($module, "Performance", "Starting operation: $operation")
    }

    [void] Dispose() {
        $this._stopwatch.Stop()
        $elapsed = $this._stopwatch.Elapsed

        $this._logger.Debug($this._module, "Performance", "Completed operation: $($this._operation)", @{
                ElapsedMs = $elapsed.TotalMilliseconds
                Operation = $this._operation
            })

        # Store metric
        $key = "$($this._module).$($this._operation)"
        [void]$this._logger.PerformanceMetrics.AddOrUpdate($key, $elapsed, { 
                param($k, $v) 
                if ($elapsed -gt $v) { $elapsed } else { $v }
            })
    }
}

# Global logger instance helper
function Get-Logger {
    return [Logger]::GetInstance()
}

﻿# SpeedTUI Null Check Utilities - Defensive programming helpers

class Guard {
    static hidden [object]$_logger = $null

    static [object] GetLogger() {
        if ($null -eq [Guard]::_logger) {
            [Guard]::_logger = Get-Logger
        }
        return [Guard]::_logger
    }
    # Throw if null with detailed context
    static [void] NotNull([object]$value, [string]$paramName) {
        $logger = [Guard]::GetLogger()
        $logger.Trace("Guard", "NotNull", "Validating parameter", @{
            ParamName = $paramName
            IsNull = $null -eq $value
        })

        if ($null -eq $value) {
            $caller = (Get-PSCallStack)[1]
            $errorMsg = "Parameter '$paramName' cannot be null. Called from: $($caller.Command) at line $($caller.ScriptLineNumber)"

            $logger.Error("Guard", "NotNull", "Null parameter validation failed", @{
                ParamName = $paramName
                Caller = $caller.Command
                LineNumber = $caller.ScriptLineNumber
            })

            throw [System.ArgumentNullException]::new($paramName, $errorMsg)
        }

        $logger.Trace("Guard", "NotNull", "Parameter validation passed")
    }

    # Throw if null or empty string
    static [void] NotNullOrEmpty([string]$value, [string]$paramName) {
        $logger = [Guard]::GetLogger()
        $logger.Trace("Guard", "NotNullOrEmpty", "Validating string parameter", @{
            ParamName = $paramName
            IsNullOrEmpty = [string]::IsNullOrEmpty($value)
        })

        if ([string]::IsNullOrEmpty($value)) {
            $caller = (Get-PSCallStack)[1]
            $errorMsg = "Parameter '$paramName' cannot be null or empty. Called from: $($caller.Command) at line $($caller.ScriptLineNumber)"

            $logger.Error("Guard", "NotNullOrEmpty", "String validation failed", @{
                ParamName = $paramName
                Caller = $caller.Command
                LineNumber = $caller.ScriptLineNumber
            })

            throw [System.ArgumentException]::new($errorMsg, $paramName)
        }

        $logger.Trace("Guard", "NotNullOrEmpty", "String validation passed")
    }

    # Throw if null or whitespace
    static [void] NotNullOrWhiteSpace([string]$value, [string]$paramName) {
        $logger = [Guard]::GetLogger()
        $logger.Trace("Guard", "NotNullOrWhiteSpace", "Validating whitespace parameter", @{
            ParamName = $paramName
            IsNullOrWhiteSpace = [string]::IsNullOrWhiteSpace($value)
        })

        if ([string]::IsNullOrWhiteSpace($value)) {
            $caller = (Get-PSCallStack)[1]
            $errorMsg = "Parameter '$paramName' cannot be null, empty, or whitespace. Called from: $($caller.Command) at line $($caller.ScriptLineNumber)"

            $logger.Error("Guard", "NotNullOrWhiteSpace", "Whitespace validation failed", @{
                ParamName = $paramName
                Caller = $caller.Command
                LineNumber = $caller.ScriptLineNumber
            })

            throw [System.ArgumentException]::new($errorMsg, $paramName)
        }

        $logger.Trace("Guard", "NotNullOrWhiteSpace", "Whitespace validation passed")
    }

    # Validate array not null or empty
    static [void] NotNullOrEmptyArray([array]$value, [string]$paramName) {
        $logger = [Guard]::GetLogger()
        $isNullOrEmpty = $null -eq $value -or $value.Count -eq 0

        $logger.Trace("Guard", "NotNullOrEmptyArray", "Validating array parameter", @{
            ParamName = $paramName
            IsNull = $null -eq $value
            Count = $(if ($null -ne $value) { $value.Count } else { 0 })
            IsNullOrEmpty = $isNullOrEmpty
        })

        if ($isNullOrEmpty) {
            $caller = (Get-PSCallStack)[1]
            $errorMsg = "Parameter '$paramName' cannot be null or empty array. Called from: $($caller.Command) at line $($caller.ScriptLineNumber)"

            $logger.Error("Guard", "NotNullOrEmptyArray", "Array validation failed", @{
                ParamName = $paramName
                Caller = $caller.Command
                LineNumber = $caller.ScriptLineNumber
            })

            throw [System.ArgumentException]::new($errorMsg, $paramName)
        }

        $logger.Trace("Guard", "NotNullOrEmptyArray", "Array validation passed")
    }

    # Validate numeric range
    static [void] InRange([int]$value, [int]$min, [int]$max, [string]$paramName) {
        $logger = [Guard]::GetLogger()
        $inRange = $value -ge $min -and $value -le $max

        $logger.Trace("Guard", "InRange", "Validating range parameter", @{
            ParamName = $paramName
            Value = $value
            Min = $min
            Max = $max
            InRange = $inRange
        })

        if (-not $inRange) {
            $caller = (Get-PSCallStack)[1]
            $errorMsg = "Parameter '$paramName' must be between $min and $max. Got: $value. Called from: $($caller.Command) at line $($caller.ScriptLineNumber)"

            $logger.Error("Guard", "InRange", "Range validation failed", @{
                ParamName = $paramName
                Value = $value
                Min = $min
                Max = $max
                Caller = $caller.Command
                LineNumber = $caller.ScriptLineNumber
            })

            throw [System.ArgumentOutOfRangeException]::new($paramName, $value, $errorMsg)
        }

        $logger.Trace("Guard", "InRange", "Range validation passed")
    }

    # Validate positive number
    static [void] Positive([int]$value, [string]$paramName) {
        $logger = [Guard]::GetLogger()
        $isPositive = $value -gt 0

        $logger.Trace("Guard", "Positive", "Validating positive parameter", @{
            ParamName = $paramName
            Value = $value
            IsPositive = $isPositive
        })

        if (-not $isPositive) {
            $caller = (Get-PSCallStack)[1]
            $errorMsg = "Parameter '$paramName' must be positive. Got: $value. Called from: $($caller.Command) at line $($caller.ScriptLineNumber)"

            $logger.Error("Guard", "Positive", "Positive validation failed", @{
                ParamName = $paramName
                Value = $value
                Caller = $caller.Command
                LineNumber = $caller.ScriptLineNumber
            })

            throw [System.ArgumentOutOfRangeException]::new($paramName, $value, $errorMsg)
        }

        $logger.Trace("Guard", "Positive", "Positive validation passed")
    }

    # Validate non-negative number
    static [void] NonNegative([int]$value, [string]$paramName) {
        $logger = [Guard]::GetLogger()
        $isNonNegative = $value -ge 0

        $logger.Trace("Guard", "NonNegative", "Validating non-negative parameter", @{
            ParamName = $paramName
            Value = $value
            IsNonNegative = $isNonNegative
        })

        if (-not $isNonNegative) {
            $caller = (Get-PSCallStack)[1]
            $errorMsg = "Parameter '$paramName' must be non-negative. Got: $value. Called from: $($caller.Command) at line $($caller.ScriptLineNumber)"

            $logger.Error("Guard", "NonNegative", "Non-negative validation failed", @{
                ParamName = $paramName
                Value = $value
                Caller = $caller.Command
                LineNumber = $caller.ScriptLineNumber
            })

            throw [System.ArgumentOutOfRangeException]::new($paramName, $value, $errorMsg)
        }

        $logger.Trace("Guard", "NonNegative", "Non-negative validation passed")
    }

    # Type validation
    static [void] IsType([object]$value, [Type]$expectedType, [string]$paramName) {
        $logger = [Guard]::GetLogger()

        $logger.Trace("Guard", "IsType", "Validating type parameter", @{
            ParamName = $paramName
            ExpectedType = $expectedType.Name
            ActualType = $(if ($null -ne $value) { $value.GetType().Name } else { "null" })
        })

        try {
            if ($null -eq $value) {
                [Guard]::NotNull($value, $paramName)
            }

            $actualType = $value.GetType()
            $isCorrectType = $actualType -eq $expectedType -or $actualType.IsSubclassOf($expectedType)

            if (-not $isCorrectType) {
                $caller = (Get-PSCallStack)[1]
                $errorMsg = "Parameter '$paramName' must be of type '$($expectedType.Name)'. Got: '$($actualType.Name)'. Called from: $($caller.Command) at line $($caller.ScriptLineNumber)"

                $logger.Error("Guard", "IsType", "Type validation failed", @{
                    ParamName = $paramName
                    ExpectedType = $expectedType.Name
                    ActualType = $actualType.Name
                    Caller = $caller.Command
                    LineNumber = $caller.ScriptLineNumber
                })

                throw [System.ArgumentException]::new($errorMsg, $paramName)
            }

            $logger.Trace("Guard", "IsType", "Type validation passed")
        } catch {
            $logger.Error("Guard", "IsType", "Type validation error", @{
                ParamName = $paramName
                Exception = $_.Exception.Message
            })
            throw
        }
    }

    # Collection contains validation
    static [void] Contains([System.Collections.IEnumerable]$collection, [object]$item, [string]$message) {
        $logger = [Guard]::GetLogger()

        $logger.Trace("Guard", "Contains", "Validating collection contains item", @{
            HasCollection = $null -ne $collection
            Item = $(if ($null -ne $item) { $item.ToString() } else { "null" })
            Message = $message
        })

        try {
            [Guard]::NotNull($collection, "collection")

            $found = $false
            $elementCount = 0
            foreach ($element in $collection) {
                $elementCount++
                if ($element -eq $item) {
                    $found = $true
                    $logger.Debug("Guard", "Contains", "Item found in collection", @{
                        Item = $item
                        Position = $elementCount
                    })
                    break
                }
            }

            if (-not $found) {
                $caller = (Get-PSCallStack)[1]
                $msg = $(if ($message) { $message } else { "Collection does not contain required item: $item" })
                $errorMsg = "$msg. Called from: $($caller.Command) at line $($caller.ScriptLineNumber)"

                $logger.Error("Guard", "Contains", "Collection validation failed", @{
                    Item = $item
                    CollectionSize = $elementCount
                    Message = $message
                    Caller = $caller.Command
                    LineNumber = $caller.ScriptLineNumber
                })

                throw [System.ArgumentException]::new($errorMsg)
            }

            $logger.Trace("Guard", "Contains", "Collection validation passed")
        } catch {
            $logger.Error("Guard", "Contains", "Collection validation error", @{
                Exception = $_.Exception.Message
            })
            throw
        }
    }

    # Custom condition validation
    static [void] Condition([bool]$condition, [string]$message) {
        $logger = [Guard]::GetLogger()

        $logger.Trace("Guard", "Condition", "Validating custom condition", @{
            Condition = $condition
            Message = $message
        })

        if (-not $condition) {
            $caller = (Get-PSCallStack)[1]
            $errorMsg = "$message. Called from: $($caller.Command) at line $($caller.ScriptLineNumber)"

            $logger.Error("Guard", "Condition", "Condition validation failed", @{
                Condition = $condition
                Message = $message
                Caller = $caller.Command
                LineNumber = $caller.ScriptLineNumber
            })

            throw [System.InvalidOperationException]::new($errorMsg)
        }

        $logger.Trace("Guard", "Condition", "Condition validation passed")
    }
}

# Safe navigation helpers
class Safe {
    static hidden [object]$_logger = $null

    static [object] GetLogger() {
        if ($null -eq [Safe]::_logger) {
            [Safe]::_logger = Get-Logger
        }
        return [Safe]::_logger
    }
    # Get property value safely
    static [object] GetProperty([object]$obj, [string]$propertyName, [object]$defaultValue) {
        $logger = [Safe]::GetLogger()

        $logger.Trace("Safe", "GetProperty", "Getting property safely", @{
            HasObject = $null -ne $obj
            ObjectType = $(if ($null -ne $obj) { $obj.GetType().Name } else { "null" })
            PropertyName = $propertyName
            HasDefaultValue = $null -ne $defaultValue
        })

        try {
            if ($null -eq $obj) { 
                $logger.Debug("Safe", "GetProperty", "Object is null, returning default")
                return $defaultValue 
            }
            if ([string]::IsNullOrEmpty($propertyName)) { 
                $logger.Debug("Safe", "GetProperty", "Property name is null/empty, returning default")
                return $defaultValue 
            }

            $value = $obj.$propertyName
            $result = $(if ($null -ne $value) { $value } else { $defaultValue })

            $logger.Debug("Safe", "GetProperty", "Property retrieved successfully", @{
                PropertyName = $propertyName
                HasValue = $null -ne $value
                UsingDefault = $null -eq $value
            })

            return $result
        } catch {
            $logger.Warn("Safe", "GetProperty", "Failed to get property, returning default", @{
                PropertyName = $propertyName
                Exception = $_.Exception.Message
            })
            return $defaultValue
        }
    }

    # Invoke method safely
    static [object] InvokeMethod([object]$obj, [string]$methodName, [object[]]$args) {
        $logger = [Safe]::GetLogger()

        $logger.Trace("Safe", "InvokeMethod", "Invoking method safely", @{
            HasObject = $null -ne $obj
            ObjectType = $(if ($null -ne $obj) { $obj.GetType().Name } else { "null" })
            MethodName = $methodName
            ArgCount = $(if ($null -ne $args) { $args.Count } else { 0 })
        })

        try {
            if ($null -eq $obj) { 
                $logger.Debug("Safe", "InvokeMethod", "Object is null, returning null")
                return $null 
            }
            if ([string]::IsNullOrEmpty($methodName)) { 
                $logger.Debug("Safe", "InvokeMethod", "Method name is null/empty, returning null")
                return $null 
            }

            $result = $obj.$methodName.Invoke($args)

            $logger.Debug("Safe", "InvokeMethod", "Method invoked successfully", @{
                MethodName = $methodName
                HasResult = $null -ne $result
            })

            return $result
        } catch {
            $logger.Error("Safe", "InvokeMethod", "Failed to invoke method '$methodName'", @{
                ObjectType = $(if ($null -ne $obj) { $obj.GetType().Name } else { "null" })
                Method = $methodName
                Exception = $_.Exception.Message
                StackTrace = $_.ScriptStackTrace
            })
            return $null
        }
    }

    # Execute scriptblock safely
    static [object] Execute([scriptblock]$scriptBlock, [object]$defaultValue) {
        $logger = [Safe]::GetLogger()

        $logger.Trace("Safe", "Execute", "Executing scriptblock safely", @{
            HasScriptBlock = $null -ne $scriptBlock
            HasDefaultValue = $null -ne $defaultValue
        })

        try {
            if ($null -eq $scriptBlock) { 
                $logger.Debug("Safe", "Execute", "ScriptBlock is null, returning default")
                return $defaultValue 
            }

            $result = & $scriptBlock
            $finalResult = $(if ($null -ne $result) { $result } else { $defaultValue })

            $logger.Debug("Safe", "Execute", "ScriptBlock executed successfully", @{
                HasResult = $null -ne $result
                UsingDefault = $null -eq $result
            })

            return $finalResult
        } catch {
            $logger.Error("Safe", "Execute", "Failed to execute scriptblock", @{
                Exception = $_.Exception.Message
                StackTrace = $_.ScriptStackTrace
            })
            return $defaultValue
        }
    }

    # Try parse with default
    static [int] ParseInt([string]$value, [int]$defaultValue) {
        $logger = [Safe]::GetLogger()

        $logger.Trace("Safe", "ParseInt", "Parsing integer safely", @{
            Value = $value
            DefaultValue = $defaultValue
            IsNullOrWhiteSpace = [string]::IsNullOrWhiteSpace($value)
        })

        try {
            if ([string]::IsNullOrWhiteSpace($value)) { 
                $logger.Debug("Safe", "ParseInt", "Value is null/whitespace, returning default")
                return $defaultValue 
            }

            $result = 0
            if ([int]::TryParse($value, [ref]$result)) {
                $logger.Debug("Safe", "ParseInt", "Parse successful", @{
                    Value = $value
                    Result = $result
                })
                return $result
            }

            $logger.Debug("Safe", "ParseInt", "Parse failed, returning default", @{
                Value = $value
                DefaultValue = $defaultValue
            })
            return $defaultValue
        } catch {
            $logger.Error("Safe", "ParseInt", "Parse operation failed", @{
                Value = $value
                Exception = $_.Exception.Message
            })
            return $defaultValue
        }
    }

    # Safe array access
    static [object] GetArrayElement([array]$array, [int]$index, [object]$defaultValue) {
        $logger = [Safe]::GetLogger()

        $logger.Trace("Safe", "GetArrayElement", "Getting array element safely", @{
            HasArray = $null -ne $array
            ArrayCount = $(if ($null -ne $array) { $array.Count } else { 0 })
            Index = $index
            HasDefaultValue = $null -ne $defaultValue
        })

        try {
            $isValidAccess = $null -ne $array -and $index -ge 0 -and $index -lt $array.Count

            if (-not $isValidAccess) {
                $logger.Debug("Safe", "GetArrayElement", "Invalid array access, returning default", @{
                    HasArray = $null -ne $array
                    Index = $index
                    ArrayCount = $(if ($null -ne $array) { $array.Count } else { 0 })
                    Reason = $(if ($null -eq $array) { "ArrayIsNull" } elseif ($index -lt 0) { "IndexNegative" } else { "IndexOutOfBounds" })
                })
                return $defaultValue
            }

            $result = $array[$index]

            $logger.Debug("Safe", "GetArrayElement", "Array element retrieved successfully", @{
                Index = $index
                HasResult = $null -ne $result
            })

            return $result
        } catch {
            $logger.Error("Safe", "GetArrayElement", "Array access failed", @{
                Index = $index
                Exception = $_.Exception.Message
            })
            return $defaultValue
        }
    }

    # Safe dictionary access
    static [object] GetDictionaryValue([hashtable]$dict, [string]$key, [object]$defaultValue) {
        $logger = [Safe]::GetLogger()

        $logger.Trace("Safe", "GetDictionaryValue", "Getting dictionary value safely", @{
            HasDict = $null -ne $dict
            DictCount = $(if ($null -ne $dict) { $dict.Count } else { 0 })
            Key = $key
            HasDefaultValue = $null -ne $defaultValue
        })

        try {
            if ($null -eq $dict -or [string]::IsNullOrEmpty($key)) {
                $logger.Debug("Safe", "GetDictionaryValue", "Invalid dictionary access, returning default", @{
                    HasDict = $null -ne $dict
                    Key = $key
                    Reason = $(if ($null -eq $dict) { "DictIsNull" } else { "KeyNullOrEmpty" })
                })
                return $defaultValue
            }

            if ($dict.ContainsKey($key)) {
                $result = $dict[$key]

                $logger.Debug("Safe", "GetDictionaryValue", "Dictionary value found", @{
                    Key = $key
                    HasResult = $null -ne $result
                })

                return $result
            }

            $logger.Debug("Safe", "GetDictionaryValue", "Key not found, returning default", @{
                Key = $key
                DictKeys = ($dict.Keys -join ", ")
            })

            return $defaultValue
        } catch {
            $logger.Error("Safe", "GetDictionaryValue", "Dictionary access failed", @{
                Key = $key
                Exception = $_.Exception.Message
            })
            return $defaultValue
        }
    }
}

# Result type for operations that can fail
class Result {
    [bool]$Success
    [object]$Value
    [string]$Error
    hidden [object]$_logger

    hidden Result([bool]$success, [object]$value, [string]$error) {
        $this._logger = Get-Logger
        $this.Success = $success
        $this.Value = $value
        $this.Error = $error

        $this._logger.Trace("Result", "Constructor", "Result created", @{
            Success = $success
            HasValue = $null -ne $value
            HasError = -not [string]::IsNullOrEmpty($error)
        })
    }

    static [Result] Ok([object]$value) {
        $logger = Get-Logger
        $logger.Trace("Result", "Ok", "Creating success result", @{
            HasValue = $null -ne $value
            ValueType = $(if ($null -ne $value) { $value.GetType().Name } else { "null" })
        })

        return [Result]::new($true, $value, $null)
    }

    static [Result] Fail([string]$error) {
        $logger = Get-Logger
        $logger.Trace("Result", "Fail", "Creating failure result", @{
            Error = $error
            HasError = -not [string]::IsNullOrEmpty($error)
        })

        return [Result]::new($false, $null, $error)
    }

    [bool] IsOk() { 
        $this._logger.Trace("Result", "IsOk", "Checking if result is success", @{
            Success = $this.Success
        })
        return $this.Success 
    }

    [bool] IsFail() { 
        $this._logger.Trace("Result", "IsFail", "Checking if result is failure", @{
            Success = $this.Success
            IsFail = -not $this.Success
        })
        return -not $this.Success 
    }
}

﻿# SpeedTUI Performance Monitor - Real-time performance tracking and metrics

using namespace System.Diagnostics
using namespace System.Collections.Concurrent

class PerformanceMetric {
    [string]$Name
    [DateTime]$Timestamp
    [double]$Value
    [string]$Unit
    [hashtable]$Tags

    PerformanceMetric([string]$name, [double]$value, [string]$unit = "", [hashtable]$tags = @{}) {
        $this.Name = $name
        $this.Timestamp = [DateTime]::Now
        $this.Value = $value
        $this.Unit = $unit
        $this.Tags = $tags
    }

    [string] ToString() {
        $tagStr = $(if ($this.Tags.Count -gt 0) {
            " (" + ($this.Tags.GetEnumerator() | ForEach-Object { "$($_.Key)=$($_.Value)" }) -join ", " + ")"
        } else { "" })

        return "$($this.Timestamp.ToString('HH:mm:ss.fff')) $($this.Name): $($this.Value)$($this.Unit)$tagStr"
    }
}

class TimingContext {
    [string]$Operation
    [Stopwatch]$Stopwatch
    [hashtable]$Tags
    [DateTime]$StartTime

    TimingContext([string]$operation, [hashtable]$tags = @{}) {
        $this.Operation = $operation
        $this.Tags = $tags
        $this.StartTime = [DateTime]::Now
        $this.Stopwatch = [Stopwatch]::StartNew()
    }

    [void] Stop() {
        $this.Stopwatch.Stop()
    }

    [double] GetElapsedMilliseconds() {
        return $this.Stopwatch.ElapsedMilliseconds
    }
}

class PerformanceMonitor {
    [ConcurrentQueue[PerformanceMetric]]$Metrics
    [ConcurrentDictionary[string, object]]$Counters
    [bool]$Enabled = $true
    [int]$MaxMetrics = 10000
    [string]$LogFile
    [object]$Logger

    static [PerformanceMonitor]$Instance

    PerformanceMonitor() {
        $this.Metrics = [ConcurrentQueue[PerformanceMetric]]::new()
        $this.Counters = [ConcurrentDictionary[string, object]]::new()
        $this.LogFile = "Logs/performance_$(Get-Date -Format 'yyyyMMdd').log"

        # Ensure logs directory exists
        $logsDir = Split-Path $this.LogFile -Parent
        if (-not (Test-Path $logsDir)) {
            New-Item -Path $logsDir -ItemType Directory -Force | Out-Null
        }
    }

    static [PerformanceMonitor] GetInstance() {
        if (-not [PerformanceMonitor]::Instance) {
            [PerformanceMonitor]::Instance = [PerformanceMonitor]::new()
        }
        return [PerformanceMonitor]::Instance
    }

    [void] SetLogger([object]$logger) {
        $this.Logger = $logger
    }

    [void] RecordMetric([string]$name, [double]$value, [string]$unit = "", [hashtable]$tags = @{}) {
        if (-not $this.Enabled) { return }

        $metric = [PerformanceMetric]::new($name, $value, $unit, $tags)
        $this.Metrics.Enqueue($metric)

        # Maintain max metrics limit
        while ($this.Metrics.Count -gt $this.MaxMetrics) {
            $null = $this.Metrics.TryDequeue([ref]$null)
        }

        # Log to file and logger if available
        $this.LogMetric($metric)
    }

    [TimingContext] StartTiming([string]$operation, [hashtable]$tags = @{}) {
        return [TimingContext]::new($operation, $tags)
    }

    [void] EndTiming([TimingContext]$context) {
        if (-not $this.Enabled) { return }

        $context.Stop()
        $elapsed = $context.GetElapsedMilliseconds()

        $this.RecordMetric(
            "timing.$($context.Operation)", 
            $elapsed, 
            "ms", 
            $context.Tags
        )
    }

    [void] IncrementCounter([string]$name, [hashtable]$tags = @{}) {
        if (-not $this.Enabled) { return }

        $key = $name
        if ($tags.Count -gt 0) {
            $tagStr = ($tags.GetEnumerator() | Sort-Object Key | ForEach-Object { "$($_.Key)=$($_.Value)" }) -join ","
            $key = "$name[$tagStr]"
        }

        $newValue = $this.Counters.AddOrUpdate($key, 1, { param($k, $v) $v + 1 })
        $this.RecordMetric("counter.$name", $newValue, "count", $tags)
    }

    [void] SetGauge([string]$name, [double]$value, [hashtable]$tags = @{}) {
        if (-not $this.Enabled) { return }

        $this.RecordMetric("gauge.$name", $value, "", $tags)
    }

    [void] RecordSystemMetrics() {
        if (-not $this.Enabled) { return }

        try {
            # Memory metrics
            $processId = [System.Diagnostics.Process]::GetCurrentDoProcess().Id
            $currentProcess = Get-Process -Id $processId
            $this.SetGauge("system.memory.working_set", $currentProcess.WorkingSet64 / 1MB, @{unit="MB"})
            $this.SetGauge("system.memory.private", $currentProcess.PrivateMemorySize64 / 1MB, @{unit="MB"})

            # CPU metrics
            $this.SetGauge("system.cpu.time", $currentProcess.TotalProcessorTime.TotalMilliseconds, @{unit="ms"})

            # Thread metrics
            $this.SetGauge("system.threads", $currentProcess.Threads.Count, @{unit="count"})

            # Handle metrics
            $this.SetGauge("system.handles", $currentProcess.HandleCount, @{unit="count"})

            # GC metrics
            $gen0 = [GC]::CollectionCount(0)
            $gen1 = [GC]::CollectionCount(1)
            $gen2 = [GC]::CollectionCount(2)
            $totalMemory = [GC]::GetTotalMemory($false) / 1MB

            $this.SetGauge("system.gc.gen0", $gen0, @{unit="count"})
            $this.SetGauge("system.gc.gen1", $gen1, @{unit="count"})
            $this.SetGauge("system.gc.gen2", $gen2, @{unit="count"})
            $this.SetGauge("system.gc.total_memory", $totalMemory, @{unit="MB"})

        } catch {
            if ($this.Logger) {
                $this.Logger.Error("PerformanceMonitor", "RecordSystemMetrics", "Failed to record system metrics: $($_.Exception.Message)")
            }
        }
    }

    [PerformanceMetric[]] GetMetrics([int]$limit = 100) {
        $resultMetrics = [System.Collections.Generic.List[PerformanceMetric]]::new()
        $tempQueue = [ConcurrentQueue[PerformanceMetric]]::new()

        # Dequeue metrics up to limit
        $count = 0
        $currentMetric = $null
        while ($count -lt $limit -and $this.Metrics.TryDequeue([ref]$currentMetric)) {
            $resultMetrics.Add($currentMetric)
            $tempQueue.Enqueue($currentMetric)
            $count++
        }

        # Put them back
        while ($tempQueue.TryDequeue([ref]$currentMetric)) {
            $this.Metrics.Enqueue($currentMetric)
        }

        return $resultMetrics.ToArray()
    }

    [PerformanceMetric[]] GetMetricsByName([string]$name, [int]$limit = 100) {
        $allMetrics = $this.GetMetrics(1000)  # Get more to filter
        $filtered = $allMetrics | Where-Object { $_.Name -like "*$name*" } | Select-Object -First $limit
        return $filtered
    }

    [hashtable] GetCounters() {
        $result = @{}
        foreach ($kvp in $this.Counters.GetEnumerator()) {
            $result[$kvp.Key] = $kvp.Value
        }
        return $result
    }

    [hashtable] GetSummaryStats([string]$metricName) {
        $foundMetrics = $this.GetMetricsByName($metricName, 1000)

        if ($foundMetrics.Count -eq 0) {
            return @{
                Count = 0
                Min = 0
                Max = 0
                Average = 0
                Total = 0
                Latest = 0
                Oldest = 0
            }
        }

        $values = $foundMetrics | ForEach-Object { $_.Value }

        return @{
            Count = $foundMetrics.Count
            Min = ($values | Measure-Object -Minimum).Minimum
            Max = ($values | Measure-Object -Maximum).Maximum
            Average = ($values | Measure-Object -Average).Average
            Total = ($values | Measure-Object -Sum).Sum
            Latest = $foundMetrics[0].Value
            Oldest = $foundMetrics[-1].Value
        }
    }

    [void] LogMetric([PerformanceMetric]$metric) {
        try {
            # Log to file
            Add-Content -Path $this.LogFile -Value $metric.ToString() -ErrorAction SilentlyContinue

            # Log to logger if available
            if ($this.Logger) {
                $this.Logger.Debug("PerformanceMonitor", "Metric", $metric.ToString())
            }
        } catch {
            # Silently fail to avoid recursion issues
        }
    }

    [string] GenerateReport() {
        $report = @()
        $report += "SpeedTUI Performance Report"
        $report += "=========================="
        $report += "Generated: $(Get-Date)"
        $report += ""

        # System metrics summary
        $report += "System Metrics:"
        $systemMetrics = $this.GetMetricsByName("system", 50) | Group-Object Name
        foreach ($group in $systemMetrics) {
            $latest = $group.Group | Sort-Object Timestamp -Descending | Select-Object -First 1
            $report += "  $($group.Name): $($latest.Value)$($latest.Unit)"
        }
        $report += ""

        # Timing summary
        $report += "Performance Timings:"
        $timingMetrics = $this.GetMetricsByName("timing", 100) | Group-Object Name
        foreach ($group in $timingMetrics) {
            $stats = $this.GetSummaryStats($group.Name)
            $report += "  $($group.Name):"
            $report += "    Count: $($stats.Count)"
            $report += "    Average: $([Math]::Round($stats.Average, 2))ms"
            $report += "    Min: $([Math]::Round($stats.Min, 2))ms"
            $report += "    Max: $([Math]::Round($stats.Max, 2))ms"
        }
        $report += ""

        # Counter summary
        $report += "Counters:"
        $allCounters = $this.GetCounters()
        foreach ($counter in $allCounters.GetEnumerator() | Sort-Object Key) {
            $report += "  $($counter.Key): $($counter.Value)"
        }

        return $report -join "`n"
    }

    [void] ClearMetrics() {
        while ($this.Metrics.TryDequeue([ref]$null)) { }
        $this.Counters.Clear()
    }

    [void] Enable() {
        $this.Enabled = $true
        if ($this.Logger) {
            $this.Logger.Info("PerformanceMonitor", "Enable", "Performance monitoring enabled")
        }
    }

    [void] Disable() {
        $this.Enabled = $false
        if ($this.Logger) {
            $this.Logger.Info("PerformanceMonitor", "Disable", "Performance monitoring disabled")
        }
    }
}

# Global function for easy access
function Get-PerformanceMonitor {
    return [PerformanceMonitor]::GetInstance()
}

# Helper functions for common operations
function Start-PerformanceTiming {
    param(
        [string]$Operation,
        [hashtable]$Tags = @{}
    )

    $monitor = Get-PerformanceMonitor
    return $monitor.StartTiming($Operation, $Tags)
}

function Stop-PerformanceTiming {
    param(
        [TimingContext]$Context
    )

    $monitor = Get-PerformanceMonitor
    $monitor.EndTiming($Context)
}

function Record-PerformanceMetric {
    param(
        [string]$Name,
        [double]$Value,
        [string]$Unit = "",
        [hashtable]$Tags = @{}
    )

    $monitor = Get-PerformanceMonitor
    $monitor.RecordMetric($Name, $Value, $Unit, $Tags)
}

function Increment-PerformanceCounter {
    param(
        [string]$Name,
        [hashtable]$Tags = @{}
    )

    $monitor = Get-PerformanceMonitor
    $monitor.IncrementCounter($Name, $Tags)
}

